INSERT INTO `dokumente` (`id`, `titel`, `dok_art`, `datum`, `datumtext`, `thematik`, `thematiktext`, `thema`, `feld1`, `feld2`, `dok_fundort`, `dok_fundorttext`, `grafik_ja`, `text`, `gif`, `previous`, `next`) VALUES (1, 'Brief von Bezirkshauptmann Heerwagen an Kirchenverwaltung, 18.1.1895', 1, '1895-01-18', '18.01.1895', 1, 'Kirchenmusik', '&auml;ltestes Dokument zur Kirchenmusik im Archiv der Pfarrei Ruhmannsfelden, Chorregentendiensteinteilung unter den Lehrern Gratzl, Krieger und Weig', 'Bezirkshauptmann Heerwagen', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'Viechtach, den 18. Januar 1895<br>\r\nN 69.<br>\r\n<br>\r\nKgl. Bezirksamt<br>\r\nViechtach<br>\r\n<br>\r\nBetreff:<br>\r\n<br>\r\nVerwertung des Me&szlig;ner und<br>\r\nChorregentendienstes in Ruhmannsfelden<br>\r\n<br>\r\nAn <br>\r\ndas k Pfarramt<br>\r\nRuhmannsfelden<br>\r\n<br>\r\nIn Erwiderung sehr gesch&auml;tzter Zuschrift vom 7. d. M. beehre ich mich ergebenst mitzuschreiben, da&szlig; nach der Erkl&auml;rung des k. Distiktsschulinspektion Viechtach II rein Rechtlich der Hilfslehrer Gratzl zum Chorregenten wegen seiner von liturgischen Erfahrungen mehr eignet als der Aushilfslehrer Krieger zum zweifachen Chor und Organistendienste.<br>\r\nBei Weigerung des Lehrers Weig f&uuml;r die Dienste seitens des Chores eine geeignete Pers&ouml;nlichkeit aufzustellen d&uuml;rfte zun&auml;chst die kgl. Distriktschulinspektion Viechtach II zur Einschreitung gegen Weig zust&auml;ndig sein.<br>\r\n<br>\r\nder k. Bezirkshautmann<br>\r\n<br>\r\nHeerwagen', '<p>\n<a href="scanns/1_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/1_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/1_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/1_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 97, 111),
(2, 'Brief von Max Rauscher an Bisch&ouml;fliche Ordinariat Regensburg, 4.11.1926', 1, '1926-11-04', '04.11.1926', 1, 'Kirchenmusik', 'Eingabe von Max Rauscher an das Bisch&ouml;fliche Ordinariat von Regensburg um Gehaltserh&ouml;hung', 'Max Rauscher', 'Bisch&ouml;fliche Ordinariat Regensburg', 2, 'Pfarrarchiv', 0, 'An das<br>\r\nHochw&uuml;rdigste bisch&ouml;fliche Ordinariat Regensburg.<br>\r\n<br>\r\nDer ergebenst unterfertige Max Rauscher, Chorregent in Ruhmannsfelden gestattet sich dem h&ouml;chw&uuml;rdigsten Ordinariat nachstehendes h&ouml;fl. und vertrauensvoll zu unterbreiten.<br>\r\nIch habe meiner Kirchenverwaltung die Bitte unterbreitet meine Anstellungs- und Gehaltsverh&auml;ltnisse nach den Richtlinien der hohen Kirchenbeh&ouml;rden zu regeln. Herr Pfarrer Fahrmeier hat mein Ersuchen abgelehnt. Er hat im Anschlu&szlig; an seine Ablehnung evt. K&uuml;ndigung in Aussicht gestellt.<br>\r\nMein heutiges Fixum ist monatlich Mk: 33.- Au&szlig;erdem beziehe ich die Stdgeb&uuml;ren. Ich bitte das hochw&uuml;rdigste Ordinariat ebenso h&ouml;flich wie dringend sofort eine kirchenbeh&ouml;rdliche Entscheidung zu f&auml;llen.<br>\r\nIch habe von meiner Kirchenverwaltung die Gruppe VI der St. B. O., sowie Mk: 30.- Ortszulage zu beanspruchen in der H&ouml;he von 180.- und Mk: 30,- = 210 Mk monatlich. Kirchensteuer hat die Gemeinde Ruhmannsfelden noch nicht erhoben. Da&szlig; die Kirchensteuer entrichtet werden mu&szlig;, haben nicht die Pfarrer oder die kirchlichen Beh&ouml;rden angeordnet sondern der bayerische Landtag durch das religionsgesellschaftliche Steuergesetz vom 1. August 1923.<br>\r\nIch bitte daher die Gehaltssache innerhalb 8 Tagen zu regeln. Sollte Herr Pfarrer Fahrmeier von sich nichts h&ouml;ren lassen, so m&uuml;&szlig;te ich die Sache gerichtlich &uuml;bergeben und meine b&uuml;rgerlichen Rechte in Anspruch nehmen. Der Ausgang der Rechtsstreites w&uuml;rde f&uuml;r mich auf alle F&auml;lle der denkbar beste sein, denn die bisch&ouml;flichen Beh&ouml;rden haben selbst erkl&auml;rt, da&szlig; es den Kirchenbeamten frei steht, die Gemeinden zu Erreichung ihres Zieles vor den ordentlichen Gerichten zu verklagen.<br>\r\nM&ouml;chte auch noch bemerken, da&szlig; die Gemeinde Ruhmannsfelden auch Parochiamen Wert auf gute Kirchenmusik legt und mit meiner Leistung voll und ganz zufrieden ist.<br>\r\nVertrauensvoll der sofortigen Bescheid der hohen Kirchenbeh&ouml;rde erwartend, zeichne ich<br>\r\n<br>\r\nmit vollster Hochachtung ergebenst<br>\r\n<br>\r\nMax Rauscher, Chorregent.', '<p>\n<a href="scanns/2_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/2_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/2_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/2_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 23, 3),
(3, 'Brief von Max Rauscher an Pfarrer Fahrmeier, 15.11.1926', 1, '1926-11-15', '15.11.1926', 1, 'Kirchenmusik', 'Anwort auf erhaltene R&uuml;ge', 'Max Rauscher', 'Pfarrer Fahrmeier', 2, 'Pfarrarchiv', 0, 'Hochw&uuml;rdiger Herrn Pfarrer Fahrmeier!<br>\r\n<br>\r\n&Uuml;ber Ihre gestrige Zurrechtweisung, die ich gerade nicht vorbildlich nennen kann, erlaube ich mir Ihnen nahe zu legen, da&szlig; - wenn Sie gegen mein Verhalten am Kirchenchor Kritik &uuml;ben wollen, sie jederzeit das Recht haben mich pers&ouml;nlich rufen zu lassen.<br>\r\n&Ouml;ffentlich vor meiner versammelten S&auml;ngerschar auff&auml;lligen Streit, oder R&uuml;gen zu erteilen, verbiete ich mir und wiese selbe ganz energisch zur&uuml;ck.<br>\r\nEs w&auml;re besser am Platze gewesen, diese zur Zeit von Lehrer H&ouml;gn, in Anwendung gebracht zu haben, nachdem damals nicht blo&szlig; Lekt&uuml;re w&auml;hrend des Gottesdienstes gelesen, sondern von seiner Tochter Liebeleien getrieben und Schokolade gegessen wurde. Wenn ich fremde G&auml;ste anstandsgem&auml;&szlig; begr&uuml;&szlig;e d&uuml;rfte Ihr Verhalten wie allgemein unter den versammelten Mitwirkenden laut wurde, ganz unangebracht gewesen sein.<br>\r\nIch w&uuml;rde bei evtl. Wiederholung sofort Beschwerde an das Bisch&ouml;fl. Ordinariat einleiten.<br>\r\nFerner erlaube ich mir die Steuerforderung vom Finanzamt beizulegen, nachdem es mir mit dem derzeitigen Gehalt nicht m&ouml;glich ist selbe zu bezahlen und auch kein Recht besteht, da&szlig; von mir Steuer gefordert werden kann, solange meine Gehaltssache nicht erledigt ist.<br>\r\nWollen Sie das weitere mit dem Finanzamt selbst in Erledigung bringen.<br>\r\n<br>\r\nDies zur Kenntnis.<br>\r\n<br>\r\nErgegenster<br>\r\n<br>\r\nMax Rauscher<br>\r\nChorregent<br>', '<p>\n<a href="scanns/3_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/3_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/3_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/3_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 2, 4),
(4, 'Brief von August H&ouml;gn an Kirchenrat, Dez. 1926', 1, '1926-12-00', 'Dez. 1926', 1, 'Kirchenmusik', 'Beschwerde wegen Anschuldigungen von Max Rauscher', 'August H&ouml;gn', 'Kirchenrat', 2, 'Pfarrarchiv', 0, 'An den sehr verehrlichen<br>\r\nKirchenrat der Pfarrei Ruhmannsfelden<br>\r\n<br>\r\nErw. Hochwohlgeboren!<br>\r\n<br>\r\nDie ungezogenen Anschuldigungen des Max Rauscher jr. in dem Brief an H. H. Pfarrer Fahrmeier dr&uuml;cken mir die Feder in die Hand mit der Bitte an den sehr verehrlichen Kirchenrat die unqualiflizierbare Frechheit des Max Rauscher jr. gegen&uuml;ber H. H. Pfarrer Fahrmeier mit den dem Kirchenrat zu Gebote stehenden Mitteln zu f&uuml;hren. Ein Mensch, der so r&uuml;cksichtslos vorgeht wie Max Rauscher jr., der verdient keine Ber&uuml;cksichtigung, ein Angestellter, der so vorgeht gegen seinen Vorgesetzten, der wird an allen Orten u. zu allen Zeiten in allen Landen von seiner Dienststelle entlassen.<br>\r\nIn der Wahrung der Autorit&auml;t u. im Ansehen unseres hochverdienten u. beliebten H. H. Pfarrer Fahrmeier ersuche ich den Max Rauscher zu k&uuml;ndigen, damit endlich diesem unerh&ouml;rten Treiben dieses jungen Mannes Einhalt geboten ist u. nicht derselbe u. noch andere mit in der Selbst&uuml;berhebung u. Geringeinsch&auml;tzung anderer gest&auml;rkt werden. Das Verhalten beider Rauscher S&ouml;hne gegen&uuml;ber H. H. Rauscher wird allgemein aufs sch&auml;rfste verurteilt, eine dementsprechende Ma&szlig;nahme seitens des sehr verehrlichen Kirchenrates von der Gesamtheit erwartet.<br>\r\n<br>\r\n<br>\r\nMit vorz&uuml;glicher Hochachtung!<br>\r\nErgebenster!<br>\r\nAugust H&ouml;gn<br>\r\nHauptlehrer', '<p>\n<a href="scanns/4_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/4_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/4_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/4_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 3, 5),
(5, 'Protokoll der Kirchenverwaltungssitzung, 30.12.1926', 3, '1926-12-30', '30.12.1926', 1, 'Kirchenmusik', 'Beschluss der K&uuml;ndigung des Chorregenten Max Rauscher', 'Protokoll der Kirchenverwaltungssitzung, 30.12.1926', '', 2, 'Pfarrarchiv', 0, 'P R O T O K O L L<br>\r\n&uuml;ber die<br>\r\nSitzung der Kath. Kirchenverwaltung Ruhmannsfelden vom<br>\r\n30. Dezember 1926.<br>\r\n<br>\r\nGegenw&auml;rtig:<br>\r\nDer Kirchenverwaltungsvorstand<br>\r\nFahrmeier<br>\r\ndie Kirchenverwaltungsmitglieder<br>\r\nFenzl<br>\r\nKraus<br>\r\nSchwarz<br>\r\nH&auml;rtl<br>\r\nZur Sitzung nicht erschienen:<br>\r\n____________<br>\r\nZu der auf heute Vormittags 8 1/4 Uhr im Pfarrhof anberaumten Sitzung wurden die s&auml;mtlichen Mitglieder der Kirchenverwaltung (Artikel 37 Absatz I der Kirchengemeindeordnung) richtig geladen.<br>\r\nVon den Geladenen sind die Nebenbezeichneten erschienen.<br>\r\nDie Mitgliederzahl der Kirchenverwaltung nach dem Sollstande (Art. 37 Abs. I der Kirchengemeindeordnung) betr&auml;gt 6; an der Beratung und Abstimmung haben die Erschienen - 6 an der Zahl - , also mehr als die H&auml;lfte aller Mitglieder teilgenommen.<br>\r\nAuf Vortrag der Kirchenverwaltungsvorstandes und nach eingehender Beratung wurde folgende Beschl&uuml;sse - soweit bei ihnen nichts anderes vermerkt ? <br>\r\ne i n s t i m m i g und in &ouml;ffentlicher Sitzung (KGO. Artikel 63 Absatz II, Allgem VV. &sect; 13 Absatz II) gefa&szlig;t:<br>\r\n<br>\r\nDer mit dem Chorregenten Max Rauscher von Ruhmannsfelden am 14. Dezember 1924 aufgestellte Dienstvertrag wird ab 1. Januar 1927 gek&uuml;ndigt aus folgenden Gr&uuml;nden:<br>\r\na) Wegen des pflichtwidrigen und ungeh&ouml;rigen Betragens gegen&uuml;ber Herrn Pfarrer Fahrmeier - dieser  K&uuml;ndigungsgrund wurde ohne Mitwirkung des Herrn Kirchenverwaltungsvorstandes ausgesprochen - , ferner wegen seines nicht v&ouml;llig einwandfreien sittlichen Betragens.<br>\r\nb) wegen seiner jeder Rechtsgrundlage entbehrenden Beschwerde bei der Regierung von Niederbayern u. bei dem Ordinariat Regensburg,<br>\r\nc) wegen &uuml;bertriebener Gehaltsforderungen,<br>\r\nd) wegen nachgewiesener &Uuml;berforderung von Stolarien.<br>\r\nDie Kirchenverwaltung ist bereit einen neuen Dienstvertrag ab 1. April 1927 im M&auml;rz 1927 mit Herrn Rauscher Max abzuschlie&szlig;en unter folgenden Bedingungen:<br>\r\n1. In der Kirchenratssitzung Abbitte zu leisten,<br>\r\n2. auf einen neuentworfenen Dienstvertrag einzugehen.<br>\r\nV. g. u. u.<br>\r\n<br>\r\nGenaue Kenntnis genommen<br>\r\nAm 30. XII. 26<br>\r\nLt. U. (gez.)<br>\r\nMax Rauscher.<br>\r\n<br>\r\n(gez.) Fahrmeier, Vorstand<br>\r\n   "     Fenzl,<br>\r\n   "     Kraus,<br>\r\n   "     Schwarz,<br>\r\n   "     Stadler,<br>\r\n   "     H&auml;rtl', '<p>\n<a href="scanns/5_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/5_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/5_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/5_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 4, 6),
(6, 'Brief von Max Rauscher sen. an Kirchenverwaltung, 14.3.1927', 1, '1927-03-14', '14.03.1927', 1, 'Kirchenmusik', 'Bitte des Vaters f&uuml;r mehr Besoldung f&uuml;r den Sohn', 'Max Rauscher sen.', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'An die verehrl. Kirchenverwaltung hier!<BR><BR>Betreff.<BR>Besoldungsfrage des Chorregenten.<BR><BR>Wenn ich mir in dieser Frage, als Vater des Chorregent, Stellung zu nehmen erlaube, so wollen die Herren H&ouml;sl entschuldigen, denn ich erkenne mit Recht, hierin seit 2 1/2 Jahren gewi&szlig;en Einblick in diesen Bereich genommen zu haben + meine finanziellen Leistung zur Hebung unserer Kirchenmusik d&uuml;rften weit &uuml;ber den der Kirchenratsmitglieder stehen.<BR>An der Aufl&ouml;sung des fr&uuml;heren Dienstleistungsvertrag w&auml;re ja an sich selbst nichts zu beanstanden, da er sich schon Anfangs als wertlos erwiesen hat, doch bin ich &uuml;ber dessen Begr&uuml;ndung noch nicht ganz im Klarem, nachdem ich eine Forderung um gewollte Besold als Grund nicht ansehen kann, somit dieses Urteil, da&szlig; sehr viel &Auml;hnlichkeit mit Versailes zeigt, mir bekanntlich Kl&auml;ger + Richter in einer Person fungirten, wirklicher Reformierung bed&uuml;rftig erscheint.<BR>Ich mu&szlig; offen gestehen, da&szlig; ich &uuml;ber das Verhalten der Kirchenratsmitglieder in dieser Sache sehr entt&auml;uscht bin. Einige &auml;ltere Kirchenratsmitglieder werden sich noch errinnern k&ouml;nnen, da&szlig; z. Z. Herrn Lehrer Weig unser Kirchenchor einen guten Namen hatte. Es kam dann die Zeit der Anna Auer, wo R-felden den Niedergang der Kirchenmusik erlebte, der unseren Ort wahrlich nicht zum Ruhme gereichte.<BR>Es ist deshalb sehr zu begr&uuml;&szlig;en, da&szlig; selbe durch Herrn H&ouml;gn eine Verbesserung erfuhr.<BR>Hat man bei &Uuml;bernahme dieses Posten meinen Sohn gegen&uuml;ber, wenig Vertrauen gesetzt, so glaube ich heute sagen zu d&uuml;rfen, da&szlig; er seine Aufgabe voll + ganz erf&uuml;llte, die im als Berufschorregent zur Pflicht auferlegt ist + die es m&ouml;glich machte unseren Kirchenchor in vollendeter H&ouml;he zu sehen.<BR>Gestatten Sie mir die Frage, hat nun dies zu erm&ouml;glichen, auch der Kirchenrat seine Bestand beizutragen?<BR>Ich mu&szlig; es mit - Nein - beantworten, sonst w&auml;re es nicht m&ouml;glich, da&szlig; das bisher geleistete so schlechte W&uuml;rdigung ungeeignete Besoldung gefunden h&auml;tte.<BR>Wo Pflichte sind, m&uuml;ssen auch Rechte bestehen + wenn der Chorregent die Pflicht hat, das beste in Kirchenmusik f&uuml;r eine Marktgemeinde zu leisten, auf die es ein Anrecht hat, so d&uuml;rfte es auch Plicht der verehrl. Kirchenverwaltung sein, den Gehalt eines Chorregent nicht auf die Stufe eine Polizeidieners zu stellen.<BR>Es zeigt sich, da&szlig; bei uns das sozialle Verst&auml;ndnis fehlt, m&ouml;cht ich hierbei nur an einer Oster- oder Weihnachtsauff&uuml;hrung errinnern, die uns jedesmal mit Ankauf von neuen Messen &uuml;ber M 100 zu stehen bekommt, da&szlig; genau dem Gehalt von 1/4 Jahr entsprach.<BR>Was sonst an direkten Auslagen enstehen, will ich gar nicht erw&auml;hnen, obwohl selbe schon oft bedeutend in die Waagschale fielen.<BR>Oder denkt die verehrl. Kirchenverwaltung, da&szlig; eine Zahl von 30 - 35 Mitwirkenden, so ohne alles seinen Dienste anbietet? - Nein - einen Chor hierorts auf der H&ouml;he zu halten, stellt ganz andere Forderungen + sind unsere wenigen Kr&auml;fte oft schneller verscheut als gewonnen, was sich bekanntlich kurz vor Weihnachten zeigte.<BR>Ist dem verehrl. Kirchenverwaltung auch bekannt, wie notwenig es ist, neue Kr&auml;fte f&uuml;r den Chorgesang auszubilden.<BR>Was dann? - wenn die Frl. Raster einmal dem Gesang den R&uuml;cken kehren?<BR>Mein Sohn w&auml;re auf Ausbildung besorgt gewesen. Allein ich konnte meiner Frau nicht zumuten w&ouml;chtenlich 2 mal den Reinigungsdienst bei Gesangsstunden der Jugend zu &uuml;bernehmen, wenn weder Dank noch Entschuldigung von Seiten der Eltern noch Beh&ouml;rden vorhanden ist.<BR>Unsere R&uuml;ckst&auml;ndigkeit im Orte ist &uuml;berall hinreichend bekannt. Deshalb meine ich, ist es besondere Pflicht das Erreichte nicht wieder verkommen zu lassen. Ich, wie meine S&ouml;hne, haben bisher alle Opfer gebracht, um Ruhmannsfelden ich welt + kirchliche Musik zu heben. Wen es leider zu Unm&ouml;glichkeit gemacht wird, f&auml;llt die Schuld auf jene, die auf eine Verbesserung dieser Zustande keinen Anteil haben wollen.<BR>Anf&uuml;gend nur davon ein Beispiel.<BR>Mein Sohn Siegfried der allsont&auml;glich in seiner zust&auml;ndigen Kirche M&uuml;nchens beim Gottesdienst mitwirkt, wurde vom dortigen Pfarrer M 20 angeboten, wenn er die Weihnachttage seine Mitwirkung zusagen w&uuml;rde. Nein - er lehnte dankend ab, mit der Begr&uuml;ndung in seinem Heimatort helfen zu m&uuml;ssen.<BR>Und in welcher Form in hier der Dank gebracht? Er wollte keinen Dank, was aber glauben die Herren, welchen Eindruck auf ihn die Mitteilung machte, das man am Jahrerschlu&szlig;, wo es sonst in hocht&ouml;nenden Worten &uuml;blich war, den Mitwirkenden am Kirchenchor zu danken, seinen Bruder die aufopfernde M&uuml;he, Kosten und Arbeit eines Neujahrswunsch, in Form eines Mi&szlig;trauensvotum + Degradierung brachte, gegen die er sich nicht einmal verteidigen konnte, da er schon vor vollendeter Tatsachen gestellt war.<BR>Wenn man auf der einen Seite durch das Get&ouml;se von Herrn Amberger, einer &Uuml;bertretung der festgelegten Taxe glaubt feststellen zu k&ouml;nnen, was in Wirklichkeit nicht der Wahrheit entspricht, warum sieht man auf der anderen Seite nicht die vielen Aushilfen von Beerdigungen und sonstigen Verrichtungen, die dem Chorregenten durch Nichtbezahlung entgehen?<BR>Ich meine, wo ein Wille ist, ist auch ein Weg zu finden, wodurch der Chorregent in der Lage versetzt wird, mit voller Kraft mit gerechter Besoldung in seinem Amt zum Ansehen unseres Orts arbeiten k&ouml;nnen. <BR><BR>In aller Hochachtung<BR>zeichnet<BR>M. Rauscher<BR>Conditor<BR><BR>NB. Eine Erkl&auml;rung meines Sohns d&uuml;rfte sich nach oben angef&uuml;hrten er&uuml;brigen. ', '<p>\n<a href="scanns/6_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/6_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/6_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/6_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/6_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/6_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/6_4_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/6_4_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 5, 9),
(7, 'Briefumschlag an Musikmeister Josef Meindl, Plattling, 15.9.43', 3, '1943-10-01', '01.10.1943', 4, 'Auff&uuml;hrungen von Kompositionen', 'Briefumschlag an Musikmeister Josef Meindl, Plattling', 'Briefumschlag an Musikmeister Josef Meindl, Plattling, 15.9.43', '', 3, 'Beilage zum Notenmaterial', 0, 'M&uuml;nchen, Hauptstadt der Bewegung 19.9.43<br>\r\n<br>\r\nAbsender: A. H&ouml;gn, Ruhmannsfelden<br>\r\nBesondere Vermerke des Absenders: Einschreiben<br>\r\nAn<br>\r\nH. Josef Meindl<br>\r\nMusikmeister<br>\r\nin Plattling<br>\r\nStraubingerstr.', '<p>\n<a href="scanns/7_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/7_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 195, 175),
(8, 'Dienstvertrag f&uuml;r Albert Schroll/Georg Ro&szlig;nagel, 31.7.1929/7.6.1927', 3, '1929-07-31', '31.07.1929', 1, 'Kirchenmusik', 'Dienstvertrag f&uuml;r Albert Schroll (Georg Ro&szlig;nagel) Umgearbeiteter Vertrag als Zweitschrift f&uuml;rs Pfarrarchiv', 'Dienstvertrag f&uuml;r Albert Schroll/Georg Ro&szlig;nagel, 31.7.1929/7.6.1927', '', 2, 'Pfarrarchiv', 0, 'D I E N S T = V E R T R A G<br>\r\n<br>\r\n&uuml;ber Besorgung des Chor= u.Organistendienstes an der Pfarrkirche zu Ruhmannsfelden.<br>\r\n________________<br>\r\n<br>\r\nZwischen der Kath.Kirchenverwaltung Ruhmannsfelden und dem Chorregenten Herrn Albert Schroll (Georg Ro&szlig;nagel) (durchgestrichen) geboren am ...zu ..... wird folgender Dienstvertrag abgeschlossen:<br>\r\n<br>\r\n&sect; 1.<br>\r\n<br>\r\nDie Kirchenerwaltung Ruhmannsfelden &uuml;bertr&auml;gt ab 1. August 1929 Herrn Schroll (Rossnagel) die nicht vollbesch&auml;ftigte Stelle des Chorregenten in Ruhmannsfelden zur Besorgung des Chor= u.Organistendienstes in der Pfarrkirche zu Ruhmannsfelden in jederzeit widerruflicher Weise.<br>\r\n<br>\r\n&sect; 2.<br>\r\n<br>\r\nAls Inhaber des Chordienstes verpflichtet sich Schroll (Rossnagel) bei allen &Auml;mtern,Begr&auml;bnissen,Vigilien,Vespern, Prozessionen, Nachmittagsandachten, Maiandachten, u. dgl. mitzuwirken mit Gesang und Orgel in herk&ouml;mmlicher Weise.<br>\r\n<br>\r\n&sect; 3.<br>\r\n<br>\r\nAls Entsch&auml;digung f&uuml;r seine Dienstleistungen erh&auml;lt Schroll (Rossnagel) ein monatliches Fixum von 100 RM (m. W. Einhundert R Mark) am Monatsende gegen Quittung durch Herrn Kirchenpfleger ausbezahlt. Au&szlig;erdem erh&auml;lt er f&uuml;r jedes bestellte Grablied den Betrag von<br>\r\n5 RM jedesmal nach Vereinnahmung durch Herrn Kirchenverwaltungsvorstand gesondert &uuml;bersandt. Bei zufriedenstellender Dienstleistung wird eine m&auml;&szlig;ige Gehaltsaufbesserung in Aussicht gestell.<br>\r\n<br>\r\n&sect; 4.<br>\r\n<br>\r\nSchroll (Rossnagel) ist f&uuml;r die Dauer seines Dienstverh&auml;ltnisses gegen Krankheit pflichtversichert. Ferner unterliegt er der Angestelltenversicherungspflicht. Die Beitr&auml;ge werden bis auf weiteres ganz von der Anstellungsbeh&ouml;rde &uuml;bernommen.<br>\r\n<br>\r\n&sect; 5.<br>\r\n<br>\r\nim Falle der Erkrankung des Ro&szlig;nagl sorgt die Kirchenverwaltung f&uuml;r eine geeignete Aushilfe. W&auml;hrend der Krankheitszeit wird eine Gehaltszahlung nicht geleistet, da von der Krankenkasse ein t&auml;gliches Krankengeld zu gew&auml;hren ist.<br>\r\n<br>\r\n&sect; 6.<br>\r\n<br>\r\nDem Schroll (Rossnagel) wird all j&auml;hrlich ein Urlaub von 14 Tagen im Benehmen mit Herrn Kirchenverwaltungsvorstand bewilligt. W&auml;hrend der Beurlaubung erfolgt Gehaltsweiterzahlung. F&uuml;r die Dauer der Beurlaubung oder einer aus dringenden Gr&uuml;nden sich ergebenden Abwesenheit -letztere ist dem Kirchenverwaltungsvorstand rechtzeitig zu melden- hat Schroll (Rossnagel) im Benehmen mit dem bezeichn.Vorstand auf eigene Kosten eine geeignete Aushilfskraft aufzustellen.<br>\r\n<br>\r\n&sect; 7.<br>\r\n<br>\r\nSchroll (Rossnagel) hat sich bei allen Dienstobliegheiten den Anordnungen des Kirchenverwaltungsvorstandes zu unterwerfen; er ist dem Letzteren f&uuml;r Aufrechterhaltung der zum Gottesdienste erforderlichen Ordnung und Ruhe auf dem Musikchore verantwortlich und verspricht f&uuml;r seine Person in Kirche und &Ouml;ffentlichkeit ein sittlich einwandfreies Betragen an den Tag legen zu wollen.<br>\r\n<br>\r\n&sect; 8.<br>\r\n<br>\r\nDas Dienstverh&auml;ltnis des Schroll (Rossnagel) zur Kirchenverwaltung ist jederzeit widerruflich. Die L&ouml;sung des Dienstverh&auml;ltnisses steht jedem Vertragsschlie&szlig;enden frei und ist beiderseits an eine drei monatliche K&uuml;ndigungsfrist gebunden.<br>\r\n<br>\r\n&sect; 9.<br>\r\n<br>\r\nF&uuml;r Streitigkeiten aus dem gegenw&auml;rtigen Dienstvertrage steht f&uuml;r beide Teile der Rechtsweg offen.<br>\r\nGegenw&auml;rtiger Vertrag zweifach ausgefertigt; ein Exemplar erhalt der Herr Chorregent Schroll (Rossnagel) und eines wird bei den Akten der Kirchenverwaltung aufbewahrt.<br>\r\n<br>\r\nRuhmannsfelden, den 31. Juli 1927 (7. Juni 1929)<br>\r\nDie Kirchenverwaltung:<br>\r\nDer Chorregent:<br>\r\nSiegel.', '<p>\n<a href="scanns/8_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/8_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/8_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/8_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 120, 170),
(9, 'Brief von Kirchenverwaltung an Max Rauscher, 26.3.1927', 1, '1927-03-26', '26.03.1927', 1, 'Kirchenmusik', 'Angebot eines neuen Dienstvertrages an Rauscher', 'Kirchenverwaltung', 'Max Rauscher', 2, 'Pfarrarchiv', 0, 'Betreff: Vollzug des Kirchenverwaltungsbeschlusses vom 30. 12. 26.<br>\r\n<br>\r\nNachstehend bringen wir Ihnen den Kirchenverwaltungsbeschlu&szlig; im Teilauszug zur Kenntnis und Darnachachtung:<br>\r\n<br>\r\n".................<br>\r\nDie Kirchenverwaltung ist bereit, einen neuen Dienstvertrag ab 1. April 1927 im M&auml;rz 1927 abzuschlie&szlig;en unter folgenden Bedingungen:<br>\r\n1. in der Kirchenratssitzung Abbitte zu leisten,<br>\r\n2. auf einen neu entworfenen Dienstvertrag einzugehen."<br>\r\nIm Vollzug dieses Beschlusses findet am Sonntag den 27. M&auml;rz 1927 Nachmittags Kirchenratssitzung im Pfarrhofe statt. Sie werden hiermit geladen, zu dieser Sitzung p&uuml;nklich um 3 1/4 Uhr Nachmittags zu erscheinen.<br>\r\nSollten Sie der Vorladung nicht Folge leisten und zudem nicht ob Ihres Verhaltens dem Kirchenverwaltungsvorstand gegen&uuml;ber und wegen Ihrer herausfordernden Schreibereien an die Kirchenverwaltung und andere Beh&ouml;rden vor den versammelten Kirchenratsmitgliedern &ouml;ffentlich Abbitte leisten, so w&uuml;rde die Kirchenverwaltung die Angelegenheit als erledigt betrachten und die Chorregentenstelle ab 1. April 1927 anderweitig neu besetzten.', '<p>\n<a href="scanns/9_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/9_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 6, 10),
(10, 'Brief von Max Rauscher an Kirchenverwaltung, 27.3.1927', 1, '1927-03-27', '27.03.1927', 1, 'Kirchenmusik', 'Aush&auml;ndiguns-Nachweis', 'Max Rauscher', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'A u s h &auml; n d i g u n g s - N a c h w e i s<br>\r\n<br>\r\nDer Unterzeichnete best&auml;tigt, heute ein Schreiben von der Kath. Kirchenverwaltung Ruhmannsfelden ausgeh&auml;ndigt erhalten zu haben.<br>\r\n<br>\r\nRuhmannsfelden, den 27. M&auml;rz 1927<br>\r\n<br>\r\nMax Rauscher, Chorregent.', '<p>\n<a href="scanns/10_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/10_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 9, 11),
(11, 'Brief von Max Rauscher an Kirchenverwaltung, 29.3.1927', 1, '1927-03-29', '29.03.1927', 1, 'Kirchenmusik', 'Ank&uuml;ndigung einer Klage am Amtsgericht Viechtach', 'Max Rauscher', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'An die Kirchenverwaltung hier!<br>\r\n<br>\r\nDie juristische Pr&uuml;fung, meiner an die Kirchenverwaltung gerichteten Briefe hat ergeben, da&szlig; s&auml;mtliche einwandfrei von Beleidigung sind.<br>\r\nEs besteht somit die L&ouml;sung meines Dienstvertrags zu Unrecht, nachdem ich mir in meinem Dienste nichts zu schulden kommen lie&szlig;.<br>\r\nIch kann schon heute, die Verwaltung darauf Aufmerksam machen, da&szlig; ich mein Recht, nach Ablauf von 8 Tagen durch Klage am Amtsgericht Viechtach erzwingen werde.<br>\r\nWir werden dann sehen, ob das Kirchenverwaltungsmitglied Herr H&auml;rtl dem juristischen Wissen & Rechten standh&auml;lt.<br>\r\n<br>\r\nMax Rauscher<br>\r\nChorregent.', '<p>\n<a href="scanns/11_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/11_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/11_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/11_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 10, 12),
(12, 'Brief von der Kirchenverwaltung an die Regierung von Niederbayern, 1.4.1927', 1, '1927-04-01', '01.04.1927', 1, 'Kirchenmusik', 'Antrag, dass August H&ouml;gn die Chorregentenstelle &uuml;bernimmt', 'der Kirchenverwaltung', 'die Regierung von Niederbayern', 2, 'Pfarrarchiv', 0, 'Betreff: Neubesetzung der Chorregentenstelle.<br>\r\n<br>\r\n./. 3 Beilagen<br>\r\nChorregent Max Rauscher von Ruhmannsfelden hat bei der Regierung sowohl als beim Bisch&ouml;flichen Ordinariate Regensburg Beschwerde erhoben gegen die Kirchenverwaltung wegen zu geringer Besoldung f&uuml;r seine Leistungen auf dem Kirchenchore. (Beilage 1) Nebenbei bemerkt betrug die Entlohnung im Jahre 1926 ca 1400 M bei durchschnittlich 5 st&uuml;ndiger Arbeitsleistung in der Woche, die widerrechtlichen &Uuml;berforderungen nicht mit eingerechnet.<br>\r\nDie Entscheidung der kirchlichen Oberbeh&ouml;rde, wonach kein anderer Ausweg sich finden lasse, als den Dienstvertrag zu l&ouml;sen liegt bei. (Beilage 2)<br>\r\nAus diesem Grunde und noch anderen Gr&uuml;nden, wie aus der Protokollabschrift des Kirchenverwaltungsbeschlusses ersichtlich ist, hat die Kirchenverwaltung den Dienstvertrag mit Rauscher ab 1. Januar 1927 gek&uuml;ndigt, ihm aber noch die M&ouml;glichkeit geboten, ab 1. April einen neuen Dienstvertrag einzugehen unter folgenden Bedingungen:<br>\r\n	1.) In der Kirchenverwaltungssitzung Abbitte zu leisten.<br>\r\n	2.) einen neu entworfenen Dienstvertrag einzugehen. (Beilage 3)<br>\r\nRauscher wurde zur Sitzung fr&uuml;hzeitig geladen und ist zu derselben rechtzeitig erschienen. Er weigerte sich trotz dreimaliger Anfrage Abbitte zu leisten. Zudem war sein Benehmen so ungeziemend, da&szlig; dies allein schon hinreichend gewesen w&auml;re, keinen neuen Vertrag mehr mit ihm abzuschlie&szlig;en. Die Angelegenheit war hiemit f&uuml;r die Kirchenverwaltung erledigt, Rauscher entlassen. Die Kirchenverwaltung k&ouml;nnte auch mit Rauscher nie mehr sich einigen. Sie wei&szlig; sich gedeckt durch die ganze Kirchengemeide.<br>\r\nDie Kirchenverwaltung stellt nunmehr an die hohe Regierung von Niederbayern die ergebenste und dringende Bitte:<br>\r\n"Es wolle gn&auml;digst genehmigt werden, da&szlig; die Chorregentenstelle in Ruhmannsfelden bis auf weiteres von einem Lehrer &uuml;bernommen werden d&uuml;rfe."<br>\r\nIn Betracht k&ouml;nnte nur Hauptlehrer H&ouml;gn kommen, der allein die zu &Uuml;bernahme des Kirchenchores notwendigen musikalischen F&auml;higkeiten und Kenntnisse besitzt und vor Rauscher schon 3 Jahre den Kirchenchor zur gr&ouml;&szlig;ten Zufriedenheit der ganzen Kirchengemeinde versehen hat.<br>\r\nDer unterzeichnete Vorstand erkl&auml;rt sich bereit, bei dienstlicher Inanspruchnahme des Herrn Hauptlehrer H&ouml;gn w&auml;hrend der Schulzeit dieselbe mit Religionsunterricht auszuf&uuml;llen. Bei levit. Leichen, die &auml;u&szlig;erst selten sind - schon volle 8 Monate war keine mehr - w&uuml;rde der Ausfall der Stunden durch gewissenhafte Nachholung derselben wieder hereingebracht werden.<br>\r\nDie politische Gemeinde Ruhmannsfelden sowohl, als auch die gr&ouml;&szlig;tenteils hier eingepfarrte Gemeinde Zachenberg schlie&szlig;t sich der Bitte der Kirchenverwaltung an; erstere verspricht sobald die Verh&auml;ltnisse es gestatten eine Wohnung bereit zu stellen und es so zu erm&ouml;glichen, einen pensionierten Lehrer als Chorregent anzustellen. Andere Chorregenten k&ouml;nnten angesichts der mi&szlig;lichen finanziellen Lage der Kirche wie auch der Kirchengemeinde nicht in frage kommen.<br>\r\nDie Kirchenverwaltung wie auch die beteiligten Gemeinder&auml;te sehen mit vollem Vertrauen der wohlwollenden Gew&auml;hrung ihrer vorgetragenen Bitte entgegen.<br>\r\n<br>\r\nDer Kirchenverwaltungsvorstand', '<p>\n<a href="scanns/12_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/12_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/12_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/12_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/12_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/12_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 11, 13),
(13, 'Brief von der Kirchenverwaltung an die Regierung von Niederbayern, 22.4.1927', 1, '1927-04-22', '22.04.1927', 1, 'Kirchenmusik', 'Antrag um Verl&auml;ngerung des Chorregentendienstes von August H&ouml;gn', 'der Kirchenverwaltung', 'die Regierung von Niederbayern', 2, 'Pfarrarchiv', 0, 'Betreff: Neben&auml;mter und Nebengesch&auml;fte der Volksschullehrer.<br>\r\n<br>\r\nZur Regierungsentschlie&szlig;ung v. 9.4.27, Nr. 2306 g 52<br>\r\n<br>\r\nDer Kirchenchor Ruhmannsfelden ist ab 1. Mai ohne Chorregenten. Die Kirchenverwaltung stellt daher an die hohe Regierung die ergebenste und dringende Bitte:<br>\r\n"Es wolle g&uuml;tigst genehmigt werden, da&szlig; Herr Hauptlehrer H&ouml;gn von Ruhmannsfelden die F&uuml;hrung des Kirchenchores bis zu den gro&szlig;en Ferien weiterbehalten darf."<br>\r\n<br>\r\nDie Chorregentenstelle Ruhmannsfelden wird demn&auml;chst zur Bewerbung &ouml;ffentlich ausgeschrieben werden.<br>\r\nDer wohlwollenden Gew&auml;hrung der vorgetragenden Bitte wird vertrauensvoll entgegengesehen.<br>\r\n<br>\r\nDer Kirchenverwaltungsvorstand', '<p>\n<a href="scanns/13_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/13_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/13_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/13_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 12, 14),
(14, 'Brief von Alois Hartl an Kirchenrat, 15.6.1927', 1, '1927-06-15', '15.06.1927', 1, 'Kirchenmusik', 'Antrag, Rauscher wieder als Chorregent einzustellen', 'Alois Hartl', 'Kirchenrat', 2, 'Pfarrarchiv', 0, 'Einlauf: An den Kirchenrat Ruhmannsfelden<br>\r\n<br>\r\nIch stelle hiermit Antrag den fr&uuml;heren Chorregenten Max Rauscher dahier wieder einzustellen & seine Anspr&uuml;che die er macht, welche auch voll & ganz berechtigt sind zu genehmigen.<br>\r\n<br>\r\nBegr&uuml;ndung: Max Rauscher ist & wahr der Beste Chorregent, dem ich seit Lebenszeit kennen lernte, derselbe f&uuml;hrt eine Kirchenmusik die noch nicht da war in Ruhmannsfelden & und auch nicht mehr kommen kann, denn ein Mann wie Rauscher der Tag & Nacht &uuml;bte & lernte & nur am Fortschritt war, macht ein anderer nicht, geschweige erst ein alter Pensionist, wann es Leute gibt die glauben ein Chorregent hat blo&szlig; 1 - 2 Stunden t&auml;glich Arbeitszeit, ist also Grundfalsch & somit bel&ouml;hnt zu werden, scheinbar soll der Chorregent die andere Zeit Tagl&ouml;hnerarbeit verrichten, oder sonstiges welches ihm von seinem Musikalischen &Uuml;bungen unterbinden w&uuml;rde, es ist also auch nicht & fast gar nicht zum rechnen wann eine j&auml;hrliche Umlage erhoben wird, die h&ouml;chstens f&uuml;r die mittleren Steuerzahler 15 Pfg. ausmacht, wann die Kirchenverwaltung mit diesem kleinem Sparsinn anf&auml;ngt, einem eifrigen t&uuml;chtigen einheimischen Chorregenten was ich kaum glaube nicht zu g&ouml;nnen geneigt ist, h&ouml;rt sich doch alles auf.<br>\r\nFreilich sind die Leute geschreckt wann es heisst es muss eine Umlage erhoben werden, wann es aber gesagt wird, da&szlig; es nicht der M&uuml;he wert ist davon zu reden wegen 15 Pfg. dann streibt sich kein Mensch dagegen, wie auch in Viechtach nicht. Habt Ihr Herren schon geh&ouml;rt wie es in B&ouml;brach zugeht, habt Ihr Herren schon bemerkt, da&szlig; bei uns nicht mehr soviel in die Kirche gehen, der Volksmund sagt dem Rauscher ist keiner gewachsen &  ist auch die Wahrheit, wir waren an der H&ouml;he hier in Ruhmannsfelden mit der Kirchenmusik, soll es jetzt wieder abw&auml;rts gehen? Nein Musik & Gesang ist eine Kunst wir verlangen Rauscher wieder in seine fr&uuml;here Stellung eingesetzt, mein Antrag ist rein Objektiv, ich habe mit Rauscher kein Wort davon geredet & kann es auf Eid versichern, wir verzichten auf einem fremden Chorregenten, wir haben selbst einem besseren & das ist & bleibt Rauscher!<br>\r\n<br>\r\nHochachtend!<br>\r\nAlois Hartl', '<p>\n<a href="scanns/14_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/14_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 13, 15),
(15, 'Brief von Johann Bielmeier an Kirchenverwaltung, 17.6.1927', 1, '1927-06-17', '17.06.1927', 1, 'Kirchenmusik', 'Antrag, Rauscher wieder als Chorregent einzustellen', 'Johann Bielmeier', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'An die Kirchenverwaltung Ruhmannsfelden<br>\r\n<br>\r\nIch bringe hiermit einen Antrag Rauscher wieder als Chorregenten einzusetzen, zwar deshalb weil Rauscher ein ganz hervorragender Musik u. Gesangtechnisch gut ausgebildeter Mann ist. Er w&auml;re traurig wenn man denselben f&uuml;r seine Leistung auch nicht belohnen k&ouml;nnte, was &uuml;berhaupt f&uuml;r die Steuerzahler in der Pfarrgemeinde nur ein kleines Almosen ist, eine Umlage nur im Namen etwas ausmacht, aber der Geldbeutel davon nichts kennt, wegen dieser paar Pfennige j&auml;hrlich.<br>\r\nIch kann ihnen sagen ganz aufrichtig, ich bin ein Musikfreund zumal f&uuml;r die Kirche, aber bedauern mu&szlig; ich, wenn es wieder zur&uuml;ck geht, statt vorw&auml;rts! & das kommt, denn ein alter Pensionist, hat doch diesen Eifer nicht so wie ein Junger meines Erachtens. Bemerke auch, was mir eine Chors&auml;ngerin sagte bei einem Besuch dahier. sie h&auml;tte auch gerne Lust auf den Chor hier zu singen, weil ein junger t&uuml;chtiger Regent waltet, aber mit einen alten Regenten will sie nichts anfangen weil der Eifer fehlt. Die Chors&auml;ngerin welche Alt & Sopran singt & zwar schon acht Jahre, kommt wahrscheinlich zu mir, wenn meine Tochter heiratet, weil es eine Schwester ist zum Br&auml;utigam & ich versichere heute schon, dieselbe meineswegen zu Dienst steht, aber ob dieselbe bei einem alten Chorregenten mithilft glaube ich kaum. Ich ersuche nun die Kirchenverwaltung meinen Antrag zu billigen, welchen auch der Volksmund spricht.<br>\r\n<br>\r\nGr&uuml;&szlig;e Hochachtend!<br>\r\nJoh. Bielmeier', '<p>\n<a href="scanns/15_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/15_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 14, 16),
(16, 'Brief von Bisch&ouml;fliche Ordinariat Regensburg an Kirchenverwaltung, 28.6.1927', 1, '1927-06-28', '28.06.1927', 1, 'Kirchenmusik', 'Anweisung an den Kirchenrat, Max Rauscher zu informieren, dass er durch die Kirchenverwaltung entlassen wurde, nicht durch das bisch&ouml;fliche Ordinariat', 'Bisch&ouml;fliche Ordinariat Regensburg', 'Kirchenverwaltung', 2, 'Pfarrarchiv', 0, 'D a s <br>\r\nB i s c h &ouml; f l i c h e   O r d i n a r i a t<br>\r\n<br>\r\nR E G E N S B U R G<br>\r\n<br>\r\nDem Vater des entlassenen Chorregenten Max Rauscher ist auf seine Eingabe vom 16./23. ds. Mts. durch &Uuml;bermittlung dieser Entschlie&szlig;ung im Original alsbald zu er&ouml;ffnen, da&szlig; Chorregent Max Rauscher nicht durch oberhirtliche Stelle, sonderen durch die Kirchenverwaltung entlassen wurde, und da die oberhirtliche Stelle gegen das Vorgehen der Kirchenverwaltung keine Erinnerung hat, weil der Entlassene bez&uuml;glich seine Entlohnung unerf&uuml;llbare Forderungen stellte und zum vorgesetzten Pfarrvorstande in ein dienstlich unertr&auml;gliches Verh&auml;ltnis sich setzte.<br>\r\n<br>\r\ngez. Kiefl.', '<p>\n<a href="scanns/16_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/16_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 15, 75),
(17, 'Brief von Josef Brunner an Kirchenverwaltung, 29.4.1953', 1, '1953-04-29', '29.04.1953', 1, 'Kirchenmusik', 'Bewerbung um die Nachfolge von H&ouml;gn als Chorregent', 'Josef Brunner', 'Kirchenverwaltung', 1, 'Reicheneder-Chronik', 0, 'Josef Brunner<br>\r\nChorregent<br>\r\nAchslach<br>\r\n<br>\r\nAn die <br>\r\nKirchenverwaltung Ruhmannsfelden<br>\r\n<br>\r\nBetrifft. &quot;Bewerbung als Chorregent in der Pfarrei Ruh&apos;felden&quot;<br>\r\n<br>\r\nWie ich aus ma&szlig;gebenden Personenkreis erfahre, will Herr Rektor H&ouml;gn bei Amtsantritt eines neuen Hochw. H. Pfarrers sein Amt als Chorregent aus gesundheitlichen Gr&uuml;nden niederlegen.<br>\r\nDa ich als Chorregent in Achslach schon l&auml;nger die Absicht hege, mich um einen gr&ouml;&szlig;eren Wirkungskreis umzusehen, will ich diese Gelegenheit nicht verpassen und m&ouml;chte mich auf diese Weise um die Stelle als Chorregent in der Pfarrei Ruh&apos;felden bewerben. M&ouml;chte aber ausdr&uuml;cklich betonen, das ich die Stelle erst bei einer v&ouml;llig freiwilligen K&uuml;ndigung durch H. Rektor H&ouml;gn zu &uuml;bernehmen gewillt bin.<br>\r\nSollten Sie an dieser meiner Bewerbung interre&szlig;iert sein, so w&uuml;rde ich Sie freundlich ersuchen, mir einen kurzen Bescheid zukommen zu lassen. Au&szlig;erdem w&auml;re ich, wenn Sie es f&uuml;r notwendig betrachten, zu einer pers&ouml;nlichen Aussprache jederzeit bereit.<br>\r\n<br>\r\nHochachtungsvoll<br>\r\nJos. Brunner', '<p>\n<a href="scanns/17_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/17_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 37, 41),
(18, 'Brief von August H&ouml;gn an Pfarrer Reicheneder, 25.1.1954', 1, '1954-01-25', '25.01.1954', 1, 'Kirchenmusik', 'Bekanntgabe seines R&uuml;cktritts von der Chorregentenstelle', 'August H&ouml;gn', 'Pfarrer Reicheneder', 2, 'Pfarrarchiv', 0, 'Sehr geehrter H. H. Pfarrer Reicheneder!<br>\r\n<br>\r\nGestern Sonntag mittags besuchte mich mein lieber Nachbar H. Rudolf Schwannberger. U. a. kamen wir auch zu sprechen auf die derzeitigen Verh&auml;ltnisse auf den hiesigen Kirchenchor.<br>\r\nSie wissen es, da&szlig; ich es selbst am lebhaftesten bedauert habe, da&szlig; ich unmittelbar vor dem Weihnachtsfeiertagen krank wurde u. da&szlig; dies der einzige Grund war, warum ich nicht mehr weitermachen konnte auf dem Kirchenchor. Eine andere Ursache bestand bei dieser Sache f&uuml;r mich bestimmt nicht. Alles Andere - z. B. K&uuml;ndigung seitens des H. H. Pfrs., udgl. ist unrichtig u. unwahr u. h&ouml;chstens ein bl&ouml;des Weibergeschw&auml;tz. Ich habe ja den Kirchenchordienst nur von Tag zu Tag aushilfsweise gemacht. Wohl habe ich oft u. oft H. H. Pfr. Bauer gebeten - auch im Beisein von H. H. Koop. Neugebauer - er m&ouml;ge doch vom n&auml;chsten Tage an einen anderen Chorregenten bestimmen, da ich mich voll u. ganz in den Ruhestand begegen m&ouml;chte. &quot;Kommt nicht in Frage,&quot; so hie&szlig; es da immer. Nach dem Tode des H. H. Pfr. Bauer habe ich H. H. Koop. Huber - der damals Pfarrprovisor war - ersucht, einen anderen Chorregenten f&uuml;r den hiesigen Kirchenchor zu bestimmen. Diese Bitte wurde auch abgelehnt. Ebenso habe ich Sie, H. H. Pfr. Reicheneder, nach Ihrer Ankunft hier in Ruhmannsfelden davon verst&auml;ndigt, da&szlig; ich den Kirchenchordienst hier in Ruhmannsfelden weiterhin nicht mehr versehen kann. Auf Ihr Ersuchen hin habe ich aber - wenigstens f&uuml;r kurze Zeit - wiederum zugesagt. Nun aber mu&szlig; ich Sie, H. H. Pfr. Reicheneder herzlichst bitten davon Kenntnis zu nehmen, da&szlig; ich von meinem 20. Lebensjahr bis zum 76. auf den verschiedensten Kirchench&ouml;ren der beiden Di&ouml;zesen Passau u. Regensburg t&auml;tig war als S&auml;nger, als Organist u. als Chorregent. Das d&uuml;rfte reichen zu der berechtigten Forderung auf Ruhestand auch im Kirchenchordienst. Es wird wohl im ganzem Pfarrsprengel Ruhmannsfelden u. weit hinaus niemanden geben, der da anderer Ansicht sein k&ouml;nnte. Und die ewigen N&ouml;rgler u. Bessermacher im Ruhmannsfelden sehen jetzt ihren Wunsch erf&uuml;llt, das sie jetzt endlich einen anderen Kirchenchor haben, der ihre kirchenmusikalische W&uuml;nsche u. Anspr&uuml;che besser befriedigen d&uuml;rfte als es unter Rektor H&ouml;gn der Fall war. Mein Alter (geb. 2.8.78) u. meine 55 j&auml;hrige T&auml;tigkeit auf dem Gebiete der Kirchenmusik bestimmen mich, da&szlig; ich von nun an mich nicht mehr beteilige u. best&auml;tige auf dem Kirchenchor, damit ich von dem wohlverdienten Ruhestand w&auml;hrend der kurzen Lebenszeit, die mir noch beschieden sein mag, auch ein klein bischen was habe.<br>\r\n<br>\r\nM&ouml;chte noch bef&uuml;gen, da&szlig; eine vertragliche Abmachung &uuml;ber Kirchenchordienst zwischen Pfarramt Ruhmannsfelden u. mir niemals bestanden hat.<br>\r\n<br>\r\nErsuche H. H. Pfarrer Reicheneder von Vorstehenden g&uuml;tigst Kenntnis nehmen zu wollen.<br>\r\nMit vorz&uuml;glichster Hochachtung<br>\r\nIhr Ergebenster!<br>\r\nA. H&ouml;gn<br>\r\n<br>\r\nHabe von Ehrw. Frau Oberin erfahren, da&szlig; Sie kr&auml;nklich sind. W&uuml;nsche Ihnen recht baldige Genesung.', '<p>\n<a href="scanns/18_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/18_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/18_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/18_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 41, 19),
(19, 'Brief von Pfarrer Reicheneder an August H&ouml;gn, 6.2.1954', 1, '1954-02-06', '06.02.1954', 1, 'Kirchenmusik', 'Zurkenntnisnahme des R&uuml;cktritts und Dank', 'Pfarrer Reicheneder', 'August H&ouml;gn', 2, 'Pfarrarchiv', 0, 'Sehr geehrter Herr Rektor!<br>\r\n<br>\r\nMit aufrichtigem Dank best&auml;tige ich den Empfang Ihrer gesch&auml;tzten Zeilen vom 25. Januar 1954.<br>\r\nWiederholt h&auml;tte ich Sie gerne besucht, um mich nach dem gesundheitlichen Zustand des H. Rektors zu erkundigen, doch leider konnte ich infolge einer Stimmbandl&auml;hmung nicht mehr sprechen und so war es nicht m&ouml;glich. Auch jetzt ist es noch nicht so, wie es sein soll, aber immerhin schon wieder bedeutend besser, sodass ich hoffe Ihnen bald wieder einen Besuch anstatten zu k&ouml;nnen. Ich hoffe und w&uuml;nsche, da&szlig; Ihre Gesundheit bis dahin weiterhin sich bessere.<br>\r\nVom &Uuml;brigem Inhalt Ihres werten Schreibens habe ich Kenntnis genommen. Ich bedauere diese Tatsache zwar sehr, kann aber Ihren Standpunkt voll und ganz verstehen und m&ouml;chte daher, zun&auml;chst auf diesem Wege, bis mir ein pers&ouml;nlicher Besuch m&ouml;glich ist, f&uuml;r Ihre T&auml;tigkeit auf dem hiesigem Kirchenchor, sowohl vor der Zeit meines Hierseins, wie auch bes. f&uuml;r die Monate meines hiesigen Wirkens meinen und der Kirchenverwaltung aufrichtigen Dank zum Ausdruck bringen. M&ouml;ge Ihnen der Herrgott vergelten, was Sie so zu seiner Ehre und zum Besten der Pfarrgemeinde geleistet haben, bes. dadurch, da&szlig; er Ihnen bald wieder recht gute Gesundheit schenkt und Ihnen den erw&uuml;nschten Ruhestand noch recht lange genie&szlig;en l&auml;&szlig;t.<br>\r\n<br>\r\nMit diesem aufrichtigem Wunsche<br>\r\nin aller Hochachtung<br>\r\nund mit den besten Gr&uuml;&szlig;en<br>\r\nin Dankbarkeit <br>\r\n<br>\r\nIhr<br>\r\n<br>\r\nPfr.', '<p>\n<a href="scanns/19_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/19_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 18, 124),
(20, 'Brief von Dr. Keim an August H&ouml;gn, 30.8.1922', 1, '1922-08-30', '30.08.1922', 2, 'heimatkundlichen Zeitungsartikeln', 'Ursprung des Names &quot;Ruhmannsfelden&quot;', 'Dr. Keim', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Sehr geehrter Herr Hauptlehrer!<br>\r\n<br>\r\nDie Formen Rumarsfelden, Rudmarsfelden, die der Volksmund best&auml;tigt, tragen den Stempel der Originalit&auml;t.<br>\r\nDie erste H&auml;lfte ist der Personenname Hrotmar (Hruotmar, Romar).<br>\r\nHrot(i) = Ruhm, Sieg. --mar(u) = ber&uuml;hmt. Also: der Siegber&uuml;hmte. Ich glaube, da&szlig; der Ort bei Gr&uuml;ndung des Klosters Gotteszell schon l&auml;ngst vorhanden war und halte den Mann, der dem Ort den Namen gegeben hat, f&uuml;r einen Dienstmann der Grafen von Bogen, die ja Rodungen veranstaltet haben. Wir k&auml;men so in das 11. bis 12. Jahrhundert und d&uuml;rfen die Entstehung Ruhmannsfelden um 1100 ansetzen. Mehr l&auml;&szlig;t sich einstweilen nicht sagen.<br>\r\n<br>\r\nMit vorz&uuml;glicher Hochachtung ergebenst <br>\r\n<br>\r\nDr. Keim.', '<p>\n<a href="scanns/20_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/20_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 106, 117);
INSERT INTO `dokumente` (`id`, `titel`, `dok_art`, `datum`, `datumtext`, `thematik`, `thematiktext`, `thema`, `feld1`, `feld2`, `dok_fundort`, `dok_fundorttext`, `grafik_ja`, `text`, `gif`, `previous`, `next`) VALUES (21, 'Brief von Staatsarchiv M&uuml;nchen an August H&ouml;gn, 22.9.1924', 1, '1924-09-22', '22.09.1924', 2, 'heimatkundlichen Zeitungsartikeln', 'Marktrecht und Wappen von Ruhmannsfelden', 'Staatsarchiv M&uuml;nchen', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'An Herrn Hauptlehrer H&ouml;gn in Ruhmannsfelden, Bayer. Wald. <br>\r\n<br>\r\nBetreff: Marktrecht und Wappen von Ruhmannsfelden<br>\r\n<br>\r\nZum Schreiben vom 15/18. Sept. 1924.<br>\r\n<br>\r\nWas die F&uuml;hrung des Markttitels und eines Wappens von Ruhmannsfelden anbelangt, vermag es zwar keine ersch&ouml;pfende, aber immerhin wesentlich aufkl&auml;rende Antwort gegeben zu werden.<br>\r\nJakob der Rueerer stellte am 26. April 1416 eine Urkunde aus, in welcher sich &quot;dy czeitt Richter dez Markchts zue Ruedmarnsfelden&quot; nennt. Es d&uuml;rfte kaum ein Zweifel bestehen, da&szlig; der Urkundenaussteller als Landesherrlicher Richter &uuml;ber die Markteigenschaft seines Wirkungsortes Bescheid wu&szlig;te. Wir sind deshalb berechtigt, die Markteigenschaft zu Ruhmannsfelden schon seit dem Beginn des 15. Jahrhunderts in Anspruch zu nehmen.<br>\r\nIn einem Aldersbacher Codex v. J. 1452 ist die Rede von dem forum Rudmansfelden, also Markt w&auml;hrend in einer Urkunde vom 2. April 1475 das opidum Rudmansfelden erscheint, was mehr an die befestigte Siedlung als an den Markt gemahnt.<br>\r\nAuf Bitten der &quot;Burger unnsers Margkts zu Rudmansfelden&quot; tuet ihnen Herzog Albrecht IV. von Bayern-M&uuml;nchen die Gnade: &quot;....Fryen sie auch wissennlieh in crafft des briefs, Also das ayvnd all Ir Nachkomen, sich aller der gnaden und freihait geprauchen vnd nyessen m&uuml;gen vnd die haben sollen, In allermass als annder vnnser M&auml;rkt, In Nidern Bairn, von vuunsern vordern gefreyt sein.&quot; Das Privileg ist nur in Abschrift erhalten und undatiert, steht aber zwischen zwei Urkunden desselben Jahres 1469 und darf daher als aus diesem Jahre stammend angenommen werden.<br>\r\nIn einem Literale des Klosters Gotteszell vom Jahre 1566 - 1602 kommen vor die &quot;Geschwornen des Rate und Markts R.&quot;, &quot;Rat und Gemein des Markts R.&quot; ,wie &uuml;berhaupt seit der Begnadigung von 1469 keinerlei Zweifel an dem Marktrechte Ruhmannsfelden mehr aufkommen kann.<br>\r\nDas heutige Wappen des Marktes Ruhmannsfelden weist &quot;in Rot ein von Silber und Blau in 2 Reihen geweckten Schr&auml;grechtsbalken&quot; auf. Zu dieser von O. Hupp (die Wappen und Siegel der deutschen St&auml;dte, Flecken<br>\r\nund D&ouml;rfer. Frankfurt a. M. 1912, S. 84) gegebenen Beschreibung f&uuml;gt der Verfasser noch erl&auml;uternd hinzu: &quot;Es ist gar kein altes Siegel bekannt geworden, soda&szlig; es fraglich ist, ob das beschriebene Wappen, das die B&uuml;rgermeistermedaille und ein nach dieser gefertigtes Magistratsiegel zeigen, richtig ist, oder ob das Wappen, das Mueelich, Apian und das Wappenbuch der Landschaft bringen, n&auml;mlich in blau unter 2 schr&auml;ggekreuzten silbernen Hirtenst&auml;ben eine weisse R&uuml;be mit gr&uuml;nen Bl&auml;ttern&quot;. Dieses letztere Wappen ist ein sog. redendes Wappen, welches ohne jede Autorisierung l&auml;ngere Zeit gebraucht worden zu sein scheint. Im 16. und 17. Jahrhundert hat man vielfach Rub- (Rueb-, R&uuml;b--, R&uuml;eb-) mannsfelden geschrieben und hat daf&uuml;r die R&uuml;be als Wappenbild angenommen; die Hirtenst&auml;be w&uuml;rden lediglich als Dekoration anzusehen sein. Es ist abgebildet als Nr. 641 in Philipp Apians Wappensammlung der altbayerischen Landschaft wie des zu seiner Zeit abgegangenen Adels. (Oberbayerisches Archiv XXXIX, 471 - 498). Als es um 1650 an der Kirche zu Ruhmannsfelden ohne jede Genehmigung angebracht worden war, erhob dagegen P. Gerard, Abbt bei dem Kloster Gotteszell Protest: &quot;Die Ruedtmannsfelder haben mit ihrer unnern&uuml;nftigen Rueben auff dem kh&uuml;ss an Verstandt, ganz vermesslich vnd vuuernuenftig ia vnuerantworttlich wider mich und das Closter gehandlet.... Wer hat ihnen ainmal ain wappen zue, fueren erlaubt? Vnd wan sie gleich wappenmessig waren, wer hat ihnen erlaubt solches auf der Kirchen spesa auff einen offnen thurn mallen zuelassen, alwo ihnen ainiges Recht und herschaft nit zuestehet vnd gebiertt? Vnd was ist das f&uuml;r ein Verstandt ia phantastische Einbildung ein Rueben auf einem K&uuml;ss?&quot;<br>\r\nSchade, da&szlig; es Fortsetzung der Korrespondenz &uuml;ber das Wappen und seine Anbringung der Kirche nicht vorhanden ist.<br>\r\n<br>\r\nI. V. Unterschrift.', '<p>\n<a href="scanns/21_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/21_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 107, 118),
(22, 'Brief von Hauptstaatsarchiv M&uuml;nchen an August H&ouml;gn, 7.8.1925', 1, '1925-08-07', '07.08.1925', 2, 'heimatkundlichen Zeitungsartikeln', 'Wallfahrtskapelle Osterbr&uuml;nnl bei Ruhmannsfelden', 'Hauptstaatsarchiv M&uuml;nchen', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'An Wohlgeb. Herrn Hauptlehrer H&ouml;gn in Ruhmannsfelden, bay. Wald.<br>\r\n<br>\r\nBetreff: Wallfahrtskapelle Osterbr&uuml;nnl bei Ruhmannsfelden; hier die 1550 von Hans v. Milloph, in Regensburg gegossene Glocke. z. Ges. vom 24./25. vor. Mts.<br>\r\n<br>\r\nDie hierorts gepflogenen Nachforschungen &uuml;ber eine im Jahre 1550 von Hans v. Milloph in Regensburg gegossene, nunmehr in der?. (weiter leider nicht &uuml;berliefert)', '<p>\n<a href="scanns/22_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/22_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 118, 133),
(23, 'Brief von Pfarrer Oberschmid an August H&ouml;gn, 8.9.1926', 1, '1926-09-08', '08.09.1926', 2, 'heimatkundlichen Zeitungsartikeln', 'Glocke in der Kirche Osterbr&uuml;nnl', 'Pfarrer Oberschmid', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Straubing, den 8.9.1926<br>\r\n<br>\r\nHochwohlgeb. Herrn Hauptlehrer H&ouml;gn, Ruhmannsfelden.<br>\r\n<br>\r\nBetreff: Die Glocke in der Kirche Osterbr&uuml;nnl.<br>\r\n<br>\r\nEs gereicht mir zu gro&szlig;em Vergn&uuml;gen, Euer Hochwohlgeboren den gew&uuml;nschten Aufschlu&szlig; &uuml;ber den Meister der Glocke im Osterbr&uuml;nnl an der Hand der mit Ihrer g&uuml;tigen Beihilfe selbst am letzten Freitag 3.9. aufgenommenen Schriftpause mit aller, Zuverl&auml;ssigkeit geben zu k&ouml;nnen. Wie Sie ja aus der beigegebenen Pausenkopie ersehen und Jedermann klar beweisen k&ouml;nnen, ist die Glocke ein Werk des seinerzeit hochber&uuml;hmt gewesenen und noch jetzt mit zahlreichen Glocken im weitesten Umkreis vertretenen Meisters Hans Durnknopf zu Regensburg, 1550.<br>\r\nZu allen mir m&ouml;glichen Aufschl&uuml;ssen jeder Art gerne bereit, beehre ich mich unter besten Gr&uuml;&szlig;en zu zeichnen Euer Hochwohlgeboren ganz ergebener <br>\r\n<br>\r\nJoseph Oberschmid, Pfr.', '<p>\n<a href="scanns/23_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/23_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 109, 2),
(24, 'Brief von Pater Wilhelm Fink an August H&ouml;gn, 6.7.1932', 1, '1932-07-06', '06.07.1932', 2, 'heimatkundlichen Zeitungsartikeln', 'Die Entwicklung Ruhmannsfelden in &auml;ltester Zeit', 'Pater Wilhelm Fink', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Metten, 6.7.1932 <br>\r\n<br>\r\nSehr geehrter Herr Oberlehrer!<br>\r\n<br>\r\nBesten Dank f&uuml;r Ihren Brief. Halten Sie an dem fest, was Dr. Keim und ich &uuml;ber Namen und Entstehung von Ruhmannsfelden geschrieben. Diese Artikelschreiber, die ihren Namen verbergen, wollen etwas besseres bringen, k&ouml;nnen es aber nicht. F&uuml;r die Entstehung Ruhmannsfeldens legen Sie Ihre Beobachtungen zu Grunde. Ich habe es auch so gemacht. Ob der oder die Artikelschreiber, wei&szlig; ich nicht. Man kann aus dem Kartenbild gerade so gut die Geschichte eines Platzes ablesen wie aus dem Namen. Oft sind das die &auml;ltesten und einzigen Zeugen. Wir m&uuml;ssen f&uuml;r das Werden Ruhmannsfeldens drei Ans&auml;tze annehmen:<br>\r\n1. Die &auml;lteste Siedlung mit der Kirche aus der Zeit vor den Bogener<br>\r\nGrafen, wo ein Rudmar den Wald rodete und Felder anlegte. <br>\r\n2. Die Burg aus der Zeit der Bogener Grafen.<br>\r\n3. Der Markt, der j&uuml;ngste Teil, eine viereckige Anlage, was f&uuml;r das 13. oder 14. Jahrhundert bezeichnend ist, also aus der Zeit des Klosters Gotteszell.<br>\r\nAuf diese Weise hat sich Ruhmannsfelden entwickelt, das zeigt der Augenschein. <br>\r\n<br>\r\nEs gr&uuml;&szlig;t Sie mit treuem Waldlergru&szlig; <br>\r\n<br>\r\nP. Wilhelm Fink O.S.B.', '<p>\n<a href="scanns/24_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/24_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 134, 142),
(25, 'Brief von Expositus Georg Hofmann, Sch&ouml;nau an August H&ouml;gn, 30.1.1950', 1, '1950-01-30', '30.01.1950', 8, 'Geschichte von Ruhmannsfelden', 'Marktrechtsverleihung an die Gemeinde Ruhmannsfelden', 'Expositus Georg Hofmann, Sch&ouml;nau', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Sch&ouml;nau, 30. Jan. 1950<br>\r\n<br>\r\nSehr geehrter Herr Rektor!<br>\r\nEntschuldigen Sie, da&szlig; ich erst heute auf Ihre beiden Anfragen antworten kann. Vorige Woche war ich verreist und am Freitag hatte ich einen Vortrag in B&ouml;brach zu halten.<br>\r\nI. Im Hauptstaatsarchiv M&uuml;nchen sind 35 Urkunden &uuml;ber den Markt Ruhmannsfelden von 1295 bis 1622. (Urkunden des Gerichts Viechtach, Fasc. 38 - 43 Nr. 445 a - 497), meist sind es Kaufbriefe von H&auml;usern und Grundst&uuml;cken. Schon in der ersten Urkunde Nr. 445 a vom 28. April 1295 (Eberl hat irrt&uuml;mlich 1294), ausgestellt zu Regensburg wird Ruhmannsfelden &quot;Markt&quot; genannt. Die Herz&ouml;ge Otto, Ludwig und Stephan verkaufen dr&uuml;ckender Schulden halber dem Abt und Konvent der Kirche zu Aldersbach f&uuml;r 400 Pfund Regensburger Pfennig Schlo&szlig; und Markt zu Rudmarsveld, M&uuml;hle und Hof zu Brugk (Bruckm&uuml;hle), das Dorf Arnoldesried (Arnetsried), das Dorf Lawandried (Labersried) das D&ouml;rflein Viechleinsod (Weichselsried?),die H&ouml;f zu Zierberg (Zierbach) und Leyfnaldstorf (L&auml;mmersdorf), Hof und M&uuml;hle zum Steg (Stegm&uuml;hle) und alle Besitzungen und Leute, welche Heinrich von Pf&ouml;ling sel. inne gehabt hat.<br>\r\nDiese Urkunde ist zwar nicht mehr im Original vorhanden, sondern in einer lat. Papierkopie aus dem 16.Jahrhundert. Eine eigentliche Marktrechtverleihung f&uuml;r Ruhmannsfelden ist unter diesen Urkunden nicht. Vom Markt Viechtach habe ich die &auml;lteste Verleihung gefunden von Jahre 1474, doch mit Berufung auf &auml;ltere Verleihungen, da die B&uuml;rger von Viechtach angaben solche verloren zu haben. Es stehen diese und auch weitere Erneuerungen von Jahre 1529,1599 u. a. in einem eigenen Akt. &quot;Privilegien des Marktes u. Spitals Viechtach.&quot;<br>\r\nOb f&uuml;r Ruhmannsfelden ebenfalls ein solcher Akt vorhanden ist, kann ich zur Zeit nicht angeben. Viechtach wird aber schon im Urbarbuch von 1280/1310 ebenfalls bereits als &quot;Markt&quot; bezeichnet.<br>\r\nII. Haus- und Hofbesitzer, die bereits &uuml;ber 100 Jahre auf demselben Hof sind, kann ich leider nicht angeben, da ich beim Grundbuchamt noch nichts herausgeschrieben habe. Ich habe nur die &auml;lteren G&uuml;terverzeichnisse, die im Hauptstaatsarchiv in M&uuml;nchen sind, herausgeschrieben, so von 1536, 1555, 1565, 1577, 1668, 1752 u. 1760. Auch habe ich einzelne Familien bezw. Sippen bearbeitet, so die Hacker, Hinkofer, Muhr, Steinbauer, Kufner, Geiger die teilweise auch in der Ruhmannsfeldener Gegend vorkommen, doch sind wohl noch mehrere Familien mehr als 100 Jahre auf ihrem Hof, die ich nicht n&auml;her kenne. Habe gegenw&auml;rtig auch nicht Zeit, aus dem Grundbuchamt Ausz&uuml;ge zu machen, da ich mit allerlei anderen Arbeiten in Anspruch genommen bin. Bedauere daher, da&szlig; ich Ihnen damit nicht behilflich sein kann.<br>\r\n<br>\r\nMit freundlichen Gr&uuml;ssen<br>\r\nIhr ergebenster <br>\r\nGeorg Hofmann <br>\r\nExpositus.', '<p>\n<a href="scanns/25_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/25_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 217, 26),
(26, 'Brief von Bayerisches Hauptstaatsarchiv an Marktgemeinderat, 28.2.1951', 1, '1951-02-28', '28.02.1951', 8, 'Geschichte von Ruhmannsfelden', 'Marktrechtsverleihung an die Gemeinde Ruhmannsfelden', 'Bayerisches Hauptstaatsarchiv', 'Marktgemeinderat', 1, 'Reicheneder-Chronik', 0, 'Bay. Hauptstaatsarchiv Nr. 318/1052	<br>\r\nArcisstr. 12<br>\r\n<br>\r\nAn den Marktgemeinderat Ruhmannsfelden, Niederbayern.<br>\r\nBetr. Marktverleihung z. Schreiben v. 21.2.1951.<br>\r\n<br>\r\nDie Nachforschungen &uuml;ber die Marktgemeindeverleihung an Ruhmannsfelden sind leider bisher ohne Erfolg geblieben. Der Forschung hinderlich ist namentlich der Umstand, da&szlig; ein Akt des Klosters Gotteszell, zu dem Ruhmannsfelden seit der Mitte des 15. Jahrhunderts geh&ouml;rte, &uuml;ber Streitigkeiten zwischen Kloster und Markt wegen der Marktrechte im 17. Jahrhundert nach ausw&auml;rts verlagert und daher z. Z. nicht greifbar ist. In ihm k&ouml;nnte ein Hinweis auf die Verleihung oder sogar eine Abschrift der Marktverleihung enthalten sein. Im &Uuml;brigen mu&szlig; Ruhmannsfelden sich schon sehr fr&uuml;h des Marktprivilegs gefreut haben. Denn schon in einer hier vorhandenen Urkunde vom 28.4.1295 &uuml;ber den Verkauf von Schlo&szlig; und Ort durch die Bay. Herz&ouml;ge Otto, Ludwig und Stephan an das Kloster Aldersbach wird es als Markt bezeichnet. Auch in einem der Bayerischen Urbare von 1318 (Mon. Boika 362, 417) kommt ein &quot;Ruhmannsfelder Metzen&quot; (Metrete Roudmarsvelder) vor, was das Bestehen eines Marktes anzeigt. Ob in den hier befindlichen &quot;Tomi privilegiorum&quot; das Marktprivileg enthalten ist, mu&szlig;te eine genaue, Durchsicht dieser umfangreichen B&auml;nde erst ergeben. Nach den Vorschriften m&uuml;sste dies jedoch den daran interessierten Ben&uuml;tzern selbst &uuml;berlassen werden. Ebenso m&uuml;&szlig;te noch eine Kartei &uuml;ber s&auml;mtliche vorhandene Urkunden bayerischer Herzoge, die leider mit keinem Register versehen ist, herangezogen werden.<br>\r\nErfahrungsgem&auml;&szlig; sind aber Privilegienverleihungen vor dem 14. Jahrhundert im Original nicht mehr vorhanden, da sie meist sehr fr&uuml;h verloren gegangen sind.<br>\r\n<br>\r\nI. A. Unterschrift Staatsarchivdirektor', '<p>\n<a href="scanns/26_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/26_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 25, 27),
(27, 'Brief von Bayerisches Staatsarchiv Landshut an Herrn B&uuml;rgermeister Muhr, 9.3.1951', 1, '1951-03-09', '09.03.1951', 8, 'Geschichte von Ruhmannsfelden', 'Marktrechtsverleihung an die Gemeinde Ruhmannsfelden', 'Bayerisches Staatsarchiv Landshut', 'Herrn B&uuml;rgermeister Muhr', 1, 'Reicheneder-Chronik', 0, 'Betreff: Marktrechtsverleihung an die Gemeinde Ruhmannsfelden <br>\r\n<br>\r\nZ. Schreiben vom 15/17 2. des H. A. H&ouml;gn Oberlehrer und Rektor i. R. Ruhmannsfelden.<br>\r\nEinen urkundlichen Beleg &uuml;ber die Marktrechtsverleihung an Ruhmannsfelden konnten wir im hiesigen Staatsarchiv nicht feststellen. Auch der uns zur Verf&uuml;gung stehenden Literatur konnten wird das Datum der Priveligierung nicht entnehmen.<br>\r\nWir m&ouml;chten Sie jedoch zwecks etwaiger n&auml;herer Beantwortung Ihrer Anfrage an das Bay. Hauptstaatsarchiv in M&uuml;nchen, Arcisstr. 12 verweisen bei dem der staatliche Urkundenbestand vor dem Jahre 1400 liegt und das in alten Kopialb&uuml;chern mitunter Abschriften von Privilegien der St&auml;dte und M&auml;rkte besitzt, die im Original nicht mehr vorhanden sind.<br>\r\n<br>\r\nI. V. v. Rehlinger, Staatsarchivrat.', '<p>\n<a href="scanns/27_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/27_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 26, 110),
(28, 'Brief von Finanzzollrat Anton Trellinger an August H&ouml;gn, 25.2.1952', 1, '1952-02-25', '25.02.1952', 3, 'Geschichte von Zachenberg', 'Information an H&ouml;gn, einen Aktenauszug des Landshuter Staatsarchivs angefertigt zu haben', 'Finanzzollrat Anton Trellinger', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, '<P>Sehr geehrter Herr Rektor!<BR><BR>Meine Zusammenstellung &uuml;ber die Geschichte der angegeben Orte der Gemeinde Zachenberg habe ich heute der Gemeinde Zachenberg &uuml;bersandt. Mein Material stammt fast nur aus Archivalien des hiesigen Staatsarchives. Ich habe Teile des Gemeindebezirks nur einige Male fl&uuml;chtig durchwandert, 2 Mal als ich vom Bahnhof Gotteszell nach Oberbreitenau ging u. einmal als ich nur die zwei Riesenb&auml;ume in Giggenried ansah. Ich bin froh, da&szlig; ich mit meiner Arbeit so gut es ging fertig bin, ich leide immer noch an den Folgen meiner schweren Erkrankung u. stehe immer noch in &auml;rztlicher Behandlung.<BR>Nun Herr Rektor w&uuml;rde ich Sie bitten, an die Erg&auml;nzung meiner Arbeit zu gehen, denn Sie kennen als ehemaliger Gemeindeschreiber die dortigen Gegend sehr gut u. wissen viel, k&ouml;nnten alles hinzuzuf&uuml;gen. Ich habe auch mal in den Landshuter Zeitungen &uuml;ber die Geschichte der einzelnen Orte der Landshuter Gegend geschrieben, denn ich bin an der Regierung, daher am Hauptstaatsarchiv, als Archivpfleger f&uuml;r den Landkreis Landshut aufgestellt u. ist es infolgedessen meine Pflicht mich um die Geschichte des mir anvertrauten Bezirkes zu k&uuml;mmern.<BR>Sie Herr Rektor als guter Fachmann wissen, besuchte ich dabei h&auml;ufig die Pfarrarchive und schaue mir besonders in den Pfarrmatrikeln um. Hier gehe ich besonders den auftretenden Krankheiten u. Pest, Cholera und schwarze Blattern nach. Manche Pfarrer haben auch die 1812/1813 in Russland Gebliebenen eingetragen, ab und zu findet man auch einen, der 100 und mehr Jahre alt geworden ist, auch Totschlag und Selbstmord werden dort erw&auml;hnt.<BR>Soviel ich gelesen habe, gehen in Ruhmannsfelden die Jahresb&uuml;cher zur&uuml;ck bis zum Jahr 1620 u. in Gotteszell bis zum Jahre 1652. Dann such ich immer alte Leute auf und frage sie um alles m&ouml;gliche aus und ich habe bei dieser Gelegenheit viel erfahren. Es hei&szlig;t darum in dem vom Hauptstaatsarchiv herausgegebenen B&uuml;chlein: „Ratschl&auml;ge f&uuml;r bayerische Ortsgeschichtsforscher“ auf Seite 10 nicht mit Unrecht: “Die Befragung alter Leute ist vordringlich“. Was hat mir die vor 20 &mdash; 30 Jahren verstorbene alte Huntm&uuml;llerin erz&auml;hlt. Von ihr erfuhr ich erstmals (etwas) vor dem 1. Weltkriege, vom M&uuml;hlhiasl, von der Teufelserscheinung in L&auml;mmersdorf u. von der Pest in Ruhmannsfelden. Es ist schade, da&szlig; die Ruhmannsfeldener Sterbemartrikel nicht zur&uuml;ckreichen bis zum Jahre 1613 u. welche Opfer derselbe anforderte. Ich Viechtach herrschte die Pest 1633/4.<BR>Viel &uuml;ber die Geschichte der Zachenberger Gegend wird jedenfalls in der Eberl`schen Chronik von Gotteszell zu finden sein. Ich habe es auch begr&uuml;&szlig;t, da&szlig; Herr H&ouml;gn in der Geschichte um Ruhmannsfelden auf die dortige Gegend beruhenden Sagen erw&auml;hnen kann. Ich hatte bei meinen Gesuche der Gemeindekanzleien mit einem Ihrer Herrn Kollegen zutun, so mache ich nun auf die Sagen aufmerksam: „Waltinger, niederbayerische Sagen“ besitze ich auch, ebenso wie die Sammlung "Am Sagenbaum des Bayerlandes" und die Sammlung von Steinberger "altbairische Sagen." Die in den Kriegen 1866 u 1870 gefallenen S&ouml;hne der dortigen Gegend wird man von alten Leuten noch erfahren k&ouml;nnen.<BR>Nun Herr Rektor w&uuml;rde ich Sie nochmals bitten meine Arbeit als guter Kenner der dortigen Gegend u. guter Fachmann zu erg&auml;nzen, danke Ihnen im Voraus f&uuml;r Ihre Bem&uuml;hungen u. verbleibe dieser Anf&uuml;gung der besten Gr&uuml;&szlig;e<BR><BR>ergebenster<BR><BR>Anton Trellinger</P>', '<p>\n<a href="scanns/28_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/28_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/28_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/28_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 30, 31),
(29, 'Brief von Pater Wilhelm Fink an August H&ouml;gn, 28.1.1952', 1, '1952-01-28', '28.01.1952', 3, 'Geschichte von Zachenberg', 'Ursprung des Namens &quot;Wandlhof&quot;', 'Pater Wilhelm Fink', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Sehr geehrter Herr Rektor!<br>\r\n<br>\r\nEs ist begr&uuml;&szlig;enswert, da&szlig; eine Beschreibung einer Gemeinde im Walde beabsichtigt wird. G&uuml;lt- und Stiftsverzeichnisse haben weniger der Gaben, die gereicht wurden, wegen, Wert als weil sie uns die Hofbesitzer und besonders die Herrschaften namhaft machen, die die Reichnisse empfangen haben. Die Reichnisse bleiben ja auch fast st&auml;ndig gleich, wenigstens nach 1300. Sie meinen, der Wandlhof h&auml;tte fr&uuml;her Wandldorf gehei&szlig;en, geh&ouml;rte aber der Zeit der Vorsiedlung an. Die M&ouml;glichkeit gebe ich zu. Aber zwar m&uuml;&szlig;ten noch einige Fragen gekl&auml;rt werden. Welche Lage haben die Felder? sommerseitige? Sp&auml;tere Siedlungen begn&uuml;gen sich mit weniger g&uuml;nstigen Lagen. Es m&uuml;&szlig;ten dann noch wenigstens in der Urzeit zwei kleine H&ouml;fe dabei gelegen sein. Es ist das erkenntlich an der Zahl der Tagwerke des Ackerlandes. Als ich meine Studien vor 30 Jahren aufnahm, da&szlig; der Wandlhof eine ritterliche Siedlung neben den alten Dorforten ist. Dieser Zeit geh&ouml;rt auch der Ried-ort Hafenried an. Hat er urspr&uuml;nglich aber zum Wandlhof geh&ouml;rt? Das ist die Frage. Wegen des Namens k&ouml;nnte man an den alten Namen Wantila - Wentila denken. Als Grenzbach w&auml;re er auch gut zu erkl&auml;ren. Das Gebiet war Grenzland der alten Mettener Gemarkung. Daher auch der Ort March. Grenzland ist es, da es von Seiten von Bischofsmais und Regen umfa&szlig;t wird und scheiden sich hier im Mettener Grenzland zwei Gaue, Donaugau und der Schweinachgau. So w&auml;re die Annahme, da&szlig; Wandlbach Grenzbach bedeutet wohl berechtigt.<br>\r\nInteressant ist, da&szlig; der Wandlhof den Namen Hafenried angenommen. Das w&auml;re wohl bei einer Umsiedlung kaum m&ouml;glich gewesen. So ist anzunehmen, da&szlig; der Wandlhof wohl schon anf&auml;nglich zur Rodung des Hadubet - abgek&uuml;rzt - Habe oder Hawe geh&ouml;rte.<br>\r\n<br>\r\nMit freundliche Gr&uuml;&szlig;en<br>\r\nIhr ergebener  P. Wilhelm Fink O.S.B', '<p>\n<a href="scanns/29_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/29_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/29_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/29_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 69, 30),
(30, 'Brief von Gotthard Oswald an August H&ouml;gn, 30.1.1952', 1, '1952-01-30', '30.01.1952', 3, 'Geschichte von Zachenberg', 'Ursprung der Namen &quot;Zachenberg&quot; und &quot;Hafenried&quot;', 'Gotthard Oswald', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Rinchnachm&uuml;ndt, 30.I.52<br>\r\n<br>\r\nSehr geehrter Herr Oberlehrer!<br>\r\n<br>\r\nZu Ihrer Anfrage v. 25.I. teile ich Ihnen mit:<br>\r\n<br>\r\nDie Ortsnamen sich vielfach so entstellt, da&szlig; es unm&ouml;glich ist, sie zu erkl&auml;ren, wenn nicht aus Urkunden, aus alten G&uuml;ter= u. Zehentverzeichnisse die urspr&uuml;ngliche Schreibweise herauszulesen ist. Beispielsweise hierf&uuml;r k&ouml;nnte ich Ihnen eine Menge nennen. Wenn der Ortsname Zachenberg nicht entstellt ist, dann wird ihn wohl zugrunde liegen der Personenname Zacha, die volksmundartige Abkehrung des Namens Zacharias, der im Mittelalter nicht selten als Taufname vorkommt. So hie&szlig; z. B. im 14. Jahrhundert ein Besitzer des Edelsitzes March Zacharias. Der Personenname Zacha kommt jetzt noch &ouml;fter vor als Familienname in der Form &quot;Zacher&quot; (gesprochen &quot;Zacha&quot;)<br>\r\n<br>\r\nDer Ortsname &quot;Hafenried&quot; kommt vielleicht, wenn er nicht entstellt ist, daher, da&szlig; sich hier einst ein Hafner ansiedelte. (Oder, wenn hier einst ein Hof stand, k&ouml;nnte es einst gehei&szlig;en haben &quot;Hofenried&quot; (Hofaried?).<br>\r\nDa kommt es auf die Aussprache im Volksmund an.<br>\r\nWenn P. Fink Ihnen keine Antwort gab, dann wu&szlig;te er offenbar keine Erkl&auml;rung.<br>\r\nF&uuml;r Ihre &quot;Geschichte von Ruhmannsfelden&quot; recht vielen Dank.<br>\r\n<br>\r\nMit freundlichen Gr&uuml;&szlig;en<br>\r\n<br>\r\nIhr ergebenster <br>\r\n<br>\r\nGotthard Oswald', '<p>\n<a href="scanns/30_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/30_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/30_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/30_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 29, 28),
(31, 'Brief von August H&ouml;gn an Klosterbibliothekar des  Klosters Niederalteich, 26.3.1952', 1, '1952-03-26', '26.03.1952', 3, 'Geschichte von Zachenberg', 'Anfrag um Information &uuml;ber Thad&auml;us Saller', 'August H&ouml;gn', 'Klosterbibliothekar des  Klosters Niederalteich', 1, 'Reicheneder-Chronik', 0, 'Sehr geehrter H. H. Pater!<br>\r\n<br>\r\nEs handelt sich um die Chronik der Gemeinde Zachenberg, die sich erstreckt zu beiden Seiten der Bahnlinie Station Gotteszell - Station Triefenried. Das Gebiet - angrenzend an das seinerzeitige Waldgebiet zum Kloster Niederalteich - dann im Besitz des Kloster Metten - war in der Hauptsache u. die l&auml;ngste Zeit grundbar u. zehentpflichtig zu Kloster Gotteszell. Bis die ungl&uuml;ckliche Zeit f&uuml;r die Kl&ouml;ster kam - das Jahr 1803.<br>\r\nBei dieser Chronik der Gd. Zachenberg arbeiten mit H. H. Dr. P. Amandus Bielmeier, Metten - H. H. Pfarrer Oswald Rinchnachm&uuml;ndt und H. Zollrat Trellinger, Landshut, welcher schon ein 63 Seiten umfassendes Scriptum - alles aus dem Staatsarchiv Landshut entnommen - an die Gemeindeverwaltung Zachenberg gesandt hatte. In dieser Aufzeichnung kommt unter: "Vorderdietzberg" folgendes Stelle vor: "Ein Sohn von Vietzgerg war der am 18.11.1774 geborene Thad&auml;us Saller. Wir finden ihn als M&ouml;nch in Niederalteich, er starb als Kommorant in Grafenau am 2. Sept. 1848"<br>\r\n<br>\r\nH. H. Pater! Ich w&uuml;rde Sie frdl. bitten um Mitteilung, ob &uuml;ber diesen Benediktinerm&ouml;nch Thad&auml;us Saller etwas Bemerkenwertes bekannt ist, das der obigen kurzen Mitteilung beigef&uuml;gt werden k&ouml;nnte. Es ist mir leider nicht bekannt, ob die Geschichte des Klosters Niederalteich im Druck erschienen ist u. in welchem Verlage. Da w&auml;re ich sicher beim Durchlesen auf Thad&auml;us Saller gesto&szlig;en. Ich kenne nur die diesbez. Artikel im Donauboten (G&auml;u und Wald)<br>\r\nH. H. Pater! Ich bitte Sie freundlichst um Ihre g&uuml;tigste Unterst&uuml;tzung.<br>\r\n<br>\r\nF&uuml;r Ihre Bem&uuml;hung herzlichst dankend!<br>\r\n<br>\r\nHochachtungsvollst!<br>\r\nA. H&ouml;gn', '<p>\n<a href="scanns/31_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/31_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/31_2 _gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/31_2 _klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 28, 33),
(32, 'Brief von Klosterbibliothekar Damian Merk an August H&ouml;gn, 27.3.1952', 1, '1952-03-27', '27.03.1952', 3, 'Geschichte von Zachenberg', 'Mitteilung von Information &uuml;ber Thad&auml;us Saller', 'Klosterbibliothekar Damian Merk', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'In meinem Professbuch III/364 steht Folgendes:<br>\r\n<br>\r\nP. Thad&auml;us (Franz) Sailler (Saller) geb.18.XI.1774 zu Dietlsperg oder Vorderdietzberg. <br>\r\nProfess in NA 23. Juni 1799, <br>\r\nOrdin. in  Passau 6. Juli 1800,<br>\r\nPrimiz in NA 10. August 1800, <br>\r\nKooperator in Gravenau 1803 bis 1837,  <br>\r\nKommorant in Gravenau 1837 bis 1848,<br>\r\nGestorben in Grafenau 2. September 1848.<br>\r\n<br>\r\nNiederalteich, am 27. M&auml;rz 1952<br>\r\n<br>\r\nBesten Gruss!!<br>\r\nP. Damian Merk OSB (Archivar)<br>', '<p>\n<a href="scanns/32_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/32_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 33, 34),
(33, 'Brief von August H&ouml;gn an Pfarramt Grafenau, 26.3.1952', 1, '1952-03-26', '26.03.1952', 3, 'Geschichte von Zachenberg', 'Anfrage um Information &uuml;ber Thad&auml;us Saller', 'August H&ouml;gn', 'Pfarramt Grafenau', 1, 'Reicheneder-Chronik', 0, 'H. H. Pfarrervorstand!<br>\r\n<br>\r\nEs handelt sich um die Chronik der Gemeinde Zachenberg. H. H. D. P. Amandus Bielmeier, Metten (ein Bruder des Zachenberger B&uuml;rgermeisters) H. H. Pfr. Oswald Rinchnachm&uuml;ndt u. G. Zollrat Trellinger Landshut arbeiten an der Erstellung dieser Chronik mit. Letzterer hat der Gemeinde Zachenberg ein 63 Seiten umfassendes Schreiben zu gesandt, in dem er alles Wissen verwertet &uuml;ber die einzelnen Ortschaften u. H&ouml;fe der Gd. Zachenberg aus den Archivalien des Staatsarchivs Landshut herausgeschrieben hat. Dabei kommt unter &quot;Vorderdietzberg&quot; folgende Stelle vor: &quot;Ein Sohn von Vietzgerg war der am 18.11.1774 geborene Thad&auml;us Saller. Wir finden ihn als M&ouml;nch in Niederalteich, er starb am 2. Sept. 1848 als Kommorant in Grafenau!&quot;<br>\r\nW&uuml;rde Sie, geehrter H. H. Pfarrvorstand, freundlichst bitten um Mitteilung, ob vielleicht in der dortigen Pfarrmatrikel etwas Bemerkenswertes &uuml;ber H. H. Thad&auml;us Saller zu finden ist.<br>\r\n<br>\r\nF&uuml;r Ihre Bem&uuml;hung herzlichst dankend!<br>\r\nHochachtungsvollst!<br>\r\n<br>\r\nA. H&ouml;gn<br>\r\nRektor i. R.<br>\r\nRuhmannsfelden', '<p>\n<a href="scanns/33_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/33_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 31, 32),
(34, 'Brief von Pfarrer Rankl, Grafenau an August H&ouml;gn, 27.3.1952', 1, '1952-03-27', '27.03.1952', 3, 'Geschichte von Zachenberg', 'Mitteilung von Informationen &uuml;ber Thad&auml;us Saller', 'Pfarrer Rankl, Grafenau', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Von kurzer Hand zur&uuml;ck mit dem Bemerken, da&szlig; in der Sterbematrikel hierorts sich der Eintrag findet:<br>\r\nFranz Thadd&auml; Sailer (wohl verschrieben f&uuml;r Saller) Exkonventual des Klosters Niederalteich Benediktinerordens u. von 1804 bis 1840 Cooperator in Grafenau, verstorben in Grafenau HsNr. 47 (dem heutigen Mesnerhaus, das der Kirche geh&ouml;rt) an Brand am 2. September 1848 um 8 1/2 morgens, beerdigt am 4. September durch Pfarrer Stephaner. Er war 74 Jahre alt und wurde vom Pfarrer Stephaner versehen.<br>\r\n<br>\r\nIn der hiesigen Allerseelenkapelle ist in der Nordwand die Gedenktafel dieses Priesters eingelassen. Sie tr&auml;gt die Inschrift:<br>\r\nDenkmal des Hochw&uuml;rdigen Herrn<br>\r\nFranz Thadd&auml; Sailer (merkw&uuml;rdig hei&szlig;t er auch hier wieder Sailer)<br>\r\nConventual des Benediktiner-Klosters Niederalteich und nach Aufl&ouml;sung desselben Cooperator in Grafenau bis zum Jahre 1840.<br>\r\nEr war ein Muster der Demut und ein Freund der Armen<br>\r\ngeboren am 18. November 1774<br>\r\nzum Priester geweiht am 6. Juli 1800<br>\r\ngestorben am 2. September 1884<br>\r\nR.I.P<br>\r\n<br>\r\nSelig sind die Toten, die im Herrn sterben, sie ruhen aus von ihren Leiden und ihre Werke folgen ihnen nach (Apocal 14,13)<br>\r\n<br>\r\nP.S. Im Taufbuch, wo der taufende Priester eingetragen ist hei&szlig;t es st&auml;ndig Saller, nicht Sailer bis zum Jahre 1830. Dann auf einmal Sailer mit dem Beginn eines neuen Bandes. (Unerkl&auml;rlich!)<br>\r\n<br>\r\nL. Rankl, Pfr.', '<p>\n<a href="scanns/34_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/34_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 32, 35),
(35, 'Brief von Expositus Georg Hofmann, Sch&ouml;nau an August H&ouml;gn, 23.10.1952', 1, '1952-10-23', '23.10.1952', 3, 'Geschichte von Zachenberg', 'Ursprung des Namens &quot;Zachenberg&quot;, Tipps &uuml;ber weiters Vorgehen', 'Expositus Georg Hofmann, Sch&ouml;nau', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Sch&ouml;nau, 23. Oktober 1952.<br>\r\n<br>\r\nSehr geehrter Herr Rektor!<br>\r\nEntschuldigen Sie, dass ich nicht gleich auf Ihren Brief vom 13. geantwortet habe, ich hatte in diesen Tagen viel zu tun.<br>\r\nBetreff des Namens Zachenberg kann ich mitteilen, das Studienprofessor Willibald Schmidt (ein Lehrersohn aus Moosbach, jetzt an der Realschule in Straubing) in seinem Ortsnamenverzeichnis des Bezirksamts Viechtach (1924) angibt: 1273 erscheint ein Pabo von Zachenperg. &quot;Berg eines Zacco&quot;, also mit einem alten Eigennamen zusammengesetzt, wie auch z. B. Blossersberg = um 1120 Plassansperch, Siedlung eines Plassan auf einem Berg. Den Eigennamen k&ouml;nnte man nach Schmeller, Bayerisches W&ouml;rterbuch, erkl&auml;ren entweder von &quot;zochen&quot; = langsam oder schleppend einhergehen oder von &quot;zoch&quot; = ein grober, roher, bengelhafter Mensch, dagegen bedeutet in Innertirol wieder ein Zoch Mehrzahl Zocher, einen Burschen voller Kraft und Saft. Nat&uuml;rlich sind auch noch andere Deutungen m&ouml;glich. Es l&auml;sst sich da schwer etwas bestimmtes sagen.<br>\r\nVom B&uuml;rgermeister der Gemeinde Zachenberg habe ich ein Schreiben von Herrn Zollfinanzrat Anton Trellinger nicht erhalten. Herr Trellinger hat jahrzehntelang, an der Quelle in Landshut sitzend, sehr viel Material &uuml;ber die Gemeinden des Bezirkes Viechtach gesammelt, leider hat er im Fr&uuml;hjahr einen Schlaganfall erlitten und kann daher nicht mehr so viel arbeiten.<br>\r\nDurch Umfragen in den einzelnen H&auml;usern kann man gar manches Interessante herausbringen, aber leider sind die Angaben &uuml;ber die Sesshaftigkeit der einzelnen Familien nicht immer zuverl&auml;ssig. Ich habe die alten G&uuml;terverzeichnisse, namentlich von 1668 und 1752/60 ben&uuml;tzt und dann mit der Pfarrmatrikel gearbeitet. Zuerst habe ich mir die s&auml;mtlichen Trauungen herausgeschrieben und dann verglichen, wie weit ich sie zur&uuml;ckverfolgen konnte, dann erst die Taufen und Sterbef&auml;lle. Die Pfarreien Teisnach-Geierstahl und B&ouml;brach habe ich zum Teil verzettelt und kann daher hier alles leichter &uuml;bersehen. Ruhmannsfelden ist mir zu sehr abgelegen. Doch habe ich auch von dort einige Geschlechter bearbeitet, die mit anderen zusammenh&auml;ngen, so die s&auml;mtlichen Steinbauer, Muhr, Hinkofer, Hacker.<br>\r\nAm besten beginnen Sie wohl mit dem Urkataster von ca. 1843, den Ihnen wohl Herr Trellinger abgeschrieben hat, von da k&ouml;nnen Sie dann weiter zur&uuml;ckkommen. Ich bin leider mit den dortigen Anwesen nicht ganz auf dem laufenden, bin aber gern bereit Ihnen so weit m&ouml;glich behilflich zu sein. Auch weiss ich nicht genau, welche Ortschaften zur Gemeinde Zachenberg geh&ouml;ren. Ich habe nur die Verzeichnisse vor 1800 mit den alten Grundherrschaften.<br>\r\nN&auml;chsten Sonntag ist in Viechtach ein Sippentag der Pritzl, die ich bearbeitet habe, wobei ich einen Vortrag halten soll. Bis zum Januar soll ich die Geschichte der Nussberger fertig gedruckt haben, damit sie in Jahresbericht des historischen Vereins Straubing gedruckt werden k&ouml;nnen. Leider kommen immer wieder andere Sachen dazwischen, da&szlig; ich von dieser Arbeit abgehalten werde. Vormittags habe ich fast alle Tage Schule.<br>\r\nW&uuml;nsche Ihnen recht viel Erfolg bei Ihrer Arbeit, ich weiss, es ist eine recht m&uuml;hsame Geduldsarbeit, aber man darf es sich nicht verdriessen lassen.<br>\r\n<br>\r\nRecht herzliche Gr&uuml;sse Ihr ergebener	 <br>\r\nGeorg Hofmann, Expositus', '<p>\n<a href="scanns/35_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/35_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 34, 136),
(36, 'Brief von August H&ouml;gn an Pfarrer Max Schefbeck, March, 17.1.1953', 1, '1953-01-17', '17.01.1953', 3, 'Geschichte von Zachenberg', 'Anfrage nach Informationen &uuml;ber Todesf&auml;lle beim Bahnbau in der Pfarrgemeinde March', 'August H&ouml;gn', 'Pfarrer Max Schefbeck, March', 1, 'Reicheneder-Chronik', 0, 'Sehr geehrter hochw&uuml;rdiger Herr Pfarrer!<br>\r\n<br>\r\nEs handelt sich hier um die Heimatgeschichte der Gemeinde Zachenberg. Da sind bei dem Bahnbau Deggendorf-Eisenstein im Gemeindebezirk Zachenberg deutsche u. ausl&auml;ndische Bahnarbeiter teils t&ouml;dlich verungl&uuml;ckt oder an Typhus, udgl. gestorben. Ich habe im hiesigen Pfarrhof aus den damaligen Sterberegistern entnommen, da&szlig; bei dem damaligen Bahnbau - soweit der Gemeindebezirk Zachenberg in Frage kommt - t&ouml;dlich verungl&uuml;ckt sind und hier begraben wurden: 1 Italiener, 2 B&ouml;hmen u. 2 Tiroler<br>\r\nu. da&szlig; gestorben sind von diesen ausl&auml;ndischen Bahnarbeitern: 1 Tiroler, 2 Italiener u. 4 B&ouml;hmen. Die Namen aller derer habe ich angef&uuml;hrt.<br>\r\nSehr geehrter hochw&uuml;rdiger Herr Pfarrer! D&uuml;rfte ich Sie bitten - falls   S i e   gelegentlich einmal das Sterberegister 1874 bis 1877 in H&auml;nden h&auml;tten, nachschauen zu wollen, ob da sich auch solche Eintr&auml;ge vorfinden. Wenn   j a , dann w&uuml;rde ich   S i e   herzlichst bitten, mir das g&uuml;tigst mitteilen zu wollen. Die anfallenden Geb&uuml;hren entrichte ich ganz gerne. F&uuml;r   I h r e   Bem&uuml;hung im voraus bestens dankend, zeichnet<br>\r\n<br>\r\nHochachtungsvollst!<br>\r\nErgebenster!		<br>\r\nA. H&ouml;gn Ruhmannsfelden	<br>\r\n<br>\r\nz. B. t&ouml;dlich verungl&uuml;ckt: <br>\r\nStefano Palaera, 32 Jahre alt, aus Nevaledo im S&uuml;dtirol - 1877<br>\r\ngestorben an Typhus:<br>\r\nMartinus Preghenella, 45 Jahre alt, aus Pregherr in Italien - 1875<br>', '<p>\n<a href="scanns/36_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/36_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 137, 37),
(37, 'Brief von Pfarrer Max Schefbeck, March an August H&ouml;gn, 21.1.1953', 1, '1953-01-21', '21.01.1953', 3, 'Geschichte von Zachenberg', 'Mitteilung der Opfer von Unf&auml;llen beim Bahnbau in der Pfarrgemeinde March', 'Pfarrer Max Schefbeck, March', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, '(auf die R&uuml;ckseite des vorangehenden Briefes geschrieben)<br>\r\n<br>\r\nMarch/Ndby.21.I.53<br>\r\n<br>\r\nSehr geehrter Herr Rector!<br>\r\n<br>\r\nGerne habe ich Ihren Wunsch zu erf&uuml;llen gesucht, aber leider oder Gott sei Dank nur ein Todesopfer entdeckt, das italienischen Gebl&uuml;tes ist. Ich lege Ihnen gerne den Auszug bei und hoffe Ihnen so gedient zu haben.<br>\r\n<br>\r\nBitte Familie Hertl recht herzlichst zu gr&uuml;ssen!<br>\r\n<br>\r\nErgebenst <br>\r\n<br>\r\nMax Schefbeck, Pfr.<br>\r\n(auf eine kleinem Formular beigef&uuml;gt:)<br>\r\n <br>\r\nAuszug aus dem Sterberegister der kath. Pfarrei March Ndb.<br>\r\n<br>\r\nAm 12. Februar 1876 ist in March verstorben und wurde am 13. II. 1876 in March Ndb. nach r&ouml;misch-katholischen Ritus beerdigt<br>\r\n<br>\r\nPeter Del Fabero, r&ouml;m.-katholisch<br>\r\nErdarbeiterskind z. Zt. in Triefenried (fr&uuml;hgetauft 1/4 Std. alt)<br>\r\n<br>\r\nKath. Pfarramt March Ndb.<br>\r\nM. Schefbeck, Pfr.', '<p>\n<a href="scanns/37_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/37_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 36, 17),
(38, 'Brief von Pater Wilhelm Fink, Metten an August H&ouml;gn, 23.2.1954', 1, '1954-02-23', '23.02.1954', 3, 'Geschichte von Zachenberg', 'Bericht nach Korrektur-Lesung der Geschichte von Zachenberg', 'Pater Wilhelm Fink, Metten', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'BIBLIOTHEK DER ABTEI METTEN<br>\r\n<br>\r\nMetten, den 23. 2. 54<br>\r\nSehr geehrter Herr Oberlehrer!<br>\r\n<br>\r\nSie werden entschuldigen, da&szlig; ich Sie so lange auf die Folter gespannte habe. Meine Zeit ist knapp. Da ich Ihre Arbeit genauer durchstudieren wollte, brauchte ich etwas l&auml;nger. Am besten ist der zweite Teil geraten. Er w&auml;re druckreif mit einigen kleinen &Auml;nderungen. Sie erz&auml;hlen von den Bewohnern der einzelnen H&auml;user; der Volkskundler w&uuml;rde es aber gerne sehen, wenn auch einmal so ein Haus dargestellt w&uuml;rde (Bauzeit, Einteilung der R&auml;ume, Verteilung von Stallungen, Scheunen, Schupfen, Brunnen usw., Aussehen der alten Bauernstubeck).<br>\r\nIm ersten Teil wie auch im dritten vermisse ich die rechte Anordung. Bei solchen Arbeiten wie dieser wird am besten mit einer Schilderung der Lage des betreffenden Ortes begonnen. Und Fremde, nicht Einheimische sollen die Geschichte lesen. Sie m&uuml;ssen aber wissen wo der Ort bzw. die Gemeinde liegt, z. B. allgemein zwischen Ruhmannsfelden und Regen, den Fl&uuml;ssen Teisnach und Regen, den Bahnen, Bergen etc. &Uuml;berleitung: im bayer. Wald. Sp&auml;te Besiedlung. Abschnitt &uuml;ber die Landnahme der Bayern f&auml;llt weg, ist auch nicht richtig dargestellt. Bay. Wald: bis 800 unbewohntes Waldgebiet. Hie&szlig; Nordwald, nicht zu verwechseln mit dem Nordgau, der nordwestlich von Regensburg liegt. Gegend von Zachenberg geh&ouml;rte noch zum Donaugau. Um 900 &auml;lteste Siedlung: Kloster Metten: Warum hier gesiedelt? Bodenbeschaffenheit? Wo liegen die ersten Siedlungen. Sonnen, g&uuml;nstige Seite. Gebundene Wirtschaftsform: Gr&ouml;&szlig;e der &Auml;ckerflur, davon die eine H&auml;lfte der Meier oder Gro&szlig;bauern in den D&ouml;rfern, die andere H&auml;lfte den &uuml;brigen H&ouml;fen zugeteilt. Gerade auf die Darstellung der Gr&ouml;&szlig;e der &Auml;ckerflur w&auml;re mehr Aufmerksamkeit zu schenken. 2. Siedlung: 1000 - 1200. Mittelalter: Wo liegen Burgen? Wem geh&ouml;ren sie? Nach den Arnulfingern waren die Babenberger, die zugleich Markgrafen in &Ouml;sterreich waren, Herren des Donaugaues. Ihnen geh&ouml;rte jetzt der Donaugau. Ihre Nachfolger sind 1080 die sp&auml;teren Grafen von Bogen. Ihre Ministerialen waren die Inhaber der Burgen Altnu&szlig;berg, Linden, Ruhmannsfelden, Wei&szlig;enstein, die Nu&szlig;berger, Degenberger, Pfellinger. Ried-orte. Zu beachten, da&szlig; damals auch durch die Bischofe von Passau und den Burggrafen dort die Siedlungen bis an die Grenzen der beiden Di&ouml;zesen Regensburg und Passau, der beiden alten Gaue Donau und Schweinachgau. Beide Siedlungsperioden eigen sind Burg- und Bachnamen. Aussterben der Bogener 1242, Erben Wittelsbacher, Gr&uuml;ndung von M&auml;rkten Viechtach, Regen. Viechtach Sitz eines Gerichtes, B&ouml;brach und Regen Schergen&auml;mter. Wichtig die Gr&uuml;ndung von Gotteszell. Eingehen auf seine Geschichte Zachenberg, Ort und Gemeinde in Frage kommen. Bedeutung: Seelsorge, geistiger Mittelpunkt. Aufhebung. Jetzt. Neu-Ordnung des bay. Staates: Aufhebung der Grundherschaft. Abgaben blieben bis 1848 (Abl&ouml;sung durch die Bodenzinnse) Hof und Flurfreies Eigentum des Besitzes. Schaffung der Steuergemeinden, Zusammenf&uuml;gen der Siedlungen 1818: Gemeinden Selbstverwaltungsk&ouml;rper (1. Gemeindeordnung, Schaffung der Gem. Zachenberg im Landgerichte Viechtach. Jetzt Statistiken: Gr&ouml;&szlig;e, Zahl der Einwohner in verschiedenen Perioden. Und jetzt der 2. Teil! Das Ganze mu&szlig; gar nicht aneinander wachsen. Im 3. Teil: Organisation der Gemeinde: B&uuml;rgermeister, Gemeinderat, -schreiber, -diener u. s. w. Aufgaben der Gemeinde: Baupolizei, Gewerbeaufsicht (Darstellung wann und wie viel in den einzelnen Perioden und was gebaut oder ernannt wurde welche Gewerbe alte wie Wirte, B&auml;cker, Metzger nun nach Einf&uuml;hrung der Gewerbefreiheit, Schuster, Schneider, Weber etc.) Armen-F&uuml;rsorge: Kranken: Invaliden: Arzt, Sicherheit: Gendarmerie etc. Schulen: Hat die Gemeinde Besitz? Verkehrswesen: Stra&szlig;en: Kraftfahrwesen, Bahn, Post. Telephonzugang. Gemeinn&uuml;tzige Vereine: Feuerwehr etc. Politk &auml;ndert sich. Kirche: wenig Ver&auml;nderung. Pfarrei Ruhmannsfelden. Kirchliches und religi&ouml;ses Leben, Volkskunde, Geistliche, die aus der Pfarrei hervorgegangen, Wallfahrten, Kapellen u. Volksfr&ouml;mmigkeit, prf. Volkskunde: Heimats-, Faschingsbr&auml;uche etc.<br>\r\nZum Schlusse die Schattenseiten des Lebens: Krieg, Hunger und Pest, Seuche.<br>\r\nIch glaube, da&szlig; in einer solchen Anordnung die Geschichte Zachenbergs ein angenehmes, weil &uuml;bersichtliches und ein n&uuml;tzliches Lesen sein wird. Zum Schluss darf ich auf einige Irrt&uuml;mer .... (Seitenende das Blattes, weiteres Blatt nicht &uuml;berliefert)', '<p>\n<a href="scanns/38_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/38_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/38_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/38_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 124, 39),
(39, 'Brief von August H&ouml;gn an B&uuml;rgermeister Ludwig Bielmeier, Zachenberg, 31.3.1954', 1, '1954-03-31', '31.03.1954', 3, 'Geschichte von Zachenberg', 'Brief bei der &Uuml;bergabe des vollendeten und revidierten Fassung der "Geschichte von Zachenberg"', 'August H&ouml;gn', 'B&uuml;rgermeister Ludwig Bielmeier, Zachenberg', 1, 'Reicheneder-Chronik', 0, 'Sehr geehrter Herr B&uuml;rgermeister!<br>\r\n<br>\r\nErlaube mir Ihnen hiemit die druckfertige schriftliche Arbeit &uuml;ber "Heimatgeschichte der Gemeindeflur Zachenberg mit seinen Ortschaften" vorl&auml;ufig einmal zur Einsichtnahme u. zum Durchlesen in Vorlage zu bringen.<br>\r\nDiese Arbeit wurde von H. H. Professor P.   F i n k   in Metten durchgesehen u. einer gr&uuml;ndlichen Korrektur unterzogen. Alles - was er dabei - als " nicht richtig dargestellt " gefunden hat, wurde weggelassen u. ist in dieser Arbeit nicht mehr enthalten. Was H. H. Expositus Hofmann von Sch&ouml;nau noch erg&auml;nzend gew&uuml;nscht hatte, wurde in diese Arbeit nachtr&auml;glich aufgenommen.<br>\r\nIn dieser Form d&uuml;rfte die beiliegende Arbeit doch allgemeinem Interesse begegnen, da es ein wirklich interessanter Lesestoff ist. Ich bitte H. B&uuml;rgermeister, diese Arbeit vielleicht an den Osterfeiertag durchlesen zu wollen u. sich selbst dar&uuml;ber ein Urteil zu bilden. H. H. Expositus Hofmann fragt, ob die Gemeinde Zachenberg diese Heimatgeschichte ver&ouml;ffentlichen lassen will. Damit ich auch dar&uuml;ber gleich Aufschlu&szlig; geben kann, habe ich bei Firma La&szlig;leben in Kallm&uuml;nz angefragt. Dort ist auch das B&uuml;chlein der Geschichte von Ruhmannsfelden gedruckt worden. La&szlig;leben teilte mir mit, da&szlig; 500 B&uuml;cher auf 900 DM kommen, also das Buch auf 1,80 DM, so da&szlig; dann im Verkauf das Buch auf 2,50 DM oder 3 DM k&auml;me. Als Absatzgebiet k&auml;men Gd. Zachenberg, Gd. Ruhmannsfelden, Gd. March u. Stadt Regen in Frage. Freilich m&uuml;&szlig;te zuvor Reklame gemacht werden in der Presse u. durch Anschlag einer Bekanntmachung, vielleicht in der Form: Heimatgeschichte der Gemeindeflur Zachenberg mit seinen s&auml;mtlichen Ortschaften erschienen. Interessanter Lesestoff. Preis ........ Bestellung in der Gd. Kanzlei Z. - Gd. Achslach hat den Verkauf eines solchen B&uuml;chleins selbst besorgt. Es k&ouml;nnte zuvor auch eine Vorbestellung in die Wege geleitet werden, so da&szlig; man sich &uuml;ber den Absatz des Buches einigerma&szlig;en orientieren kann.<br>\r\nDas Buch sollte wenigstens in der Gd. Zachenberg in keinem Haus u. in keiner Familie fehlen. Die 3 DM k&ouml;nnen in Zigaretten wieder hereingespart werden. Der Gemeinde Zachenberg verblieb doch ein ansehnlicher Gewinn.<br>\r\n<br>\r\nHochachtungsvoll<br>\r\n<br>\r\nA. H&ouml;gn', '<p>\n<a href="scanns/39_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/39_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/39_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/39_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 38, 40),
(40, 'Brief von B&uuml;rgermeister Ludwig Bielmeier, Zachenberg an August H&ouml;gn, 7.5.1956', 1, '1956-05-07', '07.05.1956', 3, 'Geschichte von Zachenberg', 'Danksagung an August H&ouml;gn wegen der "Geschichte von Zachenberg"', 'B&uuml;rgermeister Ludwig Bielmeier, Zachenberg', 'August H&ouml;gn', 1, 'Reicheneder-Chronik', 0, 'Betreff: Heimatgeschichte der Gemeinde Zachenberg<br>\r\n<br>\r\nHochverehrter Herr Rektor!<br>\r\nIn der ersten Sitzung der Legislaturperiode 1956/57 hat sich der Gemeinderat mit Ihrem Schreiben vom 30.4.1956 eingehend befasst und gr&ouml;&szlig;tes Interesse f&uuml;r Ihre heimatkundliche Arbeit bekundet. Es wurde zum Ausdruck gebracht, da&szlig; Ihre Arbeit f&uuml;r das heimatkundliche Geschehen unserer Gemeinde begr&uuml;&szlig;t und anerkannt wird. Der Gemeinderat &uuml;bernimmt f&uuml;r die Akten und die Chronik der Gemeinde gerne Ihre umfangreichen heimatkundlichen Aufschreibungen und sorgt bis zur Drucklegung f&uuml;r sichere Aufbewahrung.<br>\r\n&Uuml;ber die Drucklegung wird der Gemeinderat in der n&auml;chsten Sitzung beschliessen. Der Gemeinderat hat beschlossen, Ihnen f&uuml;r Ihre Arbeit eine finanzielle Anerkennung zukommen zu lassen. Ich darf Sie daher bitten, mir f&uuml;r die n&auml;chste Sitzung einen Betrag zu nennen, der Ihre Arbeit einigermassen entsch&auml;digen w&uuml;rde. Wir wissen, da&szlig; Ihre Arbeiten aus Idealismus entstanden sind. Sollten Sie mir keinen Betrag nennen, wird der Gemeinderat von sich &uuml;ber die H&ouml;he der Anerkennung f&uuml;r Ihre vorbildliche Arbeit beschliessen.<br>\r\nIch m&ouml;chte mich auf diesen Wege im Namen des Gemeinderates und auch in meinen Namen f&uuml;r Ihre bisherige Arbeit auf diesem Gebiete f&uuml;r unserer Gemeinde aufrichtig bedanken und bin &uuml;berzeugt, da&szlig; Sie auch weiterhin der Gemeinde mit Rat und Hilfe bei derartigen Arbeiten zu Verf&uuml;gung stehen.<br>\r\n<br>\r\nHochachtungsvollst!<br>\r\n<br>\r\nIhr		<br>\r\nBielmeier, 1. B&uuml;rgermeister', '<p>\n<a href="scanns/40_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/40_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 39, 96);
INSERT INTO `dokumente` (`id`, `titel`, `dok_art`, `datum`, `datumtext`, `thematik`, `thematiktext`, `thema`, `feld1`, `feld2`, `dok_fundort`, `dok_fundorttext`, `grafik_ja`, `text`, `gif`, `previous`, `next`) VALUES (41, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 15.6.1953', 2, '1953-06-15', '15.06.1953', 4, 'Auff&uuml;hrungen von Kompositionen', 'Installation von Pfarrer Reicheneder', 'Viechtacher Bayerwald-Bote', 'Feierliche Installation des neuen Pfarrherrn', 1, 'Reicheneder-Chronik', 0, 'Feierliche Installation des neuen Pfarrherrn<br>\r\nPfarrer Reicheneder empfing aus der Hand des Dekans den Kirchenschl&uuml;ssel<br>\r\n<br>\r\nRuhmannsfelden. Der neue Pfarrherr von Ruhmannsfelden Pfarrer Franz       R e i c h e n e d e r,    wurde am Sonntagvormittag unter gro&szlig;er Anteilnahme der Pfarrbev&ouml;lkerung durch den Dekan des Dekanats Unterviechtach, Geist. Rat   L i p f,  offiziell in seine neue Pfarrei eingef&uuml;hrt. Vom Pfarrhof holten die Ortsvereine den Geistlichen ab und geleiteten ihn im feierlichen Zuge unter dem Gel&auml;ute der Glocken in die festlich geschm&uuml;ckte Pfarrkirche. Den Zug begleiteten Landrat   K a u e r,  die B&uuml;rgermeister   P i e h l e r   und   B i e l m e i e r,   der Marktgemeinderat die kath. Kirchenverwaltung, die Feuerwehr, alle Ortsvereine und viele Gl&auml;ubige. ....<br>\r\n.... Das feierliche Hochamt zelebriete der neue Pfarrherr bei festlichem Gesang des Kirchenchores. Ergreifend trug der klangvolle Chor die &quot;Missa St. Josephi&quot; von Rektor H&ouml;gn vor. Mit dem feierlichen Te Deum, in das die gesamte Pfarrgemeinde einstimmte, schlo&szlig; diese kirchliche Festfeier f&uuml;r die Pfarrei Ruhmannsfelden. Anschlie&szlig;end &uuml;bergab Dekan Lipf noch den Friedhof an den Geistlichen, der die Gr&auml;ber mit Weihwasser besprengte.', '<p>\n<a href="scanns/41_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/41_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 17, 18),
(42, 'Zeitungsartikel aus Deggendorfer Zeitung, 18.3.1959', 2, '1959-03-18', '18.03.1959', 4, 'Auff&uuml;hrungen von Kompositionen', 'Ank&uuml;ndigung der Deggendorfer Erstauff&uuml;hrung der Josephs-Messe', 'Deggendorfer Zeitung', 'Josephsmesse zum Josephsfest', 3, 'Beilage zum Notenmaterial', 0, '<P>Josephsmesse zum Josephsfest<BR>Morgen Erstauff&uuml;hrung einer Komposition August H&ouml;gns<BR><BR>Am morgigen Josephstage erlebt die Josephsmesse eines geb&uuml;rtigen Deggendorfers ihre Deggendorfer Erstauff&uuml;hrung: Rektor August H&ouml;gn ist der Komponist, und in St. Martin wird seine Festmesse zum Pfarrgottesdienst um 9 Uhr erklingen. Im Hause des 1913 verstorbenen Buchh&auml;ndlers Andreas H&ouml;gn erblickte dessen Sohn August am 2. August 1878 das Licht der Welt. Der Umgang mit B&uuml;chern im elterlichen Hause und eine auffallende musikalische Begabung mochten in der Berufswahl den Ausschlag zum Lehrerberuf gegeben haben. Im Straubinger Lehrerseminar hierf&uuml;r vorbereitet, lie&szlig; sich der 30jahrige nach kurzen Zwischenstationen an niederbayerischen Dorfschulen endg&uuml;ltig in Ruhmannsfelden nieder, seiner Heimatstadt nicht allzufern und doch entr&uuml;ckt dem st&auml;dtischen Betrieb und umwoben von der Stille der Bayerwaldberge, die gerade f&uuml;r einen schaffenden Musiker so wichtig und - wie auch in diesem Fall - so ersprie&szlig;lich ist. <BR>Die Messe tr&auml;gt die Opuszahl 62, und das will - da es sich immerhin um eine nebenberufliche Liebhaberei handelt - schon etwas hei&szlig;en. Das musikalische Lebenswerk H&ouml;gns tragt den Stempel echter und guter Gebrauchsmusik, d. h. es ist Musik, die zum Gebrauch bestimmt und auch zu &quot;brauchen&quot; ist und nicht in den luftleeren Raum hineinkomponiert wurde. Die l&auml;ndliche Stille und der Umgang mit den einfachen, unkomplizierten Menschen, mit denen er in der Schule, auf dem Kirchenchor und in der Gesellschaft zu tun hatte, hat ihn auch vor sogenannten modernen Experimenten bewahrt. Trotzdem ist er auch hier Erzieher, der den Boden unter den F&uuml;&szlig;en zwar nie verliert und doch, den ethischen Bildungswert der Musik erkennend, Blick und Ohr f&uuml;r H&ouml;heres weiten m&ouml;chte. <BR>So ist auch diese Messe ein ehrliches Selbstzeugnis, des Musikers sowohl wie des Menschen. Sie verr&auml;t gediegene Handwerkskunst, die sich in einer sauberen satztechnischen Handschrift &auml;u&szlig;ert, Sinn f&uuml;r harmonische Farbigkeit hat, ohne in sp&auml;tromantische Chromatik abzugleiten, und sich der Mittel des Kontrastes in der Gegen&uuml;berstellung kraftvoller Unisoni und motivisch aufgelockerter Chors&auml;tze bedient. Doch das Wichtigste; der Mensch, der all dies zutiefst im Glauben bejaht, steht hinter dem ganzen Werk. Mag die morgige Auff&uuml;hrung darum eine herzliche Gabe &quot;postfestum&quot; an den 80-j&auml;hrigen Komponisten sein. Da&szlig; es auf den Tag genau der 60. Geburtstag von Joseph Haas ist, ist ein sch&ouml;ner Zufall. Auch ihn hat die musikalische Begabung zum Lehrberuf gef&uuml;hrt, den Grunds&auml;tzen seiner Jugend treu im Glauben, und in der Liebe bis heute geblieben. Eine Br&uuml;cke zu Anton Bruckner, dem Volksschullehrer von Windnhaag, lie&szlig;e sich von hier aus leicht schlagen. Mag die Musik dies selbst besorgen, wenn als Offertorium Bruckners &quot;Os justi&quot; und Joseph Haas'' Lied zum hl. Josephs zum Eingang der Messe erklingt, die in Anwesenheit des Komponisten morgen in St. Martin erstaufgef&uuml;hrt wird. Ihm und seinem gro&szlig;en &quot;Kollegen&quot; Joseph Haas m&ouml;chten wir ein herzliches &quot;Ad multos annos&quot; mit in die T&ouml;ne hineinverweben. <BR>Fritz Goller</P><br>\r\n<P><BR> </P>', '<p>\n<a href="scanns/42_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/42_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 49, 54),
(43, 'Zeitungsartikel aus Plattlinger Zeitung, 19.3.1957', 2, '1957-03-19', '19.03.1957', 4, 'Auff&uuml;hrungen von Kompositionen', 'Ank&uuml;ndung der Erstauff&uuml;hrung der Josephs-Messe in der Magdalenen-Kirche', 'Plattlinger Zeitung', 'Heute gibt es eine Erstauff&uuml;hrung', 3, 'Beilage zum Notenmaterial', 0, 'Heute gibt es eine Erstauff&uuml;hrung<br>\r\n<br>\r\nZum heutigen Hochamt in der Magdalenenkirche bringt der Kirchenchor eine Festmesse zu Ehren des hl. Josef in Erstauff&uuml;hrung. Es handelt sich um ein Werk des Rektors a. D. A. H&ouml;gn, der in Ruhmannsfelden lebt. H&ouml;gn ist kein Neut&ouml;ner, sondern pflegt einen romantischen, liebenswerten Kompositionsstil. Das Benediktus f&uuml;r Sopransolo und Chor wird sicher allen gefallen, die die Musik nicht erst &uuml;ber den Verstand, sondern gleich ins Herz flie&szlig;en lassen wollen.', '<p>\n<a href="scanns/43_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/43_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 127, 44),
(44, 'Brief von Gustl G&uuml;dner an August H&ouml;gn, 22.3.1957', 1, '1957-03-22', '22.03.1957', 4, 'Auff&uuml;hrungen von Kompositionen', 'Brief bei der Zur&uuml;cksendung der Josephs-Messe an August H&ouml;gn', 'Gustl G&uuml;dner', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'Plattling, 22.3.57<br>\r\nSehr geehrter Herr Rektor H&ouml;gn!<br>\r\nAnbei folgt Ihre Josephsmesse mit bestem Dank zur&uuml;ck. Sie hat am Josephsfest gut gefallen. Ich habe verschiedene Stimmen dar&uuml;ber geh&ouml;rt. Unserer geistl. Rat hier hat sich sehr gefreut &uuml;ber die Auff&uuml;hrung. Da&szlig; es so lange bis zur R&uuml;cksendung gedauert hat, bitte ich nochmals zu entschuldigen.<br>\r\nMit besten Gr&uuml;&szlig;en bin ich Ihr erg.<br>\r\nGustl G&uuml;dner', '<p>\n<a href="scanns/44_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/44_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 43, 48),
(45, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 6.8.1974', 2, '1974-08-06', '06.08.1974', 4, 'Auff&uuml;hrungen von Kompositionen', 'Verabschiedung von Pfarrer Reicheneder', 'Viechtacher Bayerwald-Bote', 'Ein schwerer, ein ehrender Abschied f&uuml;r den scheidenden Priester', 1, 'Reicheneder-Chronik', 0, 'Ein schwerer, ein ehrender Abschied f&uuml;r den scheidenden Priester<br>\r\nAbschiedsgru&szlig; an die Pfarrgemeinde: &quot;Der Friede sei mit euch&quot;<br>\r\n<br>\r\nRuhmannsfelden. Mit dem Gru&szlig;: &quot;Der Friede sei mit Euch&quot;, mit dem er vor 21 Jahren die Pfarrgemeinde begr&uuml;&szlig;t hatte, verabschiedete sich am vergangenen Sonntag Geistlicher Rat Franz S. Reicheneder von seiner Pfarrgemeinde. In einem feierlichen Abschiedsgottesdienst, der vom Kirchenchor unter Leitung von Karl Geiger mit der &quot;Josefi-Messe&quot; von A. H&ouml;gn und den Hymnen &quot;Die Himmel r&uuml;hmen&quot;, &quot;Nun danket alle Gott&quot;, &quot;Herr unser Gott&quot; festlich umrahmt wurde, unterstrich der scheidende Seelsorger die Bedeutung des Friedens in unserer Zeit. ....', '<p>\n<a href="scanns/45_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/45_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/45_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/45_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 53, 46),
(46, 'Zeitungsartikel aus Regensburger Bistumsblatt, 24.8.1974', 2, '1974-08-24', '24.08.1974', 4, 'Auff&uuml;hrungen von Kompositionen', 'Verabschiedung von Pfarrer Reicheneder\r\n', 'Regensburger Bistumsblatt', 'Nach 21 Jahren Abschied von Ruhmannsfelden', 1, 'Reicheneder-Chronik', 0, 'Nach 21 Jahren Abschied von Ruhmannsfelden<br>\r\n<br>\r\nRuhmannsfelden. Mit dem Gru&szlig; &quot;Der Friede sei mit Euch&quot;, mit dem er vor 21 Jahren die Pfarrgemeinde begr&uuml;&szlig;t hatte, verabschiedete sich nun Geistl. Rat Franz S. Reicheneder von seiner Pfarrgemeinde. In einem feierlichen Abschiedsgottesdienst, der vom Kirchenchor unter Leitung von Karl Geiger mit der &quot;Josefi-Messe&quot; von A. H&ouml;gn und den Hymnen &quot;Die Himmel r&uuml;hmen&quot;, &quot;Nun danket alle Gott&quot;, &quot;Herr unser Gott&quot; festlich umrahmt wurde, unterstrich der scheidende Seelsorger die Bedeutung des Friedens in unserer Zeit. Die F&auml;higkeit Frieden nach au&szlig;en weiterzugeben, besitze aber nur der, der selbst Frieden im Herzen trage. ....', '<p>\n<a href="scanns/46_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/46_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 45, 47),
(47, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 4.10.1974', 2, '1974-10-04', '04.10.1974', 4, 'Auff&uuml;hrungen von Kompositionen', 'Installation von Pfarrer Krottenthaler', 'Viechtacher Bayerwald-Bote', 'Mensch bleiben in einer Zeit der Hektik und des Flie&szlig;bandes', 1, 'Reicheneder-Chronik', 0, 'Mensch bleiben in einer Zeit der Hektik und des Flie&szlig;bandes<br>\r\nPfarrer Otto Krottenthaler feierlich installiert - Dank an Geistlichen Rat Reicheneder<br>\r\n<br>\r\nRuhmannsfelden. Am vergangenen Sonntag feierte die Pfarrgemeinde die feierliche Installation des neuen Pfarrers. Ein stattlicher Kirchenzug, angef&uuml;hrt vom Spielmannszug der Freiwilligen Feuerwehr, begleitete Pfarrer Otto Krottenthaler zur Pfarrkirche. Neben den kirchlichen und weltlichen Vereinen der Marktgemeinde bekundeten als Vertreter des &ouml;ffentlichen Lebens Landrat Helm. Feuchtinger sowie die Gemeinder&auml;te der Marktgemeinde Ruhmannsfelden und der Gemeinde Zachenberg mit ihren B&uuml;rgermeistern Alois Zellner und Georg Artmann ihre Anteilnahme und Verbundenheit zur hiesigen Pfarrgemeinde.<br>\r\nZu Beginn des Festgottesdienstes richtete Pfarrer Krottenthaler, nach seiner Vorstellung durch Dekan G&uuml;nthner aus Bodenmais, Begr&uuml;&szlig;ungsworte an die Pfarrgemeinde. Er versicherte, er wolle alles in seiner Macht Stehende tun, um der Gemeinde ein guter Seelsorger zu sein. Im Verlauf des Festgottesdienstes, vom Kirchenchor umrahmt mit der &quot;Josefi-Messe&quot; v. A. H&ouml;gn und den Ch&ouml;ren &quot;Gro&szlig; ist der Herr&quot; und &quot;Preiset mit feurigem Danke den Herrn&quot; wurde mit der feierlichen &Uuml;bergabe des Evangelienbuches durch Dek. G&uuml;nthner die symbolische &Uuml;bertragung der Pfarrei vollzogen. W&auml;hrend der Opferung &uuml;bergaben Dekan G&uuml;nthner u. Franz Hacker, Vorsitzender des Pfarrgemeinderates, Kelch und Hostienschale an Pfarrer Krottenthaler.', '<p>\n<a href="scanns/47_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/47_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 46, 74),
(48, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 2.8.1958', 2, '1958-08-02', '02.08.1958', 5, '80. Geburtstag', 'Kurzbiographie H&ouml;gns anl&auml;sslich seines 80. Geburtstags', 'Viechtacher Bayerwald-Bote', 'Rektor H&ouml;gn feiert 80. Geburtstag', 1, 'Reicheneder-Chronik', 0, 'Rektor H&ouml;gn feiert 80. Geburtstag<br>\r\nDer Jubilar ist Ehrenb&uuml;rger der Gemeinde<br>\r\n<br>\r\nRuhmannsfelden. Heute kann Rektor August H&ouml;gn seinen 80. Geburtstag feiern. Der Jubilar wurde am 2. August 1878 in Deggendorf als Sohn der Buchh&auml;ndlerseheleute Andreas und Helene H&ouml;gn, letztere eine geborene Z&ouml;pfl, Kaufmannstochter von Geiselh&ouml;ring, geboren. Er besuchte die Knabenschule in Deggendorf, die damalige Lateinschule in Metten und dann die Pr&auml;parandenschule in Deggendorf. Nach dem Absolutorium der Lehrerbildungsanstalt in Straubing in Jahre 1898 praktizierte er an der Knabenschule in Deggendorf unter Hauptlehrer Buchner und Edelmann, kam dann als Aushilfslehrer nach Neukirchen bei Haggn, Bezirk Bogen, sp&auml;ter nach Schaufling (Deggendorf) Geratskirchen (Eggenfelden) und wurde Hilfslehrer in Zeilarn bei Simbach am Inn. Von dort wurde Rektor H&ouml;gn nach Wallersdorf versetzt, legte 1902 an der Regierung von Niederbayern die Anstellungspr&uuml;fung ab und wurde an 1. Januar 1903 Schulverweser in Wallersdorf. Dort verehelichte er sich im Juli 1904 mit der Bierbrauerstochter Emma Gerstl. Aus der harmonischen Ehe (die Gattin starb erst 39j&auml;hring im Jahre 1926) gingen zwei Kinder hervor, ein Sohn und eine Tochter.<br>\r\nAm 1. Juni 1905 kam H&ouml;gn in den Bayerischen Wald nach Eberhardsreuth im Kreis Grafenau. Seit 1. Januar 1910 wirkt der Jubilar an der Volksschule Ruhmannsfelden, die er bis 1945 zur vollsten Zufriedenheit der Schulbeh&ouml;rde leitete. Im Jahre 1929 wurde H&ouml;gn zum Oberlehrer und 1940 zum Rektor ernannt. Nach Beendigung des Krieges mu&szlig;te er den Schuldienst quittieren und kam erst wieder 1947, jedoch nur kurze Zeit bis zu seiner Pensionierung in den Schuldienst.<br>\r\nAu&szlig;er dieser, nahezu 50j&auml;hrigen Erziehungsarbeit war er seit 1898 ununterbrochen bei den Kirchench&ouml;ren, teils als Aushilfe, teils als Chorleiter t&auml;tig. Er galt als guter S&auml;nger und t&uuml;chtiger Organist sowie auch Kirchenkomponist. Au&szlig;erdem f&uuml;hrte er die Gemeindeschreiberei der Gemeinde Zachenberg von 1913 bis 1920, war w&auml;hrend dieser Zeit auch Rechner des Darlehenskassenvereins Ruhmannsfelden und 40 volle Jahre Schriftf&uuml;hrer der Freiwilligen Feuerwehr Ruhmannsfelden. Gleichzeitig war er auf dem Gebiete der Heimatforschung schriftstellerisch t&auml;tig. Er schuf das Heimatb&uuml;chlein &quot;Geschichte des Marktes Ruhmannsfelden&quot; und die &quot;Geschichte der Gemeinde Zachenberg&quot;, die demn&auml;chst in Druck erscheinen wird.<br>\r\nDer Jubilar ist Ehrenb&uuml;rger der Gemeinde Ruhmannsfelden und Zachenberg und wurde f&uuml;r treue Mitarbeit in der Mundartforschung von der W&ouml;rterbuchkommission der Bayerischen Akademie der Wissenschaften in M&uuml;nchen bereits im Jahre 1933 mit einer Urkunde geehrt. W&auml;hrend des ersten Weltkrieges wurde der Jubilar 1915 zum Heeresdienst eingezogen und erwarb sich neben schon verliehenen Orden wie dem K&ouml;nig-Ludwig-Orden auch das Milit&auml;r-Verdienstkreuz. Sein Lieblingshobby ist die J&auml;gerei.<br>', '<p>\n<a href="scanns/48_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/48_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 44, 49),
(49, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 5.8.1958', 2, '1958-08-05', '05.08.1958', 5, '80. Geburtstag', 'Nachbericht des 80. Geburtstag von August H&ouml;gn', 'Viechtacher Bayerwald-Bote', '&quot;Sch&ouml;n die Abendglocken klangen&quot;', 1, 'Reicheneder-Chronik', 0, '&quot;Sch&ouml;n die Abendglocken klangen&quot;<br>\r\nRektor H&ouml;gn wurde zum 80. Geburtstag geehrt<br>\r\n<br>\r\nRuhmannsfelden. Ein gro&szlig;er Teil der Bev&ouml;lkerung nahm Anteil an dem 80. Geburtstag von Rektor H&ouml;gn, um so das reiche Schaffen des Jubilars zu w&uuml;rdigen. Viele sch&ouml;ne Geschenke gingen zu diesem Ehrentag im Haus in der Schulstra&szlig;e ein und gaben Zeugnis davon, da&szlig; Rektor H&ouml;gn weit &uuml;ber die Grenzen des Landkreises hinaus bekannt und geachtet ist. Wie im Vorjahr brachte der M&auml;nnerchor auch diesmal am Vorabend des Festes unter Leitung von Franz Danziger seinem Ehrenmitglied ein St&auml;ndchen. Nach dem Abendl&auml;uten sang der Chor &quot;Sch&ouml;n die Abendglocken klangen&quot; und Vorstand Hans Czech gratulierte f&uuml;r die Sangesbr&uuml;der.<br>\r\nB&uuml;rgermeister Hans Muhr sprach anschlie&szlig;end f&uuml;r die Marktgemeinde die besten Gl&uuml;ckw&uuml;nsche aus und &uuml;berreichte ein Geschenk. Damit sollte ausgedr&uuml;ckt werden, da&szlig; man das Wirken des Rektors als umsichtigen P&auml;dagogen in Ruhmannsfelden nicht vergessen hat. Den Gratulanten schlo&szlig; sich dann B&uuml;rgermeister Ludwig Bielmeier aus Zachenberg an. Auch er &uuml;berbrachte ein Geschenk. Der Vorstand der Freiwilligen Feuerwehr, Max S&uuml;&szlig;, w&uuml;nschte zum Fest viel Gl&uuml;ck und &uuml;berreichte eine Ehrenurkunde. Gleichzeitig &uuml;berbrachte er dem Jubilar die Goldene Ehrennadel des Vereins, die h&ouml;chste Auszeichnung, die die Ruhmannsfeldener Wehr zu vergeben hat. Bei der Feuerwehr ist Rektor H&ouml;gn Ehrenschriftf&uuml;hrer. F&uuml;r den Krieger- und Veteranenverein sprach Alois Steinbauer dem Ehrenmitglied die herzlichsten Gl&uuml;ckw&uuml;nsche aus und &uuml;bergab ein Geschenk.<br>\r\nRektor August H&ouml;gn dankte allen Vereinsvorst&auml;nden f&uuml;r die guten W&uuml;nsche und berichtete mit Humor kleine Episoden die er bei diesem oder jenem Verein w&auml;hrend seiner jahrzehntelangen T&auml;tigkeit erlebt hat. Dem M&auml;nnerchor versprach er, weiteres Notenmaterial zur Verf&uuml;gung zu stellen. Am Festtag selbst marschierte auch noch die Ruhmannsfeldener Blaskapelle auf, um dem Musiker H&ouml;gn eine Geburtstagsfreude zu bereiten. Unter der Stabf&uuml;hrung von Ludwig Heinrich erklang eines der Lieblingslieder des Jubilars &quot;Gr&uuml;n ist die Heide&quot;.', '<p>\n<a href="scanns/49_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/49_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 48, 42),
(50, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 14.12.1961', 2, '1961-12-14', '14.12.1961', 6, 'Tod von August H&ouml;gn', 'Todesanzeige\r\n', 'Viechtacher Bayerwald-Bote', 'Todesanzeige', 1, 'Reicheneder-Chronik', 0, 'Am Mittwoch, den 13. Dezember 1961, fr&uuml;h 5 Uhr, nahm Gott der Herr nach Empfang der hl. Sterbesakramente im 84. Lebensjahr zu sich in die Ewigkeit den ehrengeachteten <br>\r\n<br>\r\nHerrn August H&ouml;gn<br>\r\n<br>\r\nlangj&auml;hriger Lehrer und Rektor an der Volksschule Ruhmannsfelden<br>\r\n<br>\r\nDer Verstorbene versah viele Jahre hindurch mit unerm&uuml;dlichem Flei&szlig; auch den Dienst des Organisten an der hiesigen Pfarrkirche.<br>\r\n<br>\r\nEs ist uns daher ehrende Verpflichtung, dem Verstorbenen auch auf diesem Wege f&uuml;r diese T&auml;tigkeit ein aufrichtiges &quot;Vergelts Gott&quot; in die Ewigkeit nachzurufen.<br>\r\n<br>\r\nZu &auml;u&szlig;eren Zeichen der Dankbarkeit wird f&uuml;r ihm am kommenden Donnerstag, 9 Uhr, in der Pfarrkirche ein feierliches Requiem angehalten werden, an dem sich auch die hiesige Schule beteiligen wird. An alle Pfarrangeh&ouml;rigen, die ihn ja zum gro&szlig;en Teil auch als Lehrer in der Schule hatten, ergeht hiermit Einladung zur Teilnahme<br>\r\n<br>\r\nRuhmannsfelden, den 14. Dezember 1961<br>\r\n<br>\r\nDie kath. Kirchenverwaltung Ruhmannsfelden<br>\r\nGeorg Krieger, Kirchenpfleger  <br>\r\nFranz Ser. Reicheneder, Pfarrer<br>', '<p>\n<a href="scanns/50_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/50_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 112, 51),
(51, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 15.12.1961', 2, '1961-12-15', '15.12.1961', 6, 'Tod von August H&ouml;gn', 'Nachruf', 'Viechtacher Bayerwald-Bote', 'Er war Mitstreiter f&uuml;r alles Gute und Wahre', 1, 'Reicheneder-Chronik', 0, 'Er war Mitstreiter f&uuml;r alles Gute und Wahre<br>\r\nRektor a. D. August H&ouml;gn starb im 84. Lebensjahr<br>\r\n<br>\r\nRuhmannsfelden. Im 84. Lebensjahr stehend, starb am Mittwoch nach langer schwerer Krankheit Rektor a. D. August H&ouml;gn. Ein aufrechtes, arbeitsames und in G&uuml;te gereiftes Leben fand damit seinen Abschlu&szlig;. Es war das Leben eines Mannes, des sein umfangreiches Wirken auf humanistischen Grunds&auml;tzen aufgebaut und der reiches Wissen und K&ouml;nnen sehr oft und mit selbstloser Begeisterung in den Dienst seiner Mitmenschen gestellt hatte. In seiner lauteren und gel&auml;uterten Lebensart war er nicht nur ein Mitstreiter f&uuml;r alles Gute und Wahre, sondern er vermittelte auch still und bescheiden seinen vielen Freunden und Kameraden seine edle Anschauung &uuml;ber die vielf&auml;ltigen Belange des Lebens. Mit dem Tod von August H&ouml;gn, der weit &uuml;ber die Grenzen seiner Heimat bekannt, geachtet und gesch&auml;tzt war, verlieren die Marktgemeinde und auch die Gemeinde Zachenberg, deren Ehrenb&uuml;rger der Verstorbene war, einen ihrer Besten.<br>\r\nNicht nur als umsichtiger P&auml;dagoge, der sich seinen Sch&uuml;tzlingen stets sehr gewissenhaft und mit verst&auml;ndnisvoller Liebe gewidmet hatte, sondern auch als anerkannter Heimatkundler war Rektor H&ouml;gn bekannt. &quot;Die Geschichte von Ruhmannsfelden&quot; und auch &quot;Die Geschichte von Zachenberg&quot; entstammen nach langer und gr&uuml;ndlicher Forschung seiner Feder. Aber auch als Komponist, - er hatte mehrere sch&ouml;ne Messe geschrieben, - war er hervorgetreten. Jahrzehnte hatte er auch als Chorleiter und Organist in der hiesigen Pfarrkirche gewirkt, bis er dieses Amt, das ihm immer so viel bedeutet hatte und dem er zu jeder Zeit von ganzem Herzen zugetan war, krankheitshalber hat aufgeben m&uuml;ssen.<br>\r\nSeit 1910, dem Jahr seiner Ankunft in Ruhmannsfelden nach seinem Wirken in Eberhardsreuth, hatte er sich in den &ouml;rtlichen Vereinen mit seiner vitalen Kraft und mit dem seine Person kennzeichnenden Idealismus zur Verf&uuml;gung gestellt. Er war ma&szlig;gebend am Aufbau der Turnhalle beteiligt, war seit 1904 Mitglied der Feuerwehr. Unterst&uuml;tzte stets aktiv und passiv, die jeweiligen Gesangsvereinigungen, diente Jahrzehnte im Krieger- und Veteranenverein und war fast 60 Jahre Mitglied des Bayerischen Lehrervereines.<br>\r\nAlle diese genannten Verb&auml;nde hatten &quot;ihren&quot; August H&ouml;gn in W&uuml;rdigung seiner au&szlig;erordentlichen Verdienste zum Ehrenmitglied oder Ehrenfunktion&auml;r ernannt. Der Bayerische Lehrerverein hatte ihm das Goldene Ehrenblatt f&uuml;r 50j&auml;hriges Mitwirken verliehen, das Landesamt der Bayerischen Feuerwehren hatte ihm die h&ouml;chste Auszeichnung &uuml;berreicht, er war Inhaber der Goldenen Ehrennadel des Bayerischen Turnerbundes und war auch im Besitz der Goldenen Plakette des Veteranenvereins. Als bezeichnend sei noch erw&auml;hnt, da&szlig; August H&ouml;gn um 1910 bis 1920 ehrenamtlich alle schriftlichen Arbeiten f&uuml;r die Gemeinde Zachenberg erledigt hatte.<br>\r\nDas Herz eines Menschen, der schlicht und einfach, und doch so stark, sein in sich abgerundetes Leben immer wieder selbstlos in den Dienst der Gemeinschaft gestellt hatte, schl&auml;gt nicht mehr. Die sterblichen &Uuml;berreste wurden noch am Sterbetag nach Deggendorf &uuml;berf&uuml;hrt, um neben seiner, am 19. Juni 1926 verstorbenen Frau, die August H&ouml;gn am 20. Juli 1904 geehlichte hatte, beigesetzt zu werden. Trotzdem werden sich die Fahnen der Ruhmannsfeldener Vereine am Tag der Beerdigung symbolisch vor dem Grabe ihres Rektors senken zum Zeichen daf&uuml;r, da&szlig; Rektor H&ouml;gn allen seines edlen Wesens wegen Vorbild bleiben m&ouml;ge. Das Lied &quot;Vom guten Kameraden&quot; m&ouml;ge ihm letzter, herzlicher Gru&szlig; sein.', '<p>\n<a href="scanns/51_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/51_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 50, 52),
(52, 'Brief von Elfriede Schlumprecht an Lehrer Schambeck, 3.1.1962', 1, '1962-01-03', '03.01.1962', 6, 'Tod von August H&ouml;gn', 'Dankesschreiben nach der Beerdigung von August H&ouml;gn', 'Elfriede Schlumprecht', 'Lehrer Schambeck', 5, 'Schul-Chronik', 0, 'Dankesschreiben nach der Beerdigung von August H&ouml;gn<br>\r\n<br>\r\nM&uuml;nchen, den 3.1.1962<br>\r\n<br>\r\nSehr geehrter Herr Lehrer Schambeck! Sie sprachen so herzliche Worte des Abschieds am Grabe meines verstorbenen Vaters, da&szlig; ich auf das tiefste bewegt war. Es hat mir wohlgetan zu h&ouml;ren, da&szlig; mein Vater bei seiner Kollegenschaft der Ruhmannsfeldener Schule geachtet und beliebt gewesen ist. Ihnen f&uuml;r die freundlichen Worte des Mitgef&uuml;hl und f&uuml;r den sch&ouml;nen Kranz zu danken, den Sie im Namen Ihrer Kollegen am Grabe niederlegten, ist mir ein aufrichtiges Bed&uuml;rfnis.<br>\r\n<br>\r\nMit meinen besten Gr&uuml;&szlig;en und nochmaligen Dank<br>\r\n<br>\r\nIhre<br>\r\nElfriede Schlumprecht', '<p>\n<a href="scanns/52_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/52_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 51, 53),
(53, 'Brief von Elfriede Schlumprecht an Rektor Langesee, 3.1.1962', 1, '1962-01-03', '03.01.1962', 6, 'Tod von August H&ouml;gn', 'Dankesschreiben nach der Beerdigung von August H&ouml;gn', 'Elfriede Schlumprecht', 'Rektor Langesee', 5, 'Schul-Chronik', 0, 'Sehr geehrter Herr Rektor! Ich m&ouml;chte mich besonders bei Ihnen f&uuml;r die herzlichen Worte des Abschieds und der Anerkennung bedanken, die Sie meinem Vater am Grabe gewidmet haben. Es war f&uuml;r mich ein besonderer Trost, wiederum zu h&ouml;ren, da&szlig; mein lieber Vater als Mensch wie als P&auml;dagoge bei allen, die mit ihm zu tun hatten, beliebt und geachtet gewesen ist. Es ist mir ein besonderes Bed&uuml;rfnis, Ihnen f&uuml;r die Herzlichkeit, mit der Sie der Person und dem Wirkens meines Vaters gedacht haben, nochmals meinen Dank zu sagen. Diesen Dank bitte ich auch Herrn Kreisschulrat Botschafter zu &uuml;bermitteln.<br>\r\n<br>\r\nMit meinen besten Gr&uuml;ssen und Dankesworten bin ich<br>\r\n<br>\r\nIhre<br>\r\n<br>\r\nElfriede Schlumprecht', '<p>\n<a href="scanns/53_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/53_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 52, 45),
(54, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 7.5.1959', 2, '1959-05-07', '07.05.1959', 12, 'Schule', 'Einweihung des Schulanbaus', 'Viechtacher Bayerwald-Bote', 'Gemeinde hat Verst&auml;ndnis f&uuml;r ortsgebundene Landschule', 5, 'Schul-Chronik', 0, 'Gemeinde hat Verst&auml;ndnis f&uuml;r ortsgebundene Landschule<br>\r\nDer Anbau zum Schulhaus wurde feierlich von Pfarrer Reicheneder geweiht<br>\r\n<br>\r\nR u h m a n n s f e l d e n. Bei sch&ouml;nstem Sommerwetter wurde am Christihimmelfahrtstag der Zubau zum Schulhaus von Pfarrer Franz Reicheneder eingeweiht. Eine Festmesse war vorausgegangen. Architekt Ritz &uuml;bergab den Schl&uuml;ssel zum neuen Geb&auml;ude an B&uuml;rgermeister Muhr. Nach dem Weiheakt folgten Ansprachen der Vertreter, der Beh&ouml;rden und &Auml;mter. Ein Festmahl schlo&szlig; die Feier. ....<br>\r\n... Der verst&auml;rkte Kirchenchor unter Leitung von Chorleiter Danziger und die Ruhmannsfeldener Blaskapelle sorgten f&uuml;r die musikalische Umrahmung des Festgottesdienstes.<br>\r\nZwanglos und ohne Festzug begab man sich dann das kurze St&uuml;ck Weg von der Kirche zum Festakt beim Schulhaus. B&uuml;rgermeister Wolfgang Muhr konnte Oberregierungsschulrat Dr. Limmer, Landshut, MdB Dr. Stefan Dittrich, MdL Alois Rainer, Bezirksrat Ferdinand Kollmer, Landrat Rudolf Kauer, Schulrat Bothschafter, Architekt Ritz, Kreisbaumeister Pfeiffer, die B&uuml;rgermeister der benachbarten Gemeinden Glasschr&ouml;der, Gotteszell, Bielmeier, Zachenberg und Jungbeck, Patersdorf, sowie Rektor H&ouml;gn und Rektor Langesee mit den &uuml;brigen Lehrkr&auml;ften begr&uuml;&szlig;en. Nach einem Gedicht, das drei M&auml;dchen vortrugen, sang der M&auml;dchenchor unter Leitung von Lehrerin Piehler den Chor ?Br&uuml;der reicht die Hand zum Bunde" von Mozart. Architekt Ritz, der in der Bauleitung auf den umfangreichen Vorarbeiten von Kreisbaumeister Pfeiffer fu&szlig;en konnte, &uuml;bergab den Schl&uuml;ssel zum neuen Geb&auml;ude an B&uuml;rgermeister Wolfgang Muhr mit dem Wunsch, da&szlig; das Geb&auml;ude Lehrern wie Kindern eine gute Erziehungsst&auml;tte sein m&ouml;ge. ?', '<p>\n<a href="scanns/54_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/54_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 42, 70),
(55, 'Artikel in der Reicheneder-Chronik &uuml;ber August H&ouml;gn', 3, '0000-00-00', 'o. D.', 99, 'Sonstige Dokumente', 'Kurzbiographie von August H&ouml;gn', 'Artikel in der Reicheneder-Chronik &uuml;ber August H&ouml;gn', '', 1, 'Reicheneder-Chronik', 0, 'Rubrik: Schul- und Bildungswesen, Lehrerpersonal geordnet nach Rektoren<br>\r\n<br>\r\nVI. August H&ouml;gn<br>\r\n1921 bis 1945 bzw. 1947<br>\r\n<br>\r\nAugust H&ouml;gn wurde am 2. 8.1878 in Deggendorf geboren. Sein Lebenslauf bis zu seinem Wirken in Ruhmannsfelden ist enthalten in dem Berichten des Viechtacher Bayerwaldboten, die anl&auml;sslich seines 80. Geburtstages erschienen sind.<br>\r\nNach Ruhmannsfelden kam er am 1.1.1910 und wirkte dann hier als 2. Lehrer unter Auer, bis er am 1.10.1921 dessen Nachfolger als 1. Lehrer antrat. Er wurde hier Oberlehrer und Hauptlehrer und schlie&szlig;lich 1940 Rektor der Schule Ruhmannsfelden.<br>\r\nIm Jahre 1926 starb in Ruhmannsfelden seine Frau im Alter von erst 39 Jahren.<br>\r\nAls 1. Lehrer &uuml;bernahm er dann in Ruhmannsfelden auch den Organistendienst (wenn auch Schul- und Kirchendienst bereits getrennt waren) und &uuml;bte denselben auch noch in seinem Ruhestand bis zum Jahre 1953 aus.<br>\r\nEinen Namen hat er sich auch gemacht als Forscher an der Heimatgeschichte. Zahlreiche geschichtliche Abhandlungen sind von ihm in Zeitschriften und Zeitungen erschienen.<br>\r\nDie Geschichte der Gemeinde Zachenberg und die der Feuerwehr Ruhmannsfelden liegen in Schreibmaschinenschrift vor.<br>\r\n&Uuml;ber den Markt Ruhmannsfelden gab er ein B&uuml;chlein heraus: &quot;Geschichte von Ruhmannsfelden&quot;<br>\r\nAus Anla&szlig; seines 25.j&auml;hrigem Dienstjubil&auml;ums wurde er am 21.7.1923 in dankbarer Anerkennung seiner gro&szlig;en Verdienste um Gemeinde und Schule zum Ehrenb&uuml;rger der Marktgemeinde Ruhmannsfelden ernannt.<br>\r\nWie alle Lehrkr&auml;fte an den Schulen, auch an der Schule Ruhmannsfelden wurde er nach dem 2. Weltkrieg und dem Ende des Hitlerreiches seines Dienstes enthoben. Wurde aber dann 1947 nochmals kurz angestellt und ging dann noch im gleichen Jahr in den endg&uuml;ltigen Ruhestand. Er verbrachte denselben in Ruhmannsfelden. Am 13.12.1961 ist er im Alter von 84 Jahren gestorben. Die Beerdigung fand in Deggendorf statt.', '<p>\n<a href="scanns/55_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/55_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/55_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/55_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 0, 77),
(56, 'Auszug aus den Memoiren von Franz Danziger sen., 1984', 3, '1984-00-00', '1984', 99, 'Sonstige Dokumente', 'Erinnerungen an August H&ouml;gn', 'Auszug aus den Memoiren von Franz Danziger sen., 1984', '', 99, 'Sonstige Fundorte', 0, '... In der Zeit von 1921 bis 1924 galt mein besonderes Interesse der Arbeit im Katholischen Gesellen - und Meisterverein, heute Kolping. Besonders das Theaterspielen hatte es mir angetan. Wir spielten gro&szlig;e Sing- und Schauspiele wie: &quot;Bettelprinzessin&quot;, &quot;Henkersohn und Zigeunerin&quot;, &quot;Zunftmeister von N&uuml;rnberg&quot;, &quot;&Uuml;ber Land und Meer&quot;, viele Volksst&uuml;cke wie: &quot;Austragsst&uuml;berl&quot;, &quot;Das Gl&uuml;ck vom Riedhof&quot; u. andere. Auch bei dem vom Radlerverein aufgef&uuml;hrten St&uuml;ck: &quot;Der Tatzelwurm&quot; im Mai 23 und die vom Turnverein inszenierte Operette &quot;Der Postillion&quot; wirkte ich mit. Als zweiter Geiger war ich auch in dem von Rektor H&ouml;gn gef&uuml;hrten Orchester bei weltlichen und kirchlichen Veranstaltungen t&auml;tig', '<p>\n<a href="scanns/56_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/56_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 74, 0),
(57, 'Brief von Gregorius-Verlag, Regensburg an August H&ouml;gn, 16.6.1947', 1, '1947-06-16', '16.06.1947', 1, 'Kirchenmusik', 'Empfangsbest&auml;tigung der Herz-Jesu-Litanei', 'Gregorius-Verlag, Regensburg', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'Herrn Rektor A. H&ouml;gn, Ruhmannsfelden/ Ndb.<br>\r\n<br>\r\nSehr geehrter Herr Rektor!<br>\r\n<br>\r\nWir best&auml;tigen mit verbindlichem Dank den Eingang Ihrer Herz-Jesu-Litanei. Wir hoffen, Ihnen in B&auml;lde N&auml;heres mitteilen zu k&ouml;nnen und bitten bis dahin um Ihre Geduld.<br>\r\n<br>\r\nMit vorz&uuml;glicher Hochachtung<br>\r\n<br>\r\nGregorius-Verlag<br>\r\nvorm. Friedrich Pustet<br>\r\nRegensburg<br>\r\nVerlagssekretariat<br>\r\nKlein<br>\r\n<br>\r\nRegensburg, 16.6.47<br>\r\nK.', '<p>\n<a href="scanns/57_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/57_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 59, 193),
(58, 'Brief von Pfarrer J. Huber, Otzing an August H&ouml;gn, 6.9.1951', 1, '1951-09-06', '06.09.1951', 1, 'Kirchenmusik', 'Dankesschreiben f&uuml;r Offertorium', 'Pfarrer J. Huber, Otzing', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'St. Ottilien, 6.9.51<br>\r\n<br>\r\nVor hier, wo ich mehrere Tage &quot;exerzierte&quot;, sende ich Ihnen einen ergebenen Dankesgru&szlig; f&uuml;r Ihr gediegenes Offertorium, das ich gern dem Otzinger kirchenmusikalischen Repertoir zur Auff&uuml;hrung einverleibe.<br>\r\n<br>\r\nIhr<br>\r\n<br>\r\nJ. Huber, Pfr.', '<p>\n<a href="scanns/58_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/58_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 64, 76),
(59, 'Brief von Gregorius-Verlag, Regensburg an August H&ouml;gn, 9.6.1947', 1, '1947-06-09', '09.06.1947', 1, 'Kirchenmusik', 'Bitte um Zusendung der Herz-Jesu-Litanei', 'Gregorius-Verlag, Regensburg', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'Sehr geehrter Herr Rektor!<br>\r\n<br>\r\nWir sind im Besitz Ihrer Anfrage vom 25.5.47 und m&ouml;chten Sie bitten, die &quot;Herz-Jesu-Litanei&quot; uns zu einer unverbindlichen Einsichtnahme zuzusenden. Erst nach Kenntnis derselben k&ouml;nnen wir hierzu n&auml;her Stellung nehmen.<br>\r\n<br>\r\nAllerdings m&ouml;chten wir Sie heute schon darauf aufmerksam machen, dass infolge der gegenw&auml;rtigen schwierigen Lage auf dem Papiermarkt die Aussichten f&uuml;r die Ver&ouml;ffentlichung nicht sehr gross sind.<br>\r\n<br>\r\nIn letzter Zeit wurde an neuen Messen nichts herausgegeben.<br>\r\nWir sehen Ihren weiteren Nachrichten mit Interesse entgegen und begr&uuml;ssen Sie<br>\r\n<br>\r\nmit vorz&uuml;glicher Hochachtung<br>\r\n<br>\r\nGregorius-Verlag<br>\r\n<br>\r\nvorm. Friedrich Pustet<br>\r\nRegensburg<br>\r\ni. A. Klein', '<p>\n<a href="scanns/59_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/59_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 60, 57),
(60, 'Brief von Sebaldus-Verlag, Bamberg an August H&ouml;gn, 20.5.1947', 1, '1947-05-20', '20.05.1947', 1, 'Kirchenmusik', 'Absage f&uuml;r die Drucklegung der Herz-Jesus-Litanei', 'Sebaldus-Verlag, Bamberg', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'In Erledigung Ihres Schreibens vom 10.5.47 teilen wir Ihnen mit, da&szlig; wir vorl&auml;ufig keine M&ouml;glichkeit sehen, Ihre Herz-Jesu-Litanei in unser Verlagsprogramm aufzunehmen.<br>\r\n<br>\r\nWir bitten Sie sich deswegen an den Gregorius-Verlag (vormals Friedrich Pustet) in Regensburg zu wenden, der vermutlich die Herausgabe Ihrer Litanei &uuml;bernehmen wird.<br>\r\n<br>\r\nHochachtungsvoll!<br>\r\nSebaldus Verlag<br>\r\nZweigstelle Bamberg', '<p>\n<a href="scanns/60_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/60_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 184, 59),
(61, 'Brief von F. Mitterwallner, Deggendorf an August H&ouml;gn, 25.2.1947', 1, '1947-02-25', '25.02.1947', 1, 'Kirchenmusik', 'Benachrichtigung zur Abholung eines Briefes', 'F. Mitterwallner, Deggendorf', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'Herrn Rektor H&ouml;gn Ruhmannsfelden.<br>\r\nM&ouml;chte sie benachrichtigen, dass meine Tochter Martha einen Brief von Dir. Haberl Musikschule Regensburg hierher mirgenommen hat, u. Sie denselben b. Gelegenheit abholen lassen wollen.<br>\r\n<br>\r\nDeggendorf, den 25.2.47<br>\r\nMit Gruss!<br>\r\n<br>\r\nF. Mitterwallner', '<p>\n<a href="scanns/61_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/61_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 216, 65),
(62, 'Brief von Franz ..., Regen an August H&ouml;gn, 17.6.1928', 1, '1928-06-17', '17.06.1928', 1, 'Kirchenmusik', 'Bitten, ein Offertorium zu komponieren', 'Franz ..., Regen', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, 'Lieber Gustl!<br>\r\n<br>\r\nich br&auml;uchte f&uuml;r den 5. August (Sonntag) ein Offertorium f&uuml;r M&auml;nnerchor. Seiso gut u. komponiere mir ein leichtes. Wir singen gelegentlich des Heimattages in Zwiesel das Ponitifikalamt in Rinchnach.<br>\r\nN&auml;chsten Sonntag haben wir einen S&auml;ngerausflug nach Ruhmannsfelden (Vornehm.) Was gibts Neues?<br>\r\n<br>\r\nHerzl. Gr&uuml;&szlig;e<br>\r\n<br>\r\ndein Franzl<br>\r\n<br>\r\nRegen, 17.6.28', '<p>\n<a href="scanns/62_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/62_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 98, 119),
(63, 'Brief von August H&ouml;gn an Pfarrer J. Huber, Otzing, 14.8.1951', 1, '1951-08-14', '14.08.1951', 1, 'Kirchenmusik', 'Bitte um Zusendung eins Laurentius-Liedes', 'August H&ouml;gn', 'Pfarrer J. Huber, Otzing', 3, 'Beilage zum Notenmaterial', 0, 'Ruhmannsfelden, 14.8.51<br>\r\n<br>\r\nSehr geehrter H. H. Pfarrer!<br>\r\n<br>\r\nSoeben lese ich im Donauboten, da&szlig; bei dem 50-j&auml;hrigem Kirchenjubil&auml;um in Otzing vor dem Wettersegen ein sch&ouml;nens Laurentiuslied - von Ihnen vertont - gesungen wurde. Unsere Pfarrkirche hier ist auch eine Laurentiuskirche u. ich suchen schon Jahrzehnte nach einen Laurentiuslied oder passenden Text. Ich w&auml;re Ihnen sehr dankbar, wenn Sie der hiesigen Pfarrkirche dieses Lied zur Verf&uuml;gung stellen w&uuml;rden. Die Abschrift w&uuml;rde ich schnellstens besorgen u. Ihre Auslagen verg&uuml;ten.<br>\r\nIch habe das &quot;Confessio&quot; 4st. mit. Org. Begl. u. eine leicht singbare 4 st. Laurentius-Messe mit Org. Begl. (beides von mir vertont)<br>\r\n<br>\r\nMit vorz&uuml;glicher Hochachtung!<br>\r\nErgebenster<br>\r\nA. H&ouml;gn', '<p>\n<a href="scanns/63_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/63_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/63_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/63_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 113, 64),
(64, 'Brief von Pfarrer J. Huber, Otzing an August H&ouml;gn, 22.8.1951', 1, '1951-08-22', '22.08.1951', 1, 'Kirchenmusik', 'Brief zu &Uuml;bersendung des Laurentius-Liedes', 'Pfarrer J. Huber, Otzing', 'August H&ouml;gn', 3, 'Beilage zum Notenmaterial', 0, '(Wegen Unleserlichkeit macher Textpassagen sind mache W&ouml;rter erraten!)<br>\r\n<br>\r\nOtzing, 22.8.51<br>\r\n<br>\r\nGr&ouml;&szlig;ter Herr Oberlehrer!<br>\r\n<br>\r\nDie Melodie ist sanglich u. volkst&uuml;mlich. Die Orgelbegleitung habe ich wegen S&auml;ngermangel geschrieben. 4 stimmig Bla&szlig;begleitung m&uuml;sste die Wirkung erh&ouml;hen.<br>\r\nDie Melodie habe ich erst am Tag vor der Auff&uuml;hrung in Eile niedergeschrieben u. ist nett gesungen gut zu brauchen. Sie hat aber mit Orgelbegleitung gut gefallen.<br>\r\n<br>\r\nWenn sie mehr Kirchenmusikalien anschaffen wollen, kann ich Sie gut beraten, da ich 27 Jahre Chorregent war. Entschuldigen Sie meine Antwort auf diese Art! Sie geschah der Eile wegen.<br>\r\n<br>\r\nErgebenster J. Huber, Pfr.', '<p>\n<a href="scanns/64_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/64_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/64_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/64_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 63, 58),
(65, 'Brief von August H&ouml;gn an Dr. Haberl, Regensburg, Mrz. 1947', 1, '1947-03-00', 'Mrz. 1947', 1, 'Kirchenmusik', 'Enstehungegeschichte des Liedes &quot;Stille Nacht&quot;', 'August H&ouml;gn', 'Dr. Haberl, Regensburg', 3, 'Beilage zum Notenmaterial', 0, 'Sehr geehrter H. Dr. Haberl!<br>\r\n<br>\r\nF&uuml;r Ihre g&uuml;tige Mitteilung betr. &quot;Stille Nacht&quot; sage ich Ihnen recht &quot;Herzlichen Dank&quot;. Ihre vorl&auml;ufigen Ausf&uuml;hrungen haben meine Auffassung in der  Frage der Entstehung des Liedes &quot;Stille Nacht&quot; dahin bekr&auml;ftigt, da&szlig; das Lied - von Frz. Gruber 1818 komp. nicht - den Pilgern auf ihrer Wanderschaft durch das Salzburger Land - geh&ouml;rt u. in unsere heimatliche Gaue gebracht wurde. Um das Jahr 1500 herum, kannte man doch diese melodischen Lieder - wie es das einzig sch&ouml;ne Lied &quot;Stille Nacht&quot; ist - noch nicht, sondern diese Lieder sind erst in der Nachreformationszeit - anfang des 17 Jahrhunderts - Hassler, Sch&uuml;tz, Silcher etc. - entstanden. -<br>\r\n<br>\r\nSehr geehrter H. Dr. ich &uuml;bersende Ihnen wunschgem&auml;&szlig; die betr. Urkunde durch Frl. Martha Mitterwallner Deggend., die die Liebenw&uuml;rdigkeit hat, Ihnen das Schriftst&uuml;ck zu &uuml;bergeben. - Bitte Sie dasselbe durch zu lesen. Ich w&auml;re Ihnen sehr verbunden, wenn ich Ihre ma&szlig;gebliche Ansicht hier&uuml;ber erfahren k&ouml;nnte. - Indem ich die mir zugesandten Neujahrw&uuml;nsche erwidere - verbleibe ich<br>\r\n<br>\r\nmit ergebensten Gr&uuml;&szlig;en<br>\r\nIhr Ergebenster!<br>\r\n<br>\r\nA. H&ouml;gn', '<p>\n<a href="scanns/65_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/65_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/65_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/65_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/65_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/65_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 61, 191),
(66, 'Todesbenachrichtigung, 13.12.1961', 3, '1961-12-13', '13.12.1961', 6, 'Tod von August H&ouml;gn', 'Todesbenachrichtigung', 'Todesbenachrichtigung, 13.12.1961', '', 5, 'Schul-Chronik', 1, 'Am 13. Dezember 1961 entschlief unser g&uuml;tiger, treusorgender Vater, unser lieber Schiegervater, Gro&szlig;vater, Urgro&szlig;vater, Bruder und Onkel<br>\r\n<br>\r\nHerr August H&ouml;gn<br>\r\nRektor i. R.<br>\r\n<br>\r\nim gesegnetem Alter von 83 Jahren.<br>\r\n<br>\r\nIn tiefer Trauer:<br>\r\nim Namen der Familie<br>\r\nAugust H&ouml;gn<br>\r\nElfriede Schlumprecht geb. H&ouml;gn<br>\r\n<br>\r\nRuhmannsfelden, den 13. Dezember 1961<br>\r\n<br>\r\nTrauergottesdienst mit darauffolgender Beerdigung am Freitag, den 15. Dezember 1961, um 9 Uhr in der Stadtpfarrkirche Mari&auml; Himmelfahrt in Deggendorf.', '<p>\n<a href="scanns/66_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/66_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 73, 112),
(67, 'Zeugnis von 1926', 3, '1926-03-31', '31.03.1926', 12, 'Schule', 'Zeugnis mit der Unterschrift von August H&ouml;gn', 'Zeugnis von 1926', '', 99, 'Sonstige Fundorte', 1, '', '<p>\n<a href="scanns/67_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/67_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 133, 109),
(68, 'Zeugnis von 1932', 3, '1932-04-06', '06.04.1932', 12, 'Schule', 'Zeugnis mit der Unterschrift von August H&ouml;gn', 'Zeugnis von 1932', '', 99, 'Sonstige Fundorte', 1, '', '<p>\n<a href="scanns/68_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/68_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 122, 134),
(69, 'Exzerpt von August H&ouml;gn aus Fink, Ostbairische Grenzmarken, 1952', 3, '1952-00-00', '1952', 3, 'Geschichte von Zachenberg', 'Exzerpt von August H&ouml;gn aus Fink, Ostbairische Grenzmarken', 'Exzerpt von August H&ouml;gn aus Fink, Ostbairische Grenzmarken, 1952', '', 1, 'Reicheneder-Chronik', 0, 'K&ouml;ckersried<br>\r\n<br>\r\nVon P. W. Fink: &Ouml;stl. Grenzmarken 1922 Heft 8<br>\r\n<br>\r\nK&ouml;kesried: vergl. Maurer, Ortsnamen des Hochstifts Passau 57 <br>\r\n<br>\r\nGoggesreut - P. M. Cotessschalk (R. V. 122 6,3)<br>\r\n(Vorstehendes aus &Ouml;stl. Grenzmarken 1922 Heft 8<br>\r\nIn der Karte gleichen Heftes S. 139<br>\r\nK&ouml;ckersried = Eigenname, ew. im R. V.<br>\r\n<br>\r\nVorstehende Karte im gleichem Heft S. 139<br>\r\nDas Ganze Gebiet um K&ouml;ckersried war vor der Schenkung an Metten oder bei derselben, nicht besiedelt, war also ausgesprochenes Rodungsland. K&ouml;ckersried - wie alle anderen &quot;ried&quot; Namen sind erst sp&auml;ter entstanden. Zuerst kamen die mit Eigennamen zusammengesetzten &quot;ing&quot; Namen, die echten. Solche sind hier nicht. Sp&auml;ter kommen die &quot;dorf&quot; Namen (Mettner Gr&uuml;ndungen) u. &quot;berg&quot; Namen. Zachberg d&uuml;rfte &auml;lter sein wie K&ouml;ckersried. In einer Niederalteicher Urkunde v. J. 1273, in einer p&auml;pstlichen Bulle v. J. 1274 wird Zachenberg erw&auml;hnt. Die Nu&szlig;berg, dann die n&auml;chtigen Degenberger hatten in Zachenberg Besitzungen<br>\r\nVon dem bereits i. J. 1404 als teilweise Nu&szlig;berger&apos;sches Besitztum aufgef&uuml;hrten K&ouml;ckersried, so bemerkt, da&szlig; nach dem herzoglichen Salbuche v. J. 1577 2 G&uuml;ter Frau Rosina von Hauff auf Neu-Nussberg inne hatte u. diese G&uuml;ter waren dem Kaplan zu Neu Nu&szlig;berg g&uuml;ltpflichtig. Es mu&szlig; also im dortigen Scho&szlig; ein eigener Kaplan aufgestellt gewesen sein. 4 G&uuml;ter von K&ouml;ckersried geh&ouml;rten nach dem beredten Salbuche mit der G&uuml;lt dem Kloster Gotteszell. Das in K&ouml;ckersried sich befindliche an Stelle einer 1803 abgebrochenen Kapelle, erbaute Kirchlein, entstand in der Jahren 1830 - 33. Es ist dem Erzengel Michael geweiht. Messe wird in derselben nicht gelesen. K&ouml;ckersried das bis 1806 zur Pfarrei Ruhmannsfelden geh&ouml;rte, ist jetzt nach Gotteszell eingepfarrt. (Vorstehendes v. G. Trellinger Landshut in Bayerwald)<br>', '<p>\n<a href="scanns/69_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/69_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 76, 29),
(70, 'Brief von August H&ouml;gn an Stephan Leitner, 9.1.1960', 1, '1960-01-09', '09.01.1960', 7, 'private Korrespondenz', 'Brief an den ehemaligen Sch&uuml;ler Stephan Leitner nach Australien. Themen: Erster Brief nach Australien. Schilderung der Begebenheiten zur Weihnachtszeit.', 'August H&ouml;gn', 'Stephan Leitner', 4, 'Briefe aus dem Besitz von Stephan Leitner', 0, '9.I.60<br>\r\n<br>\r\nWerte Frau Leitner!<br>\r\n<br>\r\nSehr geehrter Herr Leitner!<br>\r\n<br>\r\nHabe heute 9. Januar 1960 Ihren lieben Brief aus Australien erhalten. Besten Dank f&uuml;r Ihre gro&szlig;e Aufmerksamkeit. Bei uns hier herrscht keine winterliche Stimmung. Es regnet die ganze Weihnachtszeit. Schlitten- u. Skifahren konnte man nur im Zugspitzgebiet, also Garmischer Gebiet - also wei&szlig;e Ostern. Ist auch gut. Wir sind das schon gewohnt. Von der gro&szlig;en Hitze in Australien haben aber die Zeitungsmeldungen schon Nachrichten gebracht u. von der ungew&ouml;hnlich gro&szlig;en Hitze bei Euch. Das mag nicht gut sein, zumal bei k&ouml;rperlicher Arbeit.<br>\r\nDas  Christkind kam hier wie &uuml;blich mit Christbaum u. Krippe u. den &uuml;blichen Weihnachtsgeschenken. Die Leute besuchten die Kirche. Auf der Stra&szlig;e war es ziemlich leer. F&uuml;r mich waren Weihnachten nicht trostlos, sondern langweilig u. ohne Abwechslung. Daf&uuml;r haben wir jetzt ein bischen Schnee. Zum Zeitvertreib mu&szlig; man halt lesen oder sich eine andere Arbeit suchen. Die Gegend gef&auml;llt Ihnen dort in Australien ganz gut. Es ist ein Gebiet wie ungef&auml;hr auf dem Landshuter Haus. Das trifft sich gut. Das Lager geht auch. Und die Gesellschaft wird sich schon finden u. Arbeit wird es auch bald geben. Nun guht! Dann haben wir alles beisammen. Gestern am Dreik&ouml;nigstag hatte ich Besuch von Hartl Leni, die von Westamerika zur&uuml;ckkam u. mir viel erz&auml;hlte von dort. Sie ist seinerzeit mit Dr. Grundner ausgewandert, hat ihr so la-la gegangen u. ist jetzt gl&uuml;cklich wieder daheim zu sein. Heute ist hier Feuerwehrball mit &ouml;ffentlichen Fackelzug wie fr&uuml;her! Der Fasching hat schon begonnen.<br>\r\n<br>\r\nW&uuml;nsche Ihnen u. Fr. Gemahlin recht viel Gl&uuml;ck, Gesundheit u. Segen des Himmels u. da&szlig; es Ihnen dort recht gut ergehe.<br>\r\nBitte! La&szlig;en sie gelegentlich wieder was h&ouml;ren! Es gr&uuml;&szlig;t sie vielmals u. die Fr. Gemahlin ebenso <br>\r\nIhr<br>\r\nErgebenster<br>\r\nAug. H&ouml;gn<br>\r\nOberlehrer u. Rektor i. R.<br>\r\n13 b Ruhmannsfelden<br>\r\nNdby.<br>\r\nbayer. Wald<br>\r\nSchulstr. 120', '<p>\n<a href="scanns/70_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/70_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/70_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/70_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 54, 71);
INSERT INTO `dokumente` (`id`, `titel`, `dok_art`, `datum`, `datumtext`, `thematik`, `thematiktext`, `thema`, `feld1`, `feld2`, `dok_fundort`, `dok_fundorttext`, `grafik_ja`, `text`, `gif`, `previous`, `next`) VALUES (71, 'Brief von August H&ouml;gn an Stephan Leitner, 15.3.1960', 1, '1960-03-15', '15.03.1960', 7, 'private Korrespondenz', 'Brief an den ehemaligen Sch&uuml;ler Stephan Leitner nach Australien. Themen: Schilderung der Begebenheiten im Fasching. Diebstahl. Klage &uuml;ber eigenen Bewegungsmangel', 'August H&ouml;gn', 'Stephan Leitner', 4, 'Briefe aus dem Besitz von Stephan Leitner', 0, 'Ruhmannsfelden, 15.3.60<br>\r\n<br>\r\nMeine Lieben!<br>\r\n<br>\r\nRecht herzlichen Dank f&uuml;r Euren lieben Brief. Es ist f&uuml;r uns in der Heimat immer interessant von den lieben Bekannten etwas zu h&ouml;ren. was Sie im fernen Lande treiben u. wie es Ihnen dort gesundheitlich ergeht. Nun. Ihr Beide seid gesundheitlich wohlauf u. frischen Mutes. Das freut mich am allermeisten. Und sch&ouml;n abwechslungsreich ist es dort auch. Deutsche k&ouml;nnt Ihr auch treffen, usw. Der Verdienst ist auch ausreichend. Also! Alles sch&ouml;n. Dann k&ouml;nnt Ihr es schon aushalten bis Ihr ein sch&ouml;nes S&uuml;mmchen auf der Bank liegend habt. Und die Arbeit ist auch nicht zu viel u. zu anstrendend. 40 Stunden gehen schon, freilich bei anderen klimatischen Verh&auml;ltnissen. Und der Frau Gemahlin ist auch wohlauf u. mit der Sprache seid Ihr auch gut vorbereitet. Das mag Euch von gro&szlig;em Vorteil sein?<br>\r\nBei uns in Deutschland in der Heimat ist es gewi&szlig; auch landschaftlich sehr sch&ouml;n. Aber davon kann man nicht herunterbei&szlig;en. Und die Konkurenz &uuml;berall ist bei uns in Deutschland zu gro&szlig;. Darum der Verdienst &uuml;berall nicht gro&szlig;. Hier kann man nicht reich werden, au&szlig;er man ist ein geborener Million&auml;r. Schwannberger z. B. kaufte in D.dorf  (Deggendorf) ein Anwesen, mu&szlig; er total zusammenrei&szlig;en u. neu aufbauen.<br>\r\nIm Fasching war es bei uns ziemlich &quot;mau&quot;. Nur die Fl&uuml;chlinge konnten sich was leisten. Bei einer Veranstaltung im Vornehm Saal wurde eingebrochen u. circa 1000 DM gestohlen. Da&szlig; die mit dem Geld aufkommen war klar. Die &quot;Danhof&quot;-Buben haben es sich geleistet u. etliche Fl&uuml;chtlingsbuben waren Mithelfer. Diese, in der Ultrakust besch&auml;ftigten haben sich zuerst die Hilfsmittel angefertigt, schwarze Str&uuml;mpfe &uuml;ber den Kopf gezogen, dann ging es los. Jetzt sitzen sie in D.dorf hinter den Gardinen. Aber der ganze Markt war doch in gro&szlig;er Aufregung. Und Buben waren dabei, von denen man es wirklich nicht zu denken wagt, da&szlig; sie Einbrecher sein k&ouml;nnten.<br>\r\nWinter ist Schlu&szlig;. Schnee liegt nur auf den Bergen. Darum der kalte Wind von O. u. W. Mitten in den Fr&uuml;hling hinein, wirft es &uuml;ber Nacht wieder Schnee herab, des aber nicht mehr liegen bleibt. Blumen gibt es hier nicht. Da m&uuml;ssen wir noch warten.<br>\r\nMit meiner Gesundheit ist Stillstand, weil ich gar nicht aus dem Zimmer komme. Es fehlt also jegliche Bewegung u. drau&szlig;en im Freien ist es f&uuml;r mich zu kalt.<br>\r\nFr. Kramheller hatte einen Schlaganfall. H. Anznergruber ist schwer erkrankt. In Gotteszell verstarb alte Frau Hacker.<br>\r\nIn der Schule bekommen hier die Kinder ein Fl&auml;schchen Milch. Diese wird t&auml;gl. mit Lastauto hierher gefahren. Wie geht das Gesch&auml;ft in dortiger Metzgerei? Bei uns hier ist immer ein gro&szlig;er Wechsel.<br>\r\n<br>\r\nMeine Lieben!<br>\r\n<br>\r\nW&uuml;nsche Euch Beiden &quot;Recht sch&ouml;ne u. gl&uuml;ckliche Osterfeiertage (u. gesunde!!) Hoffentlich bekomme wir trockene, warme u. milde Osterfeiertage. Bei uns war es in letzter Zeit zwar trocken, aber immer kalt u. rauher Wind, so da&szlig; ich nicht heraus konnte. Mit der Natur geht es darum langsam vorw&auml;rts.<br>\r\n<br>\r\nDie herzlichsten Ostergr&uuml;&szlig;e sendet A. H&ouml;gn R&apos;felden u. Rosa', '<p>\n<a href="scanns/71_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/71_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/71_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/71_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/71_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/71_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 70, 72),
(72, 'Brief von August H&ouml;gn an Stephan Leitner, Dez. 1960', 1, '1960-12-00', 'Dez. 1960', 7, 'private Korrespondenz', 'Brief an den ehemaligen Sch&uuml;ler Stephan Leitner nach Australien. Themen: Klage &uuml;ber schlechten Gesundheitszustand. Schilderung eines Artzbesuches. Aussage &uuml;ber &Uuml;berdr&uuml;ssigkeit der Berge, Sehnsucht nach Wasser (Donau, Deggendorf)', 'August H&ouml;gn', 'Stephan Leitner', 4, 'Briefe aus dem Besitz von Stephan Leitner', 0, 'Sehr geehrter Herr Leitner!<br>\r\n<br>\r\nNun haben Sie aber schon lange nichts mehr h&ouml;ren lassen. Wie geht es Ihnen gesundheitlich? Bei uns hier ist Sterblichkeit ziemlich gro&szlig;. M&ouml;chte Ihnen mitteilen, Petrotti u. Muhr Michl zu gleicher Zeit gestorben sind. In der Schulstra&szlig;e sind etliche Fl&uuml;chtlinge gestorben. Mein gesundheitliches Befinden ist nicht das beste. Marschieren geht sehr schlecht. War neulich in Straubing bei einem Arzt. Jetzt ist es mit meinem krankem Fu&szlig; besser u. es scheint da&szlig; Heilung eintritt. Wie geht es Fr. Gemahlin gesundheitlich? Bei Ihnen in Australien wird es w&auml;rmer sein als bei uns hier. Bei uns hier ist es neblig u. kalt. Schnee haben wir nicht! Aber kalt ist es, so da&szlig; man den ganzen Tag heizen mu&szlig;.<br>\r\nNikolaus kommt bald u. Weihnachten ist nicht mehr weit weg. Ich w&uuml;nsche Euch beiden ein sch&ouml;nes, gesundes Weihnachten u. ein gl&uuml;ckliches Frieden u. Segen bringendes Neujahr. Mit den besten Gr&uuml;&szlig;en verbleibe ich in stetem Gedenken an Euch im fernen Australien<br>\r\n<br>\r\nEuer<br>\r\nA. H&ouml;gn<br>\r\nRektor<br>\r\nRuhmannsfelden<br>\r\n<br>\r\n1000 Gr&uuml;&szlig;e aus der Heimat!<br>\r\n<br>\r\nIm Osterbr&uuml;nnl fand das 300 j&auml;hrige Jubil&auml;um statt. Es dauerte mehrere Tage. Abends war immer Lichterprozession. Baumeister Karl sen. ist immer krank u. kommt schlecht daher. Paukner ist nicht mehr im O.br&uuml;nnl.<br>\r\nIch bin jetzt seit 1898 im bayer. Wald u. seit Dezember 1910 in R&apos;felden. B&uuml;rgermeister ist Zellner Alois Kothm&uuml;hle. Birnbeck, Bettfedern hat sein Haus vollst&auml;ndig niedergerissen u. neu aufgebaut. Fr. Dr. Danziger hat ihren 92. Geburtstag gefeiert. Ihr Vater ist gesund u. geht noch flei&szlig;ig auf das Land hinaus zu den Maurerarbeiten.<br>\r\nSie gehen in der Freizeit zum Schwimmen u. Fischen. Um dieses Vergn&uuml;gen beneide ich Sie wirklich. An den Bergen des bayer. Waldes habe ich schon genug. M&ouml;chte lieber hinaus in die Ebene an die Donau, zum Wasser. Also! Petri Heil! Fangen Sie bald einen gro&szlig;en Hai. Viel Gl&uuml;ck!<br>\r\n<br>\r\nW&uuml;nsche Euch Beiden Gesundheit u. Gl&uuml;ck in allem Eueren Tun. Viele Gr&uuml;&szlig;e an die dortigen Deutschen u. viel Erfolg im Fischen - einen schweren Hai - das w&auml;re was. Aber Vorsicht!! Petri Heil!<br>\r\nBitte! Wenn m&ouml;glich, um wieder einmal eine kurze Nachricht u. besten Dank f&uuml;r letzten Brief. Habe ihn Ihren Eltern schon lesen lassen.<br>\r\nHeimatliche Gr&uuml;&szlig;e sendet Ihnen u. der Frau Gemahlin<br>\r\n<br>\r\nAug. H&ouml;gn<br>\r\nRuhmannsfelden', '<p>\n<a href="scanns/72_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/72_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/72_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/72_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/72_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/72_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 71, 73),
(73, 'Brief von August H&ouml;gn an Stephan Leitner, 10.3.1961', 1, '1961-03-10', '10.03.1961', 7, 'private Korrespondenz', 'Brief an ehemaligen Sch&uuml;ler Stephan Leitner nach Australien. Themen: Christbaumschmuck, Sohn Gustl, sein gesundheitlicher Zustand, neuer Fernsehapparat, Bauvorhaben in Ruhmannsfelden', 'August H&ouml;gn', 'Stephan Leitner', 4, 'Briefe aus dem Besitz von Stephan Leitner', 0, 'Geehrter Herr Leitner!<br>\r\n<br>\r\nZur gr&ouml;&szlig;ten &Uuml;berraschung bekam ich gestern Donnerstag 9. M&auml;rz Ihren lb. Brief, der mich sehr interessierte, denn von Ihnen bekomme ich doch wenigstens eine R&uuml;ckantwort. Nun Sie haben Weihnachten gefeiert wie wir - aber ohne Schnee, sondern in gro&szlig;er Hitze. Weihnachten im Schnee ist  sicher eine sch&ouml;ne Jahreszeit u. dazu alles mit Rodel u. Ski ausger&uuml;stet u. dazu die bunten Kleider, gibt immer ein sch&ouml;nes Bild. Mit dem Christbaum plag ich mich nicht mehr lange herum. Wenn das Abschneiden von einem Christbaum schon verboten ist, dann verzichte ich vollst&auml;ndig auf den Christbaum. Daf&uuml;r ist die Wohnung weihnachtlich dekoriert u. hergerichtet. Von den Vorhangstangen in den Zimmern h&auml;ngen rote B&auml;nder mit Silberkugeln daran in verschiedenen Gr&ouml;&szlig;en. Ebenso von den elektrischen L&uuml;stern der Beleuchtung h&auml;ngen B&auml;nder mit Kugeln, usw. u. das Lametta. Das war zwar ganz etwas Neues, aber es hat gefallen. Allen, die die weihnachtlich geschm&uuml;ckte Wohnung von innen u. au&szlig;en gesehen haben, hat es sehr gefallen - ohne Christbaum. Und das Christkind hat Euch in Australien reichlich beschenkt. Und einen Volkswagen habt Ihr auch bekommen? War der im  Verh&auml;ltnis zu uns hier teuer? Die DM Aufwertung wird nat&uuml;rlich alles teurer machen. Dann habt ihr noch billig eingekauft. <br>\r\nFreilich gings im Anfang an den Baum! Das wird h&auml;ufig vorkommen. Freilich war die Verletzung fatal, weil damit die Arbeit auch aus gesetzt werden mu&szlig;. Aber es war doch nicht so arg schlimm. Der Volkswagen ist jetzt praktisch umgewandelt in einem Wohnwagen. Das ist praktisch f&uuml;r den Aufenthalt drau&szlig;en auf den Lande oder an der Meeresk&uuml;ste. Mein Sohn Gustl, ist bei den Amerikanern, bei einer Radarstation in Herzogenaurach, hat sich ein altes Haus gekauft, hat dieses umgebaut u. jetzt vermietet an Erlanger Studenten u. ist recht gl&uuml;cklich da&szlig; er das gemacht hat. Das Meiste was er zum Umbau brauchte, bekam er von den Amerikanern, machte alles selber u. kam so billig weg. Voriges Jahr wollte er Fahrlehrer werden. Aber der Kostenpunkt war ziemlich hoch. Am Donnerstag starb Maler Hof. Gestern Freitag war die &Uuml;berf&uuml;hrung ins Leichenhaus. Gestorben sind in letzter Zeit sehr viele hier, aber gr&ouml;&szlig;tenteils Fl&uuml;chtlinge. Ich bin immer noch seit 5 Jahren linksseitig gel&auml;hmt u kann nicht marschieren. Ich habe einen Fernsehapparat gekauft (1000 DM) u sitze jetzt viel hinter Fernsehfilm. Da gibt es schon Sachen, auch z. B. von der Tierwelt Australiens. Ihr habt eine neue sch&ouml;ne Wohnung. Da werdet Ihr aber zufrieden sein? Ich denke schon, da&szlig; Heimweh u. der Gedanke an die Heimat manchmal arg r&uuml;tteln u. zupfen! Aber! Wenn das Leben zufriedenstellend ist, wie z. B. sch&ouml;ner Verdienst, freies Leben,  gute Bekanntschaft, dann wird es in Australien schon sch&ouml;ner sein als in der Heimat!<br>\r\nIhre Frau Mutter, die ich jetzt seit Januar 1910 kenne, solange bin ich in R&apos;felden, ist immer noch kr&auml;nklich u. kann nicht heraus aus den 4 W&auml;nden in der Wohnung wie ich. Da hei&szlig;t es halt Zeitvertreib. Der Vater ist immer gesund und ziemlich flei&szlig;ig. Bei dem gibt es kein Aussetzen. Das Bauen hat hier nach gelassen. Z. Zt. baut hier gar niemand. Birnbeck hat seine alte H&uuml;tte vollst&auml;ndig weggerissen u. einen Neubau aufgef&uuml;hrt. (Posthaltereck) Friedhofaufgang zum &Ouml;lberg) Birnbeck treibt Federnhandel, Bettfedern, Betten f&uuml;r Neuverm&auml;hlte u. hat richtige Preise! Holler will das alte Holler Haus (fr&uuml;her Schmiede) wegrei&szlig;en u. neuaufbauen. Zitzelsbergerbr&auml;u hat noch nicht gebaut. Die alte Ruine vom Gra&szlig;l Binder Haus steht noch wie ehedem. Die Friedhofs Mauer kommt ganz weg. Dieser Verkehrhindernden Mauer ist das Todesurteil schon gesprochen. Die alten Gr&auml;ber kommen weg auf den neuen Friedhof, die Stra&szlig;e wird breiter. Dann gibt es einmal eine sch&ouml;ne Durchfahrt durch den Markt, erst wenn das l&auml;stige Stiglbauer Haus auch noch weg w&auml;re, das man seinerzeit beim Brande des oberen Marktes rettete u. stehen lie&szlig; als Verkehrshindernis. Im Fasching war es heuer hier ziemlich matt u. lustlos. Plank Schorsch war im Krankenhaus D.dorf u. wurde operiert. Ebenso Postbote Brummer jr., der die Plank Agnes geheiratet hat u. das Plank Anwesen (Georg Plank) besitzt. Im Krankenhaus Viechtach sind auch immer R.feldner zur Heilung u. Operation. Morgen ist in Viechtach Zuchtviehmarkt. Da gibt es immer sch&ouml;ne Tiere (Stiere, K&auml;lbinnen) aber die sind teuer. 3000 - 4000 DM ein Stier, 2 - 3000 DM eine Zuchtk&auml;lbin. Neulich war gro&szlig;er Fastenmarkt in Deggendorf. Da gab es viele Leute, aber wenige K&auml;ufer. Blumen auf den Wiesen gibt es noch nicht. Da liegt zuviel Schnee noch in den W&auml;ldern, darum ist es hier trotz intensiven Sonnenscheins immer noch kalt. Auf den Bergen, Hirschenstein, Rauher Kulm, Voglsang, Loderhart, Breitenau liegt noch Schnee u. das Skifahren geht dort vorz&uuml;glich.<br>\r\n<br>\r\nLieber Herr Leitner u. werte Frau Gemahlin!<br>\r\n<br>\r\nRosa Beischmied, meine Haush&auml;lterin u. Krankenf&uuml;rsorgerin, senden Ihnen aus der Heimat 1000 beste Gr&uuml;&szlig;e u. w&uuml;nschen, da&szlig; es Ihnen in Australien recht gut geht u. Ihnen dort recht gef&auml;llt. Viele Gr&uuml;&szlig;e von Ihren lieben Eltern, Verwandten u. Bekannten.<br>\r\n<br>\r\nEs gr&uuml;&szlig;t Sie<br>\r\n<br>\r\nIhr ergebenster!<br>\r\nA. H&ouml;gn Rektor i. R.<br>\r\nu. Rosa B.', '<p>\n<a href="scanns/73_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/73_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/73_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/73_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/73_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/73_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/73_4_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/73_4_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 72, 66),
(74, 'Zeitungsartikel aus Viechtacher Bayerwald-Bote, 1983', 2, '1983-00-00', '1983', 4, 'Auff&uuml;hrungen von Kompositionen', 'Auff&uuml;hrung eines Marienliedes durch den M&auml;nnergesangsverein Achslach unter der Leitung von Franz Danziger, Verteilung der Geschichte von Ruhmannsfelden', 'Viechtacher Bayerwald-Bote', '', 99, 'Sonstige Fundorte', 0, 'Ruhmannsfelden. K&uuml;rzlich feierten die Sch&uuml;lerinnen und Sch&uuml;ler des Geburtsjahrganges 1926/27 der Volksschule Ruhmannsfelden ein fr&ouml;hliches Wiedersehen, 42 Jahre nach der Schulentlassung im Jahre 1941. Der Stichtag f&uuml;r den Schulanfang war damals der 1. Juli, so da&szlig; im damaligen Sch&uuml;lerjahrgang die sp&auml;teren 26er und die fr&uuml;hen 27er vereint waren.<br>\r\nNach mehreren Anl&auml;ufen wurde nun das Treffen nach kurzen Vorbereitungen mit Anschriftensuchen von Hans Freisinger, unterst&uuml;tzt von den Mitsch&uuml;lern Maria Pecho und Toni Stadler, organisiert und geladen. Fast alle sagten zu und erschienen am besagten Wochenende in Ruhmannsfelden. Maria Pecho (Geiger Marerl) hatte wohl den weitesten Weg von Aachen, weilte aber gerade auf Urlaub. Die Einlader warteten dann morgens vor dem Gasthof Zachskorn auf den ersten Mitsch&uuml;ler, es war dies Franz Rosenlehner aus Masselsried (fr&uuml;her Bruckberg), dann aber trudelten sie alle ein, die beiden Hansen aus M&uuml;nchen, die Hilde aus Marktschwaben, die Josefine aus Germering, die Resi aus Partenkirchen, die Anni aus Vilshofen, die Liesel und der Xaver aus Viechtach, die Vroni aus Regen und schlie&szlig;lich die ehemaligen aus Ruhmannsfelden und der Umgebung selbst. Schlie&szlig;lich z&auml;hlte man 35 Sch&uuml;lerinnen und Sch&uuml;ler, wobei, wie k&ouml;nnte es anders sein, die M&auml;dchen in der Mehrzahl waren.<br>\r\nEine Mitsch&uuml;lerin aus Murnau lie&szlig; sich entschuldigen, sechs blieben diesmal fern. Seit der Schulentlassung sind 17 ehemalige Sch&uuml;lerinnen und Mitsch&uuml;ler (zehn Burschen, sieben M&auml;del) verstorben, allein sechs Mitsch&uuml;ler mu&szlig;ten in den letzten Kriegsmonaten als junge Soldaten ihr Leben lassen.<br>\r\nNach kurzer Begr&uuml;&szlig;ung durch den Mitsch&uuml;ler Hans Freisinger im Gasthof Zachskorn und F&uuml;hrung einer Anwesenheitsliste mit Anschrift, wobei auch schon Erlebnisse der vergangenen Jahrzehnte ausgetauscht wurden, ging es zu Fu&szlig; ins nahe Osterbr&uuml;nnl, wo Dekan Krottenthaler f&uuml;r die verstorbenen ehemaligen Mitsch&uuml;lerinnen und Mitsch&uuml;ler sowie die verstorbenen Lehrkr&auml;fte und Religionslehrer das hl. Amt zelebrierte. <strong>Der Gottesdienst wurde feierlich umrahmt vom M&auml;nnergesangsverein Achslach unter Leitung von Franz Danzinger mit der bekannten „Waldler-Messe", wobei ein vom letzten Kla&szlig;lehrer Rektor August H&ouml;gn verfa&szlig;tes Marienlied zum Vortrag kam.</strong> W&auml;hrend des Dankgebetes wurden die Namen der gefallenen und verstorbenen Mitsch&uuml;ler verlesen und ein Gedenken eingelegt.<br>\r\nNach dem Gottesdienst, Fotosafari vor dem Osterbr&uuml;nnl und R&uuml;ckgang in den Markt stellte man sich vor dem Rathausaufgang dem Fotografen zum „Bitte-rechtfreundlich". Zum Mittagsmahl wurde ins Gasthof Zachskorn eingeladen. Dabei wurden schon einige Erlebnisse und Schulbubenstreiche zum Besten gegeben.<br>\r\nNachmittags wurde eine Besichtigung des neuen Hauptschulgeb&auml;udes am Lerchenfeld organisiert, was Schulhausmeister Sepp Glasl erm&ouml;glichte. Auch die Kaffeeh&auml;user des Marktes wurden aufgesucht. Abends traf man sich wiederum im kleinen gem&uuml;tlichen Saal des Gasthofes Zachskorn, wobei neben der Unterhaltung und des Gedankenaustausches zu den Kl&auml;ngen der oft mit Beifall bedachten Schrammelmusik Ochsenbauer/Mages auch das Tanzbein flei&szlig;ig geschwungen wurde, wobei H&ouml;hepunkte der J&auml;germarsch, der Polsterltanz und schlie&szlig;lich die Polonaise waren. Das Wiedersehen nach sovielen Jahren wurde immer sch&ouml;ner. <strong>Als Erinnerung erhielt jeder Mitsch&uuml;ler die seit langem vergriffene Geschichte des Marktes Ruhmannsfelden, kopiert und gebunden von Mitsch&uuml;ler Hans Freisinger,</strong> der zum Abschlu&szlig; noch kurz das Treffen streifte. Vor dem Auseinandergehen versprach man sich, bald wieder ein solches Treffen zu arrangieren. Die Mitsch&uuml;ler lie&szlig;en es sich nicht nehmen, den Verantwortlichen des Treffs zu danken und &uuml;berreichten durch Anni Madl und Thekla Ebnet sch&ouml;ne Pr&auml;sente aus Bayerwald-Glas.', '<p>\n<a href="scanns/74_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/74_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 47, 56),
(75, 'Bewerbung von Gottfried Hagemeiner, 25.10.1927', 3, '1927-10-25', '25.10.1927', 1, 'Kirchenmusik', 'Bewerbung (exemplarisch angef&uuml;hrt f&uuml;r die 5 Bewerbungen von: Gottfried Hagemeiner, Georg Ro&szlig;nagel, Emanuel Voreck, Alois Meier, Eugen Nagel)', 'Bewerbung von Gottfried Hagemeiner, 25.10.1927', '', 2, 'Pfarrarchiv', 0, 'Emmering, den 25. Oktober 1927<br>\r\n<br>\r\nAn das kath. Pfarramt Ruhmannsfelden<br>\r\n<br>\r\nHabe durch Inserat erfahren, da&szlig; in Ruhmannsfelden die Chorregentenstelle frei wird. Ich bin 27 Jahre, seit 1.I.1922 als Chorregent und Organist in einen mittleren Landpfarrei zur Zufriedenheit des Pfarramtes sowie der Pfarrgemeinde angestellt. Habe bis heute noch keine Beanstandung bekommen. Ferner k&ouml;nnte ich auch im Darlehenskassenverein mitarbeiten, ev. die Rechenstelle &uuml;bernehmen. Erwarte umgehend, wenn m&ouml;glich g&uuml;nstigen Bescheid.<br>\r\nHochachtungsvoll!<br>\r\n<br>\r\nHagemeiner Gottfried<br>\r\nChorregent<br>\r\nEmmering<br>\r\nb. A&szlig;ling Oberbayern', '<p>\n<a href="scanns/75_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/75_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 16, 98),
(76, 'Brief von Bayer. Jagdschutz- und J&auml;gerverband an alle Kameraden, 8.10.1951', 1, '1951-10-08', '08.10.1951', 9, 'Jagd', 'Resumee &uuml;ber die geleistet Arbeit vom Bayer. Jagdschutz- u. J&auml;gerverband, Aufforderung den Mitgliedsbeitrag einzuzahlen, unter anderem bei August H&ouml;gn', 'Bayer. Jagdschutz- und J&auml;gerverband', 'alle Kameraden', 3, 'Beilage zum Notenmaterial', 0, 'Bayer. Jagdschutz- u. J&auml;gerverband e. V.<br>\r\nBezirksgruppe Viechtach/Ndb.<br>\r\n<br>\r\nRuhmannsfelden, 8. Okt. 1951<br>\r\n<br>\r\nSehr geehrter Waidkamerad!<br>\r\n<br>\r\nNach jahrelanger, harter z&auml;her Arbeit des BJV ist es endlich gelungen, zu erreichen, dass wir bayerischen J&auml;ger wieder als beinahe freie Waidm&auml;nner unsere geliebte Jagd aus&uuml;ben k&ouml;nnen, und dass den J&auml;gern der Besatzungsmacht auf Privatrevieren nur mehr bescheidene Jagdm&ouml;glichkeiten geboten sind.<br>\r\n<br>\r\nMit ein Hauptverdienst an diesem ersten grossen Erfolg um die Erringung unserer Jagdhoheit geb&uuml;hrt unumstritten dem Bayerischen Jagdschutz- und J&auml;gerverband e. V., der gemeinsam mit den &uuml;brigen im DJV zusammengeschlossenen Landesjagdverb&auml;nden in unerm&uuml;dlichener und zielbewusster Arbeit seit Jahren bis heute die Interessen der Bayerischen J&auml;ger und des bayerischen Waidwerks in so hervorragender Weise vertreten hat und der nicht ruhen und rasten wird, bis sein Ziel, n&auml;mlich die Erreichung unserer restlosen Jagdfreiheit Tatsache geworden ist.<br>\r\n<br>\r\nJeder Kampf kostet aber Geld und es ist daher das Gebot der Stunde, dass s&auml;mtliche Mitglieder unsere Arbeit durch sofortige Einzahlung des Jahresbeitrages (DM 8.-) entweder bei der n&auml;chsten J&auml;gerversammlung oder bei <strong>Rektor H&ouml;gn, Ruhmannsfelden</strong> - oder bei der Kreissparkasse Viechtach auf das Konto des BJV Nr. 8685 unterst&uuml;tzen.<br>\r\n<br>\r\nWaidkameraden, die noch nicht Mitglieder unseres Verbandes sind, zeigen ihre dankbare Anerkennung f&uuml;r die von uns bisher geleistete Arbeit, indem sie geschlossen dem Bayerischen Jagdschutz- und J&auml;gerverband beitreten, denn nur eine einige bayerische J&auml;gerschaft verb&uuml;rgt die Erringung unseres gemeinsamen Zieles und es geht letzten Endes nicht an, dass die einen arbeiten und opfern und dass die anderen die Fr&uuml;chte dieser Arbeit stillschweigend mitgeniessen.<br>\r\n<br>\r\nMit Waidmannsheil!<br>\r\n<br>\r\nBayerischer Jagdschutz- u. J&auml;gerverband e. V.<br>\r\nBezirksgruppe Viechtach', '<p>\n<a href="scanns/76_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/76_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 58, 69),
(77, 'Programm einens Firmgottesdienstes', 3, '0000-00-00', 'o. D.', 1, 'Kirchenmusik', 'Programm einens Firmgottesdienstes angefertitigt von August H&ouml;gn', 'Programm einens Firmgottesdienstes', '', 3, 'Beilage zum Notenmaterial', 0, 'Firmungstag<br>\r\n<br>\r\nBeim Einzug des Bischofs in die Kirche:<br>\r\nDem Hirten la&szlig;t erschallen 3 Strophen<br>\r\nVorbereitungsgebet des Bischofs zur hl. Messe<br>\r\nBekleidung des Bischofs u. Beginn zur hl. Messe<br>\r\nSingmesse - Einheitsmesse: Zur Dir, O Gott - Gott in der H&ouml;h - Du hast, O Herr - La&szlig;t uns erheben - O du Lamm Gottes - Lobet den Herren<br>\r\nAm Schlu&szlig; der Messe betet der Bischof auf der Epistelseite<br>\r\nDer Pfarrer tr&auml;gt das Me&szlig;buch weg - Dominus vobiscum still - Segen still<br>\r\nletztes Evangelium - Die Orgel spielt - H&auml;ndewaschung.<br>\r\nInzwischen betritt ein Priester die Kanzel - die Orgel schweigt - Predigt<br>\r\nNach der Predigt geht der Bischof zum Altar - Ansprache des Bischofs<br>\r\nErneuerung des Taufgel&uuml;bdes<br>\r\nDer Bischof ruft die 7 Gaben des hl. Geistes auf die Firmlinge herab.<br>\r\n<br>\r\nResponsorien:<br>\r\n<br>\r\nSpiritus ......... a peccatis R. Amen<br>\r\nAdjutorium ..... Domini R. Qui fecit coelum et terram<br>\r\nDomine ......... meam R. Et clamor meus ad te veniat<br>\r\nDominus ........ vobiscum R. Et cum Spiritu tue<br>\r\nOremus ......... de Coelis R. Amen<br>\r\nSpiritum ........ et intellectus R. Amen<br>\r\nSpiritum ........ et fortitudines R. Amen<br>\r\nSpiritum ........ et pietatis R. Amen<br>\r\nSpiritum ........ saeculum saeculorum R. Amen<br>\r\n<br>\r\nDie Orgel schweigt<br>\r\nDer bisch&ouml;ftliche Sekret&auml;r spricht.<br>\r\nW&auml;hrend der Firmung sagt der Vorbeter auf der Kanzel die Lieder an.<br>\r\nVorspiel zu den Liedern<br>\r\nNachdem alle Firmlinge gefirmt sind, wird das Dankgebet gebetet.<br>\r\nNach dem Dankgebet stimmt der bisch&ouml;ftliche Sektret&auml;r an:<br>\r\nConfirma hoc .......... Gloria Patri ........ saecula saeculorum .......... Ohne Orgel<br>\r\n<br>\r\nHierauf singt der Bischof:<br>\r\nOstende nobis ....... Et salutare tuum da nobis<br>\r\nDomine exaudi ..... Et clamor meus ad te veniat<br>\r\nDominus vobiscum ... Et cum Spiritu tue <br>\r\nOremus ...... Amen<br>\r\nBenedicat ...... Vitam aeternam R. Amen<br>\r\nDer Bischof geht vom Altar zum Thron - der bisch&ouml;fliche Sekret&auml;r spricht - Gro&szlig;er Gott 2 Strophen<br>\r\n- Dankgebet des Bischofs u. Auszug aus der Kirche', '<p>\n<a href="scanns/77_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/77_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 55, 78),
(78, 'Skizzen zu einem Grabliedtext', 3, '0000-00-00', 'o. D.', 1, 'Kirchenmusik', 'Skizzen zu einem Grabliedtext', 'Skizzen zu einem Grabliedtext', '', 3, 'Beilage zum Notenmaterial', 0, 'Schlafe in friedlicher Grabesruh<br>\r\nSchlafe in wonnigem Schlummer du<br>\r\nLebe wohl o Vaterherz<br>\r\nRuhe sanft nach Leid und Schmerz<br>\r\nRuhe sanft nach Leid und Schmerz<br>\r\nLeb wohl du lieber Vater<br>\r\nRuhe sanft in stiller Grabesruh<br>\r\nRuhe sanft in Gott den Herrn<br>\r\nletzter Gru&szlig; ein letzter Blick lieb Vaterherz<br>\r\nSchlaf wohl lieb .... herz<br>\r\n ____________________<br>\r\n<br>\r\nmit einem sch&ouml;nen Sterne da gibts ein Wiedersehn<br>\r\nRuhe sanft Vaterherz   <br>\r\nLebe wohl O Vater Ruhe, schlafe in Gott dem Herrn<br>\r\nSchenke uns in deinem Reiche dort ein gl&uuml;cklich Wiedersehn', '<p>\n<a href="scanns/78_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/78_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 77, 166),
(79, 'Brief von Max Forstner an Rudolf Schwannberger, 31.5.1936', 1, '1936-05-31', '31.05.1936', 9, 'Jagd', 'Bericht den Pflichtschie&szlig;ens am 30.5.1936 zu dem August H&ouml;gn nicht erschienen ist.', 'Max Forstner', 'Rudolf Schwannberger', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Jagdkreis 3 Viechtach<br>\r\nHegering 5.<br>\r\nGotteszell, den 31. Mai 1936. <br>\r\n<br>\r\nAn<br>\r\nHerrn Kreisj&auml;germeister Rudolf Schwannberger<br>\r\nRuhmannsfelden.<br>\r\n<br>\r\nBetreff: Pflichtschiessen.<br>\r\nDas Pflichtschiessen des Hegering 5 wurde am Samstag, den 30. Mai 1936, nachmittags 6 Uhr auf dem Schiesstand in Ruhmannsfelden abgehalten.<br>\r\nDie Jahresjagdscheininhaber des Hegering wurden ordnungsgem&auml;ss geladen; insbesondere wurde darauf hingewiesen, dass das Pflichtschiessen mit der Waffe zu erfolgen habe, mit der die Jagd auf den Rehbock ausge&uuml;bt wird. Trotz dieses Hinweises erschien nur Herr Amberger mit der Waffe, die er auch zur Rehjagd f&uuml;hrt.<br>\r\n<strong>Nicht erschienen war Herr Oberlehrer H&ouml;gn.</strong> Herr Zitzelsberger schoss mit einem Repetierer, da er f&uuml;r seinen Drilling keine Patronen hatte. Herr Paukner hatte einen <strong>Repetierer des Herrn Oberlehrer H&ouml;gn.</strong> Herr V&ouml;lkl schoss aus der <strong>Doppelflinte (mit Damastl&auml;ufen) des Herrn Oberlehrer H&ouml;gn</strong> mit Stendebachgeschossen.<br>\r\nMit den Gewehren mit gezogenen L&auml;ufen wurde auf 80 m und mit den Doppelflinten auf 55 m Entfernung geschossen. Die Bedingungen des Pflichtschiessen wurden nur von Herrn Amberger erf&uuml;llt. Schiesslisten liegen bei. Die &uuml;brigen Jahresjagdscheininhaber erhalten Mitteilung, dass sie das Pflichtschiessen zu wiederholen haben. Ich ersuche, diese beigegebenen Mitteilungen zu unterzeichnen und der Einfachheit halber direkt an die in Frage kommenden Herrn hinauszugeben.<br>\r\n<br>\r\nHegeringleiter.<br>\r\n<br>\r\nForstner', '<p>\n<a href="scanns/79_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/79_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 146, 80),
(80, 'Brief von Max Forstner an August H&ouml;gn, 31.5.1936', 1, '1936-05-31', '31.05.1936', 9, 'Jagd', 'Auffordung an August H&ouml;gn, sich bei einer Wiederholung des Pflichtschie&szlig;ens, einzufinden', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Jagdkreis 3 Viechtach<br>\r\nHegering 5.<br>\r\nGotteszell, den 31. Mai 1936<br>\r\n<br>\r\nZum Akt Kreisj&auml;germeister.<br>\r\n<br>\r\nAn<br>\r\nHerrn Oberlehrer H&ouml;gn, Ruhmannsfelden.<br>\r\n<br>\r\nBetreff: Pflichtschiessen.<br>\r\n<br>\r\nSie haben an dem am 30. Mai 36 stattgefundenen Pflichtschiessen des Hegering 5 nicht teilgenommen. Ich verweise daher besonders auf Ziffer 3 der Schiessvorschrift f&uuml;r den Reichsbund "Deutsche J&auml;gerschaft" &uuml;ber die Pflicht zur Teilnahme an den Schiessen.<br>\r\nIn allern&auml;chster Zeit findet eine Wiederholung des Pflichtschiessens statt. Sie wollen sich daher, im Falle dies bisher noch nicht geschehen ist, mit einwandfreier Munition eindecken, um eine glatte Abwicklung des Pflichtschiessens zu sichern.<br>\r\nIch verweise nochmals auf Ziffer 13 der Schiessvorschrift wonach f&uuml;r das Pflichtschiessen nur solche Waffen ben&uuml;tzt werden d&uuml;rfen, die bei der Jagdaus&uuml;bung gef&uuml;hrt werden. Nur dann kann der Zweck des Pflichtschiessens erf&uuml;llt werden. Termin &uuml;ber das n&auml;chste Pflichtschiessen wird Ihnen noch bekannt gegeben.<br>\r\n<br>\r\nDer Kreisj&auml;germeister: Rudolf Schwannberger<br>\r\n<br>\r\nDer Hegeringleiter: Max Forstner \r\n \r\n', '<p>\n<a href="scanns/80_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/80_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 79, 82),
(81, 'Brief von Max Forstner an Rudolf Schwannberger, 18.6.1936', 1, '1936-06-18', '18.06.1936', 9, 'Jagd', 'Bericht des zweiten Pflichtschie&szlig;ens', 'Max Forstner', 'Rudolf Schwannberger', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Jagdkreis 3 Viechtach	<br>\r\nHegering 5.<br>\r\nGotteszell, 18. Juni 1936.<br>\r\n<br>\r\nAn<br>\r\nden Herrn Kreisj&auml;germeister R. Schwannberger<br>\r\nin R u h m a n n s f e l d e n.<br>\r\n<br>\r\nBetreff: Wiederholung des Pflichtschiessens.<br>\r\n\r\nAm Montag, den 15. Juni 1936, nachmittags 6 Uhr fand auf dem Schiessstand in Ruhmannsfelden die Wiederholung des Pflichtschiessens statt. Erschienen waren die Herren V&ouml;lkl und Paukner. Herr Zitzelsberger gab an, an dem Pflichtschiessen nicht teilnehmen zu m&uuml;ssen, weil er bereits 15 Jagdkarten habe und ihm kein Bock zum Abschuss freigegeben worden sei. Er wurde von mir darauf aufmerksam gemacht, dass er auch anderswo als Gast die M&ouml;glichkeit bekommen k&ouml;nne einen Bock abzuschiessen und dass er fr&uuml;her in der Hauptsache die B&ouml;cke mit Schrot geschossen habe. Er h&auml;tte also trotzdem an dem Pflichtschiessen teilnehmen m&uuml;ssen. <strong>Herr H&ouml;gn blieb ohne Entschuldigung zum zweiten Mal dem Pflichtschiessen fern.</strong><br>\r\nDie beiden anwesenden Sch&uuml;tzen haben die Bedingungen des Pflichtschiessens wieder nicht erf&uuml;llt. Ich werde daher eine zweite Wiederholung des Pflichtschiessens (auf dem Schiessstand in &Ouml;dwies) ansetzen und ich ersuche Herrn Kreisj&auml;germeister dringend im Falle weiteren passiven Wiederstandes massregelnd einzuschreiten. &Uuml;ber den Zeitpunkt der Wiederholung dis Schiessens wird noch n&auml;here Anordnung erteilt.<br>\r\n<br>\r\nHegeringleiter.<br>\r\n<br>\r\nForstner \r\n \r\n', '<p>\n<a href="scanns/81_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/81_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 82, 168),
(82, 'Brief von Max Forstner an die Herren  Zitzelsberger, V&ouml;lkl, Vogel, H&ouml;gn und Paukner, 12.6.1936', 1, '1936-06-12', '12.06.1936', 9, 'Jagd', 'Einladung zum Pflichtschie&szlig;en am 15.6.36', 'Max Forstner', 'die Herren  Zitzelsberger, V&ouml;lkl, Vogel, H&ouml;gn und Paukner', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Jagdkreis 3 Viechtach<br>\r\nHegering 5.<br>\r\nGotteszell,den 12.6.36.<br>\r\n<br>\r\nAn Herrn<br>\r\nZitzelsberger, V&ouml;lkl, Vogel, <strong>H&ouml;gn</strong> und Paukner Ruhmannsfelden.<br>\r\n<br>\r\nBetreff: Pflichtschiessen.<br>\r\n<br>\r\nUnter Bezugnahme auf mein Schreiben vom 31.5.36 teile ich Ihnen mit, dass die Wiederholung des Pflichtschiessens am 15.6.36 abends 6 Uhr auf dem Schiesstand in Ruhmannsfelden (Zitzelsbergerkeller) stattfindet. Ich ersuche um p&uuml;nktliches Erscheinen und um Beschaffung der geeigneten Munition und weise nochmals darauf hin, dass das Pflichtschiessen mit der Waffe zu erfolgen hat, die bei Aus&uuml;bung der Jagd gef&uuml;hrt wird.<br>\r\n<br>\r\nHerrn Kreisj&auml;germeister Rudolf Schwannberger<br>\r\nzur Kenntnis.<br>\r\n<br>\r\nForstner<br>\r\n<br>\r\nHegeringleiter \r\n', '<p>\n<a href="scanns/82_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/82_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 80, 81),
(83, 'Brief von Max Forstner an Rudolf Schwannberger, 17.5.1935', 1, '1935-05-17', '17.05.1935', 9, 'Jagd', 'Verzeichnis der Jagdhunde im Hegering 5', 'Max Forstner', 'Rudolf Schwannberger', 5, 'Schul-Chronik', 0, '<strong></strong>Jagdkreis 3 Viechtach	<br>\r\nHegering 5<br>\r\nVogelsang, den 17. Mai 1935.<br>\r\n<br>\r\nAn<br>\r\nden Herrn Kreisj&auml;germeister des<br>\r\nJagdkreises 3, Ruhmannsfelden.<br>\r\n<br>\r\nBetreff: Jagdgebrauchshunde, hier Verzeichnis der Jagdgebrauchshunde im Hegering 6.<br>\r\n<br>\r\n...<br>\r\n<br>\r\n<strong>9. H&ouml;gn August</strong><br>\r\n<br>\r\n<strong>Besitzer, Name und Wohnort:</strong> H&ouml;gn August, Ruhmannsfelden,<br>\r\n<strong>Hunderasse:</strong> Bracke,<br>\r\n<strong>Geschl.:</strong> w.,<br>\r\n<strong>Alter Jahre:</strong> 3<br>\r\n<strong>Farbe und Abzeichen:</strong> gelb mit schw. Sattel,<br>\r\n<br>\r\n... \r\n \r\n', '<p>\n<a href="scanns/83_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/83_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 92, 86),
(84, 'Brief von Rudolf Schwannberger an Max Forstner, 29.10.1935', 1, '1935-10-29', '29.10.1935', 9, 'Jagd', 'Aufforderung an Amberger zur Hasenjagd. Erlaubnis an H&ouml;gn Brackenjagd durchf&uuml;hren zu d&uuml;rfen', 'Rudolf Schwannberger', 'Max Forstner', 6, 'Hegeringf&uuml;hrer 35/36', 0, '29. Oktober 1935.<br>\r\n<br>\r\nHerrn<br>\r\nHegringleiter F o r s t n e r,<br>\r\nV o g l s a n g .<br>\r\n<br>\r\nIch habe Bedenken, wegen der Gemeinde Jagd Zachenberg, da&szlig; der P&auml;chter Amberger nicht in der Lage ist, die abzuliefernden Hasen innerhalb dieser Zeit abzuschiessen.<br>\r\nNachdem aber gerade bei diesem Jagdp&auml;chter die finanzielle Lage die g&uuml;nstigste ist, k&ouml;nnen wir keine Ausnahme machen. Es ist aber Amberger in Vorschlag zu bringen, dass er pro Hase den Betrag von RM 3.-- abliefern kann und wir f&uuml;r diesen Betrag das Wild dann eben beschaffen.<br>\r\nDie beiden Jagdp&auml;chter H&ouml;gn und Zitzelsberger sind darauf hinzuweisen, dass Ihnen die Brackenjagd vom 8.-16. Dezember erlaubt ist.\r\nDie Einsammlung bezw. Ablieferung des Wildes ist bei den Hegeringf&uuml;hrern zu vollziehen, da jeder Hegering gesondert mir die Strecke &uuml;bergeben muss und das Ganze statistisch festgelegt wird.<br>\r\n<br>\r\nMit Waidmannsheil<br>\r\nKreisj&auml;germeister  ', '<p>\n<a href="scanns/84_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/84_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 165, 163),
(85, 'Brief von Max Forstner an August H&ouml;gn, 12.6.1935', 1, '1935-06-12', '12.06.1935', 9, 'Jagd', 'Reklamation eines Abschussplanes mit falschen Werten, angefertigt von August H&ouml;gn', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Hegering 6 des Jagdkreises 3,Viechtach<br>\r\nVogelsang, den 12. Juni 1935.<br>\r\n<br>\r\nAn<br>\r\nHerrn Oberlehrer H&ouml;gn,<br>\r\nRuhmannsfelden.<br>\r\n<br>\r\nBetreff: Abschu&szlig;plan f&uuml;r die Gemeindejagd Zachenberg.<br>\r\n<br>\r\nDie Abschu&szlig;pl&auml;ne f&uuml;r die Gemeindejagd Zachenberg, die Sie als Vertreter des Jagdp&auml;chters, Herrn Amberger, einreichten, habe ich in 2 facher Ausfertigung dem Herrn Kreisj&auml;germeister zugestellt. Die Angaben &uuml;ber den Bestand an Rehwild halte ich f&uuml;r sehr &uuml;bertrieben. Schon von dem Standpunkt ausgehend, da&szlig; der fr&uuml;here Jagdp&auml;chter im Jahre 1934, also im letzten Pachtjahr, nur f&uuml;r 1 erlegten Bock die Wildsteuer bezahlte, l&auml;&szlig;t auf einen ganz geringen Bestand an Rehwild schlie&szlig;en. Ausserdem wurde ja von dem Herrn Jagdp&auml;chter gelegentlich einer Unterhaltung selbst best&auml;tigt, da&szlig; die Jagd v&ouml;llig ausgeraubt sei. Woher also ein Bestand von 20 starken B&ouml;cken kommen soll, ist mir jedenfalls schleierhaft. Ich nehme nicht an, da&szlig; dem Herrn Kreisj&auml;germeister und mir absichtlich die Arbeit erschwert oder unm&ouml;glich gemacht werden soll und kann daher nur auf Mangel an Revierkenntis Ihrerseits schliessen.<br>\r\nBesonders betonen will ich, da&szlig; die wirklich starken B&ouml;cke erst in der zweiten H&auml;lfte der Brunft abgeschossen werden sollen. Von Herrn Kreisj&auml;germeister Schwannberger erhalten Sie noch Mitteilung &uuml;ber die Zahl der zum Abschu&szlig; bewilligten B&ouml;cke.<br>\r\nSehr wichtig ist mir, da&szlig; Herr Amberger, der das gr&ouml;&szlig;te Interesse an der Hebung seines Revieres hat, von Ihnen im besten Sinne jagdlich beraten wird.<br>\r\n<br>\r\nWeidmannsheil', '<p>\n<a href="scanns/85_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/85_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 86, 88),
(86, 'Brief von Max Forstner an August H&ouml;gn, 24.5.1935', 1, '1935-05-24', '24.05.1935', 9, 'Jagd', 'Aufforderung an August H&ouml;gn einen Abschussplan zu erstellen', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Hegering 6 des Jagdkreises 3 Viechtach.<br>\r\nGotteszell, den 24. Mai 1935.<br>\r\n<br>\r\nAn<br>\r\nHerrn Oberlehrer Aug. H&ouml;gn, Ruhmannsfelden.<br>\r\n<br>\r\nBetreff: Abschu&szlig;pl&auml;ne.<br>\r\n<br>\r\nBeiliegend &uuml;bersende ich Ihnen die Abschu&szlig;pl&auml;ne f&uuml;r das Jagdrevier Patersdorf III in 2 facher Ausfertigung mit dem Bemerken, da&szlig; die Abschu&szlig;pl&auml;ne in dreifacher Ausfertiung beim zust&auml;ndigen Hegeringf&uuml;hrer einzureichen sind. (Siehe Seite 1 des Abschu&szlig;planes).<br>\r\nIch ersuche, auf der ersten Seite des Abschu&szlig;planes die Wald-, Feld- und Wiesenfl&auml;chen ausgeschieden anzugeben, da nur dann festzustellen ist, welcher Rehbestand in Ihrem Revier herangehegt werden kann.<br>\r\nEin Rickenabschu&szlig; kommt erst in Frage, wenn ein der Reviergr&ouml;&szlig;e entsprechender Rehbestand vorhanden ist. Dieser ist nur durch Einschr&auml;nkung des Abschusses und entsprechende Hege - wozu ich auch die strenge Beaufsichtigung des Reviers rechne - zu erreichen.<br>\r\nBez&uuml;glich des seit 3 Jahren gelt gehenden &quot;Schmalrehes&quot; d&uuml;rfte Ihnen ein Irrtum unterlaufen sein, da bekanntlich unter Schmalreh ein j&auml;hriges St&uuml;ck, das &uuml;berhaupt noch nicht gesetzt hat, zu verstehen ist. Sollte es sich aber um eine Geltgei&szlig; handeln, so ersuche ich, dieselbe durch Ihren Jagdaufseher best&auml;tig zu lassen, da ich mich vom Vorhandensein derselben pers&ouml;nlich &uuml;berzeugen will.<br>\r\nBesonders m&ouml;chte ich darauf hinweisen, da&szlig; ein Abschu&szlig; von Rehwild ohne Genehmigung des Kreisj&auml;germeisters nicht stattfinden darf.<br>\r\n<br>\r\nHeil Hitlers', '<p>\n<a href="scanns/86_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/86_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 83, 85),
(87, 'Brief von Max Forstner an Rudolf Schwannberger, 25.6.1935', 1, '1935-06-25', '25.06.1935', 9, 'Jagd', 'Brief an Rudolf Schwannberger, mit dem ein Brief von August H&ouml;gn und eine Abschrift eines Briefs von Max Frostner an August H&ouml;gn mitgeschickt wurde.', 'Max Forstner', 'Rudolf Schwannberger', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Vogelsang, den 25. Juni 1935.<br>\r\n<br>\r\nAn<br>\r\nden Kreisj&auml;germeister des Jagdkreises Viechtach, <br>\r\nHerrn Rud.Schwannberger,<br>\r\nRuhmannsfelden.<br>\r\n<br>\r\nBeiliegend &uuml;bersende ich Ihnen einen Brief des Herrn Hauptlehrer H&ouml;gn, den er mir in seiner Eigenschaft als Vertreter des Herrn Brauereibesitzer Amberger, des P&auml;chters der Gemeinde Jagd Zachenberg geschrieben hat. Eine Abschrift meines Antwortschreibens habe ich zur Kenntnisnahme ebenfalls beigenommen. Ich bitte Sie, meinen beiliegenden Schutzgewehrschein zum Umtausch dem Bezirksamt vorlegen zu wollen. Als Schutzbezirk bitte ich eintragen zu lassen: &quot;Pachtjagden des Kaufmanns Rud. Schwannberger!&quot;!<br>\r\n<br>\r\nWeidmannsheil!<br>\r\n<br>\r\nForstner', '<p>\n<a href="scanns/87_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/87_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 88, 165),
(88, 'Brief von Max Forstner an August H&ouml;gn, 21.6.1935', 1, '1935-06-21', '21.06.1935', 9, 'Jagd', 'Antwort auf eine emotionsgeladenen Brief August H&ouml;gns', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, 'Hegering 6 des Jagdkreises 3 Viechtach.<br>\r\nGotteszell, den 21. Juni 1935.<br>\r\n<br>\r\nAn<br>\r\nHerrn Oberlehrer H&ouml;gn,<br>\r\nRuhmannsfelden.<br>\r\n<br>\r\nIn Erwiderung Ihres Schreibens teile ich Ihnen Folgendes mit:<br>\r\nMeinen Brief vom 12. ds. M habe ich in meiner Eigenschaft als Hegeringf&uuml;hrer an Sie gerichtet. Als solcher ist es meine Aufgabe, offensichtliche Irrt&uuml;mer eines Jagdp&auml;chters - in unserem Falle seines Vertreters - zu berichtigen und daf&uuml;r zu sorgen, da&szlig; die Jagd weidm&auml;nnisch betrieben wird. Wenn ich also Ihre Meldung &uuml;ber den Bestand an Rehwild in der Gemeindejagd Zachenberg als &uuml;bertrieben bezeichnete, so war das meine verdammte Pflicht, da ja doch der Abschu&szlig; entsprechend dem Rehbestand erfolgen soll.<br>\r\nHaben Sie, Herr Oberlehrer, beim Schreiben Ihres Briefes darangedacht, da&szlig; Sie 20 starke B&ouml;cke im Bestand an Rehwild angegeben haben? Wenn Sie unter 25 gemeldeten B&ouml;cken 20 als stark ansprechen, dann mu&szlig; ich feststellen, da&szlig; Sie in Bezug auf Geh&ouml;rnbildung und St&auml;rke des Wildes sehr niedrige Anspr&uuml;che stellen, die sich in keiner Weise mit unseren Anschauungen decken. Und ma&szlig;gebend sind nun einmal die Gutachten des Gau- und des Kreisj&auml;germeisters und des Hegeringf&uuml;hrers. Ganz abgesehen davon halte ich Ihre Angaben &uuml;ber den Rehbestand, insbesondere &uuml;ber den der Rehb&ouml;cke, nach wie vor f&uuml;r zu hoch. Die mir mitgesandte Aufstellung mit Angabe &uuml;ber den Stand der einzelnen B&ouml;cke &auml;ndert am tats&auml;chlichen Bestand nichts. Um 25 B&ouml;cke nach St&auml;rke und Stand einwandfrei auszumachen, geh&ouml;ren viele Wochen, &uuml;ber die man v&ouml;llig frei verf&uuml;gen kann. Und &uuml;ber diese Zeit verf&uuml;gen Sie, Herr Oberlehrer, nicht.<br>\r\nUnd nun Herr Oberlehrer zur Form Ihres Briefes selbst.<br>\r\nSie haben den Brief im ersten Zorn geschrieben. Dabei haben Sie vollkommen &uuml;bersehen, da&szlig; Ihnen das Beschwerderecht zum Kreis-, bezw. zum Gauj&auml;gerrneister zusteht. Ich lasse nun bei aller Kameradschaft Ihren Vorwurf, ich sei unsachlich geworden und wolle Sie infolge von Verhetzung von gewisser Seite so quasi schikanieren, nicht auf mir sitzen. ich ersuche Sie daher, Ihre Beschwerde formgerecht beim Kreisj&auml;germeister mit dem Antrag auf disziplin&auml;res Einschreiten gegen mich einzureichen.<br>\r\nWenn Sie, Herr Oberlehrer, nachdem Sie doch das Reichsjagdgesetz kennen, den Beschwerdeweg nicht beschritten, sondern versuchten, mich durch pers&ouml;nliche Angriffe im Vertrauen auf Ihre gewandte Feder niederzuk&auml;mpfen, so ist es f&uuml;r mich nur ein Beweis, da&szlig; Sie eben von den Einrichtungen, die das Jagdgesetz gebracht hat, keinen Gebrauch machen wollen und mit dem Reichsjagdgesetz offensichtlich nicht einverstanden sind, soweit Ihnen durch dasselbe Einschr&auml;nkungen auferlegt werden sollen.<br>\r\nAusserdem aber, Herr Oberlehrer, darf ich doch von den Herren des Hegerings, also auch von Ihnen, erwarten, da&szlig; eine berichtigende, Feststellung meinerseits nicht sofort in oppositioneller Art behandelt wird, denn gerade die Art der Beantwortung meines Briefes mu&szlig; mich in der Annahme best&auml;rken, da&szlig; Sie ein Zusammenarbeiten nicht w&uuml;nschen und die T&auml;tigkeit der Vollzugsorgane nicht anerkennen wollen.<br>\r\nNebenbei bemerkt glaube ich doch, da&szlig; wir als M&auml;nner einmal eine Wahrheit vertragen k&ouml;nnten, denn als J&auml;ger wollen wir schlie&szlig;lich keine Launen haben und empfindlich sein wie eine Primadonna.<br>\r\nWie bereits erw&auml;hnt, erwarte ich, da&szlig; Sie beim Herrn Kreisj&auml;germeister in B&auml;lde Antrag auf disziplin&auml;res Einschreiten gegen mich stellen.<br>\r\n<br>\r\nHeil Hitler!', '<p>\n<a href="scanns/88_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/88_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/88_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/88_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 85, 87),
(89, 'Liste der J&auml;ger des Jagdkreises 3 Viechtach 1936', 3, '1936-00-00', '1936', 9, 'Jagd', 'Liste der J&auml;ger des Jagdkreises 3 Viechtach, an 20. Stelle ist August H&ouml;gn angef&uuml;hrt', 'Liste der J&auml;ger des Jagdkreises 3 Viechtach 1936', '', 6, 'Hegeringf&uuml;hrer 35/36', 0, '20. August H&ouml;gn', '<p>\n<a href="scanns/89_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/89_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 163, 145),
(90, 'Umschlag f&uuml;r einen Lehrplan 1941', 3, '1941-00-00', '1941', 12, 'Schule', 'Umschlag f&uuml;r einen Lehrplan 1941 mit der Unterschrift von August H&ouml;gn', 'Umschlag f&uuml;r einen Lehrplan 1941', '', 3, 'Beilage zum Notenmaterial', 0, 'Vordruck 14<br>\r\n<br>\r\nLehrplan<br>\r\n<br>\r\nf&uuml;r den 1. u. 2. Sch&uuml;lerjahrgang<br>\r\n<br>\r\nder l&auml;ndlichen Berufsschule f&uuml;r Knaben<br>\r\n<br>\r\nin Ruhmannsfelden<br>\r\n<br>\r\nSchuljahr 1941/42<br>\r\n<br>\r\nausgearbeitet von dem Kla&szlig;lehrer<br>\r\n<br>\r\nA. H&ouml;gn', '<p>\n<a href="scanns/90_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/90_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 174, 195),
(91, 'Brief von Max Forstner an August H&ouml;gn, 12.4.1935', 1, '1935-04-12', '12.04.1935', 9, 'Jagd', 'Abrundung und der Austausch aneinandergrenzender Jagdbezirke', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, '<P>Jagdkreis 3 Viechtach<BR>Hegering 6<BR>Vogelsang, den 12. Apr. 35<BR><BR>An Zitzelsberger - Amberger H&ouml;gn.<BR><BR>Mit Schreiben vom 1.4.35 hat mich der gesch&auml;ftsf&uuml;hrende Kreisj&auml;germeister des Jagdkreises 3 Viechtach zum F&uuml;hrer des Hegeringes 6, die Gemeinden Ruhmannsfelden, Zachenberg, Gotteszell und eines Bogens der Gemeinde Patersdorf umfassend, bestellt.<BR>Gem&auml;ss Schreibens Nr. 37 v. 12.4.35 des Herrn Kreisj&auml;germeisters ist es Aufgabe der Jagdp&auml;chter, im Beisein des Hegeringf&uuml;hrers die Jagdgrenzen entsprechend dem Vorschriften der R.J.G. abzugrunden, bezw. abzugleichen. Gem&auml;ss &sect; 6 (Bildung der Jagdbezirke) erfolgt die Abrundung und der Austausch aneinandergrenzender Jagdbezirke nach der Erfordernissen der Jagdpflege durch den Kreisj&auml;germeister (Hegeringf&uuml;hrers) auf Antrag eines Beteiligten oder vom Amts wegen, wobei die Gesamtgr&ouml;sse der Jagdbezirke m&ouml;glichst wenig ver&auml;ndert werden soll.<BR>Ich ersuche Sie um umgehende Mitteilung, ob Sie beabsichtigen, einen diesbez&uuml;glichen Antrag einzubringen, damit die Regelung im Einvernehmen mit Ihren Angrenzern erfolgen kann.<BR><BR>Weidmannsheil & Heil Hitler!</P>', '<p>\n<a href="scanns/91_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/91_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 139, 94),
(92, 'Aufstellung des Jagdkreises 3 Viechtach durch Kreisj&auml;germeister Rudof Schwannberger, 4.5.1935', 3, '1935-05-04', '04.05.1935', 9, 'Jagd', 'Aufstellung des Jagdkreises 3 Viechtach durch Kreisj&auml;germeister Rudof Schwannberger, August H&ouml;gn wird als Fachberater f&uuml;r Rechtswesen und Rechtsplege aufgef&uuml;hrt.', 'Aufstellung des Jagdkreises 3 Viechtach durch Kreisj&auml;germeister Rudof Schwannberger, 4.5.1935', '', 6, 'Hegeringf&uuml;hrer 35/36', 0, '<P>Ruhmannsfelden, den 4. Mai 1935<BR><BR>Aufstellung:<BR><BR>II. Als Fachberater habe ich f&uuml;r den Jagdkreis III Viechtach aufgestellt:<BR><U>"F&uuml;r Rechtswesen & Rechtspflege"<BR></U><STRONG>August H&ouml;gn</STRONG>, Oberlehrer, Ruhmannsfelden </P>', '<p>\n<a href="scanns/92_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/92_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/92_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/92_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 94, 83);
INSERT INTO `dokumente` (`id`, `titel`, `dok_art`, `datum`, `datumtext`, `thematik`, `thematiktext`, `thema`, `feld1`, `feld2`, `dok_fundort`, `dok_fundorttext`, `grafik_ja`, `text`, `gif`, `previous`, `next`) VALUES (93, 'Protokoll der Feuerwehrgeneralversammlung, 26.12.1910', 3, '1910-12-26', '26.12.1910', 13, 'Feuerwehr', 'August H&ouml;gns erstes Protokoll, das er als Schriftf&uuml;hrer der freiwilligen Feuerwehr Ruhmannsfelden geschrieben hat. Bei dieser Generalversammlung wurde er auch zum Schriftf&uuml;hrer gew&auml;hlt.', 'Protokoll der Feuerwehrgeneralversammlung, 26.12.1910', '', 99, 'Sonstige Fundorte', 0, '<P>Protokoll</P>\r\n<P>aufgenommen bei der am 26. Dezember (Stephani) 1910 im Vereinslokal stattgefundenen Generalversammlung.</P>\r\n<P>Praes.:<BR>die Unterzeichneten.</P>\r\n<P>Vorschriftsm&auml;&szlig;ig wurden die H. Mitglieder der Fr. Feuerwehr Ruhmannsfelden durch Cirkular eingeladen. Erschienen sind 25 Mitglieder.<BR>H. Vorst. Kiesenbauer er&ouml;ffnete um 1/2 1 Uhr die Versammlung u. gedachte in seinen einleitenden Worten der verstorbenen H. Mitglieder, insbesondere des verdienstvollen verstorbenen H. Kreisersatzwart u. Bezirkswarts H. Lukas. Zum Zeichen der Trauer erhoben sich s&auml;mtliche Mitglieder von ihren Sitzen.<BR>Hierauf wurde zur Tagesordnung geschritten. H. Vorstand Kiesenbauer gab folgendes bekannt:</P>\r\n<P>1. den Jahresbericht<BR>2. den Stand der Mitglieder. aktive Mitglieder: 65, passive Mitglieder: 48<BR>3. Brandstatistik (1), Alois Hartl, Zimmermann ist hiebei verungl&uuml;ckt u. erhielt Unterst&uuml;tzung aus der Landesunterst&uuml;tzungskasse.<BR>4. &Uuml;bungen. Wurden abgehalten.<BR>5. Versammlungen. Verwaltungsratsversammlungen 3, Mitgliederversammlungen 2<BR>6. Hierauf erstattete H. Kassier Ederer Rechenschaftsbericht &uuml;ber Einnahmen u. Ausgaben und wurde derselbe anstands befunden.<BR>7. H. Vorstand Kiesenbauer verliest das Protokoll vom 9. Dez. 1910. Dasselbe wurde angenommen.<BR>8. Bei der Wahl eines Schriftf&uuml;hrers wurde H. Lehrer H&ouml;gn gew&auml;hlt.<BR>9. W&uuml;nsche. Keine vorgebracht.<BR>10. Abstimmung &uuml;ber Ballmusik. Mit einer Stimmenmehrheit von 20 gegen 5 wurde die Blaskapelle Wiesinger gew&auml;hlt.<BR>10. der Jahrtag findet mit Wahl statt.<BR>11. der Jahresbeitrag von 1,20 M wurde beibehalten</P>\r\n<P>Nach dem noch auf die segensvolle Einrichtung der Sterbekasse hingewiesen wurde u. zum Beitritt ermuntert wurde, schlo&szlig; Herr Vorstand Kiesenbauer die sch&ouml;n verlaufene Versammlung, in dem er auf das Bl&uuml;hen u. Gedeihen des Vereins ein dreifach "Gut Heil" ausbrachte.<BR><BR>Kiesenbauer Vorstand<BR>Bielmeier Comad.<BR>Jos. Friedrich<BR>Pritzl<BR>H&ouml;gn</P>', '<p>\n<a href="scanns/93_1_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/93_1_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/93_2_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/93_2_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n<a href="scanns/93_3_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/93_3_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 102, 103),
(94, 'Brief von Max Forstner an August H&ouml;gn, 12.4.1935', 1, '1935-04-12', '12.04.1935', 9, 'Jagd', 'Kr&auml;henbek&auml;mpfung', 'Max Forstner', 'August H&ouml;gn', 6, 'Hegeringf&uuml;hrer 35/36', 0, '<P>Jagdkreis 3 Viechtach<BR>Hegering 6<BR>Vogelsang, den 12. April 35</P>\r\n<P>An<BR>Herrn Josef Zitzelsberger jun. <BR>Ruhmannsfelden,<BR>dgl. an H. Amberger + H&ouml;gn</P>\r\n<P>Betreff: Kr&auml;henbek&auml;mpung<BR>(Schreiben Nr. 37 v. 12.4.35 des gesch&auml;ftsf&uuml;hrenden Kreisj&auml;germeisters.)</P>\r\n<P>Der gesch&auml;ftsf&uuml;hrende Kreisj&auml;germeister hat die einheitliche Durchf&uuml;hrung der Kr&auml;henbek&auml;mpung f&uuml;r Ende dieses Monats angeordnet.<BR>Ich ersuche Sie, der Einfachheit halber, den Bedarf an Phosphoreiern zu obigen Zweck sofort telefonisch oder pers&ouml;nlich dem Herrn Kreisj&auml;germeister zu melden, da bei Grossbezug mit einer wesentlichen Verbilligung zu rechnen ist.</P>\r\n<P>Weidmannsheil &amp; Heil Hitler!</P>\r\n<P>An den Herrn Kreisj&auml;germeister zur Einsichtnahme.</P>\r\n<P>&nbsp;</P>', '<p>\n<a href="scanns/94_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/94_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 91, 92),
(95, 'Programmzettel zur Auff&uuml;hrung des Holledauer Fidels, Mrz. 1923', 3, '1923-03-00', 'Mrz. 1923', 99, 'Sonstige Dokumente', 'Programmzettel zur Auff&uuml;hrung des Holledauer Fidels', 'Programmzettel zur Auff&uuml;hrung des Holledauer Fidels, Mrz. 1923', '', 99, 'Sonstige Fundorte', 0, 'Turn-Verein Ruhmannsfelden<br>\r\n<br>\r\nNiederbayerisches Singspiel (3 Akte) v. F. J. Scherrer<br>\r\nMusik von Erh. Kutschenreuter in Dietersburg (Rottal)<br>\r\n<br>\r\nDer Holledauer Fidel<br>\r\n<br>\r\nGro&szlig;e musikalisch-theatral. Auff&uuml;hrung<br>\r\nim Saale der Brauerei Vornehm in Ruhmannsfelden<br>\r\nM&auml;rz 1923<br>\r\n<br>\r\nRegie: H. Karl Ohnesorg<br>\r\nDekoration: H. Hans Lippl<br>\r\nDirigent: H. Aug. H&ouml;gn.<br>\r\n<br>\r\nPersonen :<br>\r\n<br>\r\nJ. Sichbauer, Hopfengutsbes. in der Holledau &mdash; H. Rudi Schwannberger<br>\r\nGrette, dessen Frau &mdash; Frl. Anny Zellner<br>\r\nResl, deren Tochter &mdash; Frl. Frieda H&ouml;gn<br>\r\nF i d e l   Waldhauser, Hopfenzupfer und Holzhauer &mdash; H. Michl Zinke<br>\r\nValentin Wurmdobler, Bauer im bay. Wald &mdash; H. Josef Heigl<br>\r\nPetronilla, dessen Frau &mdash; Fr. K&auml;thi Zadler<br>\r\nVinzenz, deren Sohn &mdash; H. Xaver Birnbeck<br>\r\nLukas Machh&ouml;rndl, Schuster u. Bankagent &mdash; H. Heinrich Daumerlang<br>\r\nAnna, dessen Tochter &mdash; Fr. Anna Piller<br>\r\nKathi Krinninger, Dienstmagd &mdash; Frl. Lina Krieger<br>\r\nHieronymus Wachtveitl, Polizeidiener &mdash; H. Max K&ouml;nigbauer<br>\r\n<br>\r\nHopfenzupfer u. Holzhauer &mdash; Knechte und M&auml;gde des Sichbauern &mdash; Dorfkinder und Landvolk aus der Holledau &mdash; Festzugsteilnehmer als Feuerwehr, Musikanten, Ehrenjungfrauen etc.<br>\r\n<br>\r\nOrt der Handlung: Holledau und Bayerischer Wald<br>\r\nZirka 60 Mitwirkende<br>\r\nRauchen w&auml;hrend der ganzen Auff&uuml;hrung verboten.', '<p>\n<a href="scanns/95_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/95_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 117, 169),
(96, 'Sitzungsprotokoll des Gemeinderats Zachenberg, 17.5.1956', 3, '1956-05-17', '17.05.1956', 3, 'Geschichte von Zachenberg', 'Ehrenb&uuml;rgerw&uuml;rde der Gemeinde Zachenberg f&uuml;r H&ouml;gn', 'Sitzungsprotokoll des Gemeinderats Zachenberg, 17.5.1956', '', 99, 'Sonstige Fundorte', 0, '6. Heimatgeschichte der Gemeinde Zachenberg.<br>\r\n<br>\r\nIn der Beratung &uuml;ber die Entsch&auml;digung an den Heimatforscher Rektor H&ouml;gn, Ruhmannsfelden f&uuml;r die Erstellung der Heimatgeschichte der Gemeinde Zachenberg wurde vom Gemeinderat Karl Vogl der Antrag gestellt, Rektor H&ouml;gn, Ruhmannsfelden zum Ehrenb&uuml;rger der Gemeinde Zachenberg zu ernennen.<br>\r\nEr begr&uuml;ndete seinen Antrag damit, dass Rektor H&ouml;gn von 1908 bis 1921 die Gesch&auml;fte der Gemeindeverwaltung ehrenamtlich verwaltete und auch sonst sein Wirken als Lehrer in Ruhmannsfelden mit der Gemeinde Zachenberg verbunden war.<br>\r\nEinstimmig wurde daraufhin vom Gemeinderat beschlossen, Herrn Rektor H&ouml;gn zum Ehrenb&uuml;rger der Gemeinde Zachenberg zu ernennen. Au&szlig;erdem soll Rektor H&ouml;gn f&uuml;r seine Arbeit ein Geldgeschenk im Ermessen des 1. B&uuml;rgermeisters bei der &Uuml;berreichung der Ehrenurkunde erhalten.<br>\r\n<br>\r\nZur Best&auml;tigung<br>\r\n<br>\r\nFreisinger, Verw. Sekr.<br>\r\n<br>\r\nBielmeier, 1. B&uuml;rgermeister<br>\r\n<br>\r\nEs wird hiermit best&auml;tigt, dass die Niederschrift vom 17.5.1956 heute vorgelesen wurde. Einwendungen wurden nicht erhoben.<br>\r\n<br>\r\n7. Juni 1956<br>\r\n<br>\r\nBielmeier, 1. B&uuml;rgermeister', '<p>\n<a href="scanns/96_gross.gif?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="scanns/96_klein.gif" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', 40, 127),
