INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (1, 'Interview mit Wilhelm Ederer, Aug. 2002', 'Wilhelm Ederer', '1931-01-05', '0000-00-00', '0000', 71, '1931', 'Sch&uuml;ler unter August H&ouml;gn', 'Ruhmannsfelden', '2002-08-00', 'Aug. 2002', 30, 30, '<p><strong>Wiedergabe der Notizen, angefertigt beim Interview:</strong></p>\r\n<ul>\r\n  <li>\r\n    <p>Chorleiter vor August H&ouml;gn in Ruhmannsfelden: Herr Schroll (Gemeindeschreiber) \r\n      aus Kollnburg</p>\r\n  </li>\r\n  <li>\r\n    <p>August H&ouml;gn Chorleiter seit 1939</p>\r\n  </li>\r\n  <li>\r\n    <p>1953 Tod von Pfarrer Bauer</p>\r\n  </li>\r\n  <li>\r\n    <p>Huber Schorsch (Kooperator) &Uuml;bergangspfarrer</p>\r\n  </li>\r\n  <li>\r\n    <p>Pfarrer Reicheneder mit Maria Reisinger (neue Organistin)</p>\r\n  </li>\r\n  <li>\r\n    <p>Danziger Chorleiter nach H&ouml;gn, Adresse von Franz Danziger (Sohn) Steinstra&szlig;e \r\n      8, 97618 Niederlauer 09771/3341</p>\r\n  </li>\r\n  <li>\r\n    <p>Orchester: Familie Rauscher: Max (Organist in Simbach), Siegfried und Ludwig, \r\n      Lehrerinnen Ascherl, Schneider und Rosenbeck</p>\r\n  </li>\r\n  <li>\r\n    <p> Auff&uuml;hrungen der Laurentiusmesse an Kirchweih um 1960</p>\r\n  </li>\r\n  <li>\r\n    <p>Nachkommen von August H&ouml;gn<br>\r\n      Tochter: lediges Kind Inge H&ouml;gn (Enkelin)<br>\r\n      Tochter heiratete einen Herrn Schlumprecht (hoher Nazifunktion&auml;r in \r\n      M&uuml;nchen), f&uuml;nf Kinder aus dieser Ehe<br>\r\n      Inge nimmt Namen von Schlumprecht an, Inge wohnte in Garmisch bei Oberaudorf</p>\r\n  </li>\r\n</ul><table></table>', '<p>\n<a href="interviews/1/wilhelm_ederer_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/1/wilhelm_ederer_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\n</p>\n', '', 2, 0);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (2, 'Interview mit Barbara Essigmann, 27.12.2002', 'Barbara Essigmann', '1921-12-03', '0000-00-00', '0000', 81, '1921', 'Sch&uuml;lerin und S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2002-12-27', '27.12.2002', 74, 75, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie waren S&auml;ngerin \r\n        im Kirchenchor unter August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, 34 Jahre \r\n        lang. Als ich 14, 15 Jahre alt war, bin ich zum H&ouml;gn in die Schule \r\n        gekommen und dann hat er mich gleich zum Kirchenchor genommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben in der Schule \r\n        gut singen k&ouml;nnen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp;Ja, ich war Sopran, \r\n        habe aber auch Alt gesungen. Uns waren ja viele beim Kirchenchor. Die \r\n        ganze Lehrerschaft. Der Kestlmeier noch. Das sind die alten Schullehrer \r\n        gewesen, die sind alle in die Kirche gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie die Namen \r\n        der Lehrer noch?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Der Lehrer Schultz, \r\n        der Lehrer Kestlmeier, die sind alle schon gestorben, ja der Lehrer H&ouml;gn. \r\n        Dann haben wir einen Chorregenten gehabt, der hat Schroll gehei&szlig;en, \r\n        aus Kollnburg, der hat Orgel gespielt und der H&ouml;gn hat dann dirigiert. \r\n        Der Chorregent Schroll ist in den 30iger Jahren gestorben und dann hat \r\n        alles der H&ouml;gn &uuml;bernommen. Er hat dann Orgel gespielt und hat \r\n        dann Takt gegeben. Wenn Sonntag oder Feiertag war, waren die Lehrer da \r\n        und dann hat einer von ihnen Takt gegeben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Kennen Sie sich noch \r\n        an ein paar Chors&auml;nger erinnern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Von den Alten: \r\n        Holzfurtner, ein Schreiner, der Schwannberger, von den Lehrer: der Friedrich, \r\n        Schultz und Kestlmeier. Die Lehrer waren alle am Kirchenchor. Sogar oft \r\n        zweimal am Tag: In der Fr&uuml;hmesse und beim Hochamt. Die Fr&uuml;hmesse \r\n        war um 7 und das Hochamt war um 10 Uhr. Chors&auml;ngerinnen hatten wir \r\n        auch viele. Vier, f&uuml;nf S&auml;ngerinnen waren immer da. Stadler Hilde \r\n        war eine Alt-S&auml;ngerin. Die Raster Res war auch eine Alt-S&auml;ngerin. \r\n        Die Hetzenecker Maria, sie lebt auch noch und wohnt jetzt in Deggendorf. \r\n        Eine von Zachenberg, wei&szlig; aber ihren Namen nicht. Sopran-S&auml;ngerinnen \r\n        waren: Glasschr&ouml;der Mathild, ich, Frau Kaminkehrer Grassl, Hedi hat \r\n        man sie genannt. Dann Grassl Nannerl, ist auch schon gestorben. Alt-S&auml;ngerin \r\n        war Frau Lehrerin Ascherl, Stadler Hilde eine ganz gute S&auml;ngerin<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben gesagt, sie haben \r\n        30 Jahre im Kirchenchor mitgesungen. Wann haben Sie dann genau aufgeh&ouml;rt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Wie der Pfarrer \r\n        Reicheneder gekommen ist, habe ich aufgeh&ouml;rt. Da ist der Chor gewesen \r\n        wie sonst auch und es war an einem Samstag kurz vor Weihnachten um 3 Uhr, \r\n        da hat der Reicheneder dem H&ouml;gn einen Brief geschrieben, dass er \r\n        schon zu alt ist, er soll jetzt aufh&ouml;ren, er braucht ihn nicht mehr, \r\n        er hat jetzt junge S&auml;nger. [... Beischmied Karl und Verwandtschaft \r\n        ...] Dann ist die H&ouml;gn Rosl (Rosa Beischmied) gekommen, sie war die \r\n        K&ouml;chin, und hat dann gesagt: &quot;Komm schnell, der Herr sitzt auf \r\n        dem Kanapee und kann nichts mehr sagen und er zittert so.&quot; Dann bin \r\n        ich gleich zu ihm gegangen, Glasschr&ouml;der Mathild war auch dabei. \r\n        Es hat ihn ein bisschen getroffen. Am Vormittag hat ers ihm gesagt und \r\n        am Abend hat es ihn schon getroffen. Aber der Schwannberger hat ihn sofort \r\n        nach Regensburg gefahren zu einem Arzt. Der Arzt hat ihn gefragt: &quot;Was \r\n        sind Sie denn im Beruf gewesen.&quot; &quot;Das ganze Leben war ich ein \r\n        Schullehrer&quot; &quot;Ach, was man da mit den Kindern mitmacht&quot; \r\n        &quot;Nein, die Kinder haben mir gar nichts ausgemacht, ich bin immer \r\n        gut mit ihnen ausgekommen, aber wir haben jetzt einen Pfarrer bekommen \r\n        und nach 40 Jahren habe ich gehen m&uuml;ssen.&quot; Er hat nie ein F&uuml;nferl \r\n        bekommen. Dann hat er uns das gesagt. Und ich sag es ihnen, seit dieser \r\n        Zeit gehe ich an Weihnachten nicht mehr in die Kirche. Wir habe nichts \r\n        gekommen f&uuml;r unsere Dienste, wir haben es zu Ehre Gottes gemacht. \r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und wer wurde dann Nachfolger \r\n        von August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das wei&szlig; \r\n        ich gar nicht. Jetzt ist es Herr Lankes.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War nicht der Herr Danziger \r\n        Nachfolger von August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Der hat auch \r\n        gehen m&uuml;ssen. Herr Danziger war Jude. Er war Tenor. Wie der H&ouml;gn \r\n        gegangen ist, ist es der Danziger noch gewesen, aber dann haben Sie gleich \r\n        einen Chorregenten gekommen. Ich wei&szlig; aber nicht mehr, wer das war.<br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Man h&ouml;rt auch immer \r\n        von einer Frl. Maria?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, Frl. Maria, \r\n        die war nett. Sie hat immer das Alt&ouml;ttinger Blatt jeden Monat vorbeigebracht, \r\n        die war einmalig. Der Reicheneder hat mich immer flei&szlig;ig gegr&uuml;&szlig;t \r\n        und hat den Hut gezogen und ich habe mir gedacht: &quot;Leck mich am Arsch.&quot;<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich an \r\n        Werke von August H&ouml;gn erinnern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, er hat viele \r\n        Messen komponiert, Marienlieder und alles. Da hab ich so viele gehabt, \r\n        die habe ich alle hergeschenkt. Der Maria Hetzenecker in Deggendorf habe \r\n        ich Noten gegeben und sie hat die Noten, als ganzen Chorsatz, dann an \r\n        den Chorregenten Goller weitergegeben. Wenn ich gewusst h&auml;tte, dass \r\n        Sie sich daf&uuml;r interessieren, h&auml;tte ich die Noten f&uuml;r Sie \r\n        aufgehoben. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich habe ja doch einige \r\n        Kompositionen von August H&ouml;gn gefunden, darunter drei Messen z. B. \r\n        die Josephs-Messe</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die Josephs-Messe. \r\n        Die hat H&ouml;gn f&uuml;r den alten Doktor Stern geschrieben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich habe auch 2 Marienlieder \r\n        von August H&ouml;gn, Marienlied Nr. 3 und Marienlied Nr. 11. Waren die \r\n        Marienlieder alle durchnummeriert?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die waren \r\n        alle durchnummeriert.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und 4 Grablieder.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, an ein Grablied \r\n        kann ich mich noch erinnern. &quot;Ewige Ruhe schenkt der Herr seiner \r\n        Seele&quot; und zum Schluss: &quot;und ewige Ruhe gib er der Seele...&quot; \r\n        Ich wei&szlig; es nicht mehr. Da hat der Schwannberger auch manchmal mitgesungen. \r\n        Er ist in der Beerdigung mitgegangen und hat eingesagt. Er hat ans Fenster \r\n        geklopft und gesagt: &quot;Babettl, um die und die Zeit ist Probe&quot; \r\n        Und dann sind wir da gewesen, da hat es gar nichts anderes gegeben. [... \r\n        Rudolf Schwannberger ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann sind die H&ouml;gn-Messen \r\n        z. B. die &quot;Laurentius&quot;-Messe oder die &quot;Mater-Dei&quot;-Messe \r\n        aufgef&uuml;hrt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Wahrscheinlich \r\n        w&auml;hrend des Krieges. Geschrieben hat H&ouml;gn ja immer. Er ist vorm \r\n        Fenster gesessen und hat geschrieben. Und wenn man gesagt hat: &quot;Herr \r\n        Rektor, was schreiben sie denn.&quot; &quot;Ja, eine Messe brauchen wir \r\n        wieder!&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wo hat er denn dann eigentlich \r\n        gewohnt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Wo jetzt die \r\n        Firma Schwannberger drin ist. Dem Herrn H&auml;rtl hat das Haus fr&uuml;her \r\n        geh&ouml;rt. Herr H&auml;rtl hat oben gewohnt und der H&ouml;gn unten.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Vor der Stra&szlig;e aus \r\n        hat man in August H&ouml;gns Arbeitszimmer gesehen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja freilich! \r\n        Da hat er das Fenster aufgemacht und dann gerufen: &quot;Komm herein!&quot; \r\n        Und wenn man dann bei ihm im Haus war: &quot;Was gibt es, Herr Rektor.&quot; \r\n        &quot;Da schaut her, jetzt hab ich wieder was geschrieben, das m&uuml;ssen \r\n        wir bald probieren&quot;<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und hatte er ein Klavier \r\n        in seinem Zimmer?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, er hatte \r\n        ein Klavier, und wenn dann eine Messe fertig war, ist in seine Wohnung \r\n        zuerst der Sopran gekommen, dann der Alt, der Tenor und dann der Bass.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind dann auch alle zusammen \r\n        in seine Wohnung gekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, da sind \r\n        wir dann in die Kirche gegangen zwischen 8 und 9 Uhr abends, sogar mit \r\n        Musik, die zwei Rauscher-Buben mit Violinen, der Ludwig und der Siegfried. \r\n        An Feiertagen waren die immer da.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Es gab aber noch mehr Musiker?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja freilich. \r\n        Die Musiker der bekannten Ruhmannsfeldener Blaskapelle, Heinrich Wiggerl, \r\n        Schlagintweit Lorenz, Schwarz Sepp, er war Schuster und ist gefallen, \r\n        Ebnet Erich, Kammerl Gang. Das war noch unter dem Pfarrer Bauer. Der ist \r\n        ja so stolz gewesen, wenn Musiker da waren. Vor dem 10 Uhr Hochamt hat \r\n        er dann zu uns Chors&auml;nger oft gesagt: &quot;Gell, &uuml;berspringt \r\n        mir fei was, sonst dauert es so lange! Die Leute m&uuml;ssen ja zum Schweinern \r\n        heim.&quot; Als Belohnung gabs f&uuml;r die Chors&auml;nger manchmal Obst \r\n        aus dem Pfarrgarten. Da war er schon einmalig der Pfarrer Bauer. Ja, aber \r\n        der Reicheneder hat dann den H&ouml;gn ausgestellt. Da hat August H&ouml;gn \r\n        zu letzt nicht mehr recht reden k&ouml;nnen, wie es ihm getroffen hat.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist August H&ouml;gn nach \r\n        diesem Zwischenfall noch in die Kirche gegangen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, &uuml;berhaupt \r\n        nicht mehr. Er hat auch nicht mehr richtig gehen k&ouml;nnen. Er hatte \r\n        ein Stock und hat den Fu&szlig; nachgezogen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Er hatte also einen Schlaganfall? \r\n        Wann ist das genau passiert?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann: </strong>Wie ihm Herr Reicheneder \r\n        einen Brief geschrieben hat, dass er nicht mehr in die Kirche gehen braucht. \r\n        [... &auml;hnliche Schilderung des Vorfalls wie oben ...] Der Umgang am \r\n        Kirchenchor war recht kameradschaftlich. Wir sind alle per Du gewesen.&nbsp; \r\n        Wir habe auch zum Lehrer Kestlmeier &quot;Valentin&quot; oder &quot;Vali&quot; \r\n        gesagt. Am Chor war es dann immer ganz voll. Mit den Musikern waren wir \r\n        viele Leute. Auch der rothaarige Lehrer Gruber hat mitgesungen. Er hat \r\n        Tenor gesungen. Der Schwannberger und der Holzfurtner haben Bass gesungen. \r\n        Und wenn keine Musiker mitgespielt haben, dann haben die Musiker mitgesungen. \r\n        Ich glaube, dass wir oft drei&szlig;ig Leute waren.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich habe mir die Kompositionen \r\n        von August H&ouml;gn angeschaut und es ist oft so, dass Blechbl&auml;ser \r\n        bei gewissen Stellen mit dem Chor mitspielen. Waren die Blechbl&auml;ser \r\n        da nicht zu laut?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, die haben \r\n        sich zur&uuml;ckgehalten und H&ouml;gn hat den Musikern manchmal auch \r\n        eine Zeichen gegeben, wenn sie leiser spielen sollten.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind die Kirchenchorproben \r\n        auch manchmal im Pfarrheim abgehalten worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Im Pfarrheim \r\n        nicht, weil es das Pfarrheim damals noch gar nicht gegeben hat. Damals \r\n        war das Pfarrheim noch ein Stall. Wir haben zuerst beim H&ouml;gn jede \r\n        Stimme einzeln in seinem Arbeitszimmer geprobt. Im dem Zimmer war ein \r\n        Klavier, sein Bett, ein Tisch mit St&uuml;hlen und ein Bild von seinen \r\n        Eltern. Manchmal waren auch S&auml;ngerinnen aus Patersdorf da.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann fanden die Proben mit \r\n        den kompletten Chor statt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nach den Proben \r\n        f&uuml;r jede einzelne Stimme, sind wir mit dem ganzen Chor in die Kirche \r\n        gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">47<a name="47"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat man nur auf bestimme \r\n        Anl&auml;sse hin geprobt oder wurde regelm&auml;&szlig;ig z. B. jede Woche \r\n        geprobt. </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">48<a name="48"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein. Man hat \r\n        geprobt, wenn H&ouml;gn etwas komponiert hat. H&ouml;gn ist zu mir gekommen \r\n        und hat ans Fenster geklopft und gesagt: &quot;Gell, Babettl, am Nachmittag \r\n        kommst du. Ich hab wieder was komponiert, das musst du gleich probieren.&quot;<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">49<a name="49"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind auch Werke von anderen \r\n        Komponisten aufgef&uuml;hrt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">50<a name="50"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Eigentlich nicht. \r\n        Nur seine Werke.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">51<a name="51"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat er auch bei einem Gottesdienst, \r\n        bei dem nicht der Kirchenchor gesungen hat, Orgel gespielt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">52<a name="52"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, das gabs \r\n        noch nicht. Aber er war immer da und hat jeden Tag in der Fr&uuml;h zur \r\n        Messe Orgel gespielt.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">53<a name="53"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat sonst noch jemand Orgel \r\n        gespielt au&szlig;er H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">54<a name="54"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, den Herr \r\n        Danziger wei&szlig; ich noch und den Herrn Schroll, er ist mit 36 Jahren \r\n        an Lungenentz&uuml;ndung gestorben. Sonst war keiner mehr da, der Orgel \r\n        gespielt hat. Da war immer er da und dann hat er so gehen m&uuml;ssen. \r\n        &quot;Ich hab meine Pflicht getan&quot; hat er gesagt, &quot;der Herrgott \r\n        wirds schon wissen.&quot; Dann hat er geweint, weil er nicht mehr in die \r\n        Kirche gehen darf.&quot; Er hat jetzt vierzig Jahre Dienst gemacht, jetzt \r\n        darf er zu Hause bleiben.&quot; Das ist eine Gemeinheit gewesen vom Pfarrer \r\n        Reicheneder.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">55<a name="55"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und haben Sie August H&ouml;gn \r\n        nach seiner Entlassung &ouml;fter besucht?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">56<a name="56"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, wir sind \r\n        jeden Tag zum August H&ouml;gn gegangen. [... &Auml;hnliche Schilderung \r\n        wie 30 ...] Er hat ja viel komponiert. Ich wei&szlig; nicht, wo diese \r\n        Sachen hingekommen sind. Es ist viel verloren gegangen. Mathild und ich \r\n        sind sogar in die Kirche gegangen, wie H&ouml;gn den Brief bekommen hat, \r\n        und haben alles mitgenommen, was vom H&ouml;gn da war und haben es seiner \r\n        K&ouml;chin Rosl gegeben: &quot;Rosl heben sie das gut auf, das ist noch \r\n        vom Herrn.&quot; Und die Rosl hat das wieder in die Kirche rein getragen \r\n        und dann ist das verloren gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">57<a name="57"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ja einige Kompositionen \r\n        waren aber trotzdem in der Kirche aufzufinden. Haben Chors&auml;nger ihre \r\n        Stimme mit nach Hause bekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">58<a name="58"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, wir haben \r\n        nichts mit nach Hause bekommen, das ist alles im Kasten geblieben. Zuletzt \r\n        (nach H&ouml;gns Entlassung als Chorregent) haben wir ein paar Messen \r\n        von H&ouml;gn, darunter eine Josephi-Messe, mitgenommen und der Hetzenecker \r\n        Maria gegeben, sie soll es ihrem Chorregent in Deggendorf, Goller hat \r\n        er gehei&szlig;en, geben. Der hat viel bekommen. August H&ouml;gn hat \r\n        ja den Goller auch gekannt. Goller ist oft nach Ruhmannsfelden gekommen, \r\n        da hat er immer den H&ouml;gn besucht. Die waren ja beide Deggendorfer. \r\n        Die haben sich ja gekannt von der Jugend her schon. August H&ouml;gn ist \r\n        oft w&auml;hrend der Schule nach Deggendorf gefahren. Da haben wir Schule \r\n        halten m&uuml;ssen, bis er wieder gekommen ist. Da hat ihn ein Chauffeur \r\n        vom Schwannberger gefahren. Der alte H&ouml;gn (von der Buchhandlung) \r\n        und er, das waren ja Br&uuml;der. Da hat er alles bekommen, was er gebraucht \r\n        hat, Bl&auml;tter und Notenpapier und dann hat er nat&uuml;rlich wieder \r\n        geschrieben.<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">59<a name="59"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist August H&ouml;gn \r\n        gestorben? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">60<a name="60"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Der war ja weit \r\n        in den Achtzigern. An Altersschw&auml;che.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">61<a name="61"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War er lange vor seinem \r\n        Tod schon krank oder ist er pl&ouml;tzlich gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">62<a name="62"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das ist pl&ouml;tzlich \r\n        gekommen. Er hat halt den rechten Fu&szlig; nachgezogen, aber wenn man \r\n        dazugekommen ist, hat er sich mit dem Arm bei mir eingeh&auml;ngt und \r\n        hat oft gesagt: &quot;Ein bisschen geht es schon noch, wenn auch nicht \r\n        mehr so wie fr&uuml;her.&quot; &quot;Ja, ja Herr Rektor, Hauptsache noch \r\n        ein bisschen.&quot; &quot;Aber recht lange wird es nicht mehr dauern.&quot; \r\n        &quot;Nein, nein, lassen Sie blo&szlig; nicht den Stock aus der Hand.&quot; \r\n        &quot;Ja, du hast schon Recht.&quot; Wie der H&ouml;gn gestorben ist, \r\n        war das f&uuml;r mich, wie wenn mein Vater gestorben w&auml;re. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">63<a name="63"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Gab es dann eine gro&szlig;e \r\n        Beerdigung? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">64<a name="64"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Da waren uns \r\n        f&uuml;nf. Er ist nach Deggendorf &uuml;berf&uuml;hrt worden, weil dort \r\n        auch seine Frau beerdigt worden ist. Da waren keine drei Leute bei der \r\n        &Uuml;berf&uuml;hrung. Von der Schule war er weg, die Jungen haben ihn \r\n        &uuml;berhaupt nicht gekannt und wer geht von den Alten noch. Da waren \r\n        die Glasschr&ouml;der Mathild und ich und die Frau H&auml;rtl, vom Sekret&auml;r \r\n        H&auml;rtl, denen das Haus geh&ouml;rt hat und noch eine oder zwei andere. \r\n        Und dann sind wir mitgegangen bis zum Kramh&ouml;ller Michal und dann \r\n        ist das Auto weggefahren. Die Beerdigung war in Deggendorf, da sind uns \r\n        ein Paar gewesen von Ruhmannsfelden. Aber da waren auch keine f&uuml;nf, \r\n        sechs Leute. Wer ist denn nach Deggendorf gefahren in die Beerdigung? \r\n        Ich Ruhmannsfelden war gar nichts. Der Reicheneder hat ihm nichts gehalten. \r\n        Der hat es dann doch gemerkt, was er getan hat: Einen solchen Mann tut \r\n        man ja nicht von der Kirche weg, wenn er auch den Fu&szlig; etwas nachgezogen \r\n        hat. Wie uns H&ouml;gn den Brief hingelegt hat, hat er gesagt: &quot;Jetzt \r\n        muss ich euch was zeigen: Ich habe b&ouml;se Buben gehabt in der Schule, \r\n        aber ich habe keinen gehaut, aber der (Pfarrer Reicheneder) hat mich von \r\n        der Kirche rausgeworfen. Da war er so erledigt, da hat er, meine ich, \r\n        oft in der Nacht geweint. Das kann man sich gar nicht vorstellen, was \r\n        dieser Mann mitmachen hat m&uuml;ssen. Der ist ja aufgewachsen in der \r\n        Kirche. &quot;Ich bin in Deggendorf gewesen und dann bin ich nach Ruhmannsfelden \r\n        gekommen&quot; und er hat sofort den Kirchenchor &uuml;bernommen, weil \r\n        er lauter Musik war, &quot;und der Lackel hat mich jetzt rausgeschmissen.&quot; \r\n        Wenn man sich das vorstellt, dass ein Lehrer sagt &quot;der Lackel.&quot; \r\n        &quot;Den Brief&quot; hat er gesagt, &quot;stecke ich mir hinter den Spiel, \r\n        den hebe ich mir auf.&quot; Wir haben immer gesagt: &quot;Tun sie den \r\n        Brief weg, vergessen sie es.&quot; &quot;Nein&quot;, hat er gesagt, &quot;dass \r\n        kann man nicht vergessen. 40 Jahre war ich auf dem Chor und der Lackel \r\n        hat mich rausgeschmissen.&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">65<a name="65"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;August H&ouml;gn hatte zwei \r\n        Kinder? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">66<a name="66"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Die Frieda und \r\n        der Gustl. Der Mann von der Frieda war Oberb&uuml;rgermeister von Bayreuth \r\n        und der ist jedes Weihnachten gekommen. Der H&ouml;gn hat dann immer gesagt: \r\n        &quot;M&auml;dchen kommt, am Abend ist der .... wie hat er jetzt seinen \r\n        Schwiegersohn, den Oberb&uuml;rgermeister von Bayreuth genannt? Die waren \r\n        ja wie zusammengeschwei&szlig;t. Der Schwiegersohn hat den Vater gemocht \r\n        und der Vater den Schwiegersohn. Am heiligen Abend ist er dann wieder \r\n        heimgefahren und am 2. Weihnachtsfeiertag war er wieder da, der Schlumprecht. \r\n        <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">67<a name="67"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hatte Frau Schlumprecht \r\n        dann Kinder?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">68<a name="68"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die Lilo \r\n        ....., das wei&szlig; ich jetzt nicht mehr.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">69<a name="69"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat nicht eine Tochter Inge \r\n        gehei&szlig;en? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">70<a name="70"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das war die \r\n        Tochter vom ersten Mann, dem Bierbrauer Hans Kroi&szlig; aus Deggendorf, \r\n        der ist recht reich gewesen. Die Frieda hat sich dann scheiden lassen. \r\n        Die H&ouml;gn Frieda hat dann die Inge ledig gehabt. Sie hat dann den \r\n        Rechtsanwalt Schlumprecht geheiratet, der ist lange in Bayreuth gewesen. \r\n        Die Inge hat sich mit ihrer Mutter nicht verstanden. Wie der Umsturz war, \r\n        sind da kistenweise M&ouml;bel gekommen, Pelzm&auml;ntel und Kleider, \r\n        was sie halt alles noch fortschaffen konnten, bevor die Amerikaner gekommen \r\n        sind. Mathild und ich haben geholfen und haben die Kisten im Pfarrhof \r\n        im Stall hinaufgetragen, da haben die Amerikaner es doch nicht so erwischt. \r\n        Sp&auml;ter, wie es wieder normal war, haben sie es wieder geholt. Und \r\n        wei&szlig; ich, als Inge so 13, 14 Jahre alt war, hat sie zum Gro&szlig;vater \r\n        gesagt: &quot;Gro&szlig;papa, lass mich reinschauen, was in den Kisten \r\n        von meiner Mama drin ist.&quot; Dann hat sich die Inge einen Pelzmantel \r\n        und paar Kleider herausgenommen: &quot;Das lass ich mir &auml;ndern.&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">71<a name="71"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und die Inge habe Sie auch \r\n        gut gekannt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">72<a name="72"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die Inge \r\n        war ja lange da in Ruhmannsfelden. Wie Frieda vom Kroi&szlig; weg war \r\n        und beim Schlumprecht war, hat der H&ouml;gn die Inge zu sich genommen. \r\n        Die Inge ist hier in die Schule gegangen. Sie war ja lauter Opa.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">73<a name="73"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Und dann war noch ein Sohn da, \r\n        der Gustl?&nbsp;</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">74<a name="74"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, der Gustl. \r\n        Er war verheiratet mit einer sch&ouml;nen Frau und hatte auch Kinder. \r\n        Der Frau ist eine gro&szlig;e Schublade auf den gro&szlig;en Zeh gefallen \r\n        und sie hat daran sterben m&uuml;ssen. Dann hat er die 2. Frau geheiratet, \r\n        das war von der ersten Frau die Cousine. Die haben eine gro&szlig;e G&auml;rtnerei \r\n        gehabt, da bei Bayreuth oder Kronach. Aber die Ehe ist nichts gewesen. \r\n        Der H&ouml;gn Gustl war ein Luftikus. Wenn Fasching war, war der H&ouml;gn \r\n        Gustl dabei. Da hat der alte H&ouml;gn gesagt: &quot;Da ist er der erste, \r\n        aber in der Schule war er saudamm.&quot; Er war ein Schlosser. Er war \r\n        Pilot bei den Fliegern im 2. Weltkrieg. Zwei Mal ist er abgeschossen worden \r\n        und jedes Mal ist er wieder durchgekommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">75<a name="75"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und wer hat die Wohnung \r\n        vom August H&ouml;gn nach seinem Tod ausger&auml;umt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">76<a name="76"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht mehr. Die H&ouml;gn Rosl (Rosa Beischmied) hat dann manches weggegeben. \r\n        Der Sauermann Franz hat das Kanapee gekommen und zwei oder drei St&uuml;hle. \r\n        Der Sauermann Franz hat ja nebenan gewohnt. Der H&ouml;gn hat sch&ouml;ne \r\n        M&ouml;bel gehabt.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">77<a name="77"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War es eine sch&ouml;ne \r\n        Wohnung?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">78<a name="78"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, er hatte \r\n        eine K&uuml;che, da war ein sch&ouml;ner Diwan drin, ein K&uuml;chentisch, \r\n        drei Sessel und ein K&uuml;chenschrank und eine Kredenz. Die K&uuml;che \r\n        war nicht gro&szlig;. Im Wohnzimmer hat er geschlafen. Da war&acute;s \r\n        Klavier drin, eine runder Tisch und auch drei oder vier Sessel. Das Schlafzimmer \r\n        war noch von seiner Frau. Da m&uuml;ssen sie eine guten Schreiner gehabt \r\n        haben, der ein so sch&ouml;nes Schlafzimmer gemacht hat.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">79<a name="79"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;In seiner Wohnung waren \r\n        dann sicher viele Noten?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">80<a name="80"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, freilich! \r\n        Da ist extra ein Schrank dagewesen, wo alle Noten drin waren. Alles weggekommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">81<a name="81"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und seine Frau ist doch \r\n        recht fr&uuml;h gestorben, An was ist sie gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">82<a name="82"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Sie hatte Unterleibskrebs.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">83<a name="83"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Was waren die Kinder von \r\n        August H&ouml;gn f&uuml;r ein Jahrgang?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">84<a name="84"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht so genau. Gustl hat beim Sturm gelernt. Der H&ouml;gn hat ihn \r\n        schon gemocht. Einmal waren wir bei einer Probe beim H&ouml;gn, die Stadler \r\n        Hilde, Mathild und ich, H&auml;rtl Elle. Da hat der H&ouml;gn gesagt: \r\n        &quot;Jetzt kannst du mitsingen.&quot; &quot;Nein, Papa, das mag ich nicht.&quot; \r\n        [... Gustls Kuppel, Wirtshausbesuch beim Zitzelsberger ....] <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">85<a name="85"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat August H&ouml;gn auch \r\n        einen anderen Chor als den Ruhmannsfeldener Kirchenchor geleitet?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">86<a name="86"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">87<a name="87"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat August H&ouml;gn auch \r\n        f&uuml;r andere Besetzungen, z. B. f&uuml;r eine Blaskapelle komponiert? \r\n        </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">88<a name="88"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht. Ich glaube schon. Wenn wir mehr waren und die Bl&auml;ser dabei \r\n        waren, sind wir in die Kirche gegangen. Dann har er zum Pfarrer Bauer \r\n        gesagt: &quot;Heute Abend d&uuml;rfen Sie nicht zusperren, heute kommt \r\n        der Chor.&quot; Dann haben wir bis um 10 Uhr geprobt und dann hat er dem \r\n        Pfarrer Bauer den Schl&uuml;ssel r&uuml;bergetragen. H&ouml;gn hatte selbst \r\n        keinen Schl&uuml;ssel. Wenn er gekommen ist, dann waren die Ministranten \r\n        da, da war schon offen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">89<a name="89"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie Fotos vom H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">90<a name="90"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das muss ich \r\n        nachschauen, vielleicht an Fronleichnam mit dem Kirchenchor. An Fronleichnam \r\n        gab&acute;s zwei Prozessionen: Die erste zum Wilhelm raus und wieder rein. \r\n        Der hat der lange Gang gehei&szlig;en. Die zweite Prozession war im Markt. \r\n        Da ist eine Blaskapelle mitgegangen und die ganze Zeit ist gesungen worden, \r\n        auch bei einer Beerdigung, aber alles in Latein. Jede Beerdigung ist durch \r\n        den Markt gegangen, zuerst runter und dann rauf und beim Holler haben \r\n        sie die Totentruhe abgeladen, wenn die Toten von Ausw&auml;rts waren und \r\n        sie sind dann mit den Pferden weggefahren. Und dann ist der Pfarrer gekommen. \r\n        [... &uuml;berf&uuml;llter Friedhof bei der Kirche ...] Der Sarg wurde \r\n        durch den Markt getragen und beim Birnbeck wieder zur Kirche. Der Sarg \r\n        war zuerst in der Kirche. Von der Kirche bis zum Friedhof ist alles gesungen \r\n        worden. Dann ist man wieder in die Kirche reingegangen und dann ist das \r\n        Requiem gewesen. Requien hat H&ouml;gn ja so viele geschrieben. Und im \r\n        Mai war jeden Tag eine Maiandacht, daf&uuml;r hat H&ouml;gn immer Lieder \r\n        komponiert. Zuerst ist gebetet worden, dann war eine Messe und w&auml;hrend \r\n        der Opferung ist dann immer ein Marienlied gesungen worden.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">91<a name="91"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Bei den Marienlieder kommt \r\n        eine Sopran-Solo stimme vor. Wer hat die gesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">92<a name="92"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Entweder die \r\n        Mathild oder ich mit der Mathild zusammen. Die Glasschr&ouml;der Mathild \r\n        war eine gute S&auml;ngerin. Ich bin eigentlich nie zu sp&auml;t gekommen, \r\n        aber wenn&acute;s sehr knapp war, dann bin in die zwei Treppen hoch gerannt, \r\n        jetzt k&ouml;nnte ich es nicht mehr, dann hat der H&ouml;gn gesagt: &quot;Das \r\n        wird einmal eine Himmelfahrt werden, wenn du mal in den Himmel kommst!&quot; \r\n        Die Mathild war nie zu sp&auml;t dran, die ist immer schon eine Viertelstunde \r\n        fr&uuml;her dagewesen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">93<a name="93"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Kann ich Sie noch einmal \r\n        besuchen zu einem zweiten Interview, dann kann ich auch alte Noten von \r\n        H&ouml;gn mitbringen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">94<a name="94"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja nat&uuml;rlich. \r\n        Den Notenschrank hatte die Raster Resl &uuml;ber. Zuerst hat der H&ouml;gn \r\n        geschaut, was wir singen. Dann hat er halt was aus dem Schrank genommen. \r\n        Dann ist die Raster Resl gekommen: &quot;Nein, da haben wir was anders \r\n        heute.&quot; Und es ist nicht gesungen worden. Dann ist sie zum Schrank \r\n        gegangen und hat was raus genommen. Und dann hat der H&ouml;gn gesagt: \r\n        &quot;Was hast du denn wieder. Singen wir das nicht.&quot; &quot;Nein, \r\n        das singen wir heute nicht.&quot; &quot;Na, d&acute;Res wieder!&quot;, \r\n        hat er dann gesagt. Sie hat praktisch bestimmt, was gesungen wird. Aber \r\n        an einen Feiertag hat er bestimmt. Aber die Res hat manchmal was anderes \r\n        gehabt und das hat er dann hingeschmissen. Dann hat die Raster Res gesagt: \r\n        &quot;Ich muss immer den Blitzableiter machen&quot;. Dann hat sie die \r\n        Noten wieder zusammensammeln m&uuml;ssen. Der H&ouml;gn und die Raster \r\n        Res haben schon gestritten. Wir haben auch bei den Bittg&auml;ngen mitgehen \r\n        m&uuml;ssen, nach Gotteszell, Patersdorf, Achslach, aber da ist der H&ouml;gn \r\n        mit dem Rad gefahren und hatte die Aktentaschen hinten am Fahrrad und \r\n        da haben wir gesungen auf den Kirchenchor. Da hatte der H&ouml;gn seine \r\n        Noten dabei und bis wir in die Kirche gekommen sind, hat er schon auf \r\n        der Orgel gespielt. Dann ist er mit dem Fahrrad wieder heimgefahren, von \r\n        der Kirche raus, auf das Fahrrad rauf, die Aktentasche mit den Noten aufs \r\n        Fahrrad und wieder heim. In der Schule hat er dann irgendeinen Buben gesagt: \r\n        &quot;Ihr haltet dann Schule, bis ich wieder heimkomme&quot;. Die Buben \r\n        haben dann aufgepasst, die sind dann die Lehrer gewesen. Da hat er meistens \r\n        zwei gehabt. &quot;Die m&uuml;ssen aufpassen, eine Aufsatz schreiben, \r\n        bis ich wieder komme.&quot; Wir sind um sieben in den Bittgang fort und \r\n        zehn wieder heimgekommen, bis wir eine Rast und Brotzeit gemacht haben. \r\n        Um am Nachmittag ist der H&ouml;gn auf die Jagd gegangen, jeden Tag. Er \r\n        hat sich das Gewehr umgeh&auml;ngt und ist fort.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">95<a name="95"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und wer hat dann das Fleisch \r\n        bekommen von der Jagd!</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">96<a name="96"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das hat er verkauft. \r\n        Der Schwannberger ist auch auf die Jagd gegangen. Die sind bis auf Allersdorf \r\n        gegangen zu jagen. Er hatte oft ein Reh im Rucksack, der Kopf hat rausgeschaut \r\n        und er hat es heimgefahren.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">97<a name="97"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;H&ouml;gn hatte eine Haush&auml;lterin?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">98<a name="98"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die ist \r\n        zum ihm gekommen mit 12 Jahren, als seine Frau gestorben ist, als K&ouml;chin \r\n        und um den Haushalt zu f&uuml;hren. Ja, die ist auch alt geworden und \r\n        sie ist die ganze Zeit beim H&ouml;gn gewesen. Die Rosl hat eine Tochter, \r\n        Mathilde. Die Mathilde ist jeden Sonntag beim H&ouml;gn gewesen. Da haben \r\n        sie zu dritt gegessen. Der H&ouml;gn, die Rosl und die Mathilde. [... \r\n        Mathilde Beischmied, Ossy B&uuml;hler, Franz Sattler ...] Der Sattler \r\n        war auch immer am Chor oben, er ist Tenor gewesen. [... Franz Sattler, \r\n        n&auml;chstes Interview ...] Der Holzfurtner hat ein paar Noten gehabt, \r\n        aber die sind auch weggekommen. Er hatte eine einzige Tochter. Die Stadler \r\n        Hilde hatte vielleicht auch was, die hat dann den Barezz geheiratet. Die \r\n        Stadler war eine Alts&auml;ngerin, sie hatte einen Sprachfehler, hat mit \r\n        der Zunge angesto&szlig;en, aber das hat man im Singen nicht gemerkt. \r\n        Die hatte der H&ouml;gn recht gemocht. Die Hilde ist nicht gro&szlig; \r\n        gewesen und der H&ouml;gn hatte sie immer zu ihm auf die Orgelbank sitzen \r\n        lassen. Wir waren eine eingeschworene Truppe (Zammgschworne). Die Hetzenecker \r\n        Maria habe ich vor kurzem auf dem Friedhof getroffen. Der Hetzenecker \r\n        Schos (Georg) war immer bei uns am Chor, er war Anwalt in Deggendorf. \r\n        Der alte Hetzenecker ist im Stuhl gesessen am Chor und der junge Hetzenecker \r\n        hat mitgesungen. [... Schr&ouml;ck Maria geb. Hetzenecker ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n</table>', '<p>\r\n<a href="interviews/2/barbara_essigmann1_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/2/barbara_essigmann1_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/b/bb/August_H%C3%B6gn_-_Interviews_02_Interview_mit_Barbara_Essigmann%2C_27.12.2003.ogg', 3, 1);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (3, 'Interview mit Ida H&ouml;gn, 29.12.2002', 'Ida H&ouml;gn', '1921-06-00', '0000-00-00', '0000', 81, '1921', 'Nichte von August H&ouml;gn', 'Deggendorf', '2002-12-29', '29.12.2002', 42, 60, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Die Frieda ist dann mit \r\n        dem Schlumprecht zusammen gekommen. Wie die Inge geboren wurde ist der \r\n        Hans (Kroi&szlig;) nicht auf das Standesamt gegangen und hat es angemeldet \r\n        und wie das 2. Kind gekommen ist, das war aber schon vom Schlumprecht, \r\n        da ist er auf&acute;s Standesamt gegangen und hat es angemeldet. Das wei&szlig; \r\n        ich nur von einem Cousin, der besser in Verbindung war, weil er altersm&auml;&szlig;ig \r\n        besser dazugepasst hat.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind aus der Ehe mit dem \r\n        Herr Schlumprecht Kinder hervorgegangen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja sicher, das erste \r\n        Kind ist hier in Deggendorf geboren, da war er in Deggendorf am Gericht, \r\n        Staatsanwalt oder was er war, und dann ist er nach Bayreuth gezogen mit \r\n        der Frieda. Und da ist die Frieda, also die Tochter vom Onkel August weggezogen \r\n        und hat dem Kroi&szlig; Hans sogar die Badewanne noch ausbauen lassen \r\n        und hat sie mitgenommen. Da ist dann das M&auml;dchen da gewesen, die \r\n        Lilo, dann hat sie nach einmal ein M&auml;dchen gehabt in Bayreuth und \r\n        dann einen Sohn. Eine ist in M&uuml;nchen verheiratet, sie hat einen gewissen \r\n        &quot;von Molo&quot; geheiratet. Gewohnt haben sie in M&uuml;nchen-Solln. \r\n        Karl Schlumprecht ist dann von Bayreuth nach M&uuml;nchen gekommen. Er \r\n        war Oberb&uuml;rgermeister in Bayreuth, offenbar ein gro&szlig;er Nazi. \r\n        Die Inge war aber schon weg. Wann sie vom Gro&szlig;vater weg ist, dass \r\n        w&uuml;sste halt die Rosl.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Da frage ich mal die Tochter, \r\n        die Mathilde Beischmied?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Mathilde, die Tochter? \r\n        Das ist doch eine Schwester von der Rosl. Wenn der Onkel August nach Deggendorf \r\n        zu seiner Tochter gefahren ist und hat sie besucht, dann hat er sich, \r\n        bevor er zur Treppe hinaufgegangen ist, mit einem Taschentuch die Schuhe \r\n        abgeputzt, hat es wieder eingesteckt, und hat es der Rosl zum Waschen \r\n        gegeben. Das war so eine Eigenheit von ihm. Er ist ja viel auf die Jagd \r\n        gegangen. Da ist er in der Fr&uuml;h mit einem St&uuml;ck Brot fort, das \r\n        hat ihm gereicht, bis um 4 Uhr nachmittags, wie er wieder heim gekommen \r\n        ist. Selber hatte er kein Jagdrevier, er ist mit dem Posthalter (Amberger) \r\n        gegangen. Wir sind einmal im Jahr mit der Familie nach Ruhmannsfelden \r\n        gefahren und haben ihn besucht. Da ist man zum Vornehm gegangen auf ein \r\n        Bier, man ist erst nachmittags gefahren. Da haben ihn alle Leute gegr&uuml;&szlig;t, \r\n        die einen haben gesagt &quot;Herr Lehrer&quot;, die andere &quot;Herr \r\n        Oberlehrer&quot;, die dritten &quot;Herr Rektor&quot;. So wie sie halt \r\n        bei ihm in die Schule gegangen sind. Er war nat&uuml;rlich ein strenger \r\n        Lehrer, er war &quot;gachzornig&quot;, er hat viele Schultafel zertr&uuml;mmert. \r\n        Wenn er in Wut war, hat er die Tafeln den Sch&uuml;lern um den Hals geschlagen, \r\n        dass sie kaputt waren, er hat es ihnen aber wieder gekauft. Unser Vater \r\n        hat ihm wahrscheinlich einen ganzen Satz an Tafeln liefern m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie sind eine Nichte zum \r\n        August H&ouml;gn?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja, mein Vater und August \r\n        H&ouml;gn waren Br&uuml;der. Wie sind jetzt sie auf August H&ouml;gn gekommen?<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich habe Noten entdeckt \r\n        in der Ruhmannsfeldener Kirche.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ich nehme an, dass sich \r\n        die M&auml;dchen gar nicht interessiert haben f&uuml;r diese Noten, die \r\n        Enkelin Inge z. B., weil die ist ja schon bald weggekommen vom H&ouml;gn. \r\n        Und einen Sohn hat er gehabt, der ist ja in Ruhmannsfelden beerdigt. Ja \r\n        er war ein Vollblutmusiker. (Ida H&ouml;gn h&ouml;rt gerade das Sanctus \r\n        aus der Josephs-Messe von August H&ouml;gn, in einer Aufnahme vom Ruhmannsfelden \r\n        Kirchenchor.) Die Inge muss in Garmisch sein, vielleicht hat sie einen \r\n        Lehrer geheiratet. Der Sohn von August H&ouml;gn, war Kraftfahrzeugfahrer \r\n        und hat f&uuml;nf Kinder gehabt. Und beim 5. Kind ist ihm die Frau gestorben \r\n        und das Kind hat Kinderl&auml;hmung gehabt. Dann w&auml;ren sie an den \r\n        Onkel August herangegangen, dass er es &uuml;bernimmt, aber er war ja \r\n        damals schon alt. Das hat er nicht machen k&ouml;nnen. Er und die Rosl \r\n        waren ein eingespieltes Team. Ja mit den Pfarrer haben sie nicht recht \r\n        viel Gl&uuml;ck gehabt die Ruhmannsfeldener. Der Pfarrer Bauer war mal \r\n        in Ruhmannsfelden, der war zuerst Religionslehrer in Deggendorf. Dann \r\n        der Pfarrer Fahrmeier, der war noch vorm Bauer Pfarrer in Ruhmannsfelden, \r\n        der war praktisch ein Freud unseres Hauses. Er ist alle Wochen nach Deggendorf \r\n        gekommen. Der war klein und dick. Und dann hat das Bad hergerichtet werden \r\n        m&uuml;ssen, dann hat er zuerst gebadet, und dann hat er seine Gesch&auml;fte \r\n        in Deggendorf erledigt. Seinen Ruhestand hat er aber in Deggendorf verbracht. \r\n        [... Ossy B&uuml;hler ...] Die Ruhmannsfeldener sind ziemlich aus der \r\n        Ehe gegangen alle miteinander. Dem Onkel August seine Frau auf alle F&auml;lle. \r\n        Die ist viel mit dem Schwannberger zusammen gewesen. Wissen Sie das noch \r\n        nicht? Die Emma, seine Frau, sie ist ja 1926 schon gestorben, und dann \r\n        hat er sie in Deggendorf beerdigen lassen und hat das Grab behalten, weil \r\n        er im Ruhestand nach Deggendorf gehen wollte. Er hat es dann aber nicht \r\n        geschafft, er ist in Ruhmannsfelden geblieben, so einen alten Mann kann \r\n        man nicht mehr verpflanzen, der mit Gott und der Welt in Verbindung ist. \r\n        Die Jagd hat ihm viel gegeben, die Kirche sowie, ob er mit den Pfarrer \r\n        zurecht gekommen ist, das wei&szlig; ich nicht.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Mit dem Pfarrer Reicheneder \r\n        soll es Probleme gegeben haben, sagte mir die Frau Essigmann, das soll \r\n        auch in Verbindung mit seinem Schlaganfall gewesen sein?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Hatte er einen Schlaganfall? \r\n        Das wei&szlig; ich gar nicht. Da ist die Verbindung dann schon abgerissen. \r\n        Die Verbindungen zu den Pfarrern wird nicht so g&uuml;nstig gewesen sein. \r\n        Jeder hat seinen Willen gehabt und musikalisch waren sie wahrscheinlich \r\n        auch nicht die Pfarrer. Der Pfarrer Bauer war bestimmt nicht musikalisch. \r\n        [... Pfarrer Bauer als Religionslehrer in Deggendorf ...] Er hatte sicher \r\n        kein gutes Verh&auml;ltnis zu den Pfarrer, weil er zu musisch war und \r\n        das sind alle B&uuml;ffel gewesen in seinen Augen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie es &uuml;berhaupt \r\n        gewusst, dass August H&ouml;gn viel komponiert hat und hat er davon erz&auml;hlt? \r\n        </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das habe ich gewusst. \r\n        Er hat nicht viel dar&uuml;ber erz&auml;hlt, aber das habe ich von der \r\n        Rosl gewusst, er hat f&uuml;r sonst nichts Zeit gehabt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist er &ouml;fter auf Besuch \r\n        nach Deggendorf gefahren?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; So lange die Frieda da \r\n        war schon. Aber dann ist er nicht mehr raus gefahren, ob er aufs Grab \r\n        gegangen ist, das wei&szlig; ich nicht. Und wie sein Sohn Gustl gestorben \r\n        ist, dann h&auml;tte er in Deggendorf beerdigt werden m&uuml;ssen, aber \r\n        seine Schwester Frieda, Frau Schlumprecht, hat im nicht rein gelassen. \r\n        Er hat mit einer Frau zusammengelebt und die hat ihn in Ruhmannsfelden \r\n        beerdigen lassen. Das Grab m&uuml;sste es noch geben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Gibt es das Grab in Deggendorf \r\n        noch? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das ist aufgel&ouml;st \r\n        worden. Seine Frau ist 1926 gestorben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist sie gestorben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das wei&szlig; ich nicht. \r\n        Meine Eltern waren damals im Urlaub, das ist selten vorgekommen, in Bad \r\n        W&ouml;rishofen, und sie haben zur Beerdigung Heim fahren m&uuml;ssen. \r\n        Die Beerdigung vom Onkel August haben wir dann schon miterlebt. Ein gewisser \r\n        Lehrer Nemetz hat eine sehr lange Trauerrede gehalten. Kalt war es auf \r\n        jeden Fall und er hat nicht mehr aufgeh&ouml;rt zu reden. Das war in Deggendorf. \r\n        Und das Grab ist auch noch gepflegt worden von meinem Cousin, der das \r\n        Grab von seinen Eltern nebenan hatte. Dann ist das Grab abgelaufen und \r\n        dann hat es die Frieda aufl&ouml;sen lassen. Die Frieda ist ja auch schon \r\n        gestorben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist bei der Beerdigung Musik \r\n        vom August H&ouml;gn gespielt worden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Glaube ich nicht. Er \r\n        hat ja nicht viel ver&ouml;ffentlicht. Er hat seine Sachen nur selber \r\n        aufgef&uuml;hrt. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Goller Fritz hat 1959 eine \r\n        Messe von ihm in St. Martin aufgef&uuml;hrt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Wirklich, das habe ich \r\n        gar nicht gewusst, sonst w&auml;re ich gegangen. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Waren der August H&ouml;gn \r\n        und der Goller Fritz befreundet?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Wahrscheinlich hat es \r\n        einen Gedankenaustausch gegeben, was ich aber nicht wei&szlig;. [... Goller \r\n        Fritz ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann ist der Goller Fritz \r\n        gestorben und hatte er Kinder?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Wann er gestorben ist, \r\n        das wei&szlig; ich nicht. An St. Martin ist eine Gedenktafel. Da k&ouml;nnen \r\n        sie nachschauen. Und er hatte eine Tochter ein ganz liebes M&auml;dchen. \r\n        Die rufen wir gleich an. [... Telefonatversuch ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Mich w&uuml;rde interessieren, \r\n        ob der August H&ouml;gn Geschwister gehabt hat?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja sicher, meinen Vater \r\n        Otto, geboren am 29.6.1893, eine Therese hat es noch gegeben, die ist \r\n        aber in jungen Jahren gestorben. Ich glaube, der August H&ouml;gn war \r\n        der vorletzte. Der Josef H&ouml;gn wurde geboren am 6.12.1879. M&ouml;chten \r\n        sie wissen, wann er gestorben, dann muss ich das Sterbebild holen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie Bilder vom August \r\n        H&ouml;gn? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Nein. (Sucht das Sterbebild). \r\n        Wen haben wir jetzt schon gehabt? Ludwig H&ouml;gn, wenn ich mal wieder \r\n        auf dem Friedhof komme, schreib ich mir es genau auf. Der Josef H&ouml;gn \r\n        ist gestorben am 11.2.1968. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Das war also ein Bruder \r\n        vom August H&ouml;gn.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das war ein Bruder, der \r\n        hat hier in Deggendorf gelebt am Haus, er hat sehr schlecht geh&ouml;rt \r\n        von Geburt her schon. M&ouml;chten sie das alles wissen?<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich m&ouml;chte etwas &uuml;ber \r\n        August H&ouml;gns Kindheit raus finden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Naja, er muss in die \r\n        Pr&auml;parandenschule gegangen sein. Er war Hilfslehrer irgendwo in der \r\n        N&auml;he von Landau. In Wallersdorf. Er hatte dann die Emma kennen gelernt. \r\n        Sie hatte eine Brauerei und ein Wirtshaus zu Hause. Die Emma ist schwanger \r\n        geworden und nat&uuml;rlich muss er sie dann heiraten. Die Emma hatte \r\n        dann Zwillinge und die beiden sind dann gestorben. Und die Frieda und \r\n        der Gustl sich wahrscheinlich in Ruhmannsfelden geboren nach 1910.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;War das Haus H&ouml;gn damals \r\n        musikalisch unter Andreas H&ouml;gn?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Nein, der Andreas H&ouml;gn \r\n        war mehr politisch und hat seinen Buben am Sterbebett das Versprechen \r\n        abgenommen, dass sie sich nie um Politik k&uuml;mmern sollen. Das wei&szlig; \r\n        ich von meinem Vater. Nur der Ludwig hat sich dann f&uuml;r Politik interessiert, \r\n        er war bei der Zeitung, aber die anderen alle nicht. Der Onkel August \r\n        auf keinen Fall.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben die Geschwister und \r\n        August H&ouml;gn selber dann irgendein Instrument gelernt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja, jeder hat Klavier \r\n        gelernt sogar der Onkel Sepp, obwohl er nichts geh&ouml;rt hat. Sie haben \r\n        alles auswendig gespielt. [... Klavierspiel von ihrem Vater Otto H&ouml;gn \r\n        ...] <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen sie wann die Mutter \r\n        von August H&ouml;gn gestorben ist.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Nein, da muss ich auf \r\n        den Grabstein nachschauen. 1917 muss es gewesen sein, weil unser Vater \r\n        im Krieg war und da hat sie so lange ausgehalten, bis er wieder da war. \r\n        <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie w&uuml;rden Sie den \r\n        August H&ouml;gn charakterlich beschreiben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das k&ouml;nnten seine \r\n        Sch&uuml;lern am besten sagen, denen er die Schultafeln um die Ohren geschlagen \r\n        hat. Wahrscheinlich war er &quot;gachzornig&quot;, sonst h&auml;tte er \r\n        nicht so viele Tafeln kaputt gehauen. Aber sonst war er gutm&uuml;tig, \r\n        in sich gekehrt. Ich glaube, dass er mehr Zeit mit der Musik verbracht \r\n        hat, als mit der Familie. Das mit der Frau hat wahrscheinlich auch nicht \r\n        so geklappt, sie ist halt zum Schwannberger gegangen und er hat zu Hause \r\n        Musik gemacht.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Aber der Schwannberger und \r\n        der August H&ouml;gn waren doch gute Freunde?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja, weil sie Nachbarn \r\n        waren. Ich glaube dieses Verh&auml;ltnis hat August H&ouml;gn nie erfahren, \r\n        das hat er gar nicht wahrgenommen. Er h&auml;tte es nicht akzeptiert, \r\n        wenn es auch so gewesen ist.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">47<a name="47"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Von wem haben Sie &uuml;ber \r\n        dieses Verh&auml;ltnis erfahren?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">48<a name="48"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Von einem Cousin der \r\n        Frieda, die waren altersm&auml;&szlig;ig enger zusammen. Er war beim Kroi&szlig;br&auml;u \r\n        besch&auml;ftigt. Sie hat zwar mit der Brauerei und dem Wirtshaus nichts \r\n        zu tun gehabt, dass hat alles der Kroi&szlig; Hans gemacht. Dieses Blut \r\n        hat sie wahrscheinlich von der Mutter gehabt. Der Kroi&szlig; Hans war \r\n        n&auml;mlich ein lieber Mann, ein geselliger Mann. Ich wei&szlig; auch \r\n        nicht warum die Frieda wegen dem Schlumprecht aus der Ehe gegangen ist. \r\n        [... erneuter Telefonatversuch, um die Telefonnummer von Martina Goller \r\n        zu erfahren ...] Ihre Adresse schreibe ich mir jetzt auf, dann kann ich \r\n        Ihnen alles schreiben. Das die Inge in Oberaudorf bei Garmisch lebt, stimmt \r\n        schon, ich habe die Adresse mal gehabt, aber ob sie da jetzt noch ist. \r\n        Das die Inge eine ledige Tochter ist, das stimmt nicht. Die Frieda ist \r\n        lediglich dem Kroi&szlig; Hans davon. Das ist mir unvergesslich. Ich bin \r\n        auch noch ein Kind gewesen. Die Inge ist wahrscheinlich gleich alt wie \r\n        ich. Sie ist vom Kindergarten Heim gekommen und hat gesagt: &quot;Jetzt \r\n        muss ich nach Ruhmannsfelden.&quot; Aber ich glaube, sie war gerne drin, \r\n        weil die Rosl alles gemacht hat.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">49<a name="49"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben dann die Buchhandlung \r\n        gef&uuml;hrt. War August H&ouml;gn &ouml;fter bei ihnen im Gesch&auml;ft?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">50<a name="50"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Ja, mit meinem Bruder \r\n        zusammen. Zu meiner Zeit nicht mehr, vielleicht fr&uuml;her, wie unser \r\n        Vater noch gelebt hat. August H&ouml;gn ist nicht viel nach Deggendorf \r\n        gekommen, wenn dann zu seiner Tochter. Er hat auch gar keine Zeit gehabt. \r\n        Zuerst war er Lehrer. Und mit dem Zug war das umst&auml;ndlich. Er hat \r\n        auch nichts gegessen bei seiner Tochter, nur bei seiner Rosl.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">51<a name="51"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Was war denn das eigentlich \r\n        f&uuml;r ein Verh&auml;ltnis zwischen H&ouml;gn und der Rosl?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">52<a name="52"></a></td>\r\n    <td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Das war seine Haush&auml;lterin. \r\n        [... Ossy B&uuml;hler ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/3/ida_hoegn1_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/3/ida_hoegn1_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/3/3d/August_H%C3%B6gn_-_Interviews_03_Interview_mit_Ida_H%C3%B6gn%2C_29.12.2002.ogg', 4, 2);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (4, 'Interview mit Maria Schr&ouml;ck, 30.12.2002', 'Maria Schr&ouml;ck', '1918-00-00', '0000-00-00', '0000', 84, '1918', 'Sch&uuml;lerin und S&auml;ngerin unter August H&ouml;gn', 'Deggendorf', '2002-12-30', '30.12.2002', 76, 77, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Der Schlumprecht \r\n        war ja Nazi-Oberb&uuml;rgermeister von Bayreuth. Die H&ouml;gn Frieda \r\n        war ja zuerst im Institut, sie war eine recht Stolze, hat sich mit keinen \r\n        Menschen mehr abgegeben nach dem Lyzeum, ich wei&szlig; nicht, war \r\n        sie im Damenstift oder war sie im Freudenheim. Nach dieser Schulzeit ist \r\n        sie ganz hochn&auml;sig gewesen und hat dann den Kroi&szlig; Br&auml;u \r\n        geheiratet. Da war sie eine ganz feine Frau. Da haben sie gesagt, dass \r\n        sie drei&szlig;ig Paar Schuhe hat. Der Kroi&szlig; Br&auml;u war ja auch \r\n        &quot;flinckelig.&quot; Er hatte Autos und alles. Er war ein Lebemann. \r\n        Und dann ist die Brauerei versteigert worden. Da hat die Frieda den Schlumprecht, \r\n        er war Staatsanwalt in Deggendorf und ist immer zum Kroi&szlig; Br&auml;u \r\n        zum Essen gegangen, kennen gelernt. Der ist dann Oberb&uuml;rgermeister \r\n        von Bayreuth geworden. Und die Inge vom Kroi&szlig;, das war ein so ein \r\n        armes M&auml;del, die hat dann die Beischmied Rosl aufgezogen. Bayreuth \r\n        war ja Hauptstadt von der bayerischen Ostmark, die bis Passau runter gegangen \r\n        ist. <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie an was die Frau \r\n        vom August H&ouml;gn gestorben ist?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Wei&szlig; ich nicht. \r\n        Seine Frau Emma muss aber reich abgestammt haben und sie hat dann vermittelt, \r\n        dass die Tochter auch reich heiratet, den Kroi&szlig; Br&auml;u in Deggendorf \r\n        in der Bahnhofstra&szlig;e. In Ruhmannsfelden hat es f&uuml;nf Brauereien \r\n        gegeben und sieben Wirtsh&auml;user. In Deggendorf hat es noch ein paar \r\n        mehr gegeben. Beim Kroi&szlig; ist jetzt der Arco Grafenwirt aus Moos \r\n        drauf. 1945 ist das Geb&auml;ude durch Bomben zerst&ouml;rt worden. Na \r\n        ja, in bin ein 18er Jahrgang [... Einschulung von Frau Maria Schr&ouml;ck, \r\n        Schinder-H&ouml;he, Lehrerin Schneider ...] von der 1. bis zur 4. Klasse \r\n        war ich bei der Schneider, im alten kleinem Schulhaus, da waren vier Klassen \r\n        drin. Wir waren fast 60 Kinder, Buben und M&auml;dchen gemischt, in der \r\n        Klasse. 1925 bin ich zum H&ouml;gn gekommen, da haben wir drei Klassen \r\n        den H&ouml;gn gehabt in der 5., 6. und 7. Klasse. Wir waren immer im gleichen \r\n        Schulzimmer drin, die anderen haben &ouml;fter wechseln m&uuml;ssen. 1929 \r\n        war das gro&szlig;e Hagel-Unwetter. Den ersten Aufsatz untern H&ouml;gn \r\n        haben wir &uuml;ber dieses Unwetter schreiben m&uuml;ssen. Am 4.7.1929 \r\n        war das gro&szlig;e Unwetter. So war er recht &quot;ding&quot;, wir sind \r\n        recht zufrieden gewesen. Der Michlbauer Sepp (Zitzelsberger) aus Sintweging \r\n        hat sein Schulzeug immer mit einen Riemen zusammengebunden gehabt. Da \r\n        hat der H&ouml;gn gesagt: &quot;Dein Vater hat 100 Tagwerk Holz und du \r\n        hast keinen Schulranzen!&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie ist der Musikunterricht \r\n        beim H&ouml;gn abgelaufen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Singen war schon \r\n        wichtig. 1930 war die Rheinlandbefreiung, wo die Franzosen 1930 vom Rheinland \r\n        hinaus haben m&uuml;ssen. Da haben wir lauter patriotische Lieder gelernt, \r\n        das Deutschlandlied, &quot;Gott mit dir du Land der Bayern&quot;, &quot;Kr&auml;ftiger \r\n        Zweig der deutschen Eiche, o gr&uuml;ne, bl&uuml;he fort und fort, Gott \r\n        schenk dir ferner Gl&uuml;ck und Gnade, und segne dich du teures Land, \r\n        vom B&ouml;hmerwald zum Rheingestade, vom Maingel&auml;nd zur Alpenwand.&quot; \r\n        Das war die deutsche nationale Bewegung. Das Rheinland geh&ouml;rte damals \r\n        zu Bayern. Diese Lieder haben wir alle beim H&ouml;gn gelernt. Die anderen \r\n        hatte da keine Ahnung. Dass muss man schon sagen, das Singen haben wir \r\n        gelernt bei ihm.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie ist das Singen genau \r\n        abgelaufen, hatte er ein Klavier zum begleiten?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nein, er hatte eine \r\n        Violine. Da haben wir einmal &quot;Ich hatte einen Kameraden&quot; singen \r\n        m&uuml;ssen, mit Bruststimme. Wir haben dann immer die Nachsilben &uuml;berbetont. \r\n        Dann hatte er gesagt: &quot;Das hei&szlig;t nicht &quot;streiTE&quot; \r\n        sondern &quot;streite&quot;. Das geh&ouml;rt still gesungen. Und ich habe \r\n        mich wieder nicht daran gehalten. Dann ist der H&ouml;gn hergegangen und \r\n        hat die Violine genommen: &quot;Ich hau dir die Violine rum, auch wenn \r\n        sie dreihundert Markt kostet, hau ich sie dir rum.&quot; Dann war es wieder \r\n        gut. Was war denn noch immer? Ja sein Jagdhund, der Treff, ein recht ein \r\n        netter Jagdhund und der ist dem H&ouml;gn scheinbar &ouml;fter ausgekommen. \r\n        Da hat er ihm vom Schulzimmer nach gepfiffen, vom Schulzimmer!<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat August H&ouml;gn im \r\n        Unterricht Hochdeutsch geredet?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nicht &uuml;bertrieben, \r\n        schon normal, schon bayerisch. Damals waren die amerikanischen Knickerbocker-Hosen \r\n        modern. Das war eine St&uuml;lphose. Und wenn er mit der &quot;Knickerbocker&quot; \r\n        in die Schule gekommen war, dann war&acute;s aus, da war er den ganzen \r\n        Tag &quot;grantig&quot;, da haben wir ihm nichts recht machen k&ouml;nnen. \r\n        Die Knickerbocker hat ihm nicht gefallen und nicht gepasst. Da gab es \r\n        einen ganz kalten Winter, da sind so viele Leute gestorben, der H&ouml;gn \r\n        war noch Chorregent, da sind wir vom 9 bis 11 Uhr verwaist gewesen. Da \r\n        war er in der &quot;Leiche.&quot; Und wenn einer sehr reich war, da sind \r\n        sehr viele reiche Bauern gestorben, da gab es eine levitierte &quot;Leiche.&quot; \r\n        Das sind dann die Pfarrer von Gotteszell und Achslach gekommen und haben \r\n        an den Seitenalt&auml;ren mitzelebriert. Die Gro&szlig;kopferten praktisch. \r\n        Diese Beerdigung hat nat&uuml;rlich l&auml;nger gedauert. Bis um 11 Uhr \r\n        sind wir dann verwaist gewesen, dann ist vom linkem Klassenzimmer der \r\n        Lehrer Friedrich und vom rechten Klassenzimmer der Lehrer Martin gekommen \r\n        und hat geschaut, wenn wir eine Gaudi machen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie dann Aufgaben \r\n        machen m&uuml;ssen in der Zwischenzeit?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Geschimpft haben \r\n        sie uns halt. Die Schaffner Isolde und ich wir haben ein bisschen aufpassen \r\n        m&uuml;ssen. Aber die Buben haben ja auf uns nicht aufgepasst. Zum Leitner \r\n        Heinerl hat der H&ouml;gn einmal gesagt: &quot;Du willst Ministrant sein \r\n        und bist ein solch ein R&uuml;pel.&quot; Der Achatz Schorsch, der Geiger \r\n        Karl, Kauschinger ....<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wo hat August H&ouml;gn \r\n        zu ihrer Schulzeit gewohnt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Im alten Rathaus, \r\n        das jetzt weggerissen wurde, hat er gewohnt, da hat unten die Schaffner \r\n        gewohnt und oben er. Vom diesem Haus ist eine Holztreppe zum Schulhaus \r\n        gegangen. Die Rosl hat ihm die Haushalt gef&uuml;hrt. Zuerst war er Oberlehrer \r\n        und dann ist er Hauptlehrer geworden. Den &quot;Rektor&quot; haben wir \r\n        nicht mehr erlebt. Einmal in der Woche hat er abwechselnd, aber meistens \r\n        waren es wir f&uuml;nf, in den Schulhof geschickt, zum Hof zusammen r&auml;umen, \r\n        M&uuml;ll aufsammeln, den Hof in Ordnung bringen. Und das Kriegerdenkmal \r\n        haben wir ausgrasen m&uuml;ssen. Zwischen den Steinen das Gras ausrei&szlig;en \r\n        m&uuml;ssen. &quot;Er kontrolliert&acute;s dann. Dass ihr was arbeitet.&quot; \r\n        Sonst war er ein recht guter Kerl, er ist nicht b&ouml;s gewesen, er war \r\n        ein braver. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Tatzen hat es also nicht \r\n        gegeben? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nein gar nicht? \r\n        (Zeigt ein Bild mit einem Zeppelin drauf) Da haben wir nach Sch&ouml;n \r\n        marschieren m&uuml;ssen, das war 1929, da ist der Zeppelin in den bayerischen \r\n        Wald gekommen. Alle sieben Klassen haben nach Sch&ouml;n marschieren m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wer ist der andere &auml;ltere \r\n        Herr auf diesem Klassenfoto?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Das ist der Pfarrer \r\n        Fahrmeier. Das war der Pfarrer und der K&auml;mmerer, ein gestrenger Pfarrer \r\n        alter Art. In Deggendorf in der Pfarrkirche ist er eingegraben. Das war \r\n        ein strenger Pfarrer, da wenn man nicht in die Schulmesse gegangen ist! \r\n        Ja und die Frau Schneider, ja sie war auch eine recht Nette, Gott hab \r\n        sie selig, war ein bisschen locker und salopp und da hat sie einmal eine \r\n        recht weit ausgeschnitten Pullover an gehabt und da hat immer der Tr&auml;ger \r\n        rausgeschaut. Wir haben uns nichts gedacht, da hat der Pfarrer einmal \r\n        gesagt: &quot;Es ziemt sich nicht, wie sie beisammen sind. Sie m&uuml;ssen \r\n        sich anders kleiden.&quot; &quot;Ich kann mich kleiden wie ich will.&quot; \r\n        Dann hat sie einen ganz roten Kopf bekommen und dann war es aus zwischen \r\n        den Beiden. Da haben die Pfarrer noch so was machen k&ouml;nnen. In Bayern \r\n        hatte ja fr&uuml;her die Pfarrern die Schulaufsicht. Die Funktion der \r\n        Schulr&auml;te heute haben fr&uuml;her die Geistlichen gehabt. Da hat \r\n        man auf der Stra&szlig;e niederknien m&uuml;ssen, wenn einem ein Pfarrer \r\n        begegnet ist und &quot;Gelobt sei Jesus Christus&quot; sagen m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Jetzt habe ich was dabei, \r\n        dass muss ich ihnen zeigen: Kompositionen vom H&ouml;gn.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja er hat eine Messe \r\n        geschrieben, eine Josephs-Messe. Dann habe ich nach Jahren zu unserem \r\n        Chorregent in St. Martin, Fritz Goller, gesagt: &quot;Unser Ruhmannsfeldener \r\n        Rektor ist ein Deggendorfer, der hat eine Messe komponiert, die geh&ouml;rt \r\n        doch in Deggendorf aufgef&uuml;hrt.&quot; Na ja, er soll es halt mal bringen. \r\n        Die Noten waren alle handgeschrieben. Dann haben wir sie halt mal gesungen \r\n        an einem Sonntag.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich glaube, ich wei&szlig; \r\n        sogar das genaue Datum, da habe ich einen Zeitungsartikel, das war 1957.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja, das ist dann \r\n        in der Zeitung gestanden, dass der H&ouml;gn ein Deggendorfer war. Dann \r\n        hat er noch was komponiert, ein Kommunionlied: &quot;O Herr ich bin nicht \r\n        w&uuml;rdig, zu deinem Tisch zu gehen, du aber mach mich w&uuml;rdig, \r\n        erh&ouml;r mein st&uuml;ndlich Flehn. O stille mein Verlangen, du Seelenbr&auml;utigam, \r\n        dich geistig zu empfangen, du wahres Osterlamm.&quot; Und das war so eine \r\n        sch&ouml;ne Melodie. Der Goller hat ja wilde Sachen gespielt, das ist \r\n        ja nicht ins Geh&ouml;r gegangen. Aber der H&ouml;gn hat so sch&ouml;n \r\n        komponiert. Das Lied haben wir dann auch zur Kommunion in St. Martin gesungen. \r\n        [... Goller Vinzenz, Goller Albin, Goller Fritz ...] Das Kommunionlied \r\n        ist so ins Geh&ouml;r gegangen, das muss man dem H&ouml;gn lassen. Das \r\n        haben sie meistens bei der Erstkommunion gesungen. Das ist direkt ins \r\n        Herz gegangen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Was war denn mit dem H&ouml;gn \r\n        in der NS-Zeit?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ich bin ja 1932 \r\n        aus der Schule gekommen, da hat sich noch gar nichts ger&uuml;hrt. Obwohl \r\n        der Lehrer Schultz hat doch schon immer ein bisschen propagiert. Aber \r\n        der H&ouml;gn nicht. Der war wahrscheinlich ein alter Volksparteiler oder \r\n        ein Zentrumsw&auml;hler, er hat sich da nicht festgelegt. (Im Anblick \r\n        der handschriftlichen Noten) Und alles mit der Hand geschrieben. Wunderbar. \r\n        Er ist ein gescheiter Mann gewesen. Wirklich ein prima Mann. In der Gemeinde \r\n        hat er mitgeholfen und eine Chronik hat er geschrieben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben unterm H&ouml;gn \r\n        im Kirchenchor gesungen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nicht mehr so richtig. \r\n        Da war die Raster Resl da. Da ist der Schroll gekommen dann. Ich kann \r\n        mich nicht erinnern, dass ich mit dem H&ouml;gn gesungen habe. Ja mit \r\n        der Raster Resl, das war die Meisterin oben am Chor. Sie hat alles verteilt \r\n        und hat das Notenblatt immer so gehalten, dass man nicht reinschauen konnte.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie die Josephs-Messe \r\n        mit Instrumentalisten aufgef&uuml;hrt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja, mit Geigen, \r\n        glaube ich, nicht mit Bl&auml;sern.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie lange haben Sie im Ruhmannsfeldener \r\n        Kirchenchor gesungen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Von der 4. Klasse \r\n        weg, bis ich 1950 geheiratet habe. Da war die Raster Resl noch da, Glasschr&ouml;der \r\n        Mathild, Triendl Frieda, die beiden haben Sopran gesungen und die Raster \r\n        Resl hat Alt gesungen, wir sind nur ein Anh&auml;ngsel gewesen. Und nat&uuml;rlich \r\n        die Konditor Babett (Barbara Essigmann). An Weihnachten hat dann der alte \r\n        Voit, der Apotheker, da und hat Violine gespielt, war ein kleiner, feiner \r\n        Mann, der ganz Alte. Erst wie der junge Schroll gekommen ist, dann ist \r\n        etwas Wind in den Chor gekommen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wer war dann vor dem Schroll \r\n        der Kirchenchorleiter in Ruhmannsfelden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Das war der H&ouml;gn. \r\n        Der Schroll ist dann auch noch Gemeindeschreiber geworden. Er ist dann \r\n        kurz vor dem Krieg gestorben, was hat er denn gehabt? Irgend eine Lebersache? \r\n        Mit dem Schroll ist es dann wieder aufw&auml;rts gegangen, da ist ein \r\n        neuer Wind rein gekommen, er war halt ein Junger. Er ist von Regensburg \r\n        gekommen. Ein Singschule haben wir gehabt. Das war dann zu H&ouml;gns \r\n        Zeit nicht mehr so. Das wei&szlig; ich gar nicht, dass er so viel komponiert \r\n        hat, ich hab gemeint nur die Josephs-Messe und das Kommunionlied. Ist \r\n        das dann &uuml;berhaupt aufgef&uuml;hrt worden?<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wo hat der Kirchenchor geprobt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Oben am Chor. Da \r\n        hat es sonst nichts gegeben. Nicht in einem Wirtshaus. Ob man in H&ouml;gns \r\n        Wohnung geprobt hat, dass wei&szlig; ich nicht. Kann sein. Gott hab in \r\n        selig, der geh&ouml;rt direkt heilig gesprochen, was der alles gemacht \r\n        hat. F&uuml;r die Jugend hat er sich schon recht eingesetzt und das Singen \r\n        hat er uns gelernt und patriotisch sind wir erzogen worden. Ja wir haben \r\n        ja oft bis um 12 oder 1 Uhr Schule gehabt. Es gab auch Nachmittagsunterricht, \r\n        z. B. eine Turnstunde oder Bastelstunde. Da hat der H&ouml;gn auch oft \r\n        da sein m&uuml;ssen. Die Lehrer haben es fr&uuml;her nicht sch&ouml;n \r\n        gehabt, die haben lang dableiben m&uuml;ssen. [... lange Schulwege zur \r\n        Winterzeit, Kacheloffen in der Schule ...] <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie sind Sie zum Kirchenchor \r\n        gekommen? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Das wei&szlig; ich \r\n        selber nicht, auf einmal sind wir dann halt oben gewesen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen sich erinnern, \r\n        dass Bl&auml;ser mitgespielt haben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ab und zu sind sie \r\n        dabei gewesen. Bei den ganz hohen Feiertag oder bei einer Beerdigung. \r\n        Der Geiger Karl war vorne dran. [... Frage nach der Grabst&auml;tte H&ouml;gns \r\n        ...] Da d&uuml;rfen die Deggendorfer stolz sein, dass sie so einen gehabt \r\n        haben? Das Heftchen von Ruhmannsfelden haben sie. Da hat er sich auch \r\n        recht bem&uuml;ht, dass er das zusammengebracht hat.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie dem Goller Fritz \r\n        nur diese zwei Werk zu Auff&uuml;hrung gegeben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nein, ich kann mich \r\n        an keine anderen Werke erinnern. [... Goller Martina ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Die Frau Essigmann hat mir \r\n        in einem Interview erz&auml;hlt, dass August H&ouml;gn 1953 vom Herr Pfarrer \r\n        Reicheneder quasi ausgestellte worden w&auml;re? K&ouml;nnen Sie das best&auml;tigen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Das kann schon sein. \r\n        Der Reicheneder ist ein recht ein Schlimmer gewesen und selbstherrlich. \r\n        Ich kann mich nicht mehr so genau erinnern. Ich bin ja 1950 nach Deggendorf \r\n        gezogen. Da haben wir so wie so k&auml;mpfen m&uuml;ssen, dass sie die \r\n        Messe von H&ouml;gn aufgef&uuml;hrt haben. Unsere Chorregenten sind alle \r\n        so selbstherrlich, die m&ouml;chte nur ihre eigenen Werke auff&uuml;hren. \r\n        Ich hab halt gesagt, dass der H&ouml;gn ein Deggendorfer ist und seine \r\n        Werke geh&ouml;ren halt in Deggendorf aufgef&uuml;hrt. Aber ewig ist nichts \r\n        gegangen. Dann habe ich zum Goller gesagt: &quot;So, jetzt nehme ich meine \r\n        Messe wieder und trage sie in die Himmelfahrtskirche, da wird sie eher \r\n        aufgef&uuml;hrt.&quot; Da ist der Goller in Gang gekommen, denn es gab \r\n        eine Rivalit&auml;t zwischen St. Martin und der Himmelfahrts-Kirche. Dann \r\n        hat er an Fasching mal gesagt. &quot;Die Schr&ouml;ck Maria eine Messe \r\n        bringt, und um ihre Auff&uuml;hrung ringt, doch es hat sich nichts ergeben, \r\n        (kein Termin), sind nimmt die Messe unterm Arm und sagt zum Chorregent \r\n        br&uuml;hwarm, die Messe trag ich jetzt nach Himmelfahrt rein, da wird \r\n        dann bald die Auff&uuml;hrung sein.&quot; Drei oder vier Wochen sp&auml;ter \r\n        haben wir sie dann aufgef&uuml;hrt, weil er Angst gehabt hat, dass ich \r\n        die Messe zur Himmelfahrts-Kirche trage. [... Deggendorfer Kirchenmusik \r\n        im Allgemeinen ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist August H&ouml;gn zur \r\n        Auff&uuml;hrung der Josephs-Messe nach Deggendorf gefahren?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja, da war er da. \r\n        Da haben wir ihn begl&uuml;ckw&uuml;nscht und ihm gratuliert und: &quot;Sch&ouml;n, \r\n        sch&ouml;n&quot; gesagt. Er war bescheiden und ist dann bald gegangen \r\n        [... Schaffner Isolde, die einzige Tatze von Maria Schr&ouml;ck unter \r\n        der Lehrerein Schneider ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat es im damaligen Schulhaus \r\n        ein Lehrerzimmer gegeben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Wei&szlig; ich nicht. \r\n        Manchmal haben wir einen Zettel von Klassenzimmer zu Klassenzimmer tragen \r\n        m&uuml;ssen. &quot;Da trag ihn n&uuml;ber.&quot; Ich glaube nicht, dass \r\n        die Konferenzen hatten.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">47<a name="47"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Welche Pr&uuml;fungen sind \r\n        geschrieben worden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">48<a name="48"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Wei&szlig; ich nicht \r\n        mehr so genau.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">49<a name="49"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Kann ich mir ihr Schulzeugnis \r\n        ausleihen zum Kopieren, als Dokument mit H&ouml;gns Unterschrift.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">50<a name="50"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">51<a name="51"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben die Lehrer auch Turnunterricht \r\n        geben m&uuml;ssen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">52<a name="52"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja, das ist was \r\n        gewesen. Mir sind ja nur marschiert. Es hat ja keine Ger&auml;te gegeben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">53<a name="53"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat August H&ouml;gn auch \r\n        Turnen unterrichtet?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">54<a name="54"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nein, das glaube \r\n        ich nicht. Wir sind ja nur marschiert im Schulzimmer drin, Kniebeugen, \r\n        das war unsere ganz Turnstunde<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">55<a name="55"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Gab es einen genau festgelegten \r\n        Stundenplan?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">56<a name="56"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Ja, ich glaub schon. \r\n        Wir hatte auch immer dieselben Lehrer gehabt, da ist nicht gewechselt \r\n        worden. Haben Sie den Valentin gekannt. Wie hat er gehei&szlig;en der \r\n        Vali? [... Lehrerin Wimmer ...] Ich war auch noch in der Feiertagsschule, \r\n        nach der Volksschule ist man 3 Jahre in die, amtlich hat sie gehei&szlig;en \r\n        &quot;Volksfortbildungsschule&quot;, Feiertagsschule gegangen. Da hat \r\n        man einmal in der Woche am Sonntag von 10 bis 1 Uhr in die Schule gehen \r\n        m&uuml;ssen. Und dann ist es von Sonntag auf Montag verlegt worden. Ich \r\n        wei&szlig; es nicht warum.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">57<a name="57"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat H&ouml;gn Feiertagsschule \r\n        abgehalten?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">58<a name="58"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Nein, ich bin bei \r\n        der Schneider gewesen. Da hat man nicht viel gelernt einmal in der Woche. \r\n        [... Weitere Vorhaben von Josef Friedrich ...] Ja genau der Danziger Franzl, \r\n        war der Nachfolger vom H&ouml;gn, er war der Abk&ouml;mmling eines j&uuml;dischen \r\n        Arztes. Er war zuerst in Grafling und dann ist er nach Ruhmannsfelden \r\n        gekommen. Er hat auch Orgel gespielt. Ja und der H&ouml;gn hat auch gut \r\n        gespielt. Er war auf Trab. Da hat er fest getreten mit den F&uuml;&szlig;en. \r\n        Zuerst hat man den Blasebalg noch treten m&uuml;ssen, dann ist es elektrisch \r\n        geworden. Wer hat denn den Blasebalg getreten? Der Stadler Hupf? Der Blasebalg \r\n        ist weggekommen wie der Schroll gekommen ist. [... Kinderbewahranstalt \r\n        ...] Was aus dem Gustl wohl geworden ist?<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">59<a name="59"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie was &uuml;ber \r\n        das Verh&auml;ltnis August H&ouml;gns zu seinen Kindern?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">60<a name="60"></a></td>\r\n    <td valign="top"><p><strong>Maria Schr&ouml;ck:</strong>&nbsp; Die Frieda ist ja \r\n        nie zu Hause gewesen, die war eben eine h&ouml;here Tochter, die war in \r\n        einer h&ouml;heren Schule, im Damenstift oder im Freudenheim. Freudenheim \r\n        bei Passau oder das Damenstift in Osterhofen. Fr&uuml;her waren das Mittelschulen, \r\n        aber heute sind das Gymnasien. Das hat Lyzeum gehei&szlig;en. Sie \r\n        waren alle stolz, wenn sie ins Lyzeum gegangen sind.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/4/maria_schroeck_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/4/maria_schroeck_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/d/de/August_H%C3%B6gn_-_Interviews_04_Interview_mit_Maria_Schr%C3%B6ck%2C_30.12.2003.ogg', 5, 3);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (5, 'Interview mit Barbara Essigmann, 2.1.2003', 'Barbara Essigmann', '1921-12-03', '0000-00-00', '0000', 81, '1921', 'Sch&uuml;lerin und S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2003-01-02', '02.01.2003', 51, 57, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;(Bei der Ansicht von Handschriften \r\n        von H&ouml;gn) H&ouml;gn hat alle Stimmen geschrieben? Es hat ihm keiner \r\n        geholfen? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein, da hat \r\n        er keinen gebracht. Auf manche Sopran-Stimmen hat er dann geschrieben: \r\n        &quot;Babettl&quot; und auf die andere &quot;Mathild&quot;.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Das &quot;Ecce Sacerdos&quot; \r\n        hat er f&uuml;r einen Bischofsempfang geschrieben. Wann ist denn nach \r\n        Ruhmannsfelden ein Bischof gekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Alle 2 - 3 Jahre \r\n        zu Firmung.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und die &quot;Prozessionsges&auml;nge&quot; \r\n        sind an Fronleichnam gesungen worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, bei der \r\n        Prozession. Bei jeder Beerdigung gab es eine Prozession. Man hat den Sarg \r\n        hergefahren bis zum Holler. Dann ist der Chor dazugekommen. Man ist zum \r\n        Markt runter gegangen und dann wieder rauf und dann ist er ein Geistlicher \r\n        dazu gekommen. W&auml;hrend der ganzen Zeit ist gesungen worden. &quot;Repizorien&quot; \r\n        haben die lateinischen Ges&auml;nge gehei&szlig;en, die alle der H&ouml;gn \r\n        geschrieben hat.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie lange hat ein solche \r\n        Beerdigung gedauert?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Die hat lange \r\n        gedauert. Bei Pfarrer Bauer hat sie nicht so lange gedauert. Er hat immer \r\n        gesagt: &quot;Springen!&quot; Das hat dem H&ouml;gn gar nicht gepasst. \r\n        Manchmal ist er dann gesprungen und manchmal nicht, dann hat es halt lange \r\n        gedauert. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann wurden die &quot;Tantum \r\n        ergo&quot; gesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Die wurden immer \r\n        gesungen. [... Betrachtung der Grablieder ...] Das letzte Grablied hat \r\n        Friedhofsglocken gehei&szlig;en. Wie wir die Friedhofsglocken zum ersten \r\n        Mal gesungen haben, hat der Schwannberger gesagt: &quot;Gustl, wenn ich \r\n        mal sterbe, diese Grablied kriege ich!&quot; Dann hat er gesagt: &quot;Ja, \r\n        das hebe ich f&uuml;r dich auf.&quot; &quot;Friedhofsglocken rufen klagen, \r\n        an der Mutter oder Vaters Grabesrand. Denn aus seiner lieben Mitte nahm \r\n        der Tod seine letzte Hand. Ewige Ruhe gib, o Herr seiner Seele, und das \r\n        ewige Licht leuchte ihm.&quot; Das Grablied ist sch&ouml;n gewesen! Vierstimmig \r\n        haben wir es immer gesungen. Ja, da sind M&auml;nner dabei gewesen und \r\n        Bl&auml;ser. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat H&ouml;gn die Texte \r\n        selber gedichtet? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Das hat er alles \r\n        selber gedichtet. Er hat immer gesagt: &quot;Kommt, ich habe wieder was \r\n        geschrieben. Ihr m&uuml;sst mir sagen wie es ist.&quot;<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann sind die &quot;Veni \r\n        creator Spiritii&quot; gesungen worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Die sind vor \r\n        der Predit gesungen worden. Der Pfarrer ist nur im Chorrock herausgegangen \r\n        und hat Weihwasser verspritzt. Dann ist er in die Sakristei gegangen und \r\n        hat sich sein volles Messgewand angezogen. [... Anekdote mit der &quot;Himmelfahrt&quot; \r\n        (Siehe 1. Interview am 27.12.2002) ....]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Als Lehrer hatten Sie den \r\n        H&ouml;gn auch?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ab der 4. Klasse \r\n        bis ich aus der Schule gekommen bin. Der hat uns nicht mehr ausgelassen. \r\n        Der Lehrer Friedrich wollte auch unsere Klasse, aber der H&ouml;gn wollte \r\n        nicht tauschen. Er war der Rektor. Von den ganzen Lehrern konnte keiner \r\n        Orgelspielen, nur er. An Feiertagen oder Sonntagen hat er mit vollen Registern \r\n        gespielt. Dem Pfarrer Bauer hat es gefallen, wenn der H&ouml;gn mit vollen \r\n        Registern gespielt hat. Ein Kooperator hat mal gesagt: &quot;Wenn der \r\n        Schwannberger einamtet, dann hebt es mich vorne am Altar.&quot; Der Schwannberger \r\n        hat so viel Luft gebraucht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War der H&ouml;gn streng \r\n        in der Schule?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Zu den Buben \r\n        in unserer Klasse war er streng. Als einmal in der Pause gerauft wurde, \r\n        ist er so w&uuml;tend geworden, dass er in die H&ouml;he gesprungen ist \r\n        und sogar einen Schuh verloren hat. Dann hat er zu uns Sch&uuml;ler gesagt: \r\n        &quot;Kinder, ich habe meinen Schuh verloren.&quot; Unsere Lausbuben, \r\n        der Sauermann Franz, Rauch Sepp, Pongratz Rudl haben alles gemacht mit \r\n        dem H&ouml;gn, der hat sich alles gefallen lassen, aber wenn er w&uuml;tend \r\n        wurde, dann hat er sie &uuml;bergelegt. Die Hose fest angezogen und sechs \r\n        hinten hinauf. Der eine wie der andere. Sie haben anstehen m&uuml;ssen. \r\n        Da haben sie sich gerieben und wir M&auml;dchen haben geklatscht. Wir \r\n        hatten mal eine Sch&uuml;lerin, die Beischmied Rosa: Die hat immer gesagt: \r\n        &quot;Jetzt lacht er wieder auf den Stockz&auml;hnen.&quot; Unsere Klasse \r\n        hat sich alles erlauben d&uuml;rfen beim H&ouml;gn. Die Sch&uuml;ler des \r\n        Lehrers Friedrich, Kestlmeier und Schultz haben sich nichts erlauben d&uuml;rfen. \r\n        &quot;Ich darf dich ja nicht hauen&quot;, hat er oft gesagt, &quot;sonst \r\n        beschwert sich dein Vater und ich komme ins Zuchthaus.&quot; Aber &uuml;bergelegt \r\n        hat er sie oft. Er hat auch oft sein Heft genommen und so stark gesch&uuml;ttelt, \r\n        dass alle eingelegten Bl&auml;tter heraus gefallen sind.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Welche Lieder sind im Singunterricht \r\n        gesungen worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Wir haben alle \r\n        Lieder gesungen, die es gegeben hat. Andere Lehrer haben oft Ausfl&uuml;ge \r\n        mit ihren Sch&uuml;lern nach Gotteszell, Achslach oder auf den Hirschenstein \r\n        gemacht und haben ein Brotzeit mitgenommen. Das hat es beim H&ouml;gn \r\n        nicht gegeben. Wir haben in der Schule singen m&uuml;ssen. Jeden Tag am \r\n        Nachmittag ist eine Stunde gesungen worden. Er hat mit der Violine mitgespielt. \r\n        Wenn ein Sch&uuml;ler nicht richtig gesungen hat, hat er ihnen den Geigenbogen \r\n        auf den Kopf gehauen und gesagt: &quot;Sei ruhig, du bl&ouml;der Hammel, \r\n        du hast ja eine Stimme, wie ein Ochs.&quot; &quot;Habt ihr Ochsen daheim?&quot; \r\n        &quot;Ja.&quot; &quot;Dann gehst du in den Stall und singst mit den Ochsn.&quot; \r\n        So ist er umgegangen mit den Buben. Er hat die Buben auch manchmal an \r\n        Haarans&auml;tzen gepackt und nach oben gezogen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich an \r\n        bestimmte Lieder erinnern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Alle Lieder, \r\n        die in einem Liederbuch drin waren. Mit &quot;H&auml;nschen klein&quot; \r\n        hat es begonnen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat es einen Chor der Volksschule \r\n        Ruhmannsfelden gegeben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Nein.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat man im Sitzen gesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Wir haben stehen \r\n        m&uuml;ssen. Er hat immer gesagt: &quot;Wenn man steht, dann kann die \r\n        Lunge besser atmen. Im Sitzen kann man nicht singen.&quot; Wir haben ja \r\n        in der Kirche auch stehen m&uuml;ssen, auch bei der Probe. Das hat damals \r\n        der Schroll schon eingef&uuml;hrt. Der war Chorregent und sonst war er \r\n        Gemeindesekret&auml;r. Der Schroll ist mit 36 Jahren gestorben. Er hat \r\n        eine Lungenentz&uuml;ndung bekommen. Der Schroll war 2 Jahre mit einer \r\n        Nichte vom Zattler verheiratet. Dann hat er die Chors&auml;ngerin Koadm&uuml;hl \r\n        Hedi (Grassl) kennengelernt. Bei dem weiten Weg von der Koadm&uuml;hle \r\n        zur Kirche im strengen Winter, hat er sich wahrscheinlich erk&auml;ltet. \r\n        Die Ruhmannsfeldener Kirche war nicht geheizt und es wurden trotzdem sehr \r\n        lange Messen abgehalten. Es war im Winter oft so kalt, dass wir die Noten \r\n        in die Orgel eingezwickt haben, dass wir uns die H&auml;nde reiben konnten. \r\n        Und die Oberlichte der Orgelempore war kaputt. Der Sauermann Franz hat \r\n        uns zwar eine kleine Leiter gebaut, um das kleine Fenster zu schlie&szlig;en, \r\n        aber das Oberlicht ist bald wieder aufgesprungen. Der Schroll war noch \r\n        zwei Tage im Krankenhaus und ist dann aber gestorben. Die Beerdigung war \r\n        in Kollnburg. [... genaue Beschreibung der Beerdigung ....]<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Als der Schroll Chorregent \r\n        war, was hat dann der H&ouml;gn gemacht?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Der hat Orgel \r\n        gespielt und das war dem Schroll recht, dann hat er dirigieren k&ouml;nnen. \r\n        Unter der Woche hat dann der Schroll Orgel gespielt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat der Schroll Kompositionen \r\n        vom H&ouml;gn aufgef&uuml;hrt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, er ist oft \r\n        zum Notenschrank gegangen und hat herausgeholt, was der H&ouml;gn geschrieben \r\n        hat. Manchmal wurden auch neue Sachen gekauft. Der Pfarrer Bauer hat zum \r\n        Schwannberger oft gesagt: &quot;Rudolf, du k&ouml;nntest wieder eine Messe \r\n        kaufen, wir haben &uuml;berhaupt nichts mehr, nur die alten Sachen.&quot; \r\n        &quot;Ja, ich kaufte eine.&quot; Dann hat er eine Messe gekauft. Die ist \r\n        aber auch nichts gewesen. Der H&ouml;gn hat die Stimmen wieder anders \r\n        geschrieben, ausgebessert und was Neues geschrieben. Weil wir nichts mehr \r\n        hatten. Das hat er dann angefangen, als er in Pension war. Er ist alt \r\n        geworden. So eine Beerdigung und &Uuml;berf&uuml;hrung habe ich noch nicht \r\n        erlebt. Ich h&auml;tte weinen k&ouml;nnen. Obwohl er so viel f&uuml;r \r\n        die Kirche getan hat, waren keine 5 Leute in der &Uuml;berf&uuml;hrung. \r\n        Es waren auch sehr wenige in der Beerdigung. Wenigsten die Vereine h&auml;tte \r\n        sich zusammentun und nach Deggendorf fahren k&ouml;nnen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;In Ruhmannsfelden haben \r\n        doch die Rauscher-Buben gewohnt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, die zwei \r\n        Rauscher Buben. Der Ludwig und der Siegfried das waren Musiker. Max Rauscher, \r\n        auch ein Bruder zu ihnen, war Chorregent in Simbach am Inn. Der hat auch \r\n        den Chor in Ruhmannsfelden geleitet. Das war noch unterm Pfarrer Fahrmeier. \r\n        Das war ein ganz ein schlimmer. Zu dieser Zeit hat auch die Koadm&uuml;hl \r\n        Anne im Kirchenchor gesungen. Das war eine sehr gute S&auml;ngerin. Der \r\n        Schroll hat zu uns immer gesagt, wir sollen mit Kopfstimme singen, nicht \r\n        einfach raus schreien. Die Koadm&uuml;hl Anne hat das wahnsinnig gut beherrscht. \r\n        Die hat wie eine ausgebildete S&auml;ngerin gesungen. [... Begegnung mit \r\n        dem Chorregenten Max Rauscher ...] Die beiden Rauscher-Buben haben auch \r\n        mitgesungen. Der Ludwig Rauscher hatte eine Stimme, als wie wenn er immer \r\n        heiser gewesen w&auml;re. Die beiden haben oft mit der Violine mitgespielt. \r\n        Der H&ouml;gn hat oft gesagt: &quot;Heraus mit den Instrumenten, die m&uuml;ssen \r\n        wir heute h&ouml;ren.&quot;<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben die beiden auch Solo-St&uuml;cke \r\n        gespielt? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, bei der \r\n        Wandlung. Ob der H&ouml;gn f&uuml;r Streicher rein instrumentale St&uuml;cke \r\n        komponiert hat, wei&szlig; ich nicht. [... Koadm&uuml;hle Anne ...] Der \r\n        Gustl, der Sohn vom H&ouml;gn, h&auml;tte auch mitsingen sollen. Aber \r\n        wollte nicht und ist lieber zum Sturm gegangen, wo er auch gelernt hat. \r\n        Die Frieda H&ouml;gn war zu der Zeit schon zu alt und war ja schon mit \r\n        dem Herrn Schlumprecht verheiratet. Aber wenn die Frieda da war, dann \r\n        hat sie mitgesungen. Dann hat sie sich nah zur Orgel hin stehen m&uuml;ssen, \r\n        damit der Vater es auch geh&ouml;rt hat, wie sie gesungen hat. Da hat \r\n        er dann eine Freude gehabt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat H&ouml;gn auch Stimm&uuml;bungen \r\n        mit den S&auml;ngern gemacht?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Die sind auch \r\n        gemacht worden. Immer zuvor. Jeden Sonntag war um 2 Uhr eine Messe. Da \r\n        ist zuerst das Tantum ergo gesungen worden, dann war die Messe und am \r\n        Schluss ist immer ein Lied z. B. ein Marienlied gesungen worden. Die Messe \r\n        war dann um 15:30 oder sogar erst um 16 Uhr aus. Manchmal hat der H&ouml;gn \r\n        dann gesagt: &quot;M&auml;dchen, bleibt&acute;s da!&quot; Und wir mussten \r\n        dableiben, zum Proben. Wir wollten nat&uuml;rlich Spazierengehen oder \r\n        &auml;hnliches machen. Einmal habe ich sogar geweint. Um 2 Uhr sind wir \r\n        in die Kirche rein gegangen und um 5 Uhr wieder raus gekommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wenn was Neues gelernt wurde, \r\n        hat H&ouml;gn dann die neuen Lieder vorgesungen oder am Klavier vorgespielt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Er hat uns zuerst \r\n        am Klavier das neue St&uuml;ck vorgespielt. Zuerst haben wir St&uuml;cke \r\n        von anderen Komponisten gesungen, dann seine Kompositionen. Er hat vorgespielt, \r\n        dann einen Ton angegeben und dann ist es losgegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat H&ouml;gn auch manchmal \r\n        mitgesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Ja, er war Tenor. \r\n        Er hatte eine sch&ouml;ne Stimme. Einmal haben wir dem Schwannberger zum \r\n        Geburtstag ein St&auml;ndchen gesungen, ein J&auml;ger-Lied. Der H&ouml;gn \r\n        hat ja so viele J&auml;gerlieder komponiert. Wir haben auch in der Schule \r\n        J&auml;gerlieder von ihm gesungen. Wo nur die J&auml;gerlieder hingekommen \r\n        sind? Vielleicht hat er sie nach Deggendorf zum Goller gebracht.<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Waren das einstimmige oder \r\n        mehrstimmige Lieder?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Mehrstimmig, \r\n        1. und 2. Stimme und Bass. Auch in der Schule sind sie mehrstimmige gesungen \r\n        worden mit Tenor und Bass. Unsere Buben in der Schule haben auch singen \r\n        m&uuml;ssen. Er hat oft geschrieen: &quot;Mach dein Maul auf.&quot; Zum \r\n        Sauermann Franz hat er immer gesagt: &quot;Salomons Weisheit.&quot; Er \r\n        hat nicht lesen, nicht schreiben, er hat gar nichts gekonnt. &quot;F&uuml;r \r\n        was bist du auf der Welt? Du kannst nicht lesen, nicht schreiben, du bist \r\n        saubl&ouml;d.&quot; Dann hat er ihn noch eine auf das Hirn gegeben. Aber \r\n        singen haben sie k&ouml;nnen unsere Buben. Dreistimmig haben wir immer \r\n        gesungen. Er hat sich vor die Buben hingestellt und einen Ton angegeben \r\n        mit der Violine. Einmal hat es geklopft und der H&ouml;gn musste vor die \r\n        T&uuml;re raus. Da hat der Rauch Sepp die Violine genommen und versucht \r\n        darauf zu spielen. Er hat dabei einige Saiten abgerissen. Dann ist der \r\n        H&ouml;gn wieder gekommen und wir mussten aufstehen und er hat uns einen \r\n        langen Vortrag gehalten, dass sich im Stehen die Lungen besser weiten. \r\n        Als er die Violine dann genommen hat, ist er in die Luft gesprungen vor \r\n        lauter Wut. Im Winter wollten wir oft Schlitten fahren, wie alle anderen \r\n        Klassen auch. Er hat uns nicht Schlittenfahren lassen. Wir mussten stattdessen \r\n        singen. [... der geistig behinderte Schierer Wuckal aus Muschenried, Brandstifter \r\n        ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wussten Sie es dass August \r\n        H&ouml;gn eine Geschichte von der Feuerwehr geschrieben hat, die der Pfarrer \r\n        Reicheneder abgetippt hat.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Der Pfarrer \r\n        Reicheneder und der H&ouml;gn haben nicht zusammen gepasst. Die sind wie \r\n        Hund und Katze gewesen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie hat sich H&ouml;gn mit \r\n        dem Pfarrer Fahrmeier verstanden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n	<td valign="top"><p><strong>Barbara Essigmann:</strong>&nbsp; Mit allen hat \r\n        er sich verstanden. Da hat es nichts gegeben. Mit dem Pfarrer Bauer hat \r\n        er sich sehr gut verstanden. Die beiden haben sich oft vor dem Leichenzug \r\n        bei der Sakristei so lange unterhalten, dass die Leute schon gewartet \r\n        haben. Obwohl der Pfarrer Fahrmeier ein b&ouml;ser Pfarrer war, hat es \r\n        keine Probleme gegeben. Ich hatte den Pfarrer Fahrmeier auch in der Schule. \r\n        Er war zuletzt in Deggendorf.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/5/barbara_essigmann2_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/5/barbara_essigmann2_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/2/20/August_H%C3%B6gn_-_Interviews_05_Interview_mit_Barbara_Essigmann%2C_2.1.2003.ogg', 6, 4),
(6, 'Interview mit Wilhelm Ederer, 2.1.2003', 'Wilhelm Ederer', '1931-01-05', '0000-00-00', '0000', 71, '1931', 'Sch&uuml;ler unter August H&ouml;gn', 'Ruhmannsfelden', '2003-01-02', '02.01.2003', 30, 35, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Welcher Jahrgang sind Sie?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; 1931. In der 7. \r\n        und 8. Klasse hatte ich August H&ouml;gn als Lehrer. Der 8. Jahrgang ist \r\n        1937 eingef&uuml;hrt worden, als ich in die Schule gekommen bin. Wir waren \r\n        der letzte Jahrgang der im 3. Reich Schulentlassung gehabt hat. 3 Wochen \r\n        sp&auml;ter sind die Amerikaner gekommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;August H&ouml;gn ist dann \r\n        in Pension gegangen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja, er ist dann \r\n        in das H&auml;rtl-Haus gezogen, das dem Gemeindeschreiber H&auml;rtl geh&ouml;rt \r\n        hat. Und vorher hat er in der Wohnung gewohnt, wo jetzt Herr Lankes wohnt. \r\n        Und wenn man von hinten herein gegangen ist, kam zuerst das Lehrmittelzimmer. \r\n        Er hat uns den Schl&uuml;ssel gegeben und gesagt: &quot;Geht zur Rosl \r\n        und holt das und das.&quot; Oberhalb hat der Lehrer Ertl gewohnt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie ist der Unterricht unter \r\n        August H&ouml;gn abgelaufen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ganz prima (ironisch). \r\n        Ein Heft, ein Bleistift und ein Radiergummi, das war unser ganzes Schulzeug. \r\n        Gelernt haben wir nichts. Er war Chorregent w&auml;hrend des Krieges und \r\n        da waren die Leichen&auml;mter f&uuml;r die verstorbenen Soldaten, manchmal \r\n        in der Woche zwei. Er hat Orgel gespielt und wir waren Ministranten. Wenn \r\n        einer gefallen ist in Russland, dann war nur am Vormittag ein Gottesdienst. \r\n        Manchmal ist zum Kriegerdenkmal eine Abordnung mit sechs oder neun Mann \r\n        aus Deggendorf gekommen. Sie haben dann Salut geschossen, nicht jedes \r\n        Mal. Sie sind durch den Markt herauf marschiert und die beurlaubten Soldaten, \r\n        die da waren, quasi die Fronturlauber sind mit marschiert, mit Gewehren \r\n        und den Fahnen voran und nat&uuml;rlich die Musik voran. Da drau&szlig;en \r\n        haben sie Salut geschossen, da war die weltliche Feier und danach war \r\n        der Gottesdienst f&uuml;r die Gefallenen und da hat der H&ouml;gn Orgel \r\n        gespielt. <br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und was hat die Klasse gemacht \r\n        in der Zwischenzeit?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Die Gr&ouml;&szlig;eren \r\n        haben aufgepasst und die Anderen sind heimgegangen. Bis der H&ouml;gn \r\n        wieder da war, ist fast keiner mehr da gewesen. Und in der Pause ist er \r\n        manchmal den Ausw&auml;rtigen mit dem Fahrrad nachgefahren. Wir Marktbuben \r\n        sind heimgegangen. Bis der H&ouml;gn wieder gekommen ist, war &uuml;berhaupt \r\n        keiner mehr da. Das war nicht regelrecht, ist aber vorgekommen. Wir haben \r\n        ja die H&auml;lfte der Zeit keine Schule gehabt. Wir haben z. B Kohlenferien \r\n        gehabt, das war f&uuml;r die Norddeutschen, da haben wir Kartoffelferien \r\n        gehabt. Wenn sie keine Kohlen hatten, haben wir Kohleferien gehabt, weil \r\n        sie nichts gehabt haben zum Einheizen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Das war aber doch ein Ausnahmezustand \r\n        wegen des 2. Weltkrieges?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Nur der Einfluss \r\n        vom Krieg. Wir haben auch Kartoffelk&auml;fer sammeln m&uuml;ssen, den \r\n        haben die Amerikaner absichtlich ausgesetzt, genau wie sie Flugbl&auml;tter \r\n        abgeworfen haben und Lametta, Silberstreifen durch die sie den Funkverkehr \r\n        gest&ouml;rt haben. In unserer Klasse sind die Buben von der 7. und 8. \r\n        Klassen zusammen gewesen. 50 - 60 Leute. Ab der 7. Klasse sind die Buben \r\n        weg gekommen, dass sie nicht auf die M&auml;dchen &quot;fankerln&quot; \r\n        konnten. Wir hatten den H&ouml;gn.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Gab es einen festen Stundenplan?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja, vom 10 bis \r\n        viertel nach 10 war Pause. Im Sommer ist die Schule um 8 Uhr angegangen \r\n        und im Winter eine viertel Stunde sp&auml;ter um Viertel nach zehn. Im \r\n        Winter hat es oft zwei Tage geschneit, und die Kinder mussten bis von \r\n        Kleinried runter gehen. Von da oben waren 20, 30 Kinder. Die haben auch \r\n        fr&uuml;her gehen d&uuml;rfen, wenn es stark geschneit hat oder ein Hochwasser \r\n        war. Im Klassenzimmer gab es einen Blechschirm vor dem Ofen, der mit Kleidung \r\n        der Kinder voll behangen war, wenn es geregnet hat. Damals hat es noch \r\n        keinen Anorak gegeben.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Eine Schulstunde war damals \r\n        60 Minuten lang?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja genau, bis um \r\n        12 hatten wir Schule und dann nachmittags, das ist aber nicht lange gegangen, \r\n        bis zum Krieg halt. 1939 haben 3 Lehrer einr&uuml;cken m&uuml;ssen, sie \r\n        waren Reserveoffiziere, beim Polenfeldzug haben sie einr&uuml;cken m&uuml;ssen. \r\n        W&auml;hrend des Krieges waren nur 2 Lehrer da, der Lehrer Ertl und der \r\n        H&ouml;gn, das andere waren alles Lehrerinnen und da waren zu wenige da, \r\n        wenn 50, 60 Kinder in einer Klasse waren. Du kannst dir vorstellen, was \r\n        da los war.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie hat der H&ouml;gn dann \r\n        die Disziplin halten k&ouml;nnen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Das ist nat&uuml;rlich \r\n        ein Problem gewesen. Er hat halt wieder einen gepackt beim &quot;Kravattl&quot; \r\n        (Nacken) und hat ihn mit dem Kopf an die Tafel gesto&szlig;en. Der H&ouml;gn \r\n        hat oft gesagt: &quot;Merk dir das, du L&uuml;mmel, nimm die Kopfbedeckung \r\n        ab, wenn du in eine bedeckten Raum hinein gehst! Und sag blo&szlig; nicht, \r\n        dass du beim Lehrer H&ouml;gn in die Schule gegangen bist, wenn du mal \r\n        &uuml;ber den Hochb&uuml;hl hinauskommst.&quot; &quot;Du ordin&auml;ren \r\n        Hund, Du!&quot; Da ist keiner mit Glac&eacute;-Handschuhen angefasst worden. \r\n        [... Strafen von verschiedenen Lehrern ...]. Ja das ist der H&ouml;gn \r\n        gewesen, der hat ja nach dem Krieg noch Schule gehalten. [... Berufsschule....]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat H&ouml;gn Berufsschule \r\n        gehalten? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Das war noch zu \r\n        Zeiten meines Vaters. Die Feiertagsschule, am Sonntag, nach der 7. Klasse \r\n        ist man in die Feiertagsschule gekommen, zwei Jahre lang. Da hat man sonntags \r\n        2 Stunden Unterricht gehabt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie hat der Musikunterricht \r\n        beim H&ouml;gn ausgesehen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja, haben wir schon \r\n        gehabt, haben schon gesungen, da haben wir Lieder gelernt, aber haupts&auml;chlich \r\n        bei der Werner. Da hat ein jeder Lehrer ein Instrument spielen k&ouml;nnen. \r\n        Zumindest Geige. Ich wei&szlig; es von der Rosenbeck, von der Schneider, \r\n        die Ascherl hat am Chor gespielt, der H&ouml;gn sowieso, der Lehrer Ertl \r\n        hat gut Klavier spielen k&ouml;nnen. Er hat im Gesellenverein Klavier \r\n        gespielt in der Pause. In der 8. Klassen haben wir dann &uuml;berwiegen \r\n        die &quot;Jungvolk&quot;-Lieder gesungen von der HJ. Mit zehn ist man \r\n        zum &quot;Jungvolk&quot; gekommen, mit 14 Jahren zur HJ. Wir w&auml;ren \r\n        dann auch dazu gekommen, aber da war dann der Krieg aus. Die haben sie \r\n        ja mit 15, 16 Jahren eingezogen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Jetzt eine andere Frage: \r\n        Hat es um 58 rum in Ruhmannsfelden einen M&auml;nnergesangsverein gegeben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja, da war ich \r\n        auch dabei. Der ist im Schafferkeller abgehalten worden und gegr&uuml;ndet \r\n        worden. Der Danziger war Chorleiter. Der Chor war nicht sehr alt. Er hat \r\n        sich auch bald wieder aufgel&ouml;st. Der Chor war gar nicht so schlecht, \r\n        man hat sogar an Wettbewerbe teilgenommen. Mitglieder waren der &quot;Bibi&quot;, \r\n        der alte Bartaschek, Lippl Helmut, der alte Schweizer, Frisch Xaver. Im \r\n        Schafferkeller gab es ein Klavier und da ist geprobt worden. Im Kindergarten \r\n        gab es ein Harmonium und hier ist ein Klavier da gewesen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich frag deshalb, weil der \r\n        Chor am 80. Geburtstag von H&ouml;gn gesungen hat, 1958, da war Hans Czech, \r\n        Vorstand? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja, das kann sein, \r\n        da war ich aber nicht mehr dabei. Ich war nur 2 - 3 Jahre dabei. Sie haben \r\n        immer sonntags geprobt. [... Danziger Franz, Bericht &uuml;ber den Stand \r\n        der Interviews ...] Der Kroi&szlig; Br&auml;u aus Deggendorf war der Vater \r\n        der Inge Schlumprecht, dann hat die H&ouml;gn den Schlumprecht geheiratet, \r\n        dadurch haben sie den Namen der Inge umschreiben lassen. Der Schlumprecht \r\n        war ein ganz hohes Viech. Die politische Einteilung der Bezirke war damals \r\n        so. Wir waren Ostbayern, Gau Bayreuth. Da ist der Gauleiter W&auml;chtler \r\n        zust&auml;ndig gewesen f&uuml;r den ganzen Gau hier bei uns. Wir hatten \r\n        auf der Uniform einen Schriftzug &quot;bayerische Ostmark&quot;. Die B \r\n        85 hat nicht B 85 gehei&szlig;en sondern Ostmarkstra&szlig;e, die ist \r\n        gebaut worden unter Hitler. Als Kinder haben wir ja nichts anderes gelernt. \r\n        Wir haben jeden guten Fliegen, jeden Ritterkreuztr&auml;ger, die ausgezeichnet \r\n        wurden mit Gold und Brillant und so weiter gelernt, wie der M&ouml;lders, \r\n        Galant, Admiral Deniz. Die sind im Kalender gestanden. Haupts&auml;chlich \r\n        bei der Werner haben wir das gelernt. Sie war ein hundertprozentiger Nazi. \r\n        Wir hatten eine Kalender, da ist alles drin gestanden &uuml;ber Wehrmacht, \r\n        Luftwaffen und Heer. Manche haben einen Zettel mit heimgekriegt und dann \r\n        vor der Klasse einen Art Vortrag halten m&uuml;ssen, z. B. &uuml;ber den \r\n        General M&ouml;lders oder &uuml;ber den Admiral Deniz.</p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Gab es so was auch unterm \r\n        H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Nein, unter dem \r\n        H&ouml;gn nicht. Da haben wir erst vor kurzem dar&uuml;ber geredet. Der \r\n        H&ouml;gn und die Werner waren immer ein bisschen &uuml;bers Kreuz. Der \r\n        H&ouml;gn war von der Kirche und sie waren ein reiner Super-Nazi. Sie \r\n        hat das Kreuz vom H&ouml;gn in den Garten raus geworfen. Da sind wir in \r\n        die Schule gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat der H&ouml;gn das Kreuz \r\n        wieder ins Klassenzimmer geh&auml;ngt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Der hat es nicht \r\n        mehr reingeh&auml;ngt. Die Werner hat den Hitler reingeh&auml;ngt. Da \r\n        hat sich der H&ouml;gn immer zur&uuml;ckziehen m&uuml;ssen, obwohl er \r\n        Oberlehrer war, weil das politische einfach Vorrang hatte. Angenommen \r\n        er h&auml;tte die Werner verteufelt, dann w&auml;re er ja geflogen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Er hat sich also still gehalten \r\n        und er hat auch keine Sachen gemacht, wie die Werner mit den Fliegern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ach, der H&ouml;gn \r\n        war ja ein konservativer Mann. Das war ein Lehrer vom altem Schlag. Ich \r\n        hab schon mal zu ein paar Gemeinder&auml;ten gesagt. Alle Namen habt ihr \r\n        in der Stra&szlig;e, aber dass ihr dem Oberlehrer H&ouml;gn eine Stra&szlig;e \r\n        widmet, das w&auml;re doch angebracht. Im &ouml;ffentlichen Bereich hat \r\n        er viel geleistet. War&acute;s im kirchlichen oder im schulischem. [... \r\n        Glockenfoto...] Zu einem unserer Mitsch&uuml;ler, ich will den Namen nicht \r\n        sagen, hat der H&ouml;gn gesagt: &quot;Du bist d&uuml;mmer, als dem Peterbauer \r\n        sein Ochse!&quot; Solche Sachen hat er gesagt der Lehrer H&ouml;gn. Das \r\n        muss man so sehen: Die Kinder haben vor der Schule h&uuml;ten m&uuml;ssen. \r\n        Dann sind sie in die Schule gegangen, von der Schule heim, da ist ein \r\n        alter Gries auf dem Tisch gestanden und dann wieder raus auf die Weide \r\n        zum h&uuml;ten. Wie sollen denn die da eine Hausaufgabe machen. Das war \r\n        eine halbe Kinderarbeit.<br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und Hausaufgaben hat es \r\n        auch damals gegeben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ja freilich. Und \r\n        Strafaufgaben haben wir bekommen. Da haben wir in eine Zeile gleich vier \r\n        W&ouml;rter hingeschrieben, dass die Tafel voll geworden ist. Und wenn \r\n        man sie nicht gehabt hat, hat man Tatzen bekommen. Zuerst hatten wir eine \r\n        Tafel und dann haben wir Hefte bekommen. [... Schiefertafeln...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie wollten die Geschichte \r\n        mit dem &quot;Teppich klopfen&quot; erz&auml;hlten?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n	<td valign="top"><p><strong>Wilhelm Ederer:</strong> Es hat w&auml;hrend des \r\n        Krieges Vitamin C Tabletten gegeben, weil es keine Bonbons gegeben hat, \r\n        unter anderem ist auch das Kn&auml;ckebrot aufgekommen. Da haben wir jeden \r\n        Tag eine Vitamin C Tablette bekommen. Da haben wir einmal, der Holler \r\n        Toni und ich, ins Lehrmittelzimmer gehen m&uuml;ssen zur K&ouml;chin Rosl. \r\n        Das Lehrmittelzimmer war im H&ouml;gn seiner Wohnung, da sind seine B&uuml;cher \r\n        drin gewesen, die er gebraucht als Schulleiter. Unter anderem waren da \r\n        auch die Tabletten drin auf dem Kasten. Da habe ich zum Holler Toni gesagt: \r\n        &quot;Jetzt gehst du zur Rosl und h&auml;ltst sie ein bi&szlig;chen auf&quot; \r\n        Ich habe dann die Tabletten runter geholt und sie geklaut und in meine \r\n        Knickerbocker rein getan. Die Rosl hat nichts davon gemerkt. Dann sind \r\n        wir ins Klassenzimmer zur&uuml;ckgegangen und als wir wieder im Klassenzimmer \r\n        waren, auf einmal f&auml;llt mir die Tabletten aus der Hose. Da hat der \r\n        H&ouml;gn mich gepackt und gesagt: &quot;Friss, dass du genug kriegst.&quot; \r\n        Da sind alle m&ouml;glichen Sachen passiert. Da bin ich mal auf den sch&ouml;nen \r\n        Birnbaum auf dem Schulgel&auml;nde geklettert. Dann hat der H&ouml;gn \r\n        mich gesehen und die Fenster des Schulzimmers aufgerissen. &quot;Um Gottes \r\n        Willen Eder komm runter, du f&auml;llst mir vom Birnbaum runter.&quot; \r\n        Und dann haben wir auch Teppiche geklopft, die Teppiche aus seiner Wohnung. \r\n        Da war hinter uns ein Zwetschgenbaum und wir haben so geklopft, dass die \r\n        Zwetschgen vom Baum runter gefallen sind. Einen Hund hatte er auch der \r\n        Lehrer H&ouml;gn, weil er auf die Jagd gegangen ist.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/6/wilhelm_ederer_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/6/wilhelm_ederer_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/3/31/August_H%C3%B6gn_-_Interviews_06_Interview_mit_Wilhelm_Ederer%2C_2.1.2003.ogg', 7, 5),
(7, 'Interview mit Ida H&ouml;gn, 3.1.2003', 'Ida H&ouml;gn', '1921-06-00', '0000-00-00', '0000', 81, '1921', 'Nichte von August H&ouml;gn', 'Deggendorf (Telefon)', '2003-01-03', '03.01.2003', 4, 14, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;In welche Kirche sind die \r\n        H&ouml;gns gegangen, als August H&ouml;gn noch in Deggendorf war?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Mari&auml; Himmelfahrt, \r\n        sie sind wahrscheinlich auch in die Grabkirche gegangen, aber haupts&auml;chlich \r\n        doch nach Mari&auml; Himmelfahrt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wer war zu dieser Zeit Kirchenmusiker \r\n        in Maria Himmelfahrt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Goller Albin<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat damals jemand aus der \r\n        Familie H&ouml;gn im Kirchenchor in Deggendorf mitgesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Ida H&ouml;gn:</strong>&nbsp; Mein Vater Otto, also \r\n        August H&ouml;gns und meine Mutter haben mitgesungen. Ob August H&ouml;gn \r\n        mitgesungen hat, wei&szlig; ich nicht, es ist aber wahrscheinlich anzunehmen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/7/ida_hoegn2_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/7/ida_hoegn2_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/4/4f/August_H%C3%B6gn_-_Interviews_07_Interview_mit_Ida_H%C3%B6gn%2C_3.1.2003.ogg', 8, 6);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (8, 'Interview mit Josef Brunner, 3.1.2003', 'Josef Brunner', '1924-06-28', '0000-00-00', '0000', 78, '1924', 'Organist unter August H&ouml;gn', 'Achslach (Telefon)', '2003-01-03', '03.01.2003', 12, 22, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Sie haben in Ruhmannsfelden \r\n        Orgel gespielt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  W&auml;hrend der \r\n        Hitler-Zeit durfte August H&ouml;gn, er war Schulleiter, nicht mehr Orgelspielen \r\n        w&auml;hrend der Schulzeit. Da bin ich dann f&uuml;r ihn eingesprungen, \r\n        z. B. bei Beerdigungen. Ich kann mich an die Chors&auml;ngerinnen Raster \r\n        Res, Konditor Babett (Barbara Essigmann) und Glasschr&ouml;der Mathild \r\n        erinnern.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Von welchen Komponisten \r\n        haben sie damals Requiem aufgef&uuml;hrt, war vielleicht sogar ein Requiem \r\n        von August H&ouml;gn dabei?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Das wei&szlig; ich \r\n        nicht mehr so genau. An ein Requiem von Deschermeier kann ich mich erinnern. \r\n        Das haben sie oft gesungen. Ein Requiem von Ett und eins von Gebert.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Am Friedhof sind dann Grablieder \r\n        aufgef&uuml;hrt worden. Wissen von welchen Komponisten? Waren eventuell \r\n        auch Grablieder von H&ouml;gn dabei?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Das wei&szlig; ich \r\n        nicht mehr. Bei den Beerdigungen ist der Chor immer mitgegangen. Da war \r\n        vor dem Gottesdienst schon ein Beerdigungszug, dann war der Gottesdienst \r\n        und nach dem Gottesdienst war dann das Libera, das sind alle Leute zum \r\n        Friedhof gegangen und es ist lateinisch gesungen worden. Es gab auch sogenannte \r\n        "levitierte" Beerdigungen. Da sind drei Pfarrer zusammen gekommen \r\n        und haben eine Messe gehalten. Der 2. Pfarrer hat dann eine Beimesse gehalten. \r\n        Fr&uuml;her war es viel feierlicher. Levitierte Leichen sind nur f&uuml;r \r\n        wohlhabende Bauern oder Gesch&auml;ftleute abgehalten worden. F&uuml;r \r\n        die anderen gab?s nur ein Amt. Da hat es auch die Klassen gegeben: \r\n        1. Klasse, 2. Klasse und 3. Klasse. Das war schon eigenartig. Auf Deutsch \r\n        gesagt, es ist haupts&auml;chlich ums Geld gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wurden Sie f&uuml;rs Beerdigungs-Spielen \r\n        bezahlt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Ja man bekam schon \r\n        Geld. Ich wei&szlig; blo&szlig; nicht mehr wie viel. Chors&auml;nger bekamen \r\n        weniger und der Chorregent bekam mehr. F&uuml;r damalige Verh&auml;ltnisse \r\n        war es auch ein Geld.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Sie hatten ein Bewerbungsschreiben \r\n        als Nachfolger von August H&ouml;gn an die Pfarrgemeinde Ruhmannsfelden \r\n        geschrieben? Haben Sie eine Antwort darauf bekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Wei&szlig; ich nicht \r\n        mehr so genau. Damals war es eine reine Aushelferei. Die Lehrer mussten \r\n        in den Krieg einr&uuml;cken und H&ouml;gn durfte w&auml;hrend der Hitlerzeit \r\n        keine Nebenfunktion haben. Am Sonntag war der Kirchendienst kein Problem, \r\n        nur unter der Woche durfte er nicht Orgel spielen. Da habe ich dann ausgeholfen. \r\n        Ich bin erst kurz vorher aus der Schule gekommen. Ich habe bei drei Pfarreien \r\n        ausgeholfen: Gotteszell, Ruhmannsfelden und Achslach.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Haben sie nach dem Krieg \r\n        auch noch ausgeholfen in Ruhmannsfelden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Nein, 1947 habe \r\n        ich geheiratet und dann bin ich nach Achslach gezogen und haben den Kirchenchor \r\n        in Achslach &uuml;bernommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Viele Kompositionen haben \r\n        Sie geschrieben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Damals habe ich \r\n        viel komponiert, sogar eine Messe, eine Kurz-Messen, die hat der H&ouml;gn \r\n        mitgenommen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wissen Sie vielleicht, wo \r\n        noch irgendwelche Kompositionen von H&ouml;gn ausfindig gemacht werden \r\n        k&ouml;nnen? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Josef Brunner:</strong>  Der H&ouml;gn hat \r\n        ja sehr viel komponiert. Der H&ouml;gn war ein selten guter Musiker. So \r\n        einer steht nicht mehr auf. Die Kompositionen sind auch aufgef&uuml;hrt \r\n        worden. Ich kann mich an eine Auff&uuml;hrung einer Messe an Kirchweih \r\n        erinnern. Da habe ich Orgel gespielt und er hat dirigiert. Da war Orchester \r\n        dabei, zwei Geigen, und ich glaube eine Bassgeige. Der H&ouml;gn ist zu \r\n        uns nach Weitmannsried gekommen, wo ich geboren wurde und damals gelebt \r\n        habe, und hat gefragt, ob ich mitspiele. Drei oder vier Proben mit Orchester \r\n        gab es. Die Proben waren immer in der Kirche drinnen. Als Jugendlicher \r\n        war das f&uuml;r mich eine gro&szlig;e Ehre.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n</table>', '<p>\r\n<a href="interviews/8/josef_brunner_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/8/josef_brunner_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/b/bc/August_H%C3%B6gn_-_Interviews_08_Interview_mit_Josef_Brunner%2C_3.1.2003.ogg', 9, 7),
(9, 'Interview mit Dr. Doraliesa Wiegmann, 19.1.2003', 'Dr. Doraliesa Wiegmann', '1919-10-14', '0000-00-00', '0000', 83, '1919', 'Bekannte von August H&ouml;gn', 'Ruhmannsfelden', '2003-01-19', '19.01.2003', 32, 54, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hatten Sie August H&ouml;gn \r\n        als Lehrer in der Schule?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Dr. Doraliesa Wiegmann:</strong>&nbsp; Nein, wir \r\n        hatten die alte Frau Wimmer [... Frau Kronner, Beischmied Mathilde ...] \r\n        Sie haben Noten von August H&ouml;gn entdeckt?<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ja, etwa 25 Werke.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Dr. Doraliesa Wiegmann:</strong>&nbsp; Aber die \r\n        hat er nicht aufgef&uuml;hrt? Ich glaub&acute;s nicht. Erstens war der \r\n        Chor damals nicht besonders gut. Da war ja die Raster Res dabei. F&uuml;r \r\n        uns war August H&ouml;gn eben der Herr Rektor, eine Respekt-Person. In \r\n        der Schule hatte ich ihn nicht. Er hat jedes Mal die Orgel gespielt und \r\n        war Dirigent, das war selbstverst&auml;ndlich und er hatte eben diese \r\n        Chronik geschrieben von Ruhmannsfelden, die &quot;Geschichte von Ruhmannsfelden&quot; \r\n        [... Auflagenst&auml;rke der Ausgabe von 1949 ...] In der Schule haben \r\n        sie uns den Namen von Ruhmannsfelden so erkl&auml;rt: &quot;Ruht der Mann \r\n        im Felde&quot;, da haben sie noch nichts vom Rumar gewusst. Das hat dann \r\n        der H&ouml;gn erst erforscht. H&ouml;gn hat nach &Uuml;berresten von einer \r\n        Hofmark gesucht. Er hat &uuml;berall gesucht, z. B. in der Leiten. Er \r\n        hat immer geschaut, wo Erh&ouml;hungen sind, da Burgen oder H&ouml;fe \r\n        hier gesch&uuml;tzt waren und einen &Uuml;berblick hatten. In der N&auml;he \r\n        meines Wohnhauses ist das Pestgrab gelegen. Ich habe alte Leute, die &uuml;ber \r\n        90 Jahre alt waren, gefragt, ob hier H&auml;user standen, bevor mein Haus \r\n        gebaut wurde. Mindestens 200 Jahre vorher stand an dieser Stelle kein \r\n        Haus. Bei Gartenarbeiten habe ich rautenf&ouml;rmige Platten, Bodenkacheln \r\n        gefunden in einen halben Meter Tiefe. Ich habe mit dem Spaten runter gegraben. \r\n        Als ich die Erde weggekratzt habe, konnte man ein Rautenmuster sehen. \r\n        Das muss um 1960 gewesen sein, da habe ich mir gedacht: &quot;H&auml;tte \r\n        ich es dem H&ouml;gn gesagt, da h&auml;tte er sicher eine Freude gehabt.&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Woher wissen Sie, dass August \r\n        H&ouml;gn an anderen Stellen nach der Hofmark gesucht hat?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Dr. Doraliesa Wiegmann:</strong>&nbsp; Das haben \r\n        die Leute hier schon gewusst, dass er danach sucht. Das hat sich rum gesprochen. \r\n        Mein Vater hat nat&uuml;rlich auch erz&auml;hlt, was man so h&ouml;rt. \r\n        (Er war Arzt in Ruhmannsfelden) [... Frau Wiegmann gibt Josef Friedrich \r\n        Papier, geschnitten wie die gefunden Platten, zur Herstellung eines rautenf&ouml;rmigen \r\n        Musters ....] Das rautenf&ouml;rmige Muster ist von den Grafen von Bogen \r\n        auf das M&uuml;nchner Wappen gekommen, also zu den Wittelsbachern. So \r\n        sind die Platten einfach genial verlegt. Aus so einfach zugeschnitten \r\n        Platten ein Rautenmuster machen. Das kann kein Fu&szlig;boden von einem \r\n        Gang oder Stall gewesen sein. Der alte H&ouml;gn h&auml;tte hier eingehakt. \r\n        Das Grundst&uuml;ck liegt hat fast am Gipfel dieses H&uuml;gels. In der \r\n        Schule hat man uns gesagt, dass keine &Uuml;berreste von der Hofmark gefunden \r\n        wurden und man kann sich nicht denken, wo sie gelegen hat.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wissen Sie, an was August H&ouml;gn \r\n        gestorben ist?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Dr. Doraliesa Wiegmann:</strong>&nbsp; Wei&szlig; \r\n        ich nicht. Mein Vater h&auml;tte das sicher gewusst. Als Arzt sieht man \r\n        das, was die Leute f&uuml;r Krankheiten haben, auch wenn sie selbst nicht \r\n        bei einem in Behandlung sind. August H&ouml;gn war wahrscheinlich beim \r\n        Dr. Stern in Behandlung. Die Praxis in der direkten Nachbarschaft gelegen. \r\n        H&ouml;gn hat ja in der Geschichte von Ruhmannsfelden gar nichts von einer \r\n        &quot;Hofmark&quot; geschrieben, er war sich also nicht sicher. Viele \r\n        Orte waren fr&uuml;her Hofmarken. Ruhmannsfelden war bei den Bogenern \r\n        mit ihrem Rautenwappen. Es kann leicht sein, dass hier bei meinem Haus \r\n        Jahrhunderte nichts mehr gestanden ist, als die Bogener im 13. Jahrhundert \r\n        ausgestorben sind. Das &auml;lteste Viertel in Ruhmannsfelden mit den \r\n        &auml;ltesten noch stehenden H&auml;usern in Ruhmannsfelden ist hier in \r\n        der Nachbarschaft, das Kalteck. [... Aufbewahrung von Patienten-Karteikarten, \r\n        Todesursachenaufzeichnung im Pfarramt, Danziger Aloisia ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie die Enkel Inge \r\n        von August H&ouml;gn gekannt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Dr. Doraliesa Wiegmann:</strong>&nbsp; Gekannt \r\n        schon, aber wissen tue ich nicht viel dar&uuml;ber? Schlumprecht Inge \r\n        hat sie gehei&szlig;en und war mit einer der Baumeister Karl T&ouml;chter \r\n        befreundet. [... Hilde Karl ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n</table>', '<p>\r\n<a href="interviews/9/dr_doraliesa_wiegmann_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/9/dr_doraliesa_wiegmann_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/d/d3/August_H%C3%B6gn_-_Interviews_09_Interview_mit_Dr._Doraliesa_Wiegmann%2C_19.1.2003.ogg', 10, 8),
(10, 'Interview mit Mathilde Beischmied, 21.1.2003', 'Mathilde Beischmied', '1923-01-01', '0000-00-00', '0000', 80, '1923', 'Tochter von August H&ouml;gns Haush&auml;lterin Rosa Beischmied, wohnte lange Zeit in H&ouml;gns Wohnung', 'Gotteszell (Telefonat)', '2003-01-21', '21.01.2003', 2, 2, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Entschuldigung wegen der \r\n        St&ouml;rung, aber ich h&auml;tte noch ein paar Fragen wegen dem Herrn \r\n        H&ouml;gn? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Ich wei&szlig; \r\n        ja gar nichts mehr.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Das k&ouml;nnten Sie vielleicht \r\n        noch wissen. Wie hat denn die Enkelin von August H&ouml;gn mit Nachname \r\n        gehei&szlig;en. </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Das wei&szlig; \r\n        ich auch nicht mehr, die Enkelin ist doch geschieden? Fr&uuml;her hat \r\n        sie Schlumprecht gehei&szlig;en. Ihr Vater hat Kroi&szlig; gehei&szlig;en, \r\n        der erste Mann, und vom Schlumprecht sind auch drei Kinder da. Da kann \r\n        ich ihnen gar keine Auskunft geben. (Sie legt auf)<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/10/mathilde_beischmid1_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/10/mathilde_beischmid1_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/0/09/August_H%C3%B6gn_-_Interviews_10_Interview_mit_Mathilde_Beischmid_21.1.2003.ogg', 11, 9),
(11, 'Interview mit Dr. Josef Stern, 21.2.2003', 'Dr. Josef Stern', '1907-08-04', '2004-11-15', '15.11.2004', 96, '1907', 'Hausarzt und Nachbar von August H&ouml;gn', 'Teisnach, Altenheim (Telefon)', '2003-02-21', '21.02.2003', 14, 14, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Dr. Josef Stern:</strong>&nbsp; &Uuml;ber den \r\n        Herrn August H&ouml;gn: Ich bin 1938 nach Ruhmannsfelden gekommen. Da \r\n        war er Oberlehrer und hat in der Wohnung, wo jetzt August Lankes wohnt, \r\n        gelebt. Danach hat er im H&auml;rtel-Haus gewohnt, das jetzt zum Schwannberger-Haus \r\n        geh&ouml;rt, wo jetzt das B&uuml;ro drin ist, da hat er gewohnt. Seine \r\n        Frau war schon lange gestorben. Die Haush&auml;lterin war die Rosa Beischmied. \r\n        H&ouml;gn hatte zwei Kinder. Die &auml;lter Tochter Liese (Frieda). Ich \r\n        hatte sie pers&ouml;nlich nicht gekannt, aber die Kinder von ihr sind \r\n        gekommen. Sie hatte zuerst den Kroi&szlig; Br&auml;u in der Bahnhofstra&szlig;e \r\n        in Deggendorf geheiratet, sp&auml;ter hat sie den Staatsanwalt Schlumprecht \r\n        geheiratet. Er ist sehr bekannt gewesen und war ein Einser-Jurist. Der \r\n        hat alles gekonnt. Er war Staatsanwalt in Deggendorf und dann ist er Wirtschaftminister \r\n        in Bayern geworden. Dann war er beim Gouvernement in Belgien in der Besatzungszeit \r\n        w&auml;hrend des Krieges. Und er war ganz nationalsozialistisch. Den habe \r\n        sie nat&uuml;rlich eingesperrt, weil er die meiste Zeit eine hohe Stelle \r\n        gehabt hat. Die Amerikaner haben ihn wieder einstellen m&uuml;ssen, weil \r\n        er alles gewusst hat. Sie haben ihn wieder angestellt als Minister. Und \r\n        dann sein Sohn August, der wird so um 1910 geboren worden sein. Der ist \r\n        nicht alt geworden. Er hat Schule gehalten (August H&ouml;gn geb. 1878) \r\n        und dann hat er am Sonntag georgelt, sehr gut nat&uuml;rlich. Wir haben \r\n        immer an hohen Feiertagen, Weihnachten, Ostern, Pfingsten, beim Nachspiel \r\n        nach der Kirche gewartet. Da hat er vom Bach die Toccata mit Fuge gespielt. \r\n        Bach hat ja mehrer Toccata geschrieben, die bessere, weltber&uuml;hmte \r\n        hat er gespielt, die die Orgel ben&uuml;tzt wie ein Orchester. Ich wei&szlig; \r\n        nicht genau ob Dur oder Moll? Wir haben immer gewartet nach der Kirche. \r\n        Nat&uuml;rlich klingt das in Ruhmannsfelden nicht so gut, wie auf der \r\n        Passauer Domorgel. [... vielen Kinder von Johann Sebastian Bach und Maria \r\n        Theresia ...] August H&ouml;gn ist zu mir ein paar Mal in die Praxis gekommen \r\n        und eines Tages kommt er und sagt: &quot;Jetzt habe ich eine Messe fertig \r\n        komponiert und da wir so gut befreundet sind, d&uuml;rfen Sie der Messe \r\n        einen Namen geben.&quot; &quot;Dann nennen wir sie zu Ehren des Hl. Josephs, \r\n        das ist mein Namenspatron, die Missa ad honorem Sancti Josephi.&quot;<br>\r\n        <br>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie wann das war?<br>\r\n        </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Dr. Josef Stern:</strong>&nbsp; Das kann man nicht \r\n        sagen. Er ist mehrmals gekommen. Er hatte dann einen Schlaganfall, konnte \r\n        dann die Klink verlassen, aber hatte dann eine halbseitige Teill&auml;hmung. \r\n        Er hatte den Fu&szlig; nachgezogen und den Arm hat er in der Schlinge \r\n        getragen. Dann ist er eines Tages gestorben, um 1960 muss das gewesen \r\n        sein, aber nicht in Ruhmannsfelden, sondern meines Wissens in Deggendorf.<br>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie, wann er den \r\n        Schlaganfall gehabt hat?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Dr. Josef Stern:</strong>&nbsp; Das kann man nicht \r\n        sagen. Kartei habe ich ja nicht gef&uuml;hrt, er war ja Privatpatient. \r\n        Zwei, drei, vier Jahre hat er noch gelebt nach dem Schlaganfall. Das ist \r\n        eigentlich alles, was ich wei&szlig;. [... Kirchenbauten, Gro&szlig;eltern \r\n        von Josef Friedrich, der verkaufte Steinway von Dr. Sterns Frau, Lieblingskomponisten, \r\n        Begegnung Dr. Sterns mit dem Pianisten Wilhelm Kempff, Fernsehsendung \r\n        mit Arthur Rubinstein, Schumanns Klavierkonzert...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/11/dr_josef_stern_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/11/dr_josef_stern_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/9/93/August_H%C3%B6gn_-_Interviews_11_Interview_mit_Dr._Josef_Stern%2C_21.2.2003.ogg', 12, 10),
(12, 'Interview mit Emilie Seidl, 23.4.2003', 'Emilie Seidl', '1924-06-05', '0000-00-00', '0000', 78, '1924', 'S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2003-04-23', '23.04.2003', 14, 19, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben im Kirchenchor \r\n        mitgesungen? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Frau Essigmann hat \r\n        Ihnen sicherlich mehr sagen k&ouml;nnen als ich es kann. Sie war ja ein \r\n        st&auml;ndige S&auml;ngerin und ich habe nur sonntags mitgesungen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Seit wann haben Sie im Kirchenchor \r\n        mitgesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Ich hatte beim damaligen \r\n        Chorregenten Schroll Klavierunterricht und er hat dann gesagt, dass ich \r\n        in den Kirchenchor gehen soll. Da war ich in der 5., 6. Klasse, dass muss \r\n        1935, 1936 gewesen sein. Ich bin dann zwei Jahre nach Straubing ins Internat \r\n        gekommen, habe aber nachher schon wieder mitgesungen. So genau kann ich \r\n        mich da gar nicht mehr erinnern.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat August H&ouml;gn auch \r\n        Klavierunterricht gegeben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Nein, glaube ich nicht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hatten sie August H&ouml;gn \r\n        als Lehrer?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Nein, er hat ein Stockwerk \r\n        oberhalb unterrichtet. Er hatte den Jahrgang 1923 oder 1922 unterrichtet. \r\n        Die Frau Essigmann m&uuml;sste bei ihm in der Schule gewesen sein. Haben \r\n        sie die Mathilde Beischmied schon gefragt? Deren Mutter war Haush&auml;lterin \r\n        beim H&ouml;gn und die Mathilde hat beim H&ouml;gn gewohnt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich an \r\n        Messen erinnern, die Sie w&auml;hrend Ihrer Zeit beim Kirchenchor gesungen \r\n        haben? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Ich wei&szlig; die \r\n        Namen nicht mehr, erkenne sie aber, wenn sie am Chor gesungen werden, \r\n        dann wei&szlig; ich, das habe ich auch schon gesungen. Da k&ouml;nnte \r\n        ich sozusagen mitsingen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat Sie der Herr H&ouml;gn \r\n        gefragt, da Sie ja Klavierspielen konnten, ob Sie auch Orgelspielen wollen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Nein, da war ich erst \r\n        um die 15 Jahre alt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann waren Chorproben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; An die kann mich fast \r\n        gar nicht mehr erinnern. Ich wei&szlig; nur, wenn ein gro&szlig;er Festtag \r\n        war, dass dann Orchester dabei war. Die Rauschers haben gespielt. Das \r\n        war schon sehr feierlich damals. Zwei Violinen, Cello oder Bassgeige. \r\n        Das wei&szlig; ich auch nicht mehr so genau. Dass ist ja doch schon gut \r\n        50 Jahre her. Als ich geheiratet habe 1952, hat am Polterabend der Kirchenchor \r\n        gesungen, aber schon unter Franz Danziger, der August H&ouml;gn war zwar \r\n        noch Kirchenchorleiter, aber Herr Danziger hat schon lange bei uns gewohnt \r\n        und der hat das veranlasst. Leider kann ich nicht mehr sagen. Die Beischmied \r\n        Mathilde m&uuml;sste da mehr wissen. Ich kenne sie gut, da die Beischmied \r\n        fr&uuml;her im Leitner Haus gewohnt haben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie mir die \r\n        damaligen Chors&auml;ngerinnen nennen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Emilie Seidl:</strong>&nbsp; Frau Grassl, die Koadmaih \r\n        Hedi, Zellern Hewig, Raster Res, sie hat ja die Stellung lange gehalten. \r\n        Frau Stern, Frau Hellauer. Wir waren Anh&auml;ngsel, die Frau Grassl war \r\n        die Hauptperson im Sopran und die Frau Glasschr&ouml;der. Ich war Sopran.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/12/frau_seidl_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/12/frau_seidl_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/6/6d/August_H%C3%B6gn_-_Interviews_12_Interview_mit_Emilie_Seidl%2C_23.4.2003.ogg', 13, 11),
(13, 'Interview mit Lorenz Schlagintweit, 29.11.2003', 'Lorenz Schlagintweit', '1917-07-31', '0000-00-00', '0000', 86, '1917', 'Posaunist unter August H&ouml;gn', 'Kaikenried', '2003-11-29', '29.11.2003', 25, 67, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann haben Sie den Lehrer \r\n        August H&ouml;gn kennen gelernt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Ich habe \r\n        in Ruhmannsfelden fr&uuml;her viel Kirchenmusik gemacht. An Festtagen \r\n        hat in Ruhmannsfelden immer ein Orchester gespielt. Der Apotheker Voit, \r\n        Kraus Ludwig und ich haben Geige gespielt und manchmal habe ich Trompete \r\n        gespielt, sp&auml;ter Posaune. Der H&ouml;gn hat immer Orgel gespielt. \r\n        Er hat so gespielt, dass einmal der Bischof Buchbinder (wohl Buchberger, \r\n        Vergleich mit Reicheneder-Chronik) gesagt hat, dass er selten einen so \r\n        guten Organisten geh&ouml;rt hat, das hat er ganz gut gemacht. In Ruhmannsfelden \r\n        hatten wir auch einen amtlichen Kirchenmusiker namens Schroll, ein ganz \r\n        guter, der hat ja Kirchenmusik studiert. Der Schroll hat dann die Kirchenmusik \r\n        aufgegeben, er hat ein M&auml;dchen kennen gelernt, mit der war es bald \r\n        aus und dann hat er eine neue kennen gelernt und dann war es aus mit der \r\n        Kirchenmusik. Er hat dann bei der Gemeinde gearbeitet als Gemeindesekret&auml;r. \r\n        Solange aber der Schroll da war, hat der H&ouml;gn Orgel gespielt und \r\n        der Schroll hat dirigiert. An Festtagen! Unter der Woche hat nat&uuml;rlich \r\n        der Schroll Orgel spielen m&uuml;ssen, denn der H&ouml;gn war ja ein Lehrer. \r\n        Der H&ouml;gn war ein ausgezeichneter Musiker, er hat so viele Lieder \r\n        geschrieben, f&uuml;r Beerdigungen und was man halt so braucht in der \r\n        Kirche. Er hat zu mir immer gesagt: &quot;Da gibt es nicht viele Lieder.&quot; \r\n        Die Chors&auml;ngerinnen waren Konditor Babett (Barbara Essigmann), Raster \r\n        Res und die Glasschr&ouml;der Mathild. Die hat singen k&ouml;nnen, wie \r\n        eine Operns&auml;ngerin mit voller und gro&szlig;er Stimme. Einmalig! \r\n        Der H&ouml;gn hat f&uuml;r die Mathilde Glasschr&ouml;der extra Lieder \r\n        geschrieben mit Sopran-Solo. Der H&ouml;gn war ein sehr guter Musiker. \r\n        Die Glasschr&ouml;der Mathild ist fr&uuml;h gestorben, ich glaube an Kehlkopfkrebs \r\n        (1965 an Leuk&auml;mie). Und auf die Jagd ist er auch noch gegangen. Da \r\n        kann ich mich daran erinnern, weil er einmal einen Rehbock heimgetragen \r\n        hat.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie in welchem Gebiet \r\n        H&ouml;gn auf die Jagd gegangen ist?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht. Es muss nicht weit weg gewesen sein, denn sonst h&auml;tte \r\n        er den Rehbock nicht zu Fu&szlig; heim tragen k&ouml;nnen. Er hat in der \r\n        N&auml;he vom Schwannberger gewohnt. Das wei&szlig; ich, weil wenn irgendetwas \r\n        besonderes war, dann haben wir und der Kirchenchor beim H&ouml;gn in der \r\n        Wohnung geprobt. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und Sie haben mit der Trompete \r\n        in H&ouml;gns Wohnung mitgeprobt.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Habe ich \r\n        auch. Trompete und dann Posaune. Der Kraus Wolfgang hat Streichbass gespielt. \r\n        Der Dr. Haug, ein Tierarzt, hat gut Cello gespielt. Wir hatten einen guten \r\n        Chor beisammen. Da haben die Leute gestaunt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie lange haben sie in Ruhmannsfelden \r\n        in der Kirchenmusik mitgespielt? Sie sind dann ja nach Kaikenried gekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Von 1935 \r\n        bis 1937 bin ich zum Arbeitsdienst &quot;Musikzug&quot; nach Deggendorf \r\n        gekommen. Dann bin ich nach Garmisch zum Musikkorps gekommen. Bei den \r\n        Ruhmannsfeldener habe ich vorher gespielt, aber auch wieder sp&auml;ter, \r\n        n&auml;mlich bei der Ruhmannsfeldener Blaskapelle. Die haben mehr aus \r\n        Zufall im Bayerischen Rundfunk spielen k&ouml;nnen, und da war ich dabei. \r\n        Ich war da fast unentbehrlich, weil ich damals professionell Musik gemacht \r\n        habe. Ich habe gespielt in Saarbr&uuml;cken, Hannover, Mannheim, Heidelberg. \r\n        Der Abteilungsleiter vom Bayerischen Rundfunk hat eine Blaskapelle gesucht, \r\n        die originale bayerische T&auml;nze wie Zwiefache spielen. Die Ruhmannsfeldener \r\n        haben es wahrscheinlich am besten gekonnt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp; Das war aber nach dem Krieg \r\n        schon? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Es war so: \r\n        Nach dem Krieg hat der Heinrich Wiggerl, die Kapelle neu gegr&uuml;ndet. \r\n        1948 bin ich aus dem Krieg heimgekommen und dann habe ich mitgespielt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Mitglieder aus der \r\n        ber&uuml;hmten &quot;Ruhmannsfeldener Blaskapelle&quot; unter Heinrich \r\n        Wiggerl auch bei der Kirchenmusik unter August H&ouml;gn mitgewirkt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Lorenz Schlagintweit:</strong>&nbsp; Ja nat&uuml;rlich! \r\n        Der Hacker, Heinrich Wiggerl, Kraus Wigg, Kraus Gang, der war aus Gotteszell, \r\n        haben mitgespielt. Ja der H&ouml;gn war ein sehr guter Musiker. Ein Buben \r\n        hat er gehabt, der Gustl, den habe ich gut gekannt.</p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="centeINSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (14, 'Interview mit Johann Freisinger, 29.12.2003', 'Johann Freisinger', '1927-03-27', '0000-00-00', '0000', 76, '1927', 'Sch&uuml;ler unter August H&ouml;gn, Nachfolger von H&ouml;gn als Gemeindeschreiber der Gemeinde Zachenberg und als Schriftf&uuml;hrer der Feuerwehr Ruhmannsfelden', 'Ruhmannsfelden', '2003-12-29', '29.12.2003', 22, 57, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben August H&ouml;gn \r\n        pers&ouml;nlich gut gekannt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Ja, ich bin \r\n        1940 bis 1941 bei ihm zur Schule gegangen und in meiner Dienstzeit bin \r\n        ich auch mit ihm in Kontakt gewesen. Er war ein teils strenger Lehrer, \r\n        aber wenn man in die 8. Klasse geht, nimmt man auf einen Lehrer keine \r\n        R&uuml;cksicht. Von unserer Nachfolgerklasse ist er ein wenig arg behandelt \r\n        worden. Ich wei&szlig; auch, dass er bei der Kirche als Organist, wenn \r\n        ein Beerdigung war oder sonst was, t&auml;tig war. Ich habe ihn auch gekannt \r\n        von meiner Dienstzeit her bei der Gemeinde Zachenberg ab 1947. Ich habe \r\n        auch von ihm Unterlagen bekommen f&uuml;r die Chronik von Zachenberg. \r\n        Ich wei&szlig; auch noch wie er 80 Jahre alt geworden ist, da war ich \r\n        dabei bei dieser Gratulation.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie davon n&auml;heres \r\n        erz&auml;hlen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Er damals in \r\n        dem Haus gewohnt, das jetzt zum Schwannberger geh&ouml;rt. Er hat parterre \r\n        gewohnt und oben der Herr H&auml;rtl, der war praktisch mein Vorg&auml;nger \r\n        bei der Gemeinde Zachenberg. Der B&uuml;rgermeister der Gemeinde Zachenberg \r\n        war damals der Ludwig Bielmeier. Dann waren einige Vereine vertreten, \r\n        bei denen er t&auml;tig war. Der Turnverein und die Feuerwehr. Bei der \r\n        Feuerwehr war er mein Vorg&auml;nger als Schriftf&uuml;hrer. Er war&acute;s \r\n        von 1910 bis 1950. Die Feuerwehr hatte nicht viele Schriftf&uuml;hrer. \r\n        Zuerst war&acute;s der Gr&uuml;nder der Feuerwehr und dann irgendein Hilfslehrer. \r\n        Ab 1910 war&acute;s der H&ouml;gn. Er ist 1910 nach Ruhmannsfelden gekommen. \r\n        Die J&auml;ger waren auch bei der Geburtstagsfeier und der B&uuml;rgermeister \r\n        von Ruhmannsfelden.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich habe den Zeitungsartikel \r\n        &uuml;ber seinen 80-igsten Geburtstag gelesen, und da steht geschrieben, \r\n        dass auch der Spielmannzug und der M&auml;nnerchor gespielt haben.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Wenn dann nur \r\n        der M&auml;nnerchor, weil damals gab&acute;s den Spielmannzug noch nicht. \r\n        Das wird die Blaskapelle Heinrich gewesen sein. Der M&auml;nnerchor wurde \r\n        von Franz Danziger geleitet. Ich habe ihn halt nur ein Jahr in der Schule \r\n        gehabt und da war er auch schon Rektor. Er ist auch nicht gew&ouml;hnlich \r\n        mit 65 Jahren in Pension gegangen, sondern viel sp&auml;ter. Er hat Dienst \r\n        gemacht bis 1945 und dann sind alle Lehrer entlassen worden, weil sie \r\n        mehr oder weniger bei der Partei waren. Er ist 1947 kurz wieder eingestellt \r\n        worden und dann ist er erst in Pension gegangen. Von der Schulzeit wei&szlig; \r\n        ich noch, wenn eine Beerdigung war, dann haben wir halt gefetzt. Da hat \r\n        keiner aufgepasst. Da gab es nicht so viele Lehrer. Oder die Ministranten \r\n        sind nach der Beerdigung zum Kegeln gegangen beim Vornehm und haben Kegeln \r\n        geschoben, bis der H&ouml;gn sie geholt hat. Das war an und f&uuml;r sich \r\n        eine recht lustige Zeit. Aber wie gesagt die 8-Kl&auml;sser hinter uns, \r\n        zu denen der Geiger Franz geh&ouml;rte, haben ihn ganz sch&ouml;n genervt. \r\n        Und dann hatte er die Tugend, einem seinen Sch&uuml;sselbund mit 20 Schl&uuml;sseln \r\n        auf den Kopf zu hauen. Die Beischmied Rosa war seine Haush&auml;lterin, \r\n        nachdem seine Frau so fr&uuml;h gestorben ist. Und dich wird wahrscheinlich \r\n        interessierten, ob noch irgendwelche Nachfahren da sind. Der H&ouml;gn \r\n        hatte zwei Kinder. Die Frieda und der Gustl. Der ist &uuml;brigens in \r\n        Ruhmannsfelden eingegraben worden. Vom Gustl sollten schon Kinder da sein. \r\n        Ein Kind ist, glaube ich, 1939 geboren. Der Gustl war auch die ganze Zeit \r\n        beim Milit&auml;r. Die Frieda hat einen gewissen Kroi&szlig;, Br&auml;umeister \r\n        aus Deggendorf, geheiratet. Der ist dann t&ouml;dlich verungl&uuml;ckt. \r\n        Beim H&ouml;gn hat dann auf jeden Fall die Enkel Tochter Inge gewohnt. \r\n        Sie ist ein Jahr &auml;lter als ich, also 1926 geboren. Mit dem Metzger \r\n        Josef Holler ist sie in die Schule gegangen. Das war die Kroi&szlig; Inge. \r\n        Sie k&ouml;nnte noch leben. Das ist die einzige, die ich kenne. Ob die \r\n        Frieda in der zweiten Ehe noch Kinder hatte, wei&szlig; ich nicht. Die \r\n        Frieda H&ouml;gn, vereheliche Kroi&szlig;, hat dann den Herrn Schlumprecht \r\n        geheiratet, B&uuml;rgermeister von Bayreuth und 1944/45 Minister von Bayern \r\n        f&uuml;r Wirtschaft in M&uuml;nchen. Er ist nach 1945 nat&uuml;rlich auch \r\n        ausgeschieden. Der Herr Schlumprecht hat dann der Inge auch seinen Namen \r\n        erteilt, weil man immer gesagt hat, &quot;die Schlumprecht Inge&quot;.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Welcher Jahrgang war die \r\n        Beischmied Rosa?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Von 1878 bis \r\n        1902.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich bin im Besitz der &quot;Geschichte \r\n        der Feuerwehr&quot; in einer Abschrift von Pfarrer Reicheneder. Besitzen \r\n        Sie die Originale?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Ja, und die \r\n        Originale von der &quot;Geschichte von Zachenberg&quot; habe ich auch.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Gib es ein Feuerwehrfoto, \r\n        auf dem H&ouml;gn zu sehen ist?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Ja, eins von \r\n        1950. Fotografieren hat er sich nicht recht lassen. Da gibt es wahrscheinlich \r\n        nicht viele. (Herr Freisinger betrachtet ein Foto von H&ouml;gn) Ja, das \r\n        ist ein echtes Foto! Wie er immer auf die Z&auml;hne gebissen hat. (Die \r\n        Schulchronik von Pfarrer Reicheneder lesend) Meiner Ansicht nach hat H&ouml;gn \r\n        hier auch mitgewirkt. Pfarrer Reicheneder hat halt das dann sch&ouml;n \r\n        zu Papier gebracht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Warum ist die Geschichte \r\n        von Zachenberg nicht gedruckt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Weil die Gemeinde \r\n        kein Geld hatte und auch kein Interesse an solchen kulturellen Sachen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie hat dann H&ouml;gn darauf \r\n        reagiert? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Er hat mir nach \r\n        seinem 80. Geburtstag die Geschichte von Zachenberg &uuml;bergeben. Da \r\n        haben mehrere Autoren mitgewirkt, so z. B. der Zollrat Trellinger, der \r\n        im Archivamt in Landshut t&auml;tig war. Die Feuerwehr Chronik hat er \r\n        mir schon eher vorbeigebracht.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie ein Schulfoto \r\n        von H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Die alten Jahrg&auml;nge \r\n        sind nicht mehr fotografiert worden. Ich habe auch nur eins, als ich die \r\n        Schule begonnen habe.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie Erinnerungen von \r\n        der Kirchenmusik damals?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Ich wei&szlig; \r\n        halt, dass er Organist war, dass er gesungen und gespielt hat, und wenn \r\n        eine Trauung war, hat er in der Schule gefehlt. Da ist es etwas drunter \r\n        und dr&uuml;ber gegangen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;H&ouml;gn hat auch heimatkundliche \r\n        Zeitungsartikel geschrieben? Haben Sie von denen welche?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Nein.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;August H&ouml;gn war doch \r\n        Gemeindeschreiber von Zachenberg?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Ja, von 1913 \r\n        bis ca. 1920. Sein Nachfolger H&auml;rtl war der erste Beamte, es war \r\n        fr&uuml;her &uuml;blich, dass B&uuml;rgermeister und Schullehrer f&uuml;r \r\n        die Schreibarbeiten eingesetzt wurden. H&ouml;gn hat im alten Rathaus \r\n        gewohnt, dass schon abgerissen wurde. 1834 erbaut. Da gab es nur 2 Schulr&auml;ume \r\n        und der H&ouml;gn hat hier oben gewohnt. Dann wurde 1911 das neue Schulhaus \r\n        gebaut, wo jetzt das Rathaus untergebracht ist. Hier hat der H&ouml;gn \r\n        dann in der Wohnung unterhalb der Wohnung, wo jetzt der Herrn Lankes lebt, \r\n        gewohnt. Oben waren einzelne Zimmer f&uuml;r Lehrer, z. B. der Lehrer \r\n        Ertl hat hier gewohnt. H&ouml;gn muss Parterre gewohnt haben, weil ich \r\n        einmal bei der Rosl unten gewesen bin und sein Rad geputzt habe. Vor der \r\n        Wohnung war ein gro&szlig;er Gang. Wir haben nicht das Rad geputzt, sondern \r\n        sind mit dem Rad im Gang gefahren. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich vielleicht \r\n        noch an andere Anekdoten erinnern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Eine Anekdote \r\n        mit dem Frisch Sepp wei&szlig; ich noch: W&auml;hrend des Krieges 1940/41 \r\n        haben wir Sch&uuml;ler Vitamin-Tabletten, die in einer gro&szlig;en Flasche \r\n        aufgewahrt waren, bekommen. Einer hat diese Flasche holen m&uuml;ssen \r\n        und H&ouml;gn hat sie dann an uns Sch&uuml;ler verteilt. Einmal hat er \r\n        den Dietrich Josef runtergeschickt: &quot;Hoffentlich kriegst du das Gesch&auml;ft \r\n        nicht. Du kannst ja nicht mal die Semmel ausrechnen!&quot; Solche Spr&uuml;che \r\n        hatte der H&ouml;gn drauf. Er hat eine Sch&uuml;ler gepackt und zur Tafel \r\n        geworfen, dass die Tafel auch noch umgefallen ist. Oder er hat eine Sch&uuml;ler \r\n        f&uuml;r die T&uuml;re hinausgeworfen, wenn sie ihn ge&auml;rgert haben. \r\n        So ein Egozentriker war er. Ja und jetzt kommt der nicht mehr mit den \r\n        Tabletten. Da sitzt er in dem Kammerl und frisst die Tabletten. Der H&ouml;gn \r\n        hat ihm dann einen Haufen hingesch&uuml;ttet: &quot;Friss, Dietrich, friss, \r\n        bis du tot wirst.&quot; Einmal war ein Ausflug zum Voglsang geplant, dann \r\n        hat ihn in der Fr&uuml;h wieder jemand ge&auml;rgert, dann ist das flach \r\n        gefallen. Und was ist dann passiert. Nach der 10 Uhr Pause sind einige \r\n        Sch&uuml;ler abgehauen und alleine zum Vogelsang gegangen. <br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie noch eine Anekdote?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Es gab auch \r\n        einen Schulgarten, wo jetzt Parkpl&auml;tze sind und Hunde hatte der H&ouml;gn \r\n        auch, weil er ein J&auml;ger war. Und da hat ihm angeblich der Fleischmann \r\n        Sepp aus Zachenberg in die Hundeh&uuml;tte geschissen und dann ist ihm \r\n        der Hund nicht mehr rein gegangen. Einmal hatten einige Sch&uuml;ler in \r\n        Schulgarten arbeiten m&uuml;ssen, unter ihnen der Stadler Toni, der ist \r\n        ihm auf dem Rhabarber gestiegen. Das hat der H&ouml;gn vom Fenster aus \r\n        beobachtet. Er hat vom Fensterbrett einen Blumentopf genommen und runter \r\n        geworfen. Er hat aber nicht den Stadler Toni getroffen, sondern eine Henne. \r\n        Das letzte Jahr war ein Fiasko. Wie schon gesagt: H&ouml;gn hat mir die \r\n        Geschichte bald gegeben, wie ich am 26. Dezember 1950 zum Schriftf&uuml;hrer \r\n        gew&auml;hlt wurde. Da hat H&ouml;gn aufgeh&ouml;rt. [... Gespr&auml;ch \r\n        &uuml;ber die bisher schon durchgef&uuml;hrten Interviews ...] Es ist \r\n        vorgekommen, dass H&ouml;gn Filmvorf&uuml;hrungen abgebrochen hat, wenn \r\n        irgendjemand Dummheiten gemacht hat. Der H&ouml;gn war schon ein Nervenb&uuml;ndel. \r\n        Er war ein guter Mensch. <br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie noch etwas &uuml;ber \r\n        die Nachfahren von August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Johann Freisinger:</strong>&nbsp; Wenn die Inge \r\n        nicht mehr lebt, dann gab&acute;s hier noch eine Tochter vom Sohn Gustl. \r\n        Sie muss 1939 in Ansbach geboren sein. Das wei&szlig; ich auch nur von \r\n        meinem Dienst bei der Gemeinde. Ich war in Deggendorf bei H&ouml;gns Beerdigung.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/14/johann_freisinger_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/14/johann_freisinger_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/0/0c/August_H%C3%B6gn_-_Interviews_14_Interview_mit_Johann_Freisinger%2C_29.12.2003.ogg', 15, 13),
(15, 'Interview mit Stephan Leitner, 19.2.2004', 'Stephan Leitner', '1932-06-15', '0000-00-00', '0000', 71, '1932', 'Sch&uuml;ler von August H&ouml;gn, H&ouml;gn schrieb ihm Briefe nach Australien', 'Ruhmannsfelden, Wohnort: Australien; Rowville', '2004-02-19', '19.02.2004', 8, 59, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n    <td><p><strong>Josef Friedrich: Du warst Sch&uuml;ler von August H&ouml;gn?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td><p><strong>Stefan Leitner:</strong> Es hat sich mit ihm eine pers&ouml;nliche \r\n        Freundschaft ergeben. Und da war ich nach dem Krieg &ouml;fter bei ihm \r\n        zu Hause und wir haben uns sehr angeregt unterhalten. Er war ja Rektor \r\n        in Ruhmannsfelden. Ich wei&szlig; aber pers&ouml;nlich nichts &uuml;ber \r\n        seine T&auml;tigkeit als Chorregent. Meine Mutter hat aber unter ihm im \r\n        Chor gesungen. Er hat mir von der Geschichte von Ruhmannsfelden erz&auml;hlt \r\n        und ich habe ihn viel fragen k&ouml;nnen. Er hat sich viel daf&uuml;r \r\n        interessiert. Deshalb sind wir gute Freunde geworden und er hat mir dann \r\n        auch nach Australien geschrieben. Ein oder zwei handschriftliche Briefe \r\n        von ihm werden schon noch da sein. Er hat sich dann auch an mich von der \r\n        Schule her erinnert. Da ich aber nur die ersten vier Jahre in Ruhmannsfelden \r\n        in die Schule gegangen bin, fehlt dann irgendwie der Anschluss. Und nachdem \r\n        der Krieg aus war, wars nur ganz kurz, und dann war halt schon Feierabend. \r\n        Und das war nicht der einzige Lehrer mit dem ich pers&ouml;nlich gut ausgekommen \r\n        bin, sondern auch mein Berufsschullehrer in Deggendorf, der Lehrer Schneebauer. \r\n        [... Stefan Leitners ehemaliger Berufsschullehrer Schneeberger...] <br>\r\n        <br>\r\n      </p>\r\n      \r\n    </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td><p><strong>Josef Friedrich: Kannst du irgendwelche Geschichten aus deiner \r\n        Schulzeit mit August H&ouml;gn erz&auml;hlen? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong>Da gabs keine Schw&auml;nke. Er war \r\n        ein guter Lehrer und ich habe nie eine Ohrfeige von ihm bekommen.<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td><p><strong>Josef Friedrich: Wie lange hast du August H&ouml;gn als Lehrer \r\n        in der Schule gehabt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong>Keine ganzes Jahr, denn er war ja \r\n        der Rektor. Er hat ja nur unterrichtet, wenn gerade kein Lehrer da war \r\n        oder wenn er die Schulpr&uuml;fung machen hat m&uuml;ssen.<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n    <td><p><strong>Josef Friedrich: In welchem Jahr hattest du dann den Lehrer \r\n        H&ouml;gn?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong> Das wird 1941 gewesen sein und f&uuml;r \r\n        eine ganz kurze Zeit 1945. Ich bin dann nach W&uuml;rttemberg gekommen \r\n        und bin dann jedes Weihnachten wieder nach Hause gekommen und habe dann \r\n        auch zwischendurch den Lehrer H&ouml;gn besucht. Ich wei&szlig; nicht \r\n        wieso. Es hat sich eine lose Freundschaft entwickelt. Er war ein guter \r\n        Gastgeber: &quot;Komm Stefan, setz dich her, wir reden ein bischen.&quot; \r\n        Dann haben wir noch ein bischen &uuml;ber Schulzeiten geredet. Ich wei&szlig;, \r\n        dass er mich gern gehabt hat und das kann ich auch hoffentlich noch schriftlich \r\n        beweisen, wie er mir eben nach Australien geschrieben hat.<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n    <td><p><strong>Josef Friedrich: Aber, das war ja schon kurz vor seinem Tod?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong>Ja, ich bin 1959 nach Australien ausgewandert \r\n        und wie ich wieder zur&uuml;ckgekommen bin, hat es keinen H&ouml;gn mehr \r\n        gegeben.<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n    <td><p><strong>Josef Friedrich: War es dir gekannt, dass August H&ouml;gn \r\n        auch komponiert hat?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong>Jetzt wo wir dar&uuml;ber geredet \r\n        haben, ist mir die Verbindung gekommen, dass er Chordirigent war und Orgel \r\n        gespielt hat. Meine Mutter hat ja im Kirchenchor gesungen und die ist \r\n        ja auch bei ihm in die Schule gegangen, wie viele andere Ruhmannsfeldener, \r\n        wenn es auch schon lange zur&uuml;ck liegt. Ich wei&szlig; schon, dass \r\n        er komponiert hat. Er hat mir mal sein Heftchen &uuml;ber die Geschichte \r\n        von Ruhmannsfelden in die Finger gedr&uuml;ckt und wir haben uns dann \r\n        unterhalten z. B. &uuml;ber die Ziegelfabrik im Sichetweg, wo meine Mutter \r\n        herstammt, und er hat &uuml;ber alles Bescheid gewusst.<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n    <td><p><strong>Josef Friedrich: Du bist Jahrgang 1932?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n    <td><p><strong>Stefan Leitner: </strong>Ja, ich bin 1932 geboren?<br>\r\n        <br>\r\n      </p>\r\n      </td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/15/stefan_leitner_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/15/stefan_leitner_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/9/93/August_H%C3%B6gn_-_Interviews_15_Interview_mit_Stephan_Leitner%2C_19.2.2004.ogg', 16, 14);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (16, 'Interview mit Maria Freisinger, 25.8.2004', 'Maria Freisinger', '1929-09-18', '0000-00-00', '0000', 74, '1929', 'Sch&uuml;ler und S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2004-08-25', '25.08.2004', 74, 76, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Woher kannten Sie August \r\n        H&ouml;gn? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; In den letzten \r\n        Kriegsjahren habe ich ihn in der Schule gehabt. Er w&auml;re eigentlich \r\n        schon pensioniert worden, aber wegen des Krieges hat er noch Schule halten \r\n        m&uuml;ssen. Als ich in der 7. oder 8. Klasse war, hatten die eine Klasse \r\n        am Vormittag 3 Stunden Unterricht und dann die andere Klasse am Nachmittag \r\n        3 Stunden, M&auml;dchen und Buben getrennt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und beide Klassen hatten \r\n        August H&ouml;gn als Klassenlehrer.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, er hat beide \r\n        gehabt. Viele junge Lehrer waren ja im Krieg. Nach dem Krieg hat August \r\n        H&ouml;gn dann aufgeh&ouml;rt. Frau Ascherl, Rosenbeck, Schneider und \r\n        Herr Ertl waren auch an der Schule. Er m&uuml;sste nicht einr&uuml;cken. \r\n        Die Klassen waren auch viel gr&ouml;&szlig;er als heute.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich daran \r\n        erinnern, dass August H&ouml;gn w&auml;hrend einer Beerdigung nicht in \r\n        der Schule anwesend war?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, es fanden \r\n        nicht nur Beerdigungen, sondern sehr viele Trauergottesdienste statt, \r\n        die waren alle auch am Vormittag. Vielleicht hat auch eine andere Lehrkraft \r\n        auf uns aufgepasst, das wei&szlig; ich nicht mehr so genau. Und am Ende \r\n        des Krieges hat H&ouml;gn nicht mehr Beerdigungen spielen d&uuml;rfen \r\n        und er wurde nicht mehr befreit von der Schule. Da ist dann eine Mallersdorfer \r\n        Schwester als Organistin gekommen. Ich kann mich auch erinnern, dass diese \r\n        Schwester dann auch Singstunden gehalten hat. Solange ich unterm H&ouml;gn \r\n        gesungen haben, ist vielleicht ein oder zwei Mal eine Singstunde gewesen. \r\n        Ich wei&szlig; nicht, ob sich dann die Haupts&auml;ngerinnen wie die Glasschr&ouml;der \r\n        Mathild, die Frau Essigmann oder die Raster Res getroffen haben zu einer \r\n        Chorprobe, wenn sie was neues gelernt haben. Bei einem St&uuml;ck, wo \r\n        der Sopran geteilt war, bin ich dann, die Frau Stern war auch dabei, zum \r\n        H&ouml;gn in die Wohnung zum Proben gekommen. Es gab keinen festen Termin \r\n        f&uuml;r die Proben und wahrscheinlich sind dann nur die zwei Haupts&auml;ngerinnen \r\n        gekommen. Vielleicht der Holzfurtner im Bass und hin und wieder der Schwannberger \r\n        mitgesungen. W&auml;hrend des Krieges gab&acute;s keine jungen M&auml;nner \r\n        im Bass. Ich bin zum Chor gekommen, weil der Pfarrer Bauer mich und meine \r\n        Freundin Schindelbeck Elfriede gefragt, ob wir nicht mitsingen, da die \r\n        Frau Essigmann weg hat m&uuml;ssen, sie ist aber dann doch da geblieben. \r\n        Ich bin dann im Chor geblieben, weil mit mir die Frau Brummer, sp&auml;ter \r\n        Frau Hellauer, sie hat immer Sonntags gesungen, in den Chor gegangen ist.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Obwohl Sie mitgesungen haben, \r\n        waren Sie nicht bei den Proben dabei?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Nein, da hat \r\n        uns keiner was gesagt. Ich habe jeden Sonntag mitgesungen, wie ich das \r\n        Pflichtjahr gemacht habe. Dann habe ich mal aufgeh&ouml;rt. Die Raster \r\n        Res hat die Noten ausgeteilt, einmal hat sie mir eine Stimme gegeben und \r\n        einmal nicht. Vielleicht sind Sie auch zu wenig gewesen. Von der Glasschr&ouml;der \r\n        hat auch die Glasschr&ouml;der Anne mitgesungen, sie war schon &auml;lter. \r\n        Sie hat gesagt: &quot;Komm, schau zu mir rein.&quot; Es gab Verschiedene, \r\n        die l&auml;nger in der Arbeit fort waren oder nur auf Urlaub da waren, \r\n        und dann mitgesungen haben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Es ist jede Woche ein Messe \r\n        aufgef&uuml;hrt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, jede Woche. \r\n        Sogar jeden Tag, aber halt nur mit drei Frauen. Ich habe dann &ouml;fter \r\n        mitgesungen, zusammen mit der Frau Stern und der Frau Weghofer, wie eben \r\n        die Klosterschwester da war. Die hat dann &ouml;fter eine Probe gehalten \r\n        im Kindergarten. Am Donnerstag war immer ein Amt, wo wir auch mitgesungen \r\n        haben. Einsingen hat es da nie gegeben. Wenn es die anderen k&ouml;nnen, \r\n        kann man sich leicht einhalten. Aber man ist auch mal ausgetrickst worden, \r\n        wenn die andere h&ouml;ren wollten, was wir machen.<br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind dann jeden Sonntag \r\n        verschiedene Messen aufgef&uuml;hrt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, immer verschiedene. \r\n        Als junger Mensch lernt man das ja leicht. Und wenn man akzeptiert wird, \r\n        geht das schon. Aber manchmal hatte ich das Gef&uuml;hl, als ob es ihnen \r\n        nicht recht ist, wenn man mitsingt. Der H&ouml;gn hat da nie eingegriffen. \r\n        Wenn die Frau Triendl im Urlaub da war, dann hat die mitgesungen. Sie \r\n        war das ganze Jahr nicht da. Aber im Urlaub oder an Weihnachten hat sie \r\n        mitgesungen. Frau Seidl hat auch nur Sonntags gesungen. So wie die Plank \r\n        Marianne oder die Frau Grassl. Vielleicht war das beim H&ouml;gn anders, \r\n        als er noch j&uuml;nger war. Er war damals einfach ein alter Mann. Da \r\n        hat er sich nicht mehr mit Proben abge&auml;rgert.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;H&ouml;gn hat 1954 aufgeh&ouml;rt, \r\n        haben Sie was vom dem Chorregentenwechsel mitgekriegt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Nein, ich habe \r\n        schon vorher aufgeh&ouml;rt. 1953 ist Pfarrer Reicheneder nach Ruhmannsfelden \r\n        gekommen und er wollte es nicht mehr so, wie es beim Pfarrer Bauer war. \r\n        Pfarrer Reicheneder hat gesagt, was gesungen werden sollte. Beim Pfarrer \r\n        Bauer ist ja das Gloria und Credo nie ganz gesungen worden. Es ist immer \r\n        ein Teil ausgelassen und &uuml;bersprungen worden, auch an Ostern und \r\n        Weihnachten. An Weihnachten ist das &quot;Et incarnatus est&quot; immer \r\n        gesungen worden. An Ostern der Teil &quot;Crucifixus ...&quot;. Und dann \r\n        der Schluss. Bei Werktagsmessen ist einfach aufgeh&ouml;rt worden.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie bei Auff&uuml;hrungen \r\n        von August H&ouml;gns Kompositionen mitgewirkt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Nein, da kann \r\n        ich mich nicht erinnern. [... Zeitungsartikel &uuml;ber die Auff&uuml;hrung \r\n        der Josephs-Messe....] 1953 war er schon noch Chorregent, weil wir da \r\n        geheiratet haben und ich ihn ersucht habe, ob der Kirchenchor nicht bestimmte \r\n        Lieder singen k&ouml;nnte. Und das haben sie dann schon gemacht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Frau Essigmann hat gesagt, \r\n        dass August H&ouml;gn praktisch aus dem Kirchenchor &quot;rausgeekelt&quot; \r\n        wurde?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Sicher, da ist \r\n        was Wahres dran. Die meisten Chors&auml;nger haben dann aufgeh&ouml;rt, \r\n        wie die Frau Essigmann und die Glasschr&ouml;der Mathild, nur die Raster \r\n        Res nicht. Wie der H&ouml;gn aufgeh&ouml;rt hat, habe sie auch aufgeh&ouml;rt. \r\n        Die Frau Grassl hat dann wieder angefangen, wie der Herr Danziger dann \r\n        Nachfolger war. Der Kern des Chores hat aufgeh&ouml;rt. Der Herr Danziger \r\n        hat sich dann wieder Leute gesucht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Was hat denn dann der Pfarrer \r\n        Reicheneder ge&auml;ndert?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Er hat bestimmt \r\n        was gesungen werden soll. Es ist immer das gleiche Lied am Schluss gesungen \r\n        worden und Reicheneder wollte ein passendes Lied zu jedem Gottesdienst \r\n        haben. Ich kann mir schon vorstellen, dass sich der H&ouml;gn gedacht \r\n        hat: &quot;Jetzt bin ich so alt und habe so lange Orgel gespielt und jetzt \r\n        soll ich auf einmal so und so spielen.&quot; Ich glaube nicht, dass der \r\n        Pfarrer Reicheneder den H&ouml;gn &quot;rausekeln&quot; wollte. Jeder \r\n        Pfarrer hat seinen eigenen Stil.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie sind die Singstunden \r\n        zur Schulzeit abgelaufen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Wir haben nicht \r\n        viel Singen gehabt, bei 3 Stunden Schulzeit am Tag. Wir haben haupts&auml;chlich \r\n        in der Fr&uuml;h gesungen, anstatt des Betens. Das Beten war ja verboten \r\n        und es ist dann ein Lied gesungen worden. Er hat uns dann oft die Liederb&uuml;cher \r\n        einfach hingeworfen und wir haben uns selber was aussuchen k&ouml;nnen. \r\n        Ein Lied haben wir dann gesungen. Wir hatten ja nur 3 Stunden Schule von \r\n        8 bis 11 Uhr. Normalerweise ist dann eine Stunde Pause gewesen f&uuml;r \r\n        den Lehrer. Um 12 bis um 3 Uhr war dann die n&auml;chste Klasse dran. \r\n        Aber er, der H&ouml;gn, hat die Pause ausgelassen, die andere Klasse kam \r\n        auch um 11 Uhr. Zum Mittagessen ist er aber trotzdem gegangen. Wir haben \r\n        entweder gesungen oder er hat uns eine Aufgabe zu rechnen gegeben, w&auml;hrend \r\n        er nach unten in seine Wohnung zu essen gegangen ist und dann ist er wieder \r\n        gekommen. Da ist nat&uuml;rlich, wenn wir mit Buben in einer Klasse waren, \r\n        ein Unfug passiert. Er hat dann fr&uuml;her zur Jagd gehen k&ouml;nnen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben mal erz&auml;hlt, \r\n        dass H&ouml;gn beim Schulsingen speziell f&uuml;r sie eine Stimme dazu \r\n        geschrieben hat?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, das hat er \r\n        gemacht. Wir haben irgendein Lied gesungen und die Schindlbeck Elfriede \r\n        hat dann ganz spontan dazu geterzt. Am n&auml;chsten Tag hat uns H&ouml;gn \r\n        ein Blatt mit einer ausgeschrieben 2. Stimme gegeben. Ich wei&szlig; nicht, \r\n        ob wir die 2. Stimme dann ge&uuml;bt haben. Wir haben uns halt gefragt: \r\n        &quot;Wie sollen wir den das singen?&quot; Die Buben haben ja so wie so \r\n        nicht mitgesungen. Wir M&auml;dchen haben halt Volkslieder gesungen, die \r\n        wir singen konnten. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind die Lieder begleitet \r\n        worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Nein, es ist \r\n        nur gesungen worden. Ich wei&szlig; auch nicht mehr genau, ob er &uuml;berhaupt \r\n        mitgesungen hat. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben das Chorsingen \r\n        wieder unterm Herrn Danziger begonnen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja. Herr Danziger \r\n        hatte auch einen weltlichen Chor, in dem Fl&uuml;chtlingsfrauen und evangelische \r\n        Frauen mitgesungen haben. Gleich ist die Frl. Maria nicht gekommen und \r\n        hat Orgel gespielt, denn der Herr Danziger war sich hier nicht so sicher, \r\n        wie der H&ouml;gn. August H&ouml;gn hat Orgel gespielt, mitgesungen und \r\n        auch manchmal den Takt gegeben. Er war ein guter Organist.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Er hat also nicht allein \r\n        den Chor dirigiert und jemand anderer hat Orgel gespielt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Das hat er &uuml;berhaupt \r\n        nicht gemocht. Dirigenten hat er &uuml;berhaupt nicht gemocht. Eine Zeit \r\n        lang hat ein Fl&uuml;chtlingsehepaar aus Zachenberg im Chor mitgesungen. \r\n        Der Mann war ein recht ein guter Musiker und hat eine Messe, die &uuml;berwiegend \r\n        im a capella Stil war, komponiert und August H&ouml;gn sogar gewidmet. \r\n        Dieser Mann hat dann die Messe selber dirigiert und mit uns ein studiert. \r\n        Diese Messe haben wir nur einmal gesungen. Die Kellermeier Rita, die jetzt \r\n        Ernst hei&szlig;t hat auch mitgesungen und hat diesen Mann mit seine Frau \r\n        zu Chor gebracht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Herr Danziger hat eine Organisten \r\n        gehabt? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Die Frau Reisinger \r\n        Maria, die im Pfarrhof mitgearbeitet hat, hat Orgel gespielt. Herr Danziger \r\n        war der Chorregent und die Frl. Maria war die Organistin. Die Frl. Maria, \r\n        wie man einfach gesagt hat, war solange da, bis der Herr Pfarrer Krottenthaler \r\n        gekommen ist.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Herr Danziger hat dann von \r\n        1954 bis 1974 den Chor geleitet?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, auch nicht \r\n        immer. Er hat dann eine Zeit lang aufgeh&ouml;rt. Da hat uns dann der \r\n        Geiger Karl dirigiert. Er hat aber gesagt, Probe h&auml;lt er nicht, er \r\n        hat uns nur dirigiert. Das war zurzeit als der Kooperator Hirmer da war. \r\n        Wir haben mit dem Hirmer geprobt. Das war 1955 bis 1957. Der Herr Danziger \r\n        hat sp&auml;ter aber wieder angefangen. Ich wei&szlig; aber nicht, warum \r\n        er aufgeh&ouml;rt hat. Vielleicht hat er sich ge&auml;rgert. Auf einmal \r\n        war er wieder da.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War Herr Danziger Chorleiter \r\n        bis 1974, als Pfarrer Krottentaler gekommen ist? Er hatte ja denn Kirchenchor \r\n        selber geleitet.</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja wahrscheinlich. \r\n        Das ging, weil immer noch ein Kaplan da war. Der Kaplan hat den Hauptgottesdienst \r\n        gehalten, und Herr Krottenthaler hat den Chor geleitet. Zu der Zeit ist \r\n        dann auch Herr Lankes dazu gekommen und hat dann Orgel gespielt.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie, ob Messen von \r\n        H&ouml;gn von seinen Nachfolgern aufgef&uuml;hrt wurden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Das kann schon \r\n        sein. Die Laurentius-Messe haben wir bestimmt gesungen, denn diese hat \r\n        Herr Danziger zu einer deutschen Version umgearbeitet. [... alte deutsche \r\n        Schreibschrift ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Welcher Gesinnung war H&ouml;gn \r\n        zur Nazi-Zeit?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ein Nazi war \r\n        er nicht. Ich war zwar damals noch ein Kind, aber ich kann mir nicht vorstellen, \r\n        dass H&ouml;gn ein Nazi war.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War unter dem Lehrern Nazis?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Was hei&szlig;t \r\n        Nazis? Die Frau Schneider und Ascherl war halt bei der Frauenschaft. Wenn \r\n        dann war die Frau Werner eine Nazi. Die Lang Lola hat man sie genannt. \r\n        Als ich mein Pflichtjahr im Kindergarten gemacht habe, musste ich auch \r\n        in eine Art Berufsschule gehen, fr&uuml;her hat es Sonntagsschule gehei&szlig;en. \r\n        Im Winter des letzten Kriegsjahres war es sehr kalt und in den Schulr&auml;umen \r\n        war nicht eingeheizt und wir sind dann in den Klassenzimmern mit M&auml;ntel \r\n        gesessen. Einige von uns haben dann gejammert: &quot;Es ist so kalt! Wir \r\n        k&ouml;nnen nicht schreiben.&quot; Die Frau Werner hat dann gesagt: &quot;Jetzt \r\n        habt euch nicht so. In der Kirche ist auch nicht eingeheizt und ihr jammert \r\n        da auch nicht.&quot; Die Frau Schneider war vielleicht Schriftf&uuml;hrerin \r\n        bei der Frauenschaft. Wenn dann war die Frau Werner eine Nazi. In der \r\n        Schule hatte ich die Frau Schneider nicht. Sie muss eine strenge Lehrerin \r\n        gewesen sein. Ich hatte die Frau Rosenbeck und dann den Lehrer Hierl und \r\n        dann seine Frau. Die Frau Hierl hat aber nur im Krieg Unterricht gehalten, \r\n        weil die Lehrer einfach zu wenig waren. Die Frau Ascherl war sicher kein \r\n        Nazi, weil sie auch im Kirchenchor mitgesungen hat. [... Kriegerdenkmal \r\n        ...] Man hat sich hier anpassen m&uuml;ssen. Auch im Kindergarten, als \r\n        in das Pflichtjahr gemacht habe, war ein Hitler-Bild an der Wand. Eine \r\n        Schwester, die aus Landshut gekommen ist, hat erz&auml;hlt, dass dort \r\n        jemand von der Partei kontrolliert hat. Das war bei uns am Land nat&uuml;rlich \r\n        nicht so h&auml;ufig der Fall.<br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie den Herrn Schroll \r\n        gekannt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Als ich in die \r\n        N&auml;hschule gegangen bin - die N&auml;hschule war immer nachmittags \r\n        nach der normalen Schule bei den Mallersdorfer Schwestern - haben wir \r\n        jedes Jahr Theater gespielt. 1939 haben wir das Singspiel &quot;Die G&auml;nse \r\n        Hirtin&quot; aufgef&uuml;hrt und da hat uns der Schroll am Klavier begleitet. \r\n        Bei der Probe war er noch da und dann ist er krank geworden. Und dann \r\n        hat uns der Lehrer Ertl begleitet. Fr&uuml;her hat ja jeder Lehrer ein \r\n        Instrument gespielt, dass war ja glaube ich sogar Pflicht. Es war Winter \r\n        und es hat gehei&szlig;en, dass er beim Skifahren war und da h&auml;tte \r\n        er sich erk&auml;ltet. Der Herr Schroll war verheiratet, ob er Kinder \r\n        hatte, wei&szlig; ich nicht. Er kommt vielleicht von der Kollnburger Seite \r\n        und er hat auf der Gemeinde gearbeitet. Mein Bruder Herrmann Mader, der \r\n        schon 1937 gestorben ist, hatte bei ihm Klavierunterricht. Mein Bruder \r\n        hat auch Trompete gespielt und hat bei der Kirchenmusik mit der Geige \r\n        mitgewirkt, wie noch mehr mit Orchester gemacht wurde.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Schroll war Kirchenchorleiter?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Ja, bis er gestorben \r\n        ist. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">47<a name="47"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie die Familie Rauscher \r\n        gekannt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">48<a name="48"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; Der Name sagt \r\n        mir schon was, aber vom Kirchenchor her kenne ich Sie nicht mehr. Ich \r\n        habe bei einem Rauscher das Geigenspiel gelernt. Ich wei&szlig; nicht \r\n        mehr, ob beim Siegfried oder beim Ludwig Rauscher. [... Geigenunterricht \r\n        ...] Die Familie f&uuml;hrte eine Konditorei und kleines Cafe. Nach Krieg \r\n        haben die beiden B&auml;lle gespielt mit Klavier und Geige.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">49<a name="49"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind w&auml;hrend der Kriegszeit \r\n        festliche Messen aufgef&uuml;hrt worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">50<a name="50"></a></td>\r\n	<td valign="top"><p><strong>Maria Freisinger:</strong>&nbsp; In der 8. Klasse \r\n        hat mich, glaube ich, der Pfarrer Bauer gefragt, ob ich zum Kirchenchor \r\n        gehe. Und von da an kann ich mich nicht mehr erinnern, dass eine festliche \r\n        Messe aufgef&uuml;hrt wurde. Erst sp&auml;ter beim Hirmer haben wir wieder \r\n        festlichere Messen aufgef&uuml;hrt. Obwohl, die Lehrerin Ascherl und der \r\n        Lehrer Martl haben mit Geigen mitgespielt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/16/maria_freisinger_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/16/maria_freisinger_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/9/95/August_H%C3%B6gn_-_Interviews_16_Interview_mit_Maria_Freisinger%2C_25.8.2004.ogg', 17, 15),
(17, 'Interview mit Max Holler, 26.8.2004', 'Max Holler', '1930-05-07', '0000-00-00', '0000', 74, '1930', 'Sch&uuml;ler unter August H&ouml;gn', 'Achslach', '2004-08-26', '26.08.2004', 28, 36, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich schreibe ein Art Biographie \r\n        &uuml;ber August H&ouml;gn und da wollte ich Sie fragen, was Sie &uuml;ber \r\n        ihn wissen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Ich bin bei ihm zwei \r\n        Jahre in die Schule gegangen. Vorher hatte ich den Lehrer Hierl. Das muss \r\n        1942 oder 1943 gewesen sein.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hatten Sie wegen des Krieges \r\n        auch schon einen verk&uuml;rzten Schultag?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Das war bei uns noch \r\n        nicht so. Wir hatten noch ganz normal Schule. Von der 1. bis zur 5. Klasse \r\n        hatten wir den Hierl und dann den H&ouml;gn. Der hatte zu tun mit uns, \r\n        denn wir waren ja im Lausbubenalter. Der H&ouml;gn hatte im Schulhaus \r\n        gewohnt. Ich wei&szlig; nicht mehr genau wie die Wohnung ausgeschaut hat. \r\n        Es gab einen langen Gang und in einem Zimmer waren die Lehrmittel untergebracht \r\n        und da waren auch die Vitamintabletten drin. Der H&ouml;gn hat damals \r\n        viel mit dem Fahrrad fahren m&uuml;ssen. Er ist in der Gegend um Giggenried \r\n        auf die Jagd gegangen. Heut hei&szlig;t das Jagdrevier &quot;Zachenberg \r\n        I.&quot; Da hat er einmal einen Rehbock geschossen. Als er mit dem Rehbock \r\n        am Rad nachts heimgefahren ist, hat es ihn beim Bahnhof geschmissen. <br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen etwas &uuml;ber H&ouml;gns \r\n        Mitgliedschaft beim J&auml;gerverein?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Das war der Jagdschutzverband. \r\n        Unterm Hitler ist das dann strenger organisiert geworden. 1933 ist das \r\n        Reichsjagdgesetz herausgekommen und G&ouml;ring war Reichj&auml;germeister. \r\n        Fr&uuml;her war die Jagd nicht so reglementiert, da gab&acute;s Wilderei \r\n        und Bauernj&auml;ger. Der Herr Schwannberger ist dann Kreisj&auml;germeister \r\n        geworden. Einmal haben sie einen Lux (Anmerkung von Wilhelm Ederer: Es \r\n        war ein Dachs) erschlagen, weil er einen Skifahren angegriffen haben soll \r\n        auf der Breitenau. Der Lux musste dann beim Schwannberger abgeliefert \r\n        werden und wir durften den Lux dann von der Schule aus ansehen. Wir sind \r\n        entweder mit dem Hierl oder dem H&ouml;gn runter gegangen und haben uns \r\n        den Lux angeschaut. Vom Brunner, den Mohrpetern Sepp her, der Berufsj&auml;ger \r\n        beim Schwannberger war, wei&szlig; ich viel von den alten Zeiten. Es hat \r\n        dann mit dem H&ouml;gn auch mal Schwierigkeiten gegeben mit dem Jagdschutzverband. \r\n        G&ouml;ring hat viele Vorschriften ausgegeben und die mussten auch bei \r\n        uns durchgef&uuml;hrt werden. Vor 1933 hat man noch mit Schrot schie&szlig;en \r\n        d&uuml;rfen, nach 1933 nur noch mit einem Kugelgewehr. Viel J&auml;ger \r\n        hatten noch gar kein Kugelgewehr. Teilweise ist auch der Abschuss gesperrt \r\n        worden, wenn sie kein passendes Gewehr hatten. Und es gab auch Pflichtschie&szlig;en \r\n        zu denen die J&auml;ger antreten mussten und da ist notiert worden, wer \r\n        ein Gewehr hat oder nicht und ob die Schie&szlig;leistung stimmt. H&ouml;gn \r\n        war selber kein Jagdp&auml;chter. Es muss ein P&auml;chter da sein, der \r\n        die Jagd pachtet, und wenn man den Jagdschein hat, dann darf man bei dem \r\n        mitgehen. H&ouml;gn hat schon alleine auf die Jagd gehen d&uuml;rfen, \r\n        aber hat die Erlaubnis haben m&uuml;ssen vom Posthalter Amberger.<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat man dann die geschossenen \r\n        Tiere zum Metzger gebracht?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Normalerweise hat man \r\n        die Tier aufgemacht und die Innereien raus genommen. Lunge und Leber hat \r\n        man mitgenommen und die anderen Ged&auml;rme wurden oberfl&auml;chlich \r\n        vergraben, damit F&uuml;chse angelockt wurden und man auf sie warten konnte. \r\n        In der N&auml;he vom Hochsitz hat man den Aufbruch eingegraben. Dann hat \r\n        der H&ouml;gn den Rehbock in den Rucksack hinein getan und dann hat es \r\n        ihn wahrscheinlich geworfen. Und der Zinke Alois und ich, wir waren vom \r\n        H&ouml;gn die Hausel, und mussten am n&auml;chsten Tag in der Fr&uuml;h, \r\n        das Fahrrad wieder herrichten. Dann haben wir wieder alles hingebogen \r\n        und die G&auml;nge verdunkelt, weil das Licht am Rad nicht mehr gegangen \r\n        ist. Wir sind dann mehr mit dem Rad am Gang gefahren, als dass wir es \r\n        gerichtet h&auml;tten. Bis der H&ouml;gn gekommen ist. Dann war es nat&uuml;rlich \r\n        wieder aus! Der Rehbock ist meistens fortgeschickt worden und dann haben \r\n        wir Sch&uuml;ler den Rehbock mit einem Handwagen zur Bahn bringen m&uuml;ssen. \r\n        Der Rehbock ist dann nach M&uuml;nchen geschickt worden und wurde dort \r\n        verkauft. Wer h&auml;tte sich bei uns ein Rehfleisch leisten k&ouml;nne? \r\n        [... Aufbruch des Rehbocks ...]<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie ist der Musikunterricht \r\n        beim H&ouml;gn abgelaufen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Es ist viel gesungen \r\n        worden. Ich hatte zu der Zeit eine verh&auml;ltnism&auml;&szlig;ig gute \r\n        Stimme. Auch beim Hierl ist viel gesungen worden. Es gab nat&uuml;rlich \r\n        welche, die mitsingen sollten, sie hatten aber kein Musikgeh&ouml;r und \r\n        auch keine Stimme dazu. Da ist der H&ouml;gn wild geworden. Er war zu \r\n        der Zeit auch schon &auml;lter und er war so wie so ein nerv&ouml;ser \r\n        Mensch. Und in der 6. und 7. Klasse ist nat&uuml;rlich jeden Tag gerauft \r\n        worden. [... ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie oft ist gesungen worden</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; In der Woche schon \r\n        zwei oder drei Mal. Es sind Lied vom 3. Reich, Hitler-Lieder und Lieder \r\n        von der Hitler-Jugend gesungen worden, aber doch mehr ganz normale Volkslieder.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist einstimmig oder mehrstimmig \r\n        gesungen worden?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong> Zweistimmig ist gesungen \r\n        worden.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Mir haben viele Leute gesagt, \r\n        die ich befragt habe, dass H&ouml;gn doch recht streng war?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Ja, es hat schon Tatzen \r\n        und Ohrfeigen gegeben, wenn es sein hat m&uuml;ssen. Er war halt schon \r\n        sehr nerv&ouml;s. Einmal haben wir Holzmachen m&uuml;ssen, ich und der \r\n        Alois, eigentlich h&auml;tten wir nur das Holz aufrichten m&uuml;ssen. \r\n        Aber da wir es von zu Hause gew&ouml;hnt waren und die Rosl auch ein Beil \r\n        stecken hat lassen, haben wir das Holz gespalten. Als der H&ouml;gn da \r\n        vom Fenster heraus geschaut hat, sagt er gesagt: &quot;Holler, um Gottes \r\n        Willen!&quot; Er hat gemeint, dass wir das Holzspalten nicht k&ouml;nnen, \r\n        aber wir haben es von zu Hause her beherrscht. Dann haben wir das Holz \r\n        wieder zur Rosl in die Wohnung hinaufgetragen. Wir waren froh, wenn wir \r\n        deswegen nicht in die Schule gehen mussten. Es war damals Gang und G&auml;be, \r\n        dass man zu kleineren Arbeiten herangezogen wurde. [... Abwesenheit der \r\n        Ministranten bei Beerdigungen ...] Zum Schwarz Sepp hat der H&ouml;gn \r\n        immer gesagt: &quot;Du Hundschuft, du dreckiger.&quot; Unterm Krieg gab&acute;s \r\n        Metallsammlungen, da wurden alte Haferl oder sonstige Metallteile gesammelt \r\n        und in der Schule abgegeben. Beim Stadler-Bauern hat der Schwarz Sepp \r\n        einen gro&szlig;en Kessel gesehen, den er sich nat&uuml;rlich gleich kassiert \r\n        hat. Der Stadler-Bauer hat das aber bemerkt und als der Schwarz Sepp zur \r\n        Schule gekommen ist, hat er nat&uuml;rlich Schwierigkeiten bekommen. Dann \r\n        hat ihn der H&ouml;gn dementsprechend rasiert. Wir sollten nur schauen, \r\n        ob was rum liegt, was nicht mehr gebraucht wird. Das Eisen ist im Krieg \r\n        als Rohmaterial verwendet worden.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie, ob H&ouml;gn \r\n        ein Nazi war?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Max Holler:</strong>&nbsp; Ein Nazi war er nicht. \r\n        Ich wei&szlig; aber nicht, ob man bei der Partei sein musste, dass man \r\n        &uuml;berhaupt jagen durfte. [... G&ouml;ring ...] Der Steinbauer Josef \r\n        hat vom Herr Schwannberger Akten aufgehoben, und da steht auch was vom \r\n        H&ouml;gn drin.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/17/max_holler_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/17/max_holler_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/b/b5/August_H%C3%B6gn_-_Interviews_17_Interview_mit_Max_Holler%2C_26.8.2004.ogg', 18, 16),
(18, 'Interview mit Centa Schwannberger, 14.9.2004', 'Centa Schwannberger', '1913-02-17', '2005-02-15', '15.2.2005', 91, '1913', 'Sch&uuml;ler und Nachbarin von August H&ouml;gn', 'Viechtach, Altenheim', '2004-09-14', '14.09.2004', 19, 38, '<table border="0" cellspacing="0" cellpadding="0" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Sie waren eine Nachbarin \r\n        von August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Wenn die Kinder \r\n        ihn recht ge&auml;rgert haben, hat er gesagt: "Du L&uuml;mmel, du \r\n        siebeneckiger, der Vater kommt schon. Dem sag ich?s dann. Dann bekommst \r\n        du Pr&uuml;gel!" Der H&ouml;gn, den haben sie immer ausgelacht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wann haben sie den H&ouml;gn \r\n        in der Schule gehabt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  In der 4. und \r\n        5. Klasse. Der ist ernst und b&ouml;se gewesen. Die Buben haben ihn immer \r\n        ausgelacht. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Hat er sich nicht durchsetzen \r\n        k&ouml;nnen? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Nein, absolut \r\n        nicht.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Haben Sie viel Musik gemacht \r\n        in der Schule?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Wei&szlig; \r\n        ich nicht mehr.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wie lange hat ihr Mann im \r\n        Kirchenchor gesungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  20 Jahre schon. \r\n        Die Leute haben immer gesagt: "Schwannberger, gehst du wieder in \r\n        die Fr&uuml;hmesse, dass du wieder singen kannst." Er hatte eine \r\n        gro&szlig;e, raue Stimme. Er und der Dr. Franzl (Franz Danziger) haben \r\n        jeden Sonntag in der Fr&uuml;h gesungen, auch bei den Roraten. So sch&ouml;ne \r\n        Lieder haben sie gesungen, dass man stundenlang zuh&ouml;ren h&auml;tte \r\n        k&ouml;nnen. Der Danziger Franz, die Raster Res und die Koadm&uuml;hl \r\n        Hedi haben mitgesungen im Chor.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: War der August H&ouml;gn \r\n        und ihr Mann befreundet?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Ja, sie sind \r\n        sogar miteinander auf die Jagd gegangen. Der H&ouml;gn war nicht so scharf \r\n        auf?s Schie&szlig;en, dem haben die Rehe erbarmt, wenn er sie erschie&szlig;en \r\n        hat m&uuml;ssen. Ihm hat mehr das Drumherum gefallen. [... J&auml;gerei \r\n        ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wie war die Nachbarschaft \r\n        mit H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Hat nichts \r\n        gefehlt. Mal waren wir gut und mal waren wir zerstritten. Mit dem H&auml;rtl \r\n        hat es immer was gegeben. Ich wei&szlig; nicht, ob der Chef (Rudolf Schwannberger) \r\n        dem H&auml;rtl eine Ohrfeige gegeben hat oder umgekehrt. Wie der H&ouml;gn \r\n        von der Schule weggekommen ist, hat er zum H&auml;rtl ziehen m&uuml;ssen. \r\n        Es war eine Art Lehrerhaus. Die Lehrerin Ascherl hat auch hier gewohnt.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wo hat der H&ouml;gn gewohnt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Unten. Ja, \r\n        die Buben sind schon Lackeln gewesen, dass er ihnen nicht Herr wurde. \r\n        Dann haben sie den Lehrer Martl eingesetzt, wenn die Buben zu frech waren. \r\n        Der ist b&ouml;se gewesen. Er hat im Schulhaus gewohnt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Ihr Mann war Vorstand von \r\n        J&auml;gerverband? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Er war Kreisj&auml;germeister. \r\n        Er war es schon 20 Jahre lang. Er war es schon unterm Hitler und danach \r\n        hat er einsitzen m&uuml;ssen in Dachau. Die Amis haben es dann umgedreht. \r\n        Ein Jahr hat er schon einsitzen m&uuml;ssen. Ein halbes Jahr mindestens. \r\n        Die Frau vom Komponisten Richard Strauss hat ihn rausgeholt. Die waren \r\n        bei uns da, weil sie verfolgt wurden, weil sie Juden waren. Jetzt ist \r\n        die ganze Familie in die Villa gezogen zu uns. Und auf einmal stand &uuml;ber \r\n        Nacht an der T&uuml;r dran: "Judenburg". Nat&uuml;rlich sind \r\n        sie geflohen und waren lange verschwunden. Das waren die Buben vom Komponisten \r\n        Strauss. Da gibt es eine Gedenktafel in Garmisch. Die Elli, die Frau vom \r\n        Xaver Strauss, war Halbj&uuml;din. Die Alice hat ein paar Buben, einer \r\n        ist Chefarzt im Garmischer Krankenhaus und einer ist der gr&ouml;&szlig;te \r\n        Sammler von Briefmarken. Den M&auml;dchen sind sie nachgerannt die Strauss \r\n        Buben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: War der Herr Schwannberger \r\n        nach dem Krieg auch noch Kreisj&auml;germeister?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Sie haben ihn \r\n        abgesetzt. Er war beim normalen J&auml;gerverein, aber ob er Vorstand \r\n        war, wei&szlig; ich nicht mehr. Da ist der Schwabe von M&uuml;nchen da \r\n        gewesen und der Rebron aus Bonn. Die haben hier jagen d&uuml;rfen, daf&uuml;r \r\n        dass Chef (Rudolf Schwannberger) wieder jagen durfte. Sie haben ein Reh \r\n        besser gesch&auml;tzt, als wie einen Hirschen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wie lange ist der August \r\n        H&ouml;gn auf die Jagd gegangen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Solange wie \r\n        der Chef schon auch. Die waren ja ziemlich gleich alt. Der Chef ist solange \r\n        auf die Jagd gegangen, bis er nicht mehr gehen konnte. Sie sind miteinander \r\n        auf die Jagd gegangen mit dem Mohrpetern, der Berufsj&auml;ger vom Chef. \r\n        Der hat drei Pr&uuml;fungen gemacht. Bei uns ist er durchgefallen. In \r\n        Wien ist er durchgekommen. Dann gab es wieder Treibjagden, der Folkholz \r\n        ist dabei gewesen, der Landrat von K&ouml;tzing. Die J&auml;ger haben \r\n        schon geweibert. Eifern hat man nicht d&uuml;rfen. Meistens am Samstagnachmittag \r\n        sind die J&auml;ger alle fort zum Rauhen Kulm, zur &Ouml;dwies. Dann hat \r\n        man die jungen M&auml;dchen einsperren m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n</tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Sagt ihnen der Name Max \r\n        Forstner was?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Er war F&ouml;rster. \r\n        Er war von Gotteszell. Der ist immer dabei gewesen. Er hat zwei, sehr \r\n        sch&ouml;ne T&ouml;chter gehabt, auf die sind die J&auml;ger los wie der \r\n        Teufel.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n</tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Haben Sie die Frau vom H&ouml;gn \r\n        gekannt? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Ja, sie ist \r\n        sehr fr&uuml;h gestorben. Sie ist ziemlich b&ouml;se gewesen. Sie hat \r\n        alles ziemlich genau wissen wollen, wo er hingeht und was er gemacht hat. \r\n        [... Erlebnisse der Frau Schwannberger von der Jagd ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n</tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: War ihr Mann ein geb&uuml;rtiger \r\n        Ruhmannsfeldener? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Centa Schwannberger:</strong>  Er ist nicht \r\n        raus gekommen. Die Firma gegr&uuml;ndet und da geblieben. Er ist in Ruhmannsfelden \r\n        geboren worden. Sein Vater war Schreiner. Gelernt hat er in Langenisarhofen. \r\n        Dann hat er selber Hochzeiten eingehandelt. Damals hatten sie ja kein \r\n        Geld. [... Firma Schwannberger ...]</p></td>\r\n</tr>\r\n\r\n</table>', '<p>\r\n<a href="interviews/18/frau_schwannberger_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/18/frau_schwannberger_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/2/25/August_H%C3%B6gn_-_Interviews_18_Interview_mit_Centa_Schwannberger%2C_14.9.2004.ogg', 19, 17);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (19, 'Interview mit Mathilde Beischmied, 14.9.2004', 'Mathilde Beischmied', '1923-01-01', '0000-00-00', '0000', 81, '1923', 'Tochter von August H&ouml;gns Haush&auml;lterin Rosa Beischmied, wohnte lange Zeit in H&ouml;gns Wohnung', 'Viechtach, Altenheim', '2004-09-14', '14.09.2004', 27, 38, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Haben Sie den August H&ouml;gn \r\n        in der Schule gehabt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; In der Schule \r\n        habe ich ihn nicht gehabt. Meine Mutter war Haush&auml;lterin bei ihm. \r\n        Ich war zuerst bei den Gro&szlig;eltern und wie ich in die Schule gekommen \r\n        bin, bin ich zu meiner Mutter zum H&ouml;gn gekommen. Da war ich dann \r\n        praktisch den ganzen Tag. Bei meinen Gro&szlig;eltern konnte ich nicht \r\n        so lange bleiben, die sind ja bald weggestorben. Dann bin ich so lange \r\n        beim H&ouml;gn gewesen, bis er gestorben ist. Aber wir haben blo&szlig; \r\n        nichts mehr gefunden, weil die Schlumprechtin alle Schreibsachen wegger&auml;umt \r\n        hat und auch den Fernseher. [... Unterhaltung &uuml;ber fr&uuml;here Begegnung \r\n        mit Josef Friedrich ...] Die Frieda hat zuerst den Kroi&szlig; Hans geheiratet \r\n        aus Deggendorf. Dann sich scheiden lassen und den Schlumprecht geheiratet. \r\n        Der ist etwas Besseres gewesen. Die Inge ist noch vom Hans. Von der habe \r\n        ich noch etwas geh&ouml;rt, aber jetzt auch nicht mehr. Vom Anderen hatte \r\n        die Frieda noch drei Kinder.<br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wo hat die Inge gewohnt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Die war immer \r\n        in Garmisch und hat mir P&auml;ckchen geschickt, aber ob sie noch da ist, \r\n        wei&szlig; ich nicht? Von der H&ouml;gnin haben wir &uuml;berhaupt nichts \r\n        geh&ouml;rt und der Gustl hat Schlosser gelernt und ist dann Matrose geworden. \r\n        Dem Gustl seine Frau hat f&uuml;nf Kinder gehabt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie sich gut verstanden \r\n        mit dem H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Immer gut. \r\n        Ich bin in die Familie aufgenommen worden und er hat nichts gemeint. Im \r\n        Gegenteil! Wenn wir am Abend ein Bier gehabt haben, hat er gesagt: &quot;Kriegt \r\n        sie kein Bier heute!&quot; Der H&ouml;gn ist wirklich gut gewesen. Wir \r\n        haben uns zusammengesetzt und Bier getrunken. Der H&ouml;gn hat jeden \r\n        Tag sein Bier getrunken. Ich habe das Bier zuerst im Ma&szlig;krug geholt \r\n        von der Post. Man hat drei Quartl verlangt, aber dann meistens eine ganze \r\n        Ma&szlig; gekommen. Als Kind hat man nat&uuml;rlich verstohlen ein bisschen \r\n        was probiert. Der H&ouml;gn ist aber so gewesen, dass er zu meiner Mutter \r\n        gesagt hat: &quot;Gib ihr halt ein Bier, schenke ihr auch ein Glas ein.&quot; \r\n        Ich war aber damals auf kein Bier aus. Ich war noch ganz klein.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie sich daran \r\n        erinnern, wie er komponiert oder Klavier gespielt hat?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Das wei&szlig; \r\n        ich schon noch. Der hat immer gespielt und zum Proben sind zu uns in die \r\n        Wohnung so viele S&auml;ngerin gekommen. Die Raster Res und die Mader \r\n        Maral (Maria Freisinger) wei&szlig; ich am besten. Sie haben im Wohnzimmer \r\n        geprobt und wir haben gehorcht, was sie gesungen haben. <br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat man sich nach der Probe \r\n        zusammengesetzt?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Nein, da ist \r\n        nichts gewesen. Sie sind dann gleich gegangen. So fr&uuml;h sind sie so \r\n        wie so nicht gekommen. Um neun Uhr waren sie ja noch da.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Was war den ihre Mutter \r\n        f&uuml;r ein Jahrgang?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht genau. Gestorben ist sie mit 76 Jahren. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist die Tochter vom H&ouml;gn \r\n        &ouml;fter zu Besuch gekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Wie Sie mit \r\n        dem Kroi&szlig; Hans verheiratet war ein paar Mal, dann nicht mehr so \r\n        oft, wie sie mit dem Schlumprecht verheiratet war. Von ihm hat sie drei \r\n        Kinder bekommen, zwei M&auml;dchen und einen Buben.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie gro&szlig; war die Wohnung \r\n        vom August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Gro&szlig;, \r\n        weil wir im Schulhaus drinnen gewohnt haben. (Frau Schwannberger: Der \r\n        H&ouml;gn hat zu den Buben gesagt: &quot;Geht zur Rosl, und tragt ihr \r\n        Holz hoch.&quot;) Die haben nat&uuml;rlich eine Gaudi gehabt mit dem Holztragen. \r\n        [... Kaffe im Altenheim ...] Und beim H&ouml;gn haben wir auch nicht immer \r\n        einen Bohnenkaffee gehabt, nur wenn Besuch da war, wenn die Schlumprechtin \r\n        oder der Gustl da war. Es kamen auch noch andere Bekannte, deren Name \r\n        mir nicht einf&auml;llt, es waren auch Verwandte, aber nicht die Verwandten \r\n        in Deggendorf, die waren auch &ouml;fter da und wir sind &ouml;fter raus \r\n        gefahren.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben Familienfeste \r\n        wie Weihnachten zu dritt mit dem H&ouml;gn gefeiert?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Ja, weil sonst \r\n        niemand da war. Der H&ouml;gn war f&uuml;r mich wie ein Vater. Das kann \r\n        ich schon sagen. Es hat ja lange gedauert, bis ich meinen richtigen Vater \r\n        kennen gelernt habe. Meine Tante Agnes ist mit mir durch Ruhmannsfelden \r\n        gegangen, da sagt Sie: &quot;Schau, da geht dein Vater.&quot; &quot;Wer \r\n        ist denn das!&quot; &quot;Ja, der Lippl Maler.&quot; Das h&auml;tte ich \r\n        sonst nie erfahren. Nicht von meiner Mutter und nicht von meinen anderen \r\n        Verwandten. Bei Auswei&szlig;en hat es mich schon gewundert, dass der \r\n        Lippl Maler immer mich gefragt hat, etwas zu holen, obwohl so viele andere \r\n        Leute da waren. Er wollte mich wahrscheinlich &ouml;fter sehen, weil er \r\n        mich sonst nicht zu Gesicht bekommt. Gut dass man dann einen Ersatzvater \r\n        gehabt hat. Der H&ouml;gn ist ja ein guter Mensch gewesen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie auch mal ein Geschenk \r\n        bekommen vom H&ouml;gn z. B. an Weihnachten?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Was halt meine \r\n        Mutter gekauft hat.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hatten Sie ein eigenes Zimmer \r\n        in der Wohnung vom H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Meine Mutter \r\n        hat eigens gehabt und ich auch. Der Gustl ist wieder in einem extra Zimmer \r\n        gewesen. Die drei haben zur Familie geh&ouml;rt.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Die Frau vom H&ouml;gn ist \r\n        ja recht fr&uuml;h gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Sie ist sehr \r\n        fr&uuml;h gestorben und deswegen ist meine Mutter zum H&ouml;gn gekommen \r\n        als Haush&auml;lterin.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist seine Frau gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Das wei&szlig; \r\n        ich nicht. Da bin ich noch zu klein gewesen. Die Inge hat praktisch der \r\n        H&ouml;gn als Gro&szlig;vater aufgezogen. Sie war bis zum Ende der Schulzeit \r\n        und dann noch drei Jahre f&uuml;r eine weitere Schulausbildung da und \r\n        dann ist sie weggekommen. Sie hat bei uns in der Wohnung gelebt und dann \r\n        ist sie nach Garmisch gekommen und hat geheiratet. Es war dann doch eine \r\n        recht gro&szlig;e Familie. Und mit der Inge habe ich einerseits gerauft, \r\n        dann haben wir uns wieder vertragen. Ich habe immer nachgeben m&uuml;ssen, \r\n        weil ich die &Auml;ltere bin. Um 2 Jahre bin ich &auml;lter. Ich bin ein \r\n        1923er Jahrgang und die Inge ist 1925 geboren. Die Schlumprechtin hat \r\n        sich scheiden lassen und dann den H&ouml;gn gefragt, ob er sie zu sich \r\n        nimmt. Und derweil sind Jahre daraus geworden. Die Inge ist 7 Jahre in \r\n        die Schule gegangen und 3 Jahre in eine andere Schule gegangen, aber die \r\n        ganze Zeit sind wir zusammen gewesen. Und wenn wir gerauft haben, dann \r\n        hat der Gustl immer zu mir gehalten, weil er die Inge nicht leiden konnte. \r\n        Er hat auf die Frieda immer geeifert. Er hat sagt, dass die Frieda die \r\n        Inge selber aufziehen h&auml;tte k&ouml;nnen. Warum muss sie beim Opa \r\n        sein? [... Grab von August H&ouml;gn jun. ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ihre Mutter hat den ganzen \r\n        Haushalt gef&uuml;hrt vom H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Sie hat alles \r\n        gemacht, gekocht und man hat selber noch gewaschen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie vielleicht noch \r\n        Bilder von August H&ouml;gn und ihrer Mutter?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Leider nicht \r\n        mehr. Unsere Wohnung in Gotteszell wurde leider aufgel&ouml;st. Da w&auml;ren \r\n        auch Bilder vom H&ouml;gn dabei gewesen. (Beim Anschauen von alten Fotos): \r\n        Bei der Jagd da war der alte V&ouml;lkl, der Schwannberger und der H&ouml;gn \r\n        dabei. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Gab es bei ihnen zu Hause \r\n        beim H&ouml;gn &ouml;fter Wildfleisch zu Essen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n	<td valign="top"><p><strong>Mathilde Beischmied:</strong>&nbsp; Nein, gab&acute;s \r\n        nicht. Aber einen Fuchspelz habe ich, der vom H&ouml;gn geschossen wurde.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/19/mathilde_beischmid2_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/19/mathilde_beischmid2_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/f/f6/August_H%C3%B6gn_-_Interviews_19_Interview_mit_Mathilde_Beischmied%2C_14.9.2004.ogg', 20, 18),
(20, 'Interview mit Gertraud von Molo, 23.11.2004', 'Gertraud von Molo', '1935-04-29', '0000-00-00', '0000', 69, '1935', 'Enkelin von August H&ouml;gn', 'M&uuml;nchen', '2004-11-23', '23.11.2004', 24, 75, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; In Deggendorf \r\n        wohnt ja noch die Tochter von der Cousine meiner Mutter, Eva Ertl. [... \r\n        von Molos ...]. Seine Frau ist ja sehr fr&uuml;h mit 39 Jahren gestorben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist die gestorben?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; An einen Gallendurchbruch. \r\n        Den hat man nicht erkannt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie alt war da Ihre Mutter?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Meine Mutter \r\n        wurde 14. August 1907 (1906) geboren. Einen Bruder hat sie noch gehabt \r\n        den Ludwig, Wiggerl. (wahrscheinlich verwechselt mit einem Bruder von \r\n        August H&ouml;gn, der Ludwig hie&szlig;. Der Bruder von Frieda H&ouml;gn \r\n        hie&szlig; August)<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Der wurde 1912 geboren. \r\n        Es gibt noch ein Grab von ihm in Ruhmannsfelden.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Es gab aber \r\n        zwei Ludwig: Der Cousine, unser Onkel Wigg, der ist in Deggendorf begraben. \r\n        Unser Opa (August H&ouml;gn) ist ja auch in Deggendorf beerdigt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Aber das Grab von August \r\n        H&ouml;gn wurde aufgel&ouml;st?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Genau, als unsere \r\n        Mutter gestorben ist, hat uns der Onkel Wigg gefragt, der das Grab neben \r\n        dran hatte und sich auch sehr gek&uuml;mmert hatte um das Grab vom Opa, \r\n        das war ein Wandgrab, mit einem Marterl und sch&ouml;nen Figuren, ob wir \r\n        das Grab nicht verkaufen w&uuml;rden, an ganz gute Bekannte von ihm, die \r\n        das Grab gerne kaufen m&ouml;chten. Dann habe ich gesagt, so lange es \r\n        noch l&auml;uft, sollen wir es lassen, und wenn es dann ausgelaufen ist, \r\n        dann k&ouml;nnen sie es haben. Der Grabstein ist so belassen worden, nur \r\n        die Inschrift wurde raus genommen. Den k&ouml;nnen sie noch anschauen, \r\n        wenn man rein geht an der rechten Mauer, nebendran ist das H&ouml;gn-Grab \r\n        vom Onkel Ludwig mit einer schwarzen Platte. Wie es jetzt ist, wei&szlig; \r\n        ich nicht, weil der Onkel Wigg auch schon l&auml;ngst gestorben ist. [... \r\n        Friedhof von Ruhmannsfelden. ...] <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Wir waren nach \r\n        dem Krieg ein paar Mal als Kinder in Ruhmannsfelden. Er hat uns &ouml;fter \r\n        auf die Jagd mitgenommen. Aber an unsere Gro&szlig;mutter kann ich mich \r\n        &uuml;berhaupt nicht erinnern. Die Rosl war ja dann seine Haush&auml;lterin \r\n        bis er gestorben ist. Sie hat ihn versorgt und sich um ihn gek&uuml;mmert.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist er eigentlich \r\n        gestorben? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; An Altersschw&auml;che. \r\n        Er war ja wirklich alt. Er hat halt auch seine normalen Gebrechen gehabt. \r\n        Einmal hat er einen leichten Schlaganfall gehabt, aber er war bis zuletzt \r\n        r&uuml;stig und zu Hause. Er war ja eine bisschen ein Eigenbr&ouml;tler. \r\n        Meine Mutter war auch sehr pingelig und sauber. Aber mein Opa war noch \r\n        schlimmer. Wenn wir gekommen sind, haben wir nicht sein Besteck nehmen \r\n        d&uuml;rfen, dass ist alles extra gegangen. Solange er gelebt hat, hat \r\n        er nie ein Brot mit Rinde gegessen, die hat er immer au&szlig;en rum abgeschnitten, \r\n        weil er gesagt hat: &quot;Das haben so viele Leute in der Hand gehabt, \r\n        das esse ich nicht.&quot; Und unsere Mutter war auch so empfindlich. Sie \r\n        ist sp&auml;ter nach Passau gekommen, zu den englischen Fr&auml;ulein \r\n        und sehr viel hat sich ihre Gro&szlig;mutter um sie gek&uuml;mmert, die \r\n        die Brauerein gehabt hat in Eberhardsreuth (Wallersdorf). Da war Sie immer \r\n        in den Ferien und hat viel Zeit dort verbracht. Und nach der Volksschule \r\n        ist sie nach Passau gekommen. Ihre Gro&szlig;mutter (Mutter) war die Emma \r\n        Gerstl aus Wallersdorf. Sie habe sie recht verw&ouml;hnt. Wo zu der Zeit \r\n        ihr Bruder war, das wei&szlig; ich nicht. [... Bild von Emma H&ouml;gn \r\n        in der Wohnung ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie alt war ihre Mutter, \r\n        als sie in die Schule nach Passau gekommen ist?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ich nehme an \r\n        mit 11 Jahren, da hat man Kochen und Sticken und die ganzen praktischen \r\n        Sachen gelernt. Sie war dann im Internat. 4 - 5 Jahre hat wahrscheinlich \r\n        die Ausbildung gedauert. Das war eine h&ouml;here T&ouml;chterschule. \r\n        Hat meine Mutter mit 21 oder 24 Jahren den Kroi&szlig; geheiratet? Er \r\n        stammte aus der Steigenberger-Sippe. Es ist nicht lange gut gegangen, \r\n        sie hat sich scheiden lassen und hat meinen Vater 1932 geheiratet. Mein \r\n        Vater hat Jura studiert und seine Referendarszeit in Deggendorf gemacht.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Dass ihre Mutter einen Bierbrauer \r\n        geheiratet hat, hat sie vielleicht von ihrer Gro&szlig;mutter?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, das kann \r\n        gut sein. Im Sommer sind wir in den Ferien nach Ruhmannsfelden gefahren. \r\n        Ein bis zwei Wochen lang. Oft ist unsere Hausfrau die Luise aus Mitterteich \r\n        mitgefahren und dort geblieben. Ich kann mich nur noch daran erinnern, \r\n        dass ich dort auf dem Land bei den Bauernkindern L&auml;use bekommen habe.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben Geschwister?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, eine Schwester \r\n        und mein Bruder ist leider schon gestorben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Und so viel ich wei&szlig;, \r\n        hatte ihre Mutter aus erster Ehe auch ein Kind?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, die Inge. \r\n        Ich wei&szlig; nicht, ob sie noch lebt. Sie lebt in Farchhand bei Garmisch \r\n        und hat auch das zweite Mal geheiratet. Sie war mit einem Herrn Klink \r\n        verheiratet. Den Namen des zweiten Mannes wei&szlig; ich nicht.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Man hat mir gesagt, dass \r\n        die Inge eine Zeit lang beim Opa gewohnt hat.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Nein, wie die \r\n        Amerikaner gekommen sind, mussten wir ja aus dem Haus raus. Wir haben \r\n        in Solln gewohnt. Dann hat uns der Bruder von meinem Vater eine Zeit lang \r\n        aufgenommen, aber das geht ja nicht immer mit vier Kindern. Die Inge war \r\n        ja die &auml;lteste. Die hat dann einen Job bei den Amerikanern bekommen. \r\n        Sie hat in M&uuml;nchen gewohnt. Eine Zeit lang war sie schon beim Opa, \r\n        aber sie hat nicht da bewohnt. <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Die Inge ist in der Familie \r\n        Schlumprecht aufgewachsen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, ganz normal. \r\n        Sie hat aber Kroi&szlig; gehei&szlig;en. [... Erbstreitigkeiten und das \r\n        Kroi&szlig;&acute;sche Erbe ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Ihre Mutter hat 1923 bei \r\n        einer Theaterauff&uuml;hrung mitgespielt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Meine Mutter \r\n        hat nat&uuml;rlich Singen lernen m&uuml;ssen. Das hat sie uns erz&auml;hlt, \r\n        dass sie sehr gerne S&auml;ngerin geworden w&auml;re. Und ihr Vater war \r\n        ja streng zu ihr. Sie hatte keine Privilegien gehabt in der Schule. Er \r\n        war ja Rektor und hat im Schulhaus gewohnt, wie es halt in einem so kleinen \r\n        Schulhaus ist. Da waren vielleicht in der ersten Klasse drei Kinder und \r\n        in der anderen f&uuml;nf. Im Ganzen war es eine kleine Schar. Aber er \r\n        war schon sehr beliebt. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie haben eine &auml;ltere \r\n        Schwester? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, sie wohnt \r\n        in Reutlingen. Sie wei&szlig; aber auch nicht mehr wie ich. Wir sind ja \r\n        immer gemeinsam hingefahren. Sp&auml;ter sind wir dann ab und zu hinunter \r\n        gefahren, z. B. an Allerheiligen ans Grab. Meine Mutter hatte dann nach \r\n        dem Tod von August H&ouml;gn nur noch Kontakt nach Deggendorf, wo die \r\n        H&ouml;gn-Verwandtschaft lebt. In Ruhmannsfelden gab es ein Lebensmittelgesch&auml;ft, \r\n        da hat es alles gegeben. Vom Waschpulver, Mottenpulver, Bonbons &uuml;ber \r\n        Linsen und Erbsen. Einfach alles. Beim Schwannberger. Da sind wir halt \r\n        so gerne hingegangen, da hat es dicke Bonbons gegeben, die waren in einem \r\n        Glas drin. Da war ich aber schon in der Schule. Und dann sp&auml;ter wie \r\n        unser Opa schon tot war und irgendeine Geburtstagsfeier war, meine Schwester \r\n        war auch dabei, dann sind wir aufs Grab gegangen und nach Ruhmannsfelden \r\n        gefahren zum Schwannberger. Dann haben wir einfach hinwollen, obwohl wir \r\n        schon erwachsen waren. Wer war jetzt der Herr Mitterwallner?<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Es gibt, glaube ich auch \r\n        einen Brief von August H&ouml;gn an den Herr Mitterwallner.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">&nbsp;</td>\r\n    <td valign="top"><p><strong>Wegen technischer Probleme wurden etwa 10 Minuten \r\n        des folgenden Interviews nicht aufgezeichnet. Es folgt eine sinngem&auml;&szlig;e \r\n        Wiedergabe des Interviewsabschnitts aus dem Ged&auml;chtnis, wenige Tage \r\n        nach dem Interview.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n    <td valign="top"><p>Frau von Molo betrachtet die Kopien der Handschriften \r\n        von der Josephs-Messe. Frieda Schlumprecht hat die Josephs-Messe zu besonderen \r\n        Anl&auml;ssen (z. B. Hochzeitstag oder Sterbetag) in der Wallfahrtskirche \r\n        in Alt&ouml;tting auff&uuml;hren lassen. Frau von Molo konnte sich an \r\n        keine Einzelheiten erinnern, sie glaubt aber, dass irgendwo in ihrer Wohnung \r\n        Noten von der Josephs-Messe zu finden sein m&uuml;ssten.<br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n    <td valign="top"><p><strong>Frage wie es zu dem Arrangement der 3 Tantum ergos \r\n        durch Kapellmeister Friedrich Rein, M&uuml;nchen gekommen ist:</strong><br>\r\n        Frau von Molo konnte sich sehr gut vorstellen, dass ihre Mutter, Frieda \r\n        Schlumprecht, das in die Wege geleitet hat. Die Familie Schlumprecht hatte \r\n        seit der Zeit, als Karl Schlumprecht Oberb&uuml;rgermeister von Bayreuth \r\n        war, beste Kontakte zur Familie Wagner gekn&uuml;pft. Sie sprach vom pers&ouml;nlichen \r\n        Kontakt zur Winifried Wagner. Diese Kontakte zu gro&szlig;en Pers&ouml;nlichkeiten \r\n        aus dem Musikleben haben sich in M&uuml;nchen weiter gepflegt. So hatte \r\n        Frau Molo selbst Karten f&uuml;r die Bayreuther Festspiele bekommen durch \r\n        Bekanntschaft mit Wolfgang Wagner. Frau Molo berichtet auch, dass man \r\n        ihr erz&auml;hlt hat, wie Franz Liszt (? vielleicht ein Nachfahre) bei \r\n        der Familie Schlumprecht zu Besuch war. Ob das Arrangement ein Geschenk \r\n        f&uuml;r einen bestimmten Anlass war (Geburtstag von August H&ouml;gn), \r\n        konnte nicht gekl&auml;rt werden.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">34<a name="34"></a></td>\r\n    <td valign="top"><p><strong>Zum Verh&auml;ltnis von August H&ouml;gn und Karl \r\n        Schlumprecht:</strong><br>\r\n        August H&ouml;gn und sein Schwiegersohn Karl Schlumprecht hatten ein gutes \r\n        Verh&auml;ltnis zueinander. August H&ouml;gn hat ihn sehr gesch&auml;tzt, \r\n        weil er eine steile Karriere gemacht hat.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">35<a name="35"></a></td>\r\n    <td valign="top"><p><strong>Verbleib des Nachlasses:</strong><br>\r\n        Bis auf ein paar Fotos ist Gertraud von Molo nicht im Besitz von Werken \r\n        August H&ouml;gns oder anderen Hinterlassenschaften (Tageb&uuml;cher, \r\n        Briefen). Auch glaubt sie, dass die anderen noch lebenden Enkel oder Enkelinnen \r\n        keine wesentlichen Bestandteile seines Nachlasses besitzen. Frieda Schlumprecht \r\n        hatte die Haush&auml;lterin von August H&ouml;gn beauftragt, sich um die \r\n        Wohnungsaufl&ouml;sung zu k&uuml;mmern.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">36<a name="36"></a></td>\r\n    <td valign="top"><p><strong>Rechte an Werken August H&ouml;gns:</strong><br>\r\n        Gertraud von Molo w&auml;re gerne bereit, Josef Friedrich das Nutzungsrecht \r\n        an den Werken von August H&ouml;gn zu &uuml;bertragen, da er sich mit \r\n        der Materie besch&auml;ftigt hat. Viel Geld sei mit den Werken nicht zu \r\n        verdienen, noch wolle sie es. Sie w&auml;re bereit, einen entsprechenden \r\n        Vertrag zu unterschrieben.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">37<a name="37"></a></td>\r\n    <td valign="top"><p><strong>Unterbringung von Wertgegenst&auml;nden der Schlumprechts \r\n        in Ruhmannsfelden</strong><br>\r\n        Nach Auskunft von Gertraud von Molo wurden am Ende des Krieges wertvolle \r\n        Gegenst&auml;nde aus dem Besitz der Schlumprechts in einem Stall in Ruhmannsfelden \r\n        gelagert, um sie zu sch&uuml;tzen.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">38<a name="38"></a></td>\r\n    <td valign="top"><p><strong>Aufenthalt mit August H&ouml;gn in einem Forsthaus</strong><br>\r\n        Die Schlumprecht-Kinder wurden w&auml;hrend des Krieges, als Bombengefahr \r\n        drohte, besonders an bestimmten Tagen (z. B. Hitlers Geburtstag), mehrere \r\n        Wochen in ein Forsthaus gebracht. August H&ouml;gn passte manchmal im \r\n        Forsthaus auf die Kinder auf. So ging er manchmal mit den Kindern spazieren. \r\n        (nach Aussagen der &auml;lteren Schwester von Frau von Molo Lilo Leuze \r\n        handelte es sich hier um den Gro&szlig;vater v&auml;terlicherseits, also \r\n        den Schlumprecht Opa.) Ansonsten war August H&ouml;gn aber sehr heimatverbunden \r\n        und reiste deswegen sehr ungern. Er fand immer Ausreden, wenn es darum \r\n        ging Einladungen, die mit Reisen verbunden waren, abzusagen.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td height="69" align="center" valign="top">39<a name="39"></a></td>\r\n    <td valign="top"> <p><strong>Verh&auml;ltnis zu dem Gro&szlig;vater August \r\n        H&ouml;gn</strong><br>\r\n        Das Verh&auml;ltnis der Enkelkinder zu ihren Gro&szlig;eltern war damals \r\n        doch recht anders geartet als das heutige Verh&auml;ltnis von Enkel zu \r\n        ihren Gro&szlig;eltern. Die Gro&szlig;eltern waren auch Respektpersonen. \r\n        August H&ouml;gn hatte den Schlumprecht-Kinder zwar vorgelesen und mit \r\n        ihnen musiziert, aber sie mussten auch auf gewisse Dinge R&uuml;cksicht \r\n        nehmen, so z. B. wenn er ein Mittagsschl&auml;fchen hielt.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">40<a name="40" id="40"></a></td>\r\n    <td valign="top"><p><strong>Verh&auml;ltnis der beiden Kinder von August H&ouml;gn</strong><br>\r\n        Frieda und August jun. H&ouml;gn hatten zeitlebens ein schlechtes Verh&auml;ltnis \r\n        zueinander. Gertraud von Molo hat deswegen auch keinen Kontakt zu ihren \r\n        Cousins und Cousinen. Die Wurzel dieses zerr&uuml;tteten Verh&auml;ltnisses \r\n        liegen wohl schon in der Kindheit der beiden. Frieda Schlumprecht erz&auml;hlte \r\n        ihren Kinder oft, wie unangenehm es ihr war, auf ihren 6 Jahre j&uuml;ngeren \r\n        Bruder Gustl aufzupassen, als sie etwa 10 Jahre alt war.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">41<a name="41" id="41"></a></td>\r\n    <td valign="top"><p><strong>Jagdhund von August H&ouml;gn</strong><br>\r\n        August H&ouml;gn hatte 2 Hunde, mit denen die Schlumprecht-Kinder sehr \r\n        gerne spielten.</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">42<a name="42" id="42"></a></td>\r\n    <td valign="top"><p><strong>Beerdigung von August H&ouml;gn</strong><br>\r\n        August H&ouml;gn wurde in Deggendorf eingegraben. Bei der Beerdigung war \r\n        es sehr kalt. Viele Vereine aus Ruhmannsfelden nahmen an der Beerdigung \r\n        teil und ehrten dem Toten mit Fahnen. Eine Bl&auml;sergruppe der Feuerwehr \r\n        Ruhmannsfelden spielte das Lied &quot;Wir hatten einen Kameraden.&quot;</p></td>\r\n  </tr>\r\n  <tr> \r\n    <td align="center" valign="top">&nbsp;</td>\r\n    <td valign="top"><p><strong>Ab hier setzt die Audio-Aufzeichnung des Interviews \r\n        wieder ein.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">43<a name="43" id="43"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ich glaube, \r\n        dass die Inge Kroi&szlig; vor ein paar Jahren gestorben ist, mein Bruder \r\n        hat da mal was gesagt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">44<a name="44" id="44"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Es ist schon interessant, \r\n        dass sie Kontakt mit den Wagners hatten, Die Musik von August H&ouml;gn \r\n        ist auch leicht von Wagner beeinflusst, aber nicht durch Wagner direkt, \r\n        sondern &uuml;ber die Komponisten Griesbacher.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">45<a name="45" id="45"></a></td>\r\n    <td valign="top"><p><strong>Gertraud von Molo:</strong>&nbsp; Ja, das kann \r\n        schon sein.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/20/gertraud_von_molo_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/20/gertraud_von_molo_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/4/46/August_H%C3%B6gn_-_Interviews_20_Interview_mit_Gertraud_von_Molo%2C_23.11.2004.ogg', 21, 19),
(21, 'Interview mit Lilo Leuze, 2.12.2004', 'Lilo Leuze', '1931-07-09', '0000-00-00', '0000', 73, '1931', 'Enkelin von August H&ouml;gn', 'Reutlingen (Telefon)', '2004-12-02', '02.12.2004', 18, 22, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben Sie an Ihren Gro&szlig;vater \r\n        August H&ouml;gn irgendwelche Erinnerungen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze: </strong>Kaum, ein bisschen was kann \r\n        ich Ihnen aber schon erz&auml;hlen. Er war leidenschaftlicher J&auml;ger, \r\n        er war an der Schule und ein ganz arg guter und r&uuml;hrender Gro&szlig;vater. \r\n        Seine Frau ist sehr bald gestorben und er hat nicht mehr geheiratet, weil \r\n        er &nbsp;so traurig war.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Einige Interviewpartner \r\n        haben mir erz&auml;hlt, dass die Frau von August H&ouml;gn, bevor Ihre \r\n        Mutter geboren wurde, Zwillinge bekam und beide sehr fr&uuml;h gestorben \r\n        sind?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Davon wei&szlig; ich \r\n        nichts. Meine Mutter hatte noch einen Bruder, der lange vor meiner Mutter \r\n        gestorben ist. [... Fotos von August H&ouml;gn ...]<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sind Sie im Besitz von Kompositionen \r\n        von August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Da habe ich bestimmt \r\n        nichts. Wenn dann hat meine Schwester welche, da Sie alles gesammelt hat. \r\n        Als Kinder durften wir immer in den Ferien bei ihm sein. Ich wei&szlig; \r\n        nur, dass er sehr gut Klavier gespielt hat. Es war die Kriegszeit und \r\n        man war froh, wenn man auf dem Land war. Er hat ein Enkelkind von mir \r\n        miterlebt. Bei zweiten war er schon gestorben.<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;An was ist er gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; An und f&uuml;r sich \r\n        rein an Altersschw&auml;che. Er war in dem Sinn nicht krank. Es ging auch \r\n        recht schnell.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;War er noch vorher im Krankenhaus?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Nein, ich glaub nicht. \r\n        Wie wir den Anruf von seiner Haush&auml;lterin bekommen haben, da war \r\n        er daheim. Wenn er im Krankenhaus gewesen w&auml;re, h&auml;tte man uns \r\n        sicher vorher angerufen, dass der Opa im Krankenhaus ist. Da k&ouml;nnte \r\n        ich fast drauf schw&ouml;ren.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Er hat einen Schlaganfall \r\n        gehabt? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Ja, das stimmt. Kurz \r\n        bevor er gestorben ist, hat er keinen Schlaganfall gehabt. Wie er beerdigt \r\n        wurde, war ein sehr eisiger Winter.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Kann das sein, dass er schon \r\n        1953 einen Schlaganfall erlitten hat?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Das kann gut m&ouml;glich \r\n        sein. Meine Tochter wurde 1955 geboren und die haben wir dabei gehabt \r\n        bei der Beerdigung.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie wissen auch nicht zuf&auml;llig \r\n        die Adressen von den Kindern von August H&ouml;gns Sohn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; H&ouml;ren Sie mal, \r\n        der hat ja gar keine Kinder gehabt? Wissen Sie, dass war in der Nachkriegeszeit, \r\n        er war in der Gefangenschaft, wir waren auch verschlagen. Wir haben aus \r\n        dem Haus raus m&uuml;ssen, weil die Amerikaner uns raus geschmissen haben. \r\n        Er hat uns noch einmal besucht in Gr&uuml;ndobl, da war eine Tante von \r\n        uns und da haben wir Unterschlupf gefunden.<br/>\r\n        <br/>\r\n      </p>\r\n      </td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Und da haben Sie ihn zum \r\n        letzten Mal gesehen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Nein, wir haben ihn \r\n        schon noch besucht, er musste ja meinen Mann kennenlernen. Er konnte nicht \r\n        zu Hochzeit kommen, weil es ihm zu anstrengend war.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ihre Schwester hat mir erz&auml;hlt, \r\n        dass Sie mit August H&ouml;gn w&auml;hrend des Krieges in einem Forsthaus \r\n        untergebracht waren. Ich wei&szlig; jetzt den Namen nicht mehr?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Dickelschwaig. Das \r\n        war der F&uuml;rther Gro&szlig;vater. Meine Schwester war damals noch \r\n        so klein, dass hat sie nicht mehr gewusst. [... Kriegsende ...] Das Gut \r\n        Gr&uuml;ndobl hatte die Schwester von meiner Gro&szlig;mutter, die Tante \r\n        Maria. Das war die Tante zu meiner Mutter, die war aber nie verheiratet. \r\n        Meine Gro&szlig;mutter war die Emma Gerstl. Bei der Tante Maria waren \r\n        wir oft als Kinder im Urlaub und in den Ferien. Gr&uuml;ndobl war ein \r\n        einziges Anwesen bei Wallersdorf. Das war ein riesiges Anwesen mit einer \r\n        kleinen Brauerei.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wissen Sie auch, dass August \r\n        H&ouml;gn ein Geschichtswerk geschrieben hat?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Nein, da hat er uns \r\n        auch nie was erz&auml;hlt. Beide Gro&szlig;eltern waren Lehrer, beide \r\n        waren Kantoren, beiden waren musikalisch und beide haben selber Musik \r\n        geschrieben. Mein Vater war in jungen Jahren Staatsanwalt in Deggendorf \r\n        und ich bin in Deggendorf geboren. [... Besuch in Deggendorf und Ruhmannsfelden \r\n        vor 2 Jahren ...] Er hat ja im Schulhaus unten drin gewohnt bis er pensioniert \r\n        wurde, dann ist er in Haus gegen&uuml;ber gezogen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Sie sind ja Erbin des Werks \r\n        von August H&ouml;gn? Ich wollte Sie um das Nutzungsrecht bitten?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Lilo Leuze:</strong>&nbsp; Ja, das d&uuml;rfen \r\n        Sie machen.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/21/lilo_leuze_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/21/lilo_leuze_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/2/29/August_H%C3%B6gn_-_Interviews_21_Interview_mit_Lilo_Leuze%2C_2.12.2004.ogg', 22, 20);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (22, 'Interview mit Wilhelm Ederer, 28.12.2004', 'Wilhelm Ederer', '1931-01-05', '0000-00-00', '0000', 73, '1931', 'Sch&uuml;ler unter August H&ouml;gn', 'Ruhmannsfelden', '2004-12-28', '28.12.2004', 4, 48, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;K&ouml;nnen Sie auf dem \r\n        Foto von der Auff&uuml;hrung des Singspiels &quot;Der Holledauer Fidel&quot; \r\n        jemanden erkennen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Da ist der Schneider \r\n        Michael Wurzer zu erkennen und da der Schlosser Josef Baumann. Und da \r\n        k&ouml;nnte bestimmt ein Rauscher darunter sein. Der Tierarzt Dr. Haug \r\n        hat auch am Chor oben gespielt. Der Haug hat bei der Sparkasse gewohnt. \r\n        Ganz oben war ein Tierarzt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;1957 hat doch der Reicheneder \r\n        die Glocken nicht mehr l&auml;uten lassen. Wissen Sie wie es dazu gekommen \r\n        ist? </strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Das ist nach dem \r\n        Fasching passiert, als sie am Aschermittwoch den Fasching eingegraben \r\n        haben. Da war haupts&auml;chlich der alte Stegm&uuml;hlner beteiligt, \r\n        der Vater vom Herbert. Der Kraus Max hat den Leichnam gemacht. Und wo \r\n        jetzt das Holler-Haus steht, war fr&uuml;her eine Grube drin und da haben \r\n        sie ihn eingegraben. Sogar in der Frankfurter oder in der S&uuml;ddeutschen \r\n        Zeitung ist das drin gestanden. &Uuml;berschrift: &quot;In Ruhmannsfelden \r\n        schweigen die Glocken.&quot;<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Was hatten Sie f&uuml;r \r\n        einen Eindruck vom Pfarrer Reicheneder?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Ein ganz korrekter \r\n        Pfarrer. Er war eigensinnig. Nach seinem Kopf hat es gehen m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie war das Verh&auml;ltnis \r\n        zwischen Schwannberger und Reicheneder? Der Reicheneder hat ja die Kapelle \r\n        vom Schwannberger nicht weihen lassen.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td valign="top"><p><strong>Wilhelm Ederer:</strong>&nbsp; Der Schwannberger \r\n        wollte sich da unten auch eingraben lassen. Da hat ihm der Reicheneder \r\n        von Haus aus einen Strich durch die Rechnung gemacht. Ich wei&szlig; das, \r\n        weil ich dem Schwannberger immer die Haare geschnitten habe. [... Entlassung \r\n        von H&ouml;gn als Chorregent ...] Da wollte der Reicheneder gewisse Leute \r\n        nicht mehr oben haben. Die Maria hat selber georgelt. Um das ist es gegangen, \r\n        dass er die einstellen kann. Mit dem Danziger Franz haben sie sich auch \r\n        nicht verstanden. Der Pfarrer Reicheneder hat es sich damals noch raush&auml;ngen \r\n        lassen: &quot;Ich bin der Pfarrer.&quot; Er hat sich auch mit dem Koadm&uuml;hlner, \r\n        mit dem B&uuml;rgermeister nicht verstanden. Der Reicheneder wollte damals \r\n        das Kriegerdenkmal haben, wie es heute ist, also bei der Kirche.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/22/wilhelm_ederer_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/22/wilhelm_ederer_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/3/37/August_H%C3%B6gn_-_Interviews_22_Interview_mit_Wilhelm_Ederer%2C_28.12.2004.ogg', 23, 21),
(23, 'Interview mit Josef Raster, 28.12.2004', 'Josef Raster', '1940-08-26', '0000-00-00', '0000', 64, '1940', 'Neffe von Theres Raster, S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2004-12-28', '28.12.2004', 5, 28, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Kennen Sie August H&ouml;gn?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Josef Raster: </strong>Ich wei&szlig; halt, wie er gespielt. \r\n        Als Kinder sind wir ja gerne auf den Chor gegangen. Bei der Roraten usw. \r\n        sind ja nicht viele Leute gewesen. "Singt ihr mit!", hat er gesagt. Wenn \r\n        man auf die Orgelempore rauf geht, sind rechts B&auml;nke. Ich bin ja lange \r\n        auf den Chor hinaufgegangen, bis zugesperrt wurde. Da war ich oft mit \r\n        dem Linsmeier Sepp oben. Das war zu meiner Schulzeit, da hat er noch oben \r\n        gespielt. Ich wei&szlig; nur dass der H&ouml;gn, von der Buchhandlung aus Deggendorf \r\n        abstammt.<br/>\r\n        <br/>\r\n      </p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Die Raster Res war ihre \r\n        Tante? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Josef Raster:</strong>  Ja.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: War sie beim H&ouml;gn in der \r\n        Schule?  </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Josef Raster: </strong>Wahrscheinlich, nehme ich \r\n        schon an.   <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Von verschieden Zeitgenossen \r\n        habe ich geh&ouml;rt, dass die Raster Res auf dem Chor recht dominant \r\n        war und praktisch die Chefin war?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Josef Raster:</strong> Nein, gewiss nicht. Ich \r\n        habe geh&ouml;rt, dass sie ein bisschen mitgejodelt hat. <br/>\r\n        <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich: Wer k&ouml;nnte noch was \r\n        wissen? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Josef Raster:</strong>  Der alte Petersamer, \r\n        der im Teisnacher Altenheim ist. Der F&auml;rber Alois. Der stammt von \r\n        der Ding ab und drau&szlig;en hat er das letzte Haus rechts. Vielleicht \r\n        auch sein Bruder vom F&auml;rber-Gesch&auml;ft.<br/>\r\n        <br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/23/josef_raster_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/23/josef_raster_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/f/fd/August_H%C3%B6gn_-_Interviews_23_Interview_mit_Josef_Raster%2C_28.12.2004.ogg', 24, 22),
(24, 'Interview mit Johann Glasschr&ouml;der, 28.12.2004', 'Johann Glasschr&ouml;der', '1936-08-14', '0000-00-00', '0000', 68, '1936', 'Bekannter von Mathilde Glasschr&ouml;der, S&auml;ngerin unter August H&ouml;gn', 'Ruhmannsfelden', '2004-12-28', '28.12.2004', 42, 127, '<p><strong>Bemerkung: </strong>Ein eigenes Kapitel der Geschichte von Ruhmannsfelden \r\n  w&auml;re die Erforschung des Orts zur Zeit des<strong> 3. Reiches. </strong>Mit \r\n  einer Biographie &uuml;ber August H&ouml;gn kann ich dieses Thema nur streifen. Nur junge \r\n  Leute k&ouml;nnen nach Meinung von Johann Glasschr&ouml;der unvoreingenommen an dieses \r\n  Thema herangehen. Es w&auml;re allemal ein sehr sinnvolles Thema f&uuml;r ein Facharbeit \r\n  oder Zulassungsarbeit! Einen ersch&uuml;tternden Fall aus unserer Gegend schilderte \r\n  Herr Glasschr&ouml;der in diesem Interview: Die <strong>Ermordung eines Polen bei \r\n  Kirchweg</strong> unter den Augen polnischen Zwangsarbeiter aus der Gegend. \r\n  Sein "Verbrechen": Er hatte mit einer Einheimischen ein Kind. Damit dieses schreckliche \r\n  Kapitel in unserer Ortsgeschichte wirklich Geschichte bleibt und sich nicht \r\n  nochmal wiederholt, ist mehr von N&ouml;ten als geschichtliche Fakten zu vertuschen \r\n  nach dem Motto: "Bei uns hat es so was nicht gegeben!" Ein Gedenkstein f&uuml;r den \r\n  ermordeten Polen w&auml;re ein kleiner Beitrag zur aktiven Auseinandersetzung mit \r\n  der NS-Geschichte am Ort und vielleicht ein kleiner Beitrag zum Erhalt unserer \r\n  freien Gesellschaftsordung.</p>\r\n<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ich habe ein Gem&auml;lde, \r\n        ein Kunstdruck mit sch&ouml;nem Rahmen, das die Glasschr&ouml;der Mathild von August \r\n        H&ouml;gn geerbt hat. Der Kirchenchor bestand zu seiner Zeit f&uuml;rs gew&ouml;hnliche \r\n        aus vier Personen. Rektor H&ouml;gn an der Orgel. Der hat Orgel gespielt und \r\n        gesungen. Glasschr&ouml;der Mathild, Sopran, Konditor Babett (Barbara Essigmann) \r\n        und Raster Res.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Sie haben gesungen \r\n        bis 1953, bis der Pfarrer Reicheneder gekommen ist und danach hat der \r\n        Rektor H&ouml;gn einen Schlaganfall gehabt. Das war nicht sch&ouml;n seiner Zeit. \r\n        Das war eine ganz schwierige Geschichte. Der Reicheneder hat ein M&uuml;ndel \r\n        gehabt und die hat Orgel gespielt. Die Maria Reisinger. Und die wollte \r\n        er unbedingt da herholen, damit sie auch etwas Geld verdient als Organistin. \r\n        Sie sind ja ein bisschen bezahlt worden. Und im Haushalt hat er sie auch \r\n        gebraucht. Und da der Reicheneder dauern an dem Chor was zu meckern gehabt. \r\n        Dann hat er die zwei Weiber (Glasschr&ouml;der Mathild und Barbara Essigmann) \r\n        so ver&auml;rgert und dem Rektor H&ouml;gn hat dann der Schlag getroffen. Dann hat \r\n        er so wie so nicht mehr spielen k&ouml;nnen, nur noch ein bisschen umherhumpeln. \r\n        Er hat aber nicht mehr singen und musizieren k&ouml;nnen. Dann hat der Reicheneder \r\n        die Maria her geholt: &quot;Er h&auml;tte schon eine.&quot; Dann sind sie ihm \r\n        draufgekommen, dass er auch nicht ehrlich gespielt hat. Dann haben sie \r\n        keinen Chor mehr gehabt. Wir hatten damals eine weltliche Chorgemeinschaft \r\n        die Liedertafel, wo ich auch mitgesungen habe und wir haben dann die Herz-Josefs-Messe \r\n        einstudiert und an Weihnachten gesungen. Der Albrecht und der Krieger \r\n        Edi waren dabei. Als &Uuml;berbr&uuml;ckung haben wir an Weihnachten gesungen. <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Die Maria hat dann \r\n        erst wieder einen Kirchenchor aufgebaut. Die Maria hat dann auch das Pfarrb&uuml;ro \r\n        gemacht hat. Sie hat zum Kirchenchor junge Leute z. B. aus der Landjugend \r\n        heraus, gebracht. Da war mein Frau (Frau Glasschr&ouml;der), Frau Fischer und \r\n        ihre Schwester darunter. Der Danziger hat die Alten gehabt. Da war die \r\n        Frau Freisinger, die Raster Res, die Frau Grassl dabei. Und einmal war \r\n        eine gro&szlig;e Auff&uuml;hrung, da haben wir alle zusammen singen m&uuml;ssen, weil \r\n        der Danziger mit seinen Raster Res und Konsorten nicht mehr viel anfangen \r\n        konnte.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Die Raster Res hat \r\n        sich nicht abwimmeln lassen. Wie hat der Bader Hans (Johann Ederer), der \r\n        Tierarzt gesagt? "Die Res wenn ich h&ouml;re, das ist genau so, wie wenn eine \r\n        Gei&szlig; auf eine Trommel schei&szlig;t, wenn sie singt." Eine alte Jungfer! Wenn \r\n        die Raster Res Bl&auml;tter verteilt hat, hat sie uns welche auf den Boden \r\n        geschmissen. Die Alten wollten die Jungen nicht dabei haben. Bis der Krottenthaler \r\n        gekommen ist. Und der hat dann einen sch&ouml;nen Chor gegr&uuml;ndet.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Anl&auml;sslich des 80. \r\n        Geburtstags von H&ouml;gn hat der M&auml;nnerchor von Ruhmannsfelden gesungen. Den \r\n        hat auch der Danziger geleitet. Der Bibi (Richard Bartascheck) hatte eine \r\n        gro&szlig;e Ansprache gehalten. Werde ich nie vergessen. Da hat er gesagt, \r\n        dass der H&ouml;gn "der Mozart von Ruhmannsfelden." Das war eine saubere Rede. \r\n        Reden hat er ja k&ouml;nnen der Bartascheck. Er m&ouml;chte sagen, H&ouml;gn ist &quot;der \r\n        Mozart von Ruhmannsfelden&quot; und das war er auch.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Der H&ouml;gn war ein \r\n        ganz ein strenger Lehrer. Aber gerecht, was ich von den Alten immer h&ouml;re. \r\n        Durch die Entnazifizierung hat er ewig keine Schule halten d&uuml;rfen nach \r\n        dem Krieg. Dann hat er ein Jahr Schule gehalten und dann ist er in Pension \r\n        gekommen. Ich habe ihn gern gemocht, weil er ein sehr korrekter Mensch \r\n        war. Sehr zur&uuml;ckgezogen, sehr freundlich. Unten hat der H&ouml;gn gewohnt und \r\n        oben der H&auml;rtl im H&auml;rtl-Haus, das jetzt dem Schwannberger geh&ouml;rt. Und \r\n        die zwei haben ein Postfach gehabt. Nach der Kirche hat sich der H&auml;rtl \r\n        gleich die Post geholt, dann haben sich die beiden beim Vornehm getroffen: \r\n        "Guten Morgen, Herr Rektor!" "Guten Morgen, Herr Inspektor!" Darauf hat \r\n        man immer warten k&ouml;nnen. <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Der H&auml;rtl war Gemeindesekret&auml;r \r\n        bei der Gemeinde Zachenberg. Er war kein Nazi, aber wenn die Leute nicht \r\n        "Heil Hitler!" gesagt haben, wenn sie zu ihm gekommen sind, hat er sie \r\n        wieder rausgeschickt und sie gefragt, ob sie nicht wissen, wie man gr&uuml;&szlig;t. \r\n        Ein war eben korrekt. Er hat niemanden was wollen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Und wie ist der H&ouml;gn \r\n        zu den Dingen gestanden im 3. Reich?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Nein, der hat nix, \r\n        und der wollte nix machen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Obwohl sein Schwiegersohn \r\n        so ein hoher Nazi war?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Aber der H&ouml;gn nicht. \r\n        So viel ich wei&szlig; hat er sich nirgends beteiligt. Das w&auml;re auch eine Aufgabe, \r\n        das 3. Reich in Ruhmannsfelden aufzuarbeiten. Im 3. Reich war es ja verboten, \r\n        das ein Pole ein Kind mit einer Deutschen hat. Da ist die Todesstrafe \r\n        drauf gestanden. Ein Pole, ich wei&szlig; nicht mehr von welchen Bauern, hat \r\n        mit einer Deutschen ein Kind gehabt. Dann haben sie ihr die Haare geschert \r\n        und durch den Regen gezogen: "Ich bin eine Polenhure" und er ist aufgeh&auml;ngt \r\n        worden. Dann haben die ganzen Polen vom ganzen Revier zusammenkommen und \r\n        zuschauen m&uuml;ssen. Beim Vornehm waren sechs oder sieben, jeder Bauer hat \r\n        Polen gehabt als Arbeiter. Und da ist bis heute noch kein Gedenkstein \r\n        da! F&uuml;r diesen unschuldigen Polen, der durch die Willk&uuml;r des NS-Regimes \r\n        ermordet wurde, w&auml;re es mehr als angebracht, einen Gedenkstein zu errichten. \r\n        Ich habe mich daf&uuml;r im Pfarrgemeinderat von Ruhmannsfelden eingesetzt, \r\n        aber leider ohne Erfolg. Vielleicht m&uuml;ssen junge Leute, die unvoreingenommen \r\n        sind, an diese Materie herantreten. Ich w&uuml;rde mich sehr gerne an der Errichtung \r\n        eines Gedenksteines finanziell beteiligen. Es waren auch einige Juden \r\n        da in Ruhmannsfelden: Dr. Grundner, der ist ausgewandert nach Peru, Dr. \r\n        Danziger, der hatte drei Kinder, eine Tochter, den Paul und den Franz, \r\n        die haben sie auch noch fort. Und wie sie den Dr. Grundner fort haben, \r\n        ist die Nachbarschaft drau&szlig;en gestanden und hat geklatscht. So astrein, \r\n        wie jetzt immer getan wird, war das Ruhmannsfelden nicht. Es gab viele \r\n        Mitl&auml;ufer. Der alte Zitzelsberger hat geweint, wenn eine Aufnahme war \r\n        (Hitler-Rede im Rundfunk). Der hat aber niemanden was wollen. Der alte \r\n        Baumeister war Ortsgruppenf&uuml;hrer. Der hat die Nachricht &uuml;berbringen m&uuml;ssen, \r\n        wenn jemand gefallen ist. Nach dem Krieg haben sie ihm nichts anh&auml;ngen \r\n        k&ouml;nnen. F&uuml;r dieses Thema w&auml;re der Josef Stenkala, jetzt ist er schon gestorben, \r\n        der richtige Mann gewesen. Der hat stundenlang erz&auml;hlen k&ouml;nnen. Es war \r\n        vorgeschrieben, wie viel die Polen zum essen bekommen. Die ganz alte Frau \r\n        Vornehm hat gesagt: "Wenn die Leute bei mir arbeiten, bekommen sie auch \r\n        anst&auml;ndig zu essen." Es gab einen Polizisten, eine Seele von einem Menschen, \r\n        nachher hat er noch beim B&auml;rnbr&auml;u gewohnt, dann ist er auf Achslach rauf \r\n        gezogen. Er hat gesagt, wenn er kontrolliert hat: "Habt&acute;s ja eh \r\n        nicht mehr zu essen, wie vorgeschrieben war." Obwohl die Sch&uuml;ssel voll \r\n        war. Und die Polen mussten um 9 Uhr im Bett sein. Er hat gesagt: "Wie \r\n        sie schnarchen." Obwohl keiner im Schlafzimmer war. Es gab auch andere. \r\n        Einer ist sogar am Ross mit der SA-Uniform umher geritten und hat die \r\n        Leute schikaniert.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Und der H&ouml;gn wahr wirklich \r\n        kein &uuml;berzeugter Anh&auml;nger von Hitler, obwohl er einen Weihegesang \r\n        mit nationalsozialistischem Text geschrieben hat?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong>  Das ist wieder \r\n        was anderes. Er war Musiker und vielleicht haben sie ihn dazu gedr&auml;ngt. \r\n        Es kann sein, dass er davon &uuml;berzeugt war, aber beteiligt hat er \r\n        sich nicht. Es ist mir nichts bekannt, dass er einmal mit SA-Uniform zu \r\n        sehen gewesen w&auml;re.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Ein Widerst&auml;ndler war \r\n        er sicher auch nicht.</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong>  Nein, ein \r\n        Widerst&auml;ndler war er nicht. Ein Widerst&auml;ndler war der Donauer. \r\n        Da ist der Schwannberger mal zu Glasschr&ouml;der Anne gekommen und hat \r\n        gesagt: "Den Donauer haben sie verhaftet, du hast Zeit, du musst \r\n        beten, dass sie ihn nicht hinrichten." Und sie haben ihn nicht hingerichtet. \r\n        Das war der einzige von Ruhmannsfelden, der aktiv im Widerstand war. Sonst \r\n        ist mir keiner gekannt. Da hat es viele Mitl&auml;ufer gegeben. Ein paar \r\n        ganz fanatische.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Das eine hat der \r\n        H&ouml;gn gemacht. Er hat immer einen Gesellschaftstag ausgerichtet jeden Monat \r\n        beim Amberger. Da war der Schwannberger dabei. Die besseren B&uuml;rger. Da \r\n        hat sich die Prominenz einmal im Monat in einem Wirtshaus getroffen. Das \r\n        hat der H&ouml;gn arrangiert. Das war schon w&auml;hrend des Krieges und auch nach \r\n        dem Krieg, hat aber nichts mit dem Hitler zu tun gehabt. Das B&uuml;rgertum \r\n        in Ruhmannsfelden war ja immer etwas Besonderes. Die haben immer nur befohlen. \r\n        Die haben nie eine Biersteuer gezahlt, wenn man nachschaut in den alten \r\n        Akten, und keine Hand- und Spanndienste geleistet. Die Gro&szlig;en, Zitzelsberger, \r\n        Vornehm, Posthalter (Amberger), Aschenbrenner (Stadler), B&auml;rnbr&auml;u (Wilhelm-Br&auml;u), \r\n        die haben nie was geleistet. So hat es mir mal der Niedermeier erz&auml;hlt. \r\n        Die haben immer ein Ausrede gehabt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Das Gloria und das \r\n        Credo ist zu H&ouml;gns Zeiten nie ganz gesungen worden. Da hat der H&ouml;gn immer \r\n        eine &Uuml;berleitung mit der Hand auf Bl&auml;tter geschrieben. Die Pfarrgemeinde \r\n        hatte kein Geld zum Notenkaufen, dann hat er mit der Hand einige Stimmen \r\n        dazugeschrieben, damit die anderen auch ein Blatt kriegen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Wenn man die alten \r\n        Leute reden h&ouml;rt, h&ouml;rt man selten ein schlechtes Wort &uuml;bern H&ouml;gn. Er war \r\n        sehr korrekt und sehr streng. Ich wei&szlig; noch, wie er das letzte Jahr \r\n        Schule gehalten hat. Der Starke Dieter ist am H&ouml;gn vorbeigegangen und \r\n        hat ihn nicht gegr&uuml;&szlig;t. Dann ist er wieder zur&uuml;ckgegangen und batsch hat \r\n        er eine drin gehabt: "Wei&szlig;t du nicht, dass man gr&uuml;&szlig;t." Das war die Zeit \r\n        und die Leute haben gar nicht so Unrecht gehabt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Eins muss dran sein. \r\n        Denn der alten Glasschr&ouml;der und die Oma waren ja absolut gegen den Hitler. \r\n        Sie haben kein Hitler-Bild aufgeh&auml;ngt und nichts. Bei Aufm&auml;rsche haben \r\n        sie immer gesagt: "Ja, ja wenn es nur so bleibt! Ihr werdet euch noch \r\n        anschauen!" Dann h&auml;tten die mit dem H&ouml;gn, wenn er ein Nazi gewesen w&auml;re, \r\n        keinen so innigen Kontakt gehabt, wie sie gehabt haben. Seine K&ouml;chin war \r\n        oft bei uns da, wenn sie Eier oder Milch gebraucht hat oder zum Ratschen \r\n        gekommen ist. Der H&ouml;gn ist nicht auf dem Ratschmarkt gegangen. Ich w&uuml;sste \r\n        nicht, dass der zum Ratschen in ein Haus gegangen w&auml;re. Nicht einmal mit \r\n        dem Herrn Inspektor H&auml;rtl.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">31<a name="31"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: In einem Zeitungsartikel \r\n        steht, dass H&ouml;gn dem M&auml;nnerchor Notenmaterial zur Verf&uuml;gung \r\n        stellen wollte?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">32<a name="32"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Der Danziger hat \r\n        was gekommen. Teilweise selber komponierte Sachen und teilweise gekaufte \r\n        Sachen. Was es genau war, wei&szlig; ich nicht mehr. [... Aufl&ouml;sung des M&auml;nnerchores \r\n        ...] Da m&uuml;ssen ja Unmengen an Notenmaterial vom Danziger dagewesen, \r\n        denn er hat ja selber komponiert. Er hat selber eine Messe geschrieben.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">33<a name="33"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Den H&ouml;gn als Chorregent \r\n        haben Sie nicht mehr erlebt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">34<a name="34"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Nein, aber wir haben \r\n        ihm mal ein St&auml;ndchen gesungen. Glasschr&ouml;der Mathild, Konditor Babett, \r\n        ich, der Lindner und der Danziger ist dabei gewesen. Ich wei&szlig; aber nicht \r\n        mehr zu welchem Anlass. [... Danziger ...] [... Alibuti ...] Ich habe \r\n        mich auch manchmal mit dem Alten H&ouml;gn vom Buchgesch&auml;ft getroffen, der \r\n        hat viel erz&auml;hlt &uuml;ber den Onkel August. Er ist alle Jahre 2 - 3 nach Ruhmannsfelden \r\n        gekommen. Der H&ouml;gn hatte auch eine Buben, Gustl hat er gehei&szlig;en, das war \r\n        ein bisschen ein missratener Sohn. Mit dem hatte er keine Freude. Er muss \r\n        kein unsympathischer Kerl gewesen sein und ist auch nicht gerade auf die \r\n        schiefe Bahn gekommen sein. Aber gelernt hat er halt nichts. Und seine \r\n        Frau ist ja schon fr&uuml;h gestorben. [... Erz&auml;hlungen von Josef Stenkala \r\n        ...]<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">35<a name="35"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Ich muss jetzt nochmal auf H&ouml;gns \r\n        Abschied aus dem Kirchenchor unter Reicheneder zur&uuml;ckkommen. Der Reicheneder \r\n        hat dem H&ouml;gn einen Brief geschrieben und darauf hat er einen Schlaganfall \r\n        bekommen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">36<a name="36"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Nicht gleich darauf, \r\n        aber kurze Zeit sp&auml;ter. Das war die gr&ouml;&szlig;te Missetat vom Reicheneder, die \r\n        er da gemacht hat. In dem Bewusstsein, das er da jemanden hat und die \r\n        ungedingt unterbringen will und wie soll er das machen? Der H&ouml;gn ist alt \r\n        und gefallen hat es ihm auch nicht. Drei Weiber k&ouml;nnen nicht so klingen \r\n        wie ein gro&szlig;er Chor und der Rektor hat ja auch keine Stimme zu singen \r\n        gehabt. Der hat nicht sch&ouml;n gesungen, aber die Orgel hat er beherrscht. \r\n        Wie bringt er die los, dass der die Maria her bringt? Das war dem Reicheneder \r\n        sein Problem. Dann hat er dem H&ouml;gn einen Brief geschrieben. Ich habe dem \r\n        Brief sogar mal gelesen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">37<a name="37"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Was ist da drin gestanden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">38<a name="38"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ich kann es nicht \r\n        mehr genau sagen. Ich wei&szlig; nur, dass er sich f&uuml;r seine Mitarbeit bedankt \r\n        hat. Da ist ein ganz ber&uuml;hmter Satz drin gestanden, den hat die Glasschr&ouml;der \r\n        Mathild immer gesagt. Die zwei Weiber (Glasschr&ouml;der Mathilde, Essigmann \r\n        Barbara) haben auch den Brief gekriegt. Ein Brief an den H&ouml;gn und die \r\n        zwei S&auml;ngerinnen haben auch einen Brief gekommen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">39<a name="39"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: War es dann ein K&uuml;ndigung?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">40<a name="40"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Es war keine K&uuml;ndigung. \r\n        Es hat sich f&uuml;r die Mitarbeit bedankt. Und hat die Sache f&uuml;r erledigt \r\n        angesehen. Ich wei&szlig;, die Glasschr&ouml;der Mathild hat den Brief aufgehoben \r\n        wie ein Schatz. Da ist die Abrechnung noch mit dabei gewesen und das Geld \r\n        auch. Der H&ouml;gn hat aber nicht gro&szlig;es Aufsehen gemacht, er hat den Brief \r\n        nur den Weibern gezeigt.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">41<a name="41"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Hat es da einen Grund gegeben \r\n        f&uuml;r die Briefe?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">42<a name="42"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Weil sie nicht zur \r\n        Chorprobe erschienen sind. Weil sie gesagt haben, dass sie nicht mitsingen. \r\n        Die zwei sind ja auch bescheuert gewesen, auf deutsch gesagt, die haben \r\n        ja auch ihren Teil dazu beigetragen. Sie waren etwas zickig. "Wir haben \r\n        das Recht, wir haben jetzt 20 Jahre seit unserer Kindheit hier gesungen. \r\n        Und jetzt kommen da Neue daher und w&uuml;rden eine neue Methode einf&uuml;hren \r\n        und haben uns nicht gefragt." Und wenn sie gefragt wurden, dann sind sie \r\n        nicht hingegangen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">43<a name="43"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Soviel ich wei&szlig;, haben \r\n        sie fr&uuml;her beim H&ouml;gn in der Wohnung geprobt. Hat der Reicheneder \r\n        dann Proben eingef&uuml;hrt?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">44<a name="44"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ja freilich, weil \r\n        er Chorverst&auml;rkung haben wollte. Zur Pfarrer Bauers Zeit waren immer ein \r\n        paar an den Feiertagen zur Verst&auml;rkung hergekommen, z. B. ein Verwandter \r\n        von Pfarrer Bauer, der bei den Domspatzen war. Das ganze Jahr haben ja \r\n        nur die vier Zwetschgen gesungen. Und fr&uuml;her hat die Glasschr&ouml;der Anne \r\n        noch gesungen, wie sie noch auf den Chor hat gehen k&ouml;nnen. Und die Frau \r\n        Grassl, die hat gekn&ouml;delt wie ein Ofenrohr. Vom Stern Viktor die \r\n        Mutter. Und dann ist die Frau Piehler gekommen und hat sich gleich nach \r\n        dem H&ouml;gn oder noch w&auml;hrend dem H&ouml;gn eingemischt. Sie war geschieden und \r\n        hat dann im Schuldienst keine Anstellung bekommen. Dann sind die nicht \r\n        zu den Proben gegangen. Dann hat der Reicheneder schon darauf gewartet \r\n        und ihnen ein Briefen geschrieben und aus.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">45<a name="45"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Wenn der Reicheneder Proben ansetzt, \r\n        dann hat er doch eine Leiter gebraucht. War das dann der H&ouml;gn?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">46<a name="46"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ja, anscheinend. \r\n        Mit anderen Worten: Der Reicheneder hat darauf gewartet bis sie aufh&ouml;ren. \r\n        Die neuen Leute sollten von der Liedertafel kommen. Der Kirchenchor war \r\n        ein eingefahrenes Ding. Ja tunlichst schauen, dass keine neuen dazu kommen, \r\n        denn da ist es ja ums Geld auch gegangen. Je mehr da dabei gewesen w&auml;ren, \r\n        desto weniger haben sie verdient. Das waren die einzigen Einnahmen, die \r\n        sie gehabt haben alle Vierteljahr. Das waren ja doch so 40 - 50 Mark. \r\n        Der Raster Res hat das nichts ausgemacht. Die ist zu jeder Probe gegangen. \r\n        Die wollte der Reicheneder auch loswerden, hat sie aber nicht los gebracht. \r\n        Bei der war das Problem, erstens eine f&uuml;rchterliche Stimme und dann war \r\n        sie immer zu tief.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">47<a name="47"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Wie war das Verh&auml;ltnis \r\n        von der Maria zum Reicheneder, wie ist er auf sie gekommen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">48<a name="48"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Der Reicheneder \r\n        hat auch seine Vorteile gehabt. Er ist oft ein sturrer Bocke gewesen, \r\n        wie er die Glocken mal nicht l&auml;uten hat lassen, weil der Holler Toni am \r\n        Aschermittwoch im Sautrog gesessen ist und ein Marienlied gesungen hat. \r\n        Ich bin teilweise mit dem Reicheneder gut ausgekommen, teilweise war ich \r\n        nicht mit seiner Meinung. Da haben die Leute immer gesagt: "Der Reicheneder \r\n        hat&acute;s mit ihr." Alles ein Schmarrn. Die Maria war ein Waisenkind \r\n        und da hat sich der Reicheneder gek&uuml;mmert. Und dem Reicheneder seine Schwester \r\n        war wieder eifers&uuml;chtig, die wollte sie nicht. Da war immer der Zwist \r\n        drin. Und wie der Reicheneder gestorben ist, hat die Maria geheiratet. \r\n        Da hat die Raster Res immer gesagt: "Was sie heiratet? Pfui Teufel!" Die \r\n        Maria ist dann mit dem Reicheneder nach Frontenhausen gegangen. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">49<a name="49"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Der Bartascheck hat das gesagt \r\n        mit dem "Mozart von Ruhmannsfelden"?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">50<a name="50"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ja, der war im M&auml;nnerchor \r\n        erster Tenor. Der hat Gesang studiert in Prag am Konservatorium. Er hatte \r\n        schon eine Anstellung in Pforzheim. Dann hat er die Frieda kennen gelernt \r\n        und dann hat er abw&auml;gen m&uuml;ssen.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">51<a name="51"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Wann hat die Glasschr&ouml;der Mathild \r\n        mit dem Singen begonnen?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">52<a name="52"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Die hat gesungen \r\n        wie ein Zeiserl. Die hat das Ave Maria gesungen in Gotteszell, als sie \r\n        nicht mehr beim Reicheneder gesungen hat, wie man es oft im Fernseher \r\n        nicht h&ouml;rt. Sie hat mit dem Mattock gesungen. Der war Gemeindeassistent \r\n        und hat bei den Domspatzen gesungen. Und in Viechtach hat er Operetten \r\n        aufgef&uuml;hrt, Schwarzwaldm&auml;dl etc. Und die zwei haben immer miteinander \r\n        gesungen. Weit und breit gab&acute;s da keine so gute S&auml;ngerin. Eine kr&auml;ftige \r\n        Stimme und ein reiner Sopran.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">53<a name="53"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Sie ist nicht ausgebildet \r\n        worden?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">54<a name="54"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Nein. Den sie wollte, \r\n        hat sie nicht gekommen. Der Kellermeier Franz war ihre gro&szlig;e Liebe. Dann \r\n        ist sie mit 48 Jahren gestorben, hat sich den Fu&szlig; gebrochen und \r\n        hat Leuk&auml;mie gekommen. <br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">55<a name="55"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: Haben sie ein Bild mit August \r\n        H&ouml;gn drauf?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">56<a name="56"></a></td>\r\n    <td valign="top"><p><strong>Johann Glasschr&ouml;der:</strong> Ja, das ist bei \r\n        einer Fronleichnamsprozession aufgenommen worden. Da ist er und der Kirchenchor \r\n        drauf.<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/24/johann_glasschroeder_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/24/johann_glasschroeder_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/a/a2/August_H%C3%B6gn_-_Interviews_24_Interview_mit_Johann_Glasschr%C3%B6der%2C_28.12.2004.ogg', 25, 23),
(25, 'Interview mit Lilo Leuze, 14.1.2005', 'Lilo Leuze', '1931-07-09', '0000-00-00', '0000', 73, '1931', 'Enkelin von August H&ouml;gn', 'Reutlingen (Telefon)', '2005-01-14', '14.01.2005', 2, 2, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">1<a name="1"></a></td>\r\n    <td valign="top"><p><strong>Josef Friedrich: (Niederschrift direkt nach \r\n        dem kurzem Telefonat) Vielen Dank f&uuml;r das Schicken der Bilder?</strong></p></td>\r\n  </tr>\r\n  <tr> \r\n    <td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n    <td valign="top"><p><strong>Lilo Leuze: </strong>Gerne geschehen. Ich nehme \r\n        an das die Bilder zur Hochzeit von August und Emma H&ouml;gn aufgenommen \r\n        wurden. Dass das junge Ehepaar zuerst Zwillinge hatte, die gleich nach \r\n        der Geburt gestorben sind, stimmt. Da haben sie richtig recherchiert. \r\n        Ich hatte selbst auch Zwillinge und da hatte meine Mutter &ouml;fter Kommentare \r\n        abgegeben, wie: "Ob das mal gut gehen wird."<br/>\r\n        <br/>\r\n      </p></td>\r\n  </tr>\r\n</table>', '<p>\r\n<a href="interviews/25/lilo_leuze_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/25/lilo_leuze_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', '', 26, 24);
INSERT INTO `interviews` (`id`, `titel`, `interviewter`, `geburtstag`, `todestag`, `todestagtext`, `interviewalter`, `jahrgang`, `bezug`, `ort`, `datum`, `datumtext`, `dauer`, `dauergesamt`, `text`, `jpg`, `audio`, `next`, `previous`) VALUES (26, 'Interview mit Eva Ertl, 9.2.2005', 'Eva Ertl', '1931-00-00', '0000-00-00', '0000', 73, '1931', 'Gro&szlig;nichte von August H&ouml;gn', 'Deggendorf', '2005-02-09', '09.02.2005', 18, 74, '<table border="0" cellspacing="0" cellpadding="2" width="100%">\r\n<tr>\r\n	<td width ="30" align="center" valign="top">2<a name="2"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nDie alten H&ouml;gns waren ja alle musikalisch. Mein Gro&szlig;vater auch. Er hie&szlig; Ludwig\r\nund war der &auml;lteste der H&ouml;gn Geschwister. Wir hatten auch ein Klavier im Haus.\r\nMein Gro&szlig;vater hatte ein Gesch&auml;ft in Straubing und seine Kinder durften auch\r\nalle Klavier spielen lernen. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">3<a name="3"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Normalerweise h&auml;tte ihr Gro&szlig;vater,\r\n	      da er der &auml;lteste war, das Buchgesch&auml;ft &uuml;bernehmen sollen, warum hat\r\n	      es dann aber der j&uuml;ngste Otto bekommen?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">4<a name="4"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nNormalerweise w&auml;re es meinen Gro&szlig;vater zugestanden. Aber das waren\r\nAngelegenheiten von vergangenen Generationen, aber damals ist, auch wie heute,\r\nauch nicht alles so gelaufen, wie es sein sollte. Das lag an der ganzen alten\r\nFrau H&ouml;gn. Ihr Mann\r\nist gestorben und dann hat sie entscheiden k&ouml;nnen, wem sie das Gesch&auml;ft &uuml;bergibt. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">5<a name="5"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Der Otto war ein Nachz&uuml;gler?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">6<a name="6"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nJa, mein Gro&szlig;vater war der &auml;lteste. Er wurde in den 70iger Jahren geboren. Dann\r\ngab es noch eine Schwester, sie war unverheiratet. Der Josef, der Otto.<br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">7<a name="7"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Haben sie August H&ouml;gn pers&ouml;nlich\r\n	      gekannt? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">8<a name="8"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp; Ja, freilich. Ich bei ein\r\n	    31 Jahrgang. Wir haben ihn in den letzten Lebensjahren besucht. Wir sind\r\n	    nach Ruhmannsfelden rein gefahren. Wir waren bei der Bergmesse und dann\r\n	    haben wir noch einen Abstecher zum Onkel August gemacht. Das Grab hatte\r\n	    er neben uns gehabt und das ist in der Zwischenzeit aufgel&ouml;st. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">9<a name="9"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ich hab geh&ouml;rt, dass\r\n	      der alte Grabstein von den neuen Besitzern von August H&ouml;gns ehemaligem\r\n	      Grab\r\n	      &uuml;bernommen wurde? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">10<a name="10"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\n Ich wei&szlig; nicht, hat das Grab der Onkel Wigg oder die Gertraud von Molo\r\n verkauft? Wer h&auml;tte das sonst pflegen sollen? Es ist schade, aber das bringt\r\n die Zeit mit sich, wenn keiner mehr da ist.<br/>\r\n <br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">11<a name="11"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie was von August\r\n	      H&ouml;gns Sohn? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">12<a name="12"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp; Das m&uuml;sste eigentlich die\r\n	    Gertraud von Molo wissen. Ist der nicht in M&uuml;nchen? <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">13<a name="13"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie sie August H&ouml;gn besucht\r\n	      haben, hatte er da schon einen Schlaganfall? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">14<a name="14"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nNein, da hatte er noch keinen. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">15<a name="15"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Hat er erz&auml;hlt, wenn sie ihn\r\n	      besucht haben, dass er komponiert? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">16<a name="16"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nWir haben uns halt privat unterhalten. Damals war er schon noch r&uuml;stig, aber\r\nschon im Ruhestand. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">17<a name="17"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wann ist dann ihr Opa gestorben?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">18<a name="18"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;1948<br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">19<a name="19"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wie war das Verh&auml;ltnis zwischen\r\n	      den Br&uuml;dern?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">20<a name="20"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp; Gut. Es hat keine Probleme\r\n	    gegeben. [... Theres H&ouml;gn, H&ouml;gn-Grab ...] <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">21<a name="21"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Ist der Andreas H&ouml;gn nach\r\n	      Deggendorf gezogen oder war die H&ouml;gns schon l&auml;nger ans&auml;ssig in Deggendorf?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">22<a name="22"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nDie m&uuml;ssten hier geboren sein. Unmittelbar neben dem H&ouml;gn-Haus war\r\ndas W&auml;rtzinger-Haus von meiner Gro&szlig;mutter m&uuml;tterlicherseits.\r\n[... W&auml;rtzinger-Haus\r\n...] <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">23<a name="23"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Was war ihr Gro&szlig;vater von\r\n	      Beruf? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">24<a name="24"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nDer war auch Buchh&auml;ndler. Er hatte ein Gesch&auml;ft in Straubing und hat sich schon\r\nfest eingerichtet gehabt in Straubing mit Haus und Kindern. Und als der Andreas\r\nH&ouml;gn gestorben ist, ist er noch Deggendorf zur&uuml;ckgegangen. Hat aber das Gesch&auml;ft\r\nnicht bekommen. Die alte Frau H&ouml;gn hat es dem Otto &uuml;bergeben. Mein Gro&szlig;vater\r\nhat alles hergegeben und dann war alles weg. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">25<a name="25"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Der Josef H&ouml;gn ist am Haus\r\n	      geblieben? </strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">26<a name="26"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nEr hat viel im Gesch&auml;ft mitgeholfen. Er war gehandicapt, weil er ganz schlecht\r\ngeh&ouml;rt hat. [... Unfall von Josef H&ouml;gn ...] Mein Gro&szlig;vater hat\r\nsich dann hier ein Haus gesucht und hat beim Deggendorfer Donauboten viel geschrieben. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">27<a name="27"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Wissen Sie wie August H&ouml;gn\r\n	      zum Nationalsozialismus gestanden ist?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">28<a name="28"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nIch glaube, dass er wie mein Gro&szlig;vater eher negativ dazu eingestellt war. <br/>\r\n<br/></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">29<a name="29"></a></td>\r\n	<td valign="top"><p><strong>Josef Friedrich:&nbsp;Der Schwiegersohn von August\r\n	      H&ouml;gn hat unterm Nationalsozialismus eine gro&szlig;e Karriere gemacht?</strong></p></td>\r\n</tr>\r\n<tr>\r\n	<td width ="30" align="center" valign="top">30<a name="30"></a></td>\r\n	<td valign="top"><p><strong>Eva Ertl:</strong>&nbsp;\r\nEr war Staatsanwalt in Deggendorf und dann Gouverneur in Paris, wie Paris besetzt\r\nwurde. [... Anschauen von alten Fotos ...] Das war das Gesch&auml;ft von Ludwig H&ouml;gn\r\nin Straubing. Da ist heute der Hafner drin. <br/>\r\n<br/></p></td>\r\n</tr>\r\n	\r\n</table>', '<p>\r\n<a href="interviews/26/eva_ertl_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="interviews/26/eva_ertl_klein.jpg" border="0" align="absmiddle"></a>&nbsp;&nbsp;\r\n</p>\r\n', 'http://upload.wikimedia.org/wikipedia/commons/9/95/August_H%C3%B6gn_-_Interviews_26_Interview_mit_Eva_Ertl_9.2.2005.ogg', 0, 25);
INSERT INTO `komponisten` (`id`, `titel`, `titel_kurz`, `nachname`, `vorname`, `geburtsdatum`, `geburtsort`, `sterbedatum`, `sterbeort`, `biographie`, `next`, `previous`) VALUES (1, 'Griesbacher, Peter (25.3.1864 - 28.1.1933)', 'Griesbacher, P. ', 'Griesbacher', 'Peter', '1864-03-25', 'Egglham (Niederbayern)', '1933-01-28', 'Regensburg', 'MGG (alt) Seite 910 - 911<br>\r\n<br>\r\nGriesbacher, Peter<br>\r\n<br>\r\n* 25. M&auml;rz 1864 in Egglham (Niederbayern), + 28. Jan. 1933 in Regensburg. In den Passauer Di&ouml;zesanseminarien erhielt er seine erste mus. Ausbildung, die er dann durch eifriges Selbststudium theoretischer Werke vervollst&auml;ndigte. 1886 zum Priester geweiht, &uuml;bte er einige Jahre seelsorgerische T&auml;tigkeit aus. 1894 &uuml;bernahm er die Musikpr&auml;fektenstelle am Studienseminar St. Emmeram in Regensburg, die er aber im folgenden Jahre aus gesundheit- lichen Gr&uuml;nden mit einem Benefizium in Osterhofen vertauschte. 1911 wurde er an die KM.-Schule Regensburg als Lehrer f&uuml;r Kp. berufen und erhielt am Kollegiatstift St. Johann ein Kanonikat. Ab 1906 red. er den Literarischen Handweiser f&uuml;r Freunde der kath. KM. (Regensburg seit 1893), 1919 die nur kurze Zeit ersch. MonatsZs. f&uuml;r die gesamte kath. KM. Sursum corda, und seit 1925 f&uuml;hrte er die Schriftl. der Monatshefte f&uuml;r kath. KM. Einen bedeutenden Ruf geno&szlig; Grie&szlig;bacher als Glockensachverst&auml;ndiger. Er leitete 1927 den ersten Glockengie&szlig;er- und Expertentag in Frankfurt a. M. und legte dabei sein Verfahren zur Erfassung und Fixierung des schwer bestimmbaren Schlagtones &ouml;ffentl. vor.<br>\r\n<br>\r\nWerke. <br>\r\nA. Kompositionen: <br>\r\nDas Gesamtschaffen umfa&szlig;t 318 Werke; davon 254 gedr. m. Op.-Zahlen, 57 ohne Bezeichnung u. 7 ungedr. kirchl. u. weltl. Kompos. u. Bearb v. Werken alter Meister (ausf&uuml;hrliches Werkverz. bei Tremmel, s. u.). <br>\r\nB. Literarische Werke:<br>\r\nKontrapunkt, Lehrbuch u. &Uuml;bungsbuch zum Kp., 2 H., Regensburg 1910, Coppen- rath; Km. Stilistik u. Formenlehre, 4 Bde., ebda. 1912 ff.; Stud. zu Bruckners Te deum m. F&uuml;hrer, Regensburg 1919, Pustet; Glockenmusik (Lehrbuch) m. Nachtrag, Glockenstimmen, Glockenfragen, Regensburg 1927 u. 1929, Coppenrath.<br>\r\n<br>\r\nGriesbacher hat sich als Autodidakt aus den grundlegenden Lehrwerken und durch das Studium der alten Meister eine gewandte Satztechnik zu eigen gemacht. Am Anfang seiner km. Laufbahn steht er auch ganz in der Einflu&szlig;sph&auml;re seiner Vorbilder. Da&szlig; er das Abtr&auml;gliche des blo&szlig;en Nachahmens erkannte und f&uuml;r die KM. durch eine lebendige Textgestaltung mit leitmotivischer Verkn&uuml;pfung, durch Weitung der Harmonik und durch Einbeziehung des Chromas die Ausdrucksmittel der zeitgen&ouml;ss. weltl. Musik zu gewinnen suchte, kennzeichnet ihn als fortschrittlichen Kirchenmusiker. Diese Entwicklung beginnt 1894 mit der Emmeramsmesse op. 14 und f&uuml;hrt &uuml;ber die Missa &quot;Stella maris&quot; op. 141 (1910), die monumentale Friedensmesse op. 200 (1917, uraufgef. 1920), die Messe &quot;Virgo potens&quot; op. 220 (1923) zur gewaltigen Petrus-Canisius- Messe op. 240 (1925). Mehr als bisher werden dabei der Orgel &uuml;ber das Ma&szlig; der gew&ouml;hnlichen Begl. hinaus orchestrale Effekte zugewiesen. Mit der Einbeziehung moderner Harmonik in die Choralbegl. stie&szlig; Grie&szlig;bacher auf ernsten und berechtigten Widerspruch. Die heutige Einstellung zur Chromatik r&uuml;ckt sowohl die Bestrebungen Griesbachers, wie die seinerzeit heftige Kontroverse um seine Kompos. in den Bereich einer sachlichen Beurteilung, wobei vielleicht manches bisher unbeachtete Werk an Bedeutung gewinnt. Neben den kompositorischen Leistungen ist seine T&auml;tigkeit als Musikschriftsteller hervorzuheben; besonders mit den Beitr. zur Glockenkunde hat sich Griesbacher internat. Anerkennung verschafft.<br>\r\n<br>\r\nLiteratur: <br>\r\nM. Tremmel, Peter Griesbacher, Passau 1935;<br>\r\nK. Weinmann , Geschichte der KM., 1913;<br>\r\nO. Ursprung , Kath. KM., Potsdam 1931;<br>\r\nP. Griesbacher, Km. Stilistik u. Formenlehre IV, 1912 ff.;<br>\r\nMoserL, 4/1955;<br>\r\nRiemannL, 11/1929.<br>\r\n<br>\r\nAugust Scharnagl\r\n\r\nGRIESBACHER, Peter, Kirchenmusiker, Musiktheoretiker, * 25.3. 1864 in Egglham (Niederbayern) als Sohn eines M&uuml;llers, ? 28.1. 1933 in Regensburg. - Als Seminarist in Passau erhielt G. seine erste musikalische Ausbildung und empfing dort 1886 die Priesterweihe. Er war dann einige Jahre als Seelsorger t&auml;tig und widmete sich als Autodidakt eifrig musikalischen Studien. 1894 wurde G. Musikpr&auml;fekt am Studienseminar St. Emmeram in Regensburg und war dann aus Gesundheitsgr&uuml;nden l&auml;ngere Zeit Benefiziat in Osterhofen an der Donau, von 1911 an wieder in Regensburg als Kanonikus am Kollegiatstift St. Johann und als Lehrer f&uuml;r Kontrapunkt und Stil-Lehre an der Kirchenmusikschule und Redakteur und Herausgeber kirchenmusikalischer Zeitschriften. - G. wurde bekannt als Komponist zahlreicher musikalischer Werke, Musikschriftsteller und international anerkannter Glockenexperte. \r\n\r\nWerke: 254 Opera (und 64 St&uuml;cke ohne Opuszahl), darunter 40 Messen (Emmeramsmesse op. 14; Benedictus-Messe op. 133; Missa Stella maris op. 141; Friedensmesse op. 200; Missa Virgo potens op. 222; Canisius-Messe op. 240); mehrere Requiem, ein Te Deum, ein Stabat Mater, Motetten, Litaneien, Gradualien, Singspiele, weltl. Kantaten u. Liederzyklen. - Schrr.: Kontrapunkt. &Uuml;bungsbuch, 2 Hh., 1910; Kirchenmusikal. Stilistik u. Formenlehre, 4 Bde., 1912-16; Stud. zu Bruckners Te Deum. F&uuml;hrer, 1919; Glockenmusik. Ein Buch f. Glockenexperten und Glockenfreunde mit Anleitung z. Glockenpr&uuml;fung, 1927. 1929. - Gab heraus: Literar. Handweiser f. Freunde der kath. Kirchenmusik, 1906 ff.; Sursum Corda. Monats-Zschr. f. kath. Kirchenmusik, 1919; Mhh. f. kath. Kirchenmusik, 1925 ff. \r\n\r\nLit.: Karl Weinmann, Gesch. der Kirchenmusik, 1913; - Otto Ursprung, Die kath. Kirchenmusik, 1931; - Max Tremmel, P. G. Sein Leben u. sein Werk, 1935; - Ders., P. G., ein altbayer. Komponist v. Weltruf, in: Heimat zw. Inn u. Rott 11, 1964; - Gesch. der kath. Kirchenmusik, hrsg. v. Karl Gustav Fellerer, II, 1976; - MGG V, 910 f.; - Riemann I, 680; ErgBd. I, 456; - Moser I, 453; - Kosch, KD 1130; - NDB VII, 63; - LThK IV, 1236. \r\nhttp://www.bautz.de/bbkl/g/griesbacher_p.shtml\r\n\r\nInnstadt (rh). Dieser Priester war mit vielen Begabungen gesegnet: Er war Musiker, Komponist, Dozent und Sachverst&auml;ndiger f&uuml;r Glocken. Die Rede ist von Peter Griesbacher, nach dem wir im Stra&szlig;ennamen-R&auml;tsel gefragt haben.\r\nGriesbacher kam 1864 in Egglham zur Welt, wurde 1886 zum Priester geweiht. Seine erste Seelsorge-Stelle f&uuml;hrte ihn an die Wallfahrtskirche Mariahilf. Bereits als Theologiestudent hatte ihn die Liebe zur Musik, vor allem Kirchenmusik, gepackt. Schnell machte er sich als Komponist einen Namen - und zwar einen so guten, dass er 1911 als Dozent an die Kirchenmusikschule Regensburg berufen wurde. Fast gleichzeitig wurde er zum Vikar und sp&auml;ter zum Kanonikus am Kollegiatstift St. Johann in Regensburg ernannt. 1930 w&auml;hlte ihn das Stiftskapitel zum Dekan.\r\nPeter Griesbacher komponierte &uuml;ber 250 Werke, darunter 40 Messen. Manche nennen ihn den &quot;Wagner der Kirchenmusik&quot;. International bekannt wurde er vor allem durch die &quot;Missa stella maris&quot; (op. 141). Gro&szlig;en Ruf erworben hat sich Griesbacher auch als Experte f&uuml;r Kirchen-Glocken. Ida Maria Perner von der gleichnamigen Passauer Glockengie&szlig;erei schrieb uns: &quot;Als Glockensachverst&auml;ndiger leitete er 1927 den ersten Glockengie&szlig;er- und Expertentag in Frankfurt. Er befasste sich in &uuml;ber 20 Werken mit dem geheimnisvollen Wesen des Glockenklangs und wurde sogar ins europ&auml;ische Ausland und nach Amerika gerufen, um Glocken zu begutachten.&quot;\r\nGriesbacher starb im Januar 1933, vermutlich an Magen- Darmkrebs. Beigesetzt ist er in Regensburg, wo er zuletzt wirkte. Der in Passau nach ihm benannte, 1958 angelegte Peter-Griesbacher-Weg zweigt gegen&uuml;ber dem Exerzitienhaus Mariahilf von der Sch&auml;rdinger Stra&szlig;e ab und m&uuml;ndet sp&auml;ter wieder in selbige ein. &quot;Urspr&uuml;nglich sollte sie nach Johann Strau&szlig; benannt werden&quot;, so Stadtheimatpfleger Franz Mader, &quot;doch der Kulturauschuss beschloss mit sechs zu drei Stimmen, sie nach einem einheimischen Musiker zu benennen&quot;.\r\nDie mit Abstand ausf&uuml;hrlichste und auch beste Antwort lieferte diesmal eine Sch&uuml;lerin des Auersperg- Gymnasiums: Ursula Meier aus der K 12, wo sie den Leistungskurs Musik belegt. Nicht der einzige Grund, warum sie sich f&uuml;r Peter Griesbacher interessiert. &quot;Als begeisterte Domchor-S&auml;ngerin und Domorchester-Geigerin freue ich mich, dass das Stra&szlig;ennamenr&auml;tsel diesmal einen Komponisten und Musiker aus unserer Gegend betrifft. Zudem steht mein Elternhaus am Mariahilfberg in der N&auml;he des Peter-Griesbacher-Weges.&quot; Ursula Meier wei&szlig; auch, welche Griesbacher-Messe im Passauer Dom als n&auml;chste zur Auff&uuml;hrung kommt: Es ist die Missa Americana (op. 235), am 16. Juni um 9.30 Uhr im Rahmen der Europ&auml;ischen Wochen.\r\nhttp://www.kmz.de/presse/berichte/pnp20020413.htm', 82, 48),
