INSERT INTO `kompositionen` (`id`, `bildverweis`, `ssort`, `titel`, `titeloriginal`, `imslp`, `wikicommons`, `titel_def`, `gattungen_id`, `gatt_nr`, `tonart`, `opus`, `opuszusatz`, `opusnummer`, `titelliedtextja`, `kom_fundort`, `fundorttext`, `status`, `statustext`, `statusgif`, `bes_ser`, `bes_grup`, `bes_grup_u_inst`, `bes_ser2`, `bes_grup2`, `bes_grup_u_inst2`, `textdichter`, `liedtext`, `deutschertext_ja`, `jpg`, `pdf`, `gif`, `file`, `previous`, `next`) VALUES (1, 0, 1, '&quot;Laurentius&quot;-Messe C-Dur op. 14', 'Leichte Messe zu Ehren des hl. Laurentius mit Offertorium f&uuml;r 4 st. gem. Chor, Orgel u. 4. st. Bla&szlig;musik (ad libid.) op. 14', 'http://imslp.org/wiki/Mass_in_C_major_%27Laurentius%27,_Op.14_(H%C3%B6gn,_August)', 'August_H%C3%B6gn_-_Kompositionen;_Laurentius-Messe_(Manuskript).pdf', '&quot;Laurentius&quot;-Messe', 1, 0, '1', 14, '', 0, 0, 1, 'Notenschrank in der Pfarrkirche', '', '', '<img src="../bilder/pfeile/editiertxxxx.png" width="18" height="18">', 'a:3:{s:3:"gch";a:4:{s:1:"s";i:1;s:1:"a";i:1;s:1:"t";i:1;s:1:"b";i:1;}s:3:"bbl";a:4:{s:3:"tr1";i:1;s:3:"tr2";i:1;s:3:"tho";i:1;s:3:"tbn";i:1;}s:3:"org";a:1:{s:3:"ped";i:1;}}', 'gemischter Chor, Blechbl&auml;ser, Orgel', 'gemischter Chor (Sopran, Alt, Tenor, Bass); Blechbl&auml;ser (Trompete I, Trompete II, Tenorhorn, Posaune); Orgel (mit Pedal)', '', '', '', '', 'Kyrie:<br>\r\n<br>\r\nKyrie eleison,<br>\r\nChriste eleison,<br>\r\nKyrie eleison.<br>\r\n<br>\r\nGloria:<br>\r\n<br>\r\nGloria in excelsis Deo. <br>\r\nEt in terra pax hominibus bonae voluntatis.<br>\r\nLaudamus te. <br>\r\nBenedicimus te. <br>\r\nAdoramus te. <br>\r\nGlorificamus te. <br>\r\nGratias agimus tibi propter magnam gloriam tuam. <br>\r\nDomine Deus, Rex coelestis, Deus Pater omnipotens. <br>\r\nDomine Fili unigenite, Jesus Christe. <br>\r\nDomine Deus, Agnus Dei Filius Patris.<br>\r\nQui tollis peccata mundi, miserere nobis. <br>\r\nQui tollis peccata mundi, suscipe deprecationem nostram. <br>\r\nQui sedes ad dexteram Patris, miserere nobis. <br>\r\nQuoniam tu solus sanctus. <br>\r\nTu solus Dominus. <br>\r\nTu solus Altissimus, Jesu Christe. <br>\r\nCum Sancto Spiritu, in gloria Dei Patris. <br>\r\nAmen.<br>\r\n   <br>\r\nCredo:<br>\r\n<br>\r\nCredo in unum Deum, Patrem omnipotentem, factorem coeli et terra, visibilium omnium et invisibilium.<br>\r\nEt in unum Dominum Jesum Christum, Filium Dei unigenitum.<br>\r\nEt ex Patre natum ante omnia saecula.<br>\r\nDeum de Deo, lumen de lumine, Deum verum de Deo vero, genitum non factum consubstantialem Patri: per quem omnia facta sunt.<br>\r\nQui propter nos homines et propter nostram salutem descendit de coelis.<br>\r\nEt incarnatus est de Spiritu Sancto ex Maria Virgine, et homo factus est.<br>\r\nCrucifixus etiam pro nobis sub Pontio Pilato, passus et sepultus est.<br>\r\nEt resurrexit tertia die, secundum Scripturas.<br>\r\nEt ascendit in coelum, sedet ad dexteram Patris.<br>\r\nEt iterum venturus est cum gloria, judicare vivos et mortuos, cujus regni non erit finis.<br>\r\nEt in Spiritum Sanctum, Dominum et vivificantem: qui ex Patre Filioque procedit.<br>\r\nQui cum Patre et Filio simul adoratur et conglorificatur: qui locutus est per Prophetas.<br>\r\nEt unam, sanctam, catholicam et apostolicam Ecclesiam. Confiteor unum baptisma in remissionem peccatorum.<br>\r\nEt exspecto resurrctionem mortuorum. Et vitam venturi saeculi.<br>\r\nAmen.<br>\r\n   <br>\r\nSanctus:<br>\r\n<br>\r\nSanctus, Sanctus, Sanctus Dominus Deus Sabaoth. <br>\r\nPleni sunt coeli et terra gloria tua. <br>\r\nHosanna in excelsis.<br>\r\n   <br>\r\nBenedictus:<br>\r\n<br>\r\nBenedictus, qui venit in nomine Domini.<br>\r\nHosanna in excelsis.<br>\r\n   <br>\r\nAgnus Dei:<br>\r\n<br>\r\nAgnus Dei, qui tollis peccata mundi: miserere nobis.<br>\r\nAgnus Dei, qui tollis peccata mundi: miserere nobis.<br>\r\nAgnus Dei, qui tollis peccata mundi: dona nobis pacem', 0, '<p><a href="1/0019_gross.jpg?phpMyAdmin=5BxldHDXX5DRu6TWG1gu1Ts0nqa"><img src="1/0019_klein.jpg" bord